  activeTab,
  setActiveTab,
  draggedItem,
  setDraggedItem,
  setIsDragging,
  addToTimeline,
  formatTime,
}) => {
  const fileInputRef = useRef(null);
  const audioInputRef = useRef(null);
  const imageInputRef = useRef(null);
  
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState(null);
  const [compressionProgress, setCompressionProgress] = useState(0);
  const [isCompressing, setIsCompressing] = useState(false);

  // File size limits (in bytes)
  const FILE_SIZE_LIMITS = {
    videos: 100 * 1024 * 1024, // 100MB
    audios: 100 * 1024 * 1024, // 100MB  
    images: 100 * 1024 * 1024  // 100MB
  };

  // Format file size for display
  const formatFileSize = (bytes) => {
    if (bytes === 0) return '0 Bytes';
    const k = 1024;
    const sizes = ['Bytes', 'KB', 'MB', 'GB'];
    const i = Math.floor(Math.log(bytes) / Math.log(k));
    return parseFloat((bytes / Math.pow(k, i)).toFixed(2)) + ' ' + sizes[i];
  };

  // Check if file size is acceptable
  const checkFileSize = (file, type) => {
    const limit = FILE_SIZE_LIMITS[type];
    if (file.size > limit) {
      return {
        valid: false,
        message: `File "${file.name}" is ${formatFileSize(file.size)}. Maximum allowed: ${formatFileSize(limit)}`
      };
    }
    return { valid: true };
  };

  // Compress video file
  const compressVideo = async (file) => {
    return new Promise((resolve, reject) => {
      const canvas = document.createElement('canvas');
      const ctx = canvas.getContext('2d');
      const video = document.createElement('video');
      
      video.onloadeddata = () => {
        // Set canvas dimensions (reduce for compression)
        const maxWidth = 1280;
        const maxHeight = 720;
        let { videoWidth, videoHeight } = video;
        
        if (videoWidth > maxWidth || videoHeight > maxHeight) {
          const ratio = Math.min(maxWidth / videoWidth, maxHeight / videoHeight);
          videoWidth *= ratio;
          videoHeight *= ratio;
        }
        
        canvas.width = videoWidth;
        canvas.height = videoHeight;
        
        // Create MediaRecorder for compression
        const stream = canvas.captureStream(30); // 30 FPS
        const mediaRecorder = new MediaRecorder(stream, {
          mimeType: 'video/webm;codecs=vp9',
          videoBitsPerSecond: 2000000 // 2 Mbps
        });
        
        const chunks = [];
        mediaRecorder.ondataavailable = (e) => chunks.push(e.data);
        
        mediaRecorder.onstop = () => {
          const compressedBlob = new Blob(chunks, { type: 'video/webm' });
          const compressedFile = new File([compressedBlob], 
            file.name.replace(/\.[^/.]+$/, '_compressed.webm'), 
            { type: 'video/webm' }
          );
          resolve(compressedFile);
        };
        
        let currentTime = 0;
        const duration = video.duration;
        
        const drawFrame = () => {
          if (currentTime < duration) {
            video.currentTime = currentTime;
            ctx.drawImage(video, 0, 0, videoWidth, videoHeight);
            currentTime += 1/30; // 30 FPS
            setCompressionProgress((currentTime / duration) * 100);
            requestAnimationFrame(drawFrame);
          } else {
            mediaRecorder.stop();
          }
        };
        
        mediaRecorder.start();
        drawFrame();
      };
      
      video.onerror = () => reject(new Error('Failed to load video for compression'));
      video.src = URL.createObjectURL(file);
    });
  };

  // Enhanced file upload handler
  const handleFileUpload = async (files, type) => {
    setError(null);
    const fileArray = Array.from(files);
    
    // Check all files first
    const oversizedFiles = [];
    const validFiles = [];
    
    fileArray.forEach(file => {
      const sizeCheck = checkFileSize(file, type);
      if (sizeCheck.valid) {
        validFiles.push(file);
      } else {
        oversizedFiles.push({ file, message: sizeCheck.message });
      }
    });
    
    // Show errors for oversized files
    if (oversizedFiles.length > 0) {
      setError(`${oversizedFiles.length} file(s) exceeded size limit:\n${oversizedFiles.map(f => f.message).join('\n')}`);
    }
    
    // Process valid files
    for (const file of validFiles) {
      try {
        setIsLoading(true);
        
        let processedFile = file;
        
        // Offer compression for large video files (over 50MB)
        if (type === 'videos' && file.size > 50 * 1024 * 1024) {
          const shouldCompress = window.confirm(
            `"${file.name}" is ${formatFileSize(file.size)}. Would you like to compress it to reduce file size?`
          );
          
          if (shouldCompress) {
            setIsCompressing(true);
            setCompressionProgress(0);
            try {
              processedFile = await compressVideo(file);
              console.log(`Compressed from ${formatFileSize(file.size)} to ${formatFileSize(processedFile.size)}`);
            } catch (compressionError) {
              console.error('Compression failed:', compressionError);
              setError(`Failed to compress ${file.name}. Using original file.`);
            } finally {
              setIsCompressing(false);
              setCompressionProgress(0);
            }
          }
        }
        
        // Create media item (simulating MediaStorage.saveFile)
        const mediaItem = {
          id: Date.now() + Math.random(),
          name: processedFile.name,
          url: URL.createObjectURL(processedFile),
          blob: processedFile,
          size: processedFile.size,
          type: processedFile.type,
          duration: type !== 'images' ? await getMediaDuration(processedFile) : 0,
          width: type === 'images' ? await getImageDimensions(processedFile).width : 0,
          height: type === 'images' ? await getImageDimensions(processedFile).height : 0,
        };
        
        setMediaLibrary((prev) => ({
          ...prev,
          [type]: [...prev[type], mediaItem],
        }));
        
      } catch (error) {
        console.error(`Failed to save ${file.name}:`, error);
        setError(`Failed to save ${file.name}: ${error.message}`);
      }
    }
    
    setIsLoading(false);
  };

  // Get media duration
  const getMediaDuration = (file) => {
    return new Promise((resolve) => {
      const media = file.type.startsWith('video/') ? 
        document.createElement('video') : 
        document.createElement('audio');
      
      media.onloadedmetadata = () => {
        resolve(media.duration || 0);
      };
      
      media.onerror = () => resolve(0);
      media.src = URL.createObjectURL(file);
    });
  };

  // Get image dimensions
  const getImageDimensions = (file) => {
    return new Promise((resolve) => {
      const img = new Image();
      img.onload = () => {
        resolve({ width: img.width, height: img.height });
      };
      img.onerror = () => resolve({ width: 0, height: 0 });
      img.src = URL.createObjectURL(file);
    });
  };

  const handleVideoUpload = (event) => {
    if (event.target.files) {
      handleFileUpload(event.target.files, 'videos');
      event.target.value = '';
    }
  };

  const handleAudioUpload = (event) => {
    if (event.target.files) {
      handleFileUpload(event.target.files, 'audios');
      event.target.value = '';
    }
  };

  const handleImageUpload = (event) => {
    if (event.target.files) {
      handleFileUpload(event.target.files, 'images');
      event.target.value = '';
    }
  };

  const removeFromLibrary = (itemId, type) => {
    setMediaLibrary((prev) => ({
      ...prev,
      [type]: prev[type].filter((item) => item.id !== itemId),
    }));
  };

  const handleDragStart = (e, mediaItem) => {
    setDraggedItem(mediaItem);
    setIsDragging(true);
    e.dataTransfer.effectAllowed = "copy";
    e.dataTransfer.setData("text/plain", "");
  };

  const handleDragEnd = () => {
    setDraggedItem(null);
    setIsDragging(false);
  };

  const clearError = () => {
    setError(null);
  };

  return (
    <div className="w-80 min-w-72 max-w-80 bg-white border-2 border-blue-200 flex flex-col rounded-2xl shadow-lg overflow-hidden flex-shrink-0">
      <div className="bg-blue-50 px-4 py-3 border-b border-blue-100">
        <h3 className="font-bold text-blue-800 flex items-center">
          <Upload size={18} className="mr-2" />
          Enhanced Media Library
          {(isLoading || isCompressing) && <Loader2 size={16} className="ml-2 animate-spin" />}
        </h3>
      </div>

      {/* Compression Progress */}
      {isCompressing && (
        <div className="bg-blue-50 px-4 py-2 border-b border-blue-100">
          <div className="flex items-center text-sm text-blue-700">
            <Zap size={14} className="mr-2" />
            Compressing video... {Math.round(compressionProgress)}%
          </div>
          <div className="w-full bg-blue-200 rounded-full h-2 mt-1">
            <div 
              className="bg-blue-600 h-2 rounded-full transition-all duration-300"
              style={{ width: `${compressionProgress}%` }}
            ></div>
          </div>
        </div>
      )}

      {/* Error Display */}
      {error && (
        <div className="bg-red-50 border-l-4 border-red-400 p-3 mx-4 mt-2 rounded">
          <div className="flex items-start">
            <AlertTriangle size={16} className="text-red-400 mr-2 mt-0.5 flex-shrink-0" />
            <div className="flex-1">
              <pre className="text-sm text-red-700 whitespace-pre-wrap">{error}</pre>
            </div>
            <button
              onClick={clearError}
              className="text-red-400 hover:text-red-600 ml-2"
            >
              ×
            </button>
          </div>
        </div>
      )}

      <div className="p-4 pb-0">
        <div className="flex bg-gray-100 rounded-xl p-1">
          {[
            { id: "videos", name: "Videos", icon: Video },
            { id: "audios", name: "Audio", icon: Music },
            { id: "images", name: "Images", icon: Image },
          ].map((tab) => {
            const IconComponent = tab.icon;
            return (
              <button
                key={tab.id}
                onClick={() => setActiveTab(tab.id)}
                className={`flex-1 p-3 text-sm font-medium transition-all duration-200 rounded-lg ${
                  activeTab === tab.id
                    ? "bg-white text-blue-600 shadow-sm"
                    : "text-gray-600 hover:text-gray-800"
                }`}
              >
                <IconComponent size={16} className="mr-2 inline" />
                {tab.name}
              </button>
            );
          })}
        </div>
      </div>

      <div className="p-4">
        <button
          onClick={() => {
            if (isLoading || isCompressing) return;
            if (activeTab === "videos") fileInputRef.current?.click();
            else if (activeTab === "audios") audioInputRef.current?.click();
            else imageInputRef.current?.click();
          }}
          disabled={isLoading || isCompressing}
          className={`w-full p-6 border-2 border-dashed border-gray-300 rounded-2xl hover:border-blue-400 hover:bg-blue-50/50 transition-all duration-200 text-center group ${
            (isLoading || isCompressing) ? 'opacity-50 cursor-not-allowed' : ''
          }`}
        >
          <div className="w-12 h-12 bg-gray-100 rounded-xl flex items-center justify-center mx-auto mb-3 group-hover:bg-blue-100 transition-colors">
            {(isLoading || isCompressing) ? (
              <Loader2 size={20} className="text-blue-500 animate-spin" />
            ) : (
              <Upload
                size={20}
                className="text-gray-500 group-hover:text-blue-500"
              />
            )}
          </div>
          <span className="text-sm font-medium text-gray-700 group-hover:text-blue-600">
            {isCompressing ? 'Compressing...' : 
             isLoading ? 'Uploading...' : 
             `Upload ${activeTab === "videos" ? "Video" : activeTab === "audios" ? "Audio" : "Images"}`}
          </span>
          <p className="text-xs text-gray-500 mt-1">
            {activeTab === "videos"
              ? "MP4, AVI, MOV up to 100MB (compression available)"
              : activeTab === "audios"
              ? "MP3, WAV, M4A up to 100MB"
              : "JPG, PNG, GIF up to 100MB"}
          </p>
        </button>
      </div>

      <input
        ref={fileInputRef}
        type="file"
        accept="video/*"
        multiple
        onChange={handleVideoUpload}
        className="hidden"
        disabled={isLoading || isCompressing}
      />
      <input
        ref={audioInputRef}
        type="file"
        accept="audio/*"
        multiple
        onChange={handleAudioUpload}
        className="hidden"
        disabled={isLoading || isCompressing}
      />
      <input
        ref={imageInputRef}
        type="file"
        accept="image/*"
        multiple
        onChange={handleImageUpload}
        className="hidden"
        disabled={isLoading || isCompressing}
      />

      <div className="flex-1 overflow-y-auto px-4 pb-4">
        <div className="space-y-2">
          {(mediaLibrary[activeTab] || []).map((item) => (
            <div
              key={item.id}
              draggable="true"
              onDragStart={(e) => handleDragStart(e, item)}
              onDragEnd={handleDragEnd}
              className={`flex items-center p-3 bg-gray-50 rounded-xl hover:bg-gray-100 hover:shadow-sm transition-all duration-200 cursor-grab active:cursor-grabbing group ${
                draggedItem?.id === item.id ? "opacity-50" : ""
              }`}
              onClick={() => addToTimeline && addToTimeline(item)}
            >
              <div className="w-12 h-12 bg-white rounded-xl flex items-center justify-center mr-3 shadow-sm group-hover:shadow">
                {activeTab === "videos" && (
                  <Video size={20} className="text-blue-500" />
                )}
                {activeTab === "audios" && (
                  <Music size={20} className="text-green-500" />
                )}
                {activeTab === "images" && (
                  <Image size={20} className="text-purple-500" />
                )}
              </div>
              <div className="flex-1 min-w-0">
                <p className="text-sm font-medium text-gray-900 truncate">
                  {item.name}
                </p>
                <div className="flex items-center text-xs text-gray-500 space-x-2">
                  <span>
                    {activeTab === "images"
                      ? `${item.width || 0}×${item.height || 0}`
                      : formatTime ? formatTime(item.duration || 0) : '0:00'}
                  </span>
                  {item.size && (
                    <>
                      <span>•</span>
                      <span>{formatFileSize(item.size)}</span>
                    </>
                  )}
                </div>
              </div>
              <button
                onClick={(e) => {
                  e.stopPropagation();
                  removeFromLibrary(item.id, activeTab);
                }}
                className="opacity-0 group-hover:opacity-100 p-2 text-red-400 hover:text-red-600 hover:bg-red-50 rounded-lg transition-all duration-200"
              >
                <Trash2 size={14} />
              </button>
            </div>
          ))}
          {(!mediaLibrary[activeTab] || mediaLibrary[activeTab].length === 0) && !isLoading && !isCompressing && (
            <div className="text-center py-8">
              <div className="w-16 h-16 bg-gray-100 rounded-2xl flex items-center justify-center mx-auto mb-3">
                {activeTab === "videos" && (
                  <Video size={24} className="text-gray-400" />
                )}
                {activeTab === "audios" && (
                  <Music size={24} className="text-gray-400" />
                )}
                {activeTab === "images" && (
                  <Image size={24} className="text-gray-400" />
                )}
              </div>
              <p className="text-sm text-gray-500">
                No {activeTab} uploaded yet
              </p>
              <p className="text-xs text-gray-400 mt-1">
                Click upload button above
              </p>
            </div>
          )}
        </div>
      </div>
    </div>
  );
};
