import React, { useState, useEffect } from 'react';
import { loadStripe } from '@stripe/stripe-js';
import { Check, Star, Zap, Building, Crown, Loader2 } from 'lucide-react';

// Инициализация Stripe
const stripePromise = loadStripe(process.env.REACT_APP_STRIPE_PUBLISHABLE_KEY);

const Pricing = () => {
  const [plans, setPlans] = useState([]);
  const [loading, setLoading] = useState(true);
  const [processingPlan, setProcessingPlan] = useState(null);
  const [userInfo, setUserInfo] = useState(null);
  const [error, setError] = useState(null);

  useEffect(() => {
    fetchPlans();
    fetchUserInfo();
    
    // Проверяем, была ли отменена оплата
    const urlParams = new URLSearchParams(window.location.search);
    if (urlParams.get('canceled') === 'true') {
      setError('Payment was canceled. You can try again anytime.');
      // Очищаем URL через 3 секунды
      setTimeout(() => {
        window.history.replaceState({}, document.title, window.location.pathname);
        setError(null);
      }, 3000);
    }
  }, []);

  const fetchPlans = async () => {
    try {
      // ✅ Используем полный URL для разработки
      const baseURL = process.env.NODE_ENV === 'development' 
        ? 'http://localhost:5000' 
        : '';
      
      const response = await fetch(`${baseURL}/api/payments/plans`);
      const data = await response.json();
      
      if (data.success) {
        setPlans(data.data.plans);
      } else {
        setError('Failed to load plans');
      }
    } catch (error) {
      console.error('Error fetching plans:', error);
      setError('Failed to connect to server. Make sure backend is running on port 5000.');
    } finally {
      setLoading(false);
    }
  };

  const fetchUserInfo = async () => {
    try {
      const token = localStorage.getItem('token');
      if (!token) return;

      // ✅ Используем полный URL для разработки
      const baseURL = process.env.NODE_ENV === 'development' 
        ? 'http://localhost:5000' 
        : '';

      const response = await fetch(`${baseURL}/api/auth/me`, {
        headers: {
          'Authorization': `Bearer ${token}`
        }
      });
      const data = await response.json();
      
      if (data.success) {
        setUserInfo(data.data.user);
      }
    } catch (error) {
      console.error('Error fetching user info:', error);
    }
  };

  const handleUpgrade = async (priceId, planId) => {
    try {
      setProcessingPlan(planId);
      setError(null);
      
      const token = localStorage.getItem('token');
      if (!token) {
        setError('Please login first');
        setProcessingPlan(null);
        return;
      }

      console.log('Creating checkout session for:', { priceId, planId });

      // ✅ Используем полный URL для разработки
      const baseURL = process.env.NODE_ENV === 'development' 
        ? 'http://localhost:5000' 
        : '';

      const response = await fetch(`${baseURL}/api/payments/create-checkout-session`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${token}`
        },
        body: JSON.stringify({
          priceId,
          planId
        })
      });

      const data = await response.json();
      console.log('Checkout session response:', data);
      
      if (data.success) {
        const stripe = await stripePromise;
        
        if (!stripe) {
          throw new Error('Stripe failed to load');
        }

        const { error } = await stripe.redirectToCheckout({
          sessionId: data.data.sessionId
        });

        if (error) {
          console.error('Stripe redirect error:', error);
          setError('Payment failed: ' + error.message);
        }
      } else {
        setError(data.error || 'Failed to create checkout session');
      }
    } catch (error) {
      console.error('Upgrade error:', error);
      setError('Something went wrong: ' + error.message);
    } finally {
      setProcessingPlan(null);
    }
  };

  const getPlanIcon = (planId) => {
    switch (planId) {
      case 'creator': return <Star className="w-8 h-8 text-blue-600" />;
      case 'pro': return <Zap className="w-8 h-8 text-purple-600" />;
      case 'agency': return <Building className="w-8 h-8 text-orange-600" />;
      default: return <Star className="w-8 h-8 text-gray-600" />;
    }
  };

  const getPlanStyles = (planId) => {
    switch (planId) {
      case 'creator': 
        return {
          border: 'border-blue-200 hover:border-blue-300',
          button: 'bg-blue-600 hover:bg-blue-700'
        };
      case 'pro': 
        return {
          border: 'border-purple-200 hover:border-purple-300 ring-2 ring-purple-200',
          button: 'bg-purple-600 hover:bg-purple-700'
        };
      case 'agency': 
        return {
          border: 'border-orange-200 hover:border-orange-300',
          button: 'bg-orange-600 hover:bg-orange-700'
        };
      default: 
        return {
          border: 'border-gray-200',
          button: 'bg-gray-600 hover:bg-gray-700'
        };
    }
  };

  if (loading) {
    return (
      <div className="min-h-screen flex items-center justify-center">
        <div className="text-center">
          <Loader2 className="animate-spin h-12 w-12 text-blue-600 mx-auto mb-4" />
          <p className="text-gray-600">Loading plans...</p>
        </div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="min-h-screen flex items-center justify-center">
        <div className="text-center">
          <div className="bg-red-50 border border-red-200 rounded-lg p-6 max-w-md">
            <h3 className="text-lg font-medium text-red-800 mb-2">Error</h3>
            <p className="text-red-600">{error}</p>
            <button 
              onClick={() => window.location.reload()}
              className="mt-4 px-4 py-2 bg-red-600 text-white rounded-md hover:bg-red-700"
            >
              Try Again
            </button>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gradient-to-br from-gray-50 to-gray-100 py-12 px-4">
      <div className="max-w-7xl mx-auto">
        {/* Header */}
        <div className="text-center mb-12">
          <h1 className="text-4xl font-bold text-gray-900 mb-4">
            Choose Your Plan
          </h1>
          <p className="text-xl text-gray-600 max-w-3xl mx-auto">
            Create amazing content with AI-powered scripts, voiceovers, and thumbnails.
          </p>
        </div>

        {/* Current Plan Status */}
        {userInfo && (
          <div className="mb-8 bg-white border border-gray-200 rounded-lg p-6 max-w-2xl mx-auto">
            <div className="flex items-center justify-between">
              <div>
                <div className="flex items-center space-x-2 mb-2">
                  <Crown className="w-5 h-5 text-yellow-500" />
                  <p className="font-medium text-gray-900">
                    Current Plan: <span className="capitalize text-blue-600">{userInfo.subscription}</span>
                  </p>
                </div>
                <div className="text-sm text-gray-600 grid grid-cols-1 md:grid-cols-3 gap-4">
                  <div>
                    <span className="font-medium">Scripts:</span> {userInfo.usage?.scriptsGenerated || 0}/{userInfo.limits?.scriptsGenerated === -1 ? '∞' : userInfo.limits?.scriptsGenerated || 0}
                  </div>
                  <div>
                    <span className="font-medium">Audio:</span> {Math.floor((userInfo.usage?.audioGenerated || 0)/1000)}k/{userInfo.limits?.audioGenerated === -1 ? '∞' : Math.floor((userInfo.limits?.audioGenerated || 0)/1000)}k chars
                  </div>
                  <div>
                    <span className="font-medium">Thumbnails:</span> {userInfo.usage?.thumbnailsGenerated || 0}/{userInfo.limits?.thumbnailsGenerated === -1 ? '∞' : userInfo.limits?.thumbnailsGenerated || 0}
                  </div>
                </div>
              </div>
            </div>
          </div>
        )}

        {/* Error Message */}
        {error && (
          <div className="mb-8 bg-red-50 border border-red-200 rounded-lg p-4 max-w-2xl mx-auto">
            <p className="text-red-700">{error}</p>
          </div>
        )}

        {/* Free Plan */}
        <div className="mb-8">
          <div className="bg-white rounded-lg border border-gray-200 shadow-sm p-6 max-w-md mx-auto">
            <div className="text-center">
              <h3 className="text-xl font-semibold text-gray-900 mb-2">Starter</h3>
              <div className="mb-4">
                <span className="text-4xl font-bold text-gray-900">$0</span>
                <span className="text-gray-600">/month</span>
              </div>
              <p className="text-gray-600 mb-6">Perfect for trying out our platform</p>
              
              <ul className="text-left space-y-3 mb-6">
                <li className="flex items-center">
                  <Check className="w-4 h-4 text-green-500 mr-3" />
                  <span className="text-sm">3 scripts per month</span>
                </li>
                <li className="flex items-center">
                  <Check className="w-4 h-4 text-green-500 mr-3" />
                  <span className="text-sm">10 minutes of audio</span>
                </li>
                <li className="flex items-center">
                  <Check className="w-4 h-4 text-green-500 mr-3" />
                  <span className="text-sm">5 thumbnails</span>
                </li>
              </ul>

              <button 
                className="w-full py-2 px-4 bg-gray-600 text-white rounded-md transition-colors cursor-not-allowed"
                disabled
              >
                {userInfo?.subscription === 'free' ? 'Current Plan' : 'Free Forever'}
              </button>
            </div>
          </div>
        </div>

        {/* Paid Plans */}
        <div className="grid md:grid-cols-3 gap-8 max-w-6xl mx-auto">
          {plans.map((plan) => {
            const styles = getPlanStyles(plan.planId);
            const isCurrentPlan = userInfo?.subscription === plan.planId;
            const isProcessing = processingPlan === plan.planId;
            
            return (
              <div 
                key={plan.id} 
                className={`bg-white rounded-lg border-2 shadow-sm p-6 relative transition-all ${styles.border}`}
              >
                {plan.planId === 'pro' && (
                  <div className="absolute -top-3 left-1/2 transform -translate-x-1/2">
                    <span className="bg-purple-600 text-white px-3 py-1 rounded-full text-sm font-medium">
                      Most Popular
                    </span>
                  </div>
                )}

                <div className="text-center">
                  {/* Icon */}
                  <div className="flex justify-center mb-4">
                    <div className="p-3 bg-gray-50 rounded-full">
                      {getPlanIcon(plan.planId)}
                    </div>
                  </div>

                  {/* Plan Name */}
                  <h3 className="text-xl font-semibold text-gray-900 mb-2">
                    {plan.name}
                  </h3>

                  {/* Price */}
                  <div className="mb-4">
                    <span className="text-4xl font-bold text-gray-900">
                      ${(plan.amount / 100).toFixed(0)}
                    </span>
                    <span className="text-gray-600">/{plan.interval}</span>
                  </div>

                  {/* Description */}
                  <p className="text-gray-600 mb-6">{plan.description}</p>
                  
                  {/* Features */}
                  <ul className="text-left space-y-3 mb-6">
                    {plan.features.map((feature, index) => (
                      <li key={index} className="flex items-center">
                        <Check className="w-4 h-4 text-green-500 mr-3 flex-shrink-0" />
                        <span className="text-sm">{feature}</span>
                      </li>
                    ))}
                  </ul>

                  {/* CTA Button */}
                  <button
                    onClick={() => handleUpgrade(plan.id, plan.planId)}
                    disabled={isCurrentPlan || isProcessing}
                    className={`w-full py-3 px-4 text-white rounded-md transition-colors font-medium ${
                      isCurrentPlan
                        ? 'bg-gray-400 cursor-not-allowed'
                        : isProcessing 
                          ? 'bg-gray-400 cursor-not-allowed' 
                          : styles.button
                    }`}
                  >
                    {isCurrentPlan ? (
                      'Current Plan'
                    ) : isProcessing ? (
                      <div className="flex items-center justify-center">
                        <Loader2 className="animate-spin h-4 w-4 mr-2" />
                        Processing...
                      </div>
                    ) : (
                      `Upgrade to ${plan.planId.charAt(0).toUpperCase() + plan.planId.slice(1)}`
                    )}
                  </button>
                </div>
              </div>
            );
          })}
        </div>

        {/* FAQ */}
        <div className="mt-16 max-w-4xl mx-auto">
          <h2 className="text-2xl font-bold text-center text-gray-900 mb-8">
            Frequently Asked Questions
          </h2>
          
          <div className="grid md:grid-cols-2 gap-8">
            <div className="space-y-6">
              <div>
                <h3 className="font-medium text-gray-900 mb-2">
                  Can I change plans anytime?
                </h3>
                <p className="text-gray-600 text-sm">
                  Yes! You can upgrade or downgrade your plan at any time. Changes take effect immediately.
                </p>
              </div>
              
              <div>
                <h3 className="font-medium text-gray-900 mb-2">
                  What payment methods do you accept?
                </h3>
                <p className="text-gray-600 text-sm">
                  We accept all major credit cards through our secure payment processor Stripe.
                </p>
              </div>
            </div>
            
            <div className="space-y-6">
              <div>
                <h3 className="font-medium text-gray-900 mb-2">
                  What happens if I exceed my limits?
                </h3>
                <p className="text-gray-600 text-sm">
                  You'll be notified when you're close to your limits. You can upgrade your plan or wait for the next billing cycle.
                </p>
              </div>
              
              <div>
                <h3 className="font-medium text-gray-900 mb-2">
                  Can I cancel anytime?
                </h3>
                <p className="text-gray-600 text-sm">
                  Yes! You can cancel your subscription at any time. You'll continue to have access until the end of your billing period.
                </p>
              </div>
            </div>
          </div>
        </div>

        {/* Contact Support */}
        <div className="mt-12 text-center">
          <p className="text-gray-600">
            Need help choosing a plan?{' '}
            <a href="mailto:support@yourapp.com" className="text-blue-600 hover:text-blue-800 font-medium">
              Contact support
            </a>
          </p>
        </div>
      </div>
    </div>
  );
};

export default Pricing;
