import { useState, useRef, useEffect, useCallback } from "react";
import {
  Video,
  Music,
  Copy,
  Trash2,
  Scissors,
  Film,
  Plus,
  Layers,
  ZoomIn,
  ZoomOut,
  RotateCcw,
  Type,
  Magnet,
  Eye,
  EyeOff,
  Lock,
  Unlock,
  ChevronUp,
  ChevronDown,
  Move,
  Square,
  Play,
  Pause,
  SkipBack,
  SkipForward,
  Volume2,
  Settings,
  Grid,
  MousePointer,
  Hand,
  Maximize2,
  Minimize2,
} from "lucide-react";

export const Timeline = ({
  timelineItems,
  setTimelineItems,
  currentTime,
  videoDuration,
  setVideoDuration,
  selectedTimelineItem,
  setSelectedTimelineItem,
  timelineZoom,
  setTimelineZoom,
  tracks,
  setTracks,
  draggedItem,
  draggedTimelineItem,
  setDraggedTimelineItem,
  dropPreview,
  setDropPreview,
  isDragging,
  isTimelineDragging,
  setIsTimelineDragging,
  isResizing,
  setIsResizing,
  copiedItem,
  formatTime,
  seekTo,
  splitTimelineItem,
  copyTimelineItem,
  pasteTimelineItem,
  deleteSelectedItem,
  addToTimeline,
  insertWithRipple,
  applyRippleEffect,
  removeFromTimeline,
}) => {
  const timelineRef = useRef(null);
  const timelineScrollRef = useRef(null);
  const trackLabelsRef = useRef(null);
  const autoScrollIntervalRef = useRef(null);

  // CapCut Professional States
  const [isSnappingEnabled, setIsSnappingEnabled] = useState(true);
  const [snapThreshold] = useState(15); // pixels
  const [dragOffset, setDragOffset] = useState(0);
  const [resizeHandle, setResizeHandle] = useState(null);
  const [resizeItem, setResizeItem] = useState(null);
  const [originalItemData, setOriginalItemData] = useState(null);
  const [snapIndicators, setSnapIndicators] = useState([]);
  
  // CapCut Track Controls
  const [trackHeight, setTrackHeight] = useState(80); // Adjustable track height
  const [verticalZoom, setVerticalZoom] = useState(1); // Vertical zoom factor
  const [isRippleMode, setIsRippleMode] = useState(true); // Ripple edit mode
  const [editMode, setEditMode] = useState('select'); // select, razor, hand
  const [showWaveforms, setShowWaveforms] = useState(true);
  const [showThumbnails, setShowThumbnails] = useState(true);
  
  // Multi-selection
  const [selectedItems, setSelectedItems] = useState(new Set());
  const [selectionBox, setSelectionBox] = useState(null);
  const [isBoxSelecting, setIsBoxSelecting] = useState(false);
  
  // Playback controls
  const [isPlaying, setIsPlaying] = useState(false);
  const [playbackSpeed, setPlaybackSpeed] = useState(1);
  
  // Auto-scroll enhanced
  const [autoScrollSpeed, setAutoScrollSpeed] = useState(1);
  const [isAutoScrolling, setIsAutoScrolling] = useState(false);

  // Helper functions
  const getPixelsPerSecond = useCallback(() => 50 * timelineZoom, [timelineZoom]);
  const getTrackHeightAdjusted = useCallback(() => trackHeight * verticalZoom, [trackHeight, verticalZoom]);

  // CapCut-style Snap Points
  const getSnapPoints = useCallback(() => {
    const snapPoints = [];
    const pixelsPerSecond = getPixelsPerSecond();
    
    // Playhead snap point
    snapPoints.push({
      time: currentTime,
      pixel: currentTime * pixelsPerSecond,
      type: 'playhead',
      color: 'bg-red-400'
    });
    
    // Clip edges snap points
    timelineItems.forEach(item => {
      if (!selectedItems.has(item.id)) {
        snapPoints.push({
          time: item.startTime,
          pixel: item.startTime * pixelsPerSecond,
          type: 'clip-start',
          clipId: item.id,
          color: 'bg-blue-400'
        });
        snapPoints.push({
          time: item.startTime + item.duration,
          pixel: (item.startTime + item.duration) * pixelsPerSecond,
          type: 'clip-end',
          clipId: item.id,
          color: 'bg-blue-400'
        });
      }
    });
    
    // Timeline markers (every 10 seconds)
    for (let i = 0; i <= videoDuration; i += 10) {
      snapPoints.push({
        time: i,
        pixel: i * pixelsPerSecond,
        type: 'marker',
        color: 'bg-yellow-400'
      });
    }
    
    return snapPoints;
  }, [currentTime, timelineItems, selectedItems, getPixelsPerSecond, videoDuration]);

  const findNearestSnap = useCallback((targetPixel) => {
    if (!isSnappingEnabled) return null;
    
    const snapPoints = getSnapPoints();
    let nearestSnap = null;
    let minDistance = snapThreshold;
    
    snapPoints.forEach(point => {
      const distance = Math.abs(point.pixel - targetPixel);
      if (distance < minDistance) {
        minDistance = distance;
        nearestSnap = point;
      }
    });
    
    return nearestSnap;
  }, [isSnappingEnabled, getSnapPoints, snapThreshold]);

  // Enhanced Auto-scroll with acceleration
  const stopAutoScroll = useCallback(() => {
    if (autoScrollIntervalRef.current) {
      clearInterval(autoScrollIntervalRef.current);
      autoScrollIntervalRef.current = null;
      setIsAutoScrolling(false);
    }
  }, []);

  const startAutoScroll = useCallback((direction, intensity = 1) => {
    stopAutoScroll();
    setIsAutoScrolling(true);
    
    let currentSpeed = 1;
    const maxSpeed = 10;
    const acceleration = 0.1;
    
    autoScrollIntervalRef.current = setInterval(() => {
      if (timelineScrollRef.current) {
        // Accelerate over time
        currentSpeed = Math.min(maxSpeed, currentSpeed + acceleration);
        const scrollAmount = 20 * currentSpeed * intensity * autoScrollSpeed;
        
        const currentScrollLeft = timelineScrollRef.current.scrollLeft;
        const maxScroll = timelineScrollRef.current.scrollWidth - timelineScrollRef.current.clientWidth;

        if (direction === "left" && currentScrollLeft > 0) {
          timelineScrollRef.current.scrollLeft = Math.max(0, currentScrollLeft - scrollAmount);
        } else if (direction === "right" && currentScrollLeft < maxScroll) {
          timelineScrollRef.current.scrollLeft = Math.min(maxScroll, currentScrollLeft + scrollAmount);
        }
      }
    }, 16);
  }, [stopAutoScroll, autoScrollSpeed]);

  const checkAutoScroll = useCallback((clientX) => {
    if (!timelineScrollRef.current || editMode === 'hand') return;

    const rect = timelineScrollRef.current.getBoundingClientRect();
    const scrollZoneWidth = 150; // Larger scroll zone
    const relativeX = clientX - rect.left;
    const edgeIntensity = 0.3; // Sensitivity near edges

    if (relativeX < scrollZoneWidth && timelineScrollRef.current.scrollLeft > 0) {
      const intensity = ((scrollZoneWidth - relativeX) / scrollZoneWidth) * 4 + edgeIntensity;
      startAutoScroll("left", intensity);
    } else if (relativeX > rect.width - scrollZoneWidth) {
      const maxScroll = timelineScrollRef.current.scrollWidth - timelineScrollRef.current.clientWidth;
      if (timelineScrollRef.current.scrollLeft < maxScroll) {
        const intensity = ((relativeX - (rect.width - scrollZoneWidth)) / scrollZoneWidth) * 4 + edgeIntensity;
        startAutoScroll("right", intensity);
      }
    } else {
      stopAutoScroll();
    }
  }, [startAutoScroll, stopAutoScroll, editMode]);

  // CapCut Track Management
  const adjustTrackHeight = useCallback((delta) => {
    setTrackHeight(prev => Math.max(40, Math.min(200, prev + delta)));
  }, []);

  const adjustVerticalZoom = useCallback((factor) => {
    setVerticalZoom(prev => Math.max(0.5, Math.min(3, prev * factor)));
  }, []);

  // Zoom functions enhanced
  const zoomIn = useCallback(() => {
    setTimelineZoom(prev => Math.min(10, prev * 1.4));
  }, []);

  const zoomOut = useCallback(() => {
    setTimelineZoom(prev => Math.max(0.1, prev / 1.4));
  }, []);

  const resetZoom = useCallback(() => {
    setTimelineZoom(1);
    setVerticalZoom(1);
    setTrackHeight(80);
  }, []);

  const fitToTimeline = useCallback(() => {
    if (timelineScrollRef.current && videoDuration > 0) {
      const containerWidth = timelineScrollRef.current.clientWidth;
      const optimalZoom = (containerWidth - 100) / (videoDuration * 50);
      setTimelineZoom(Math.max(0.1, Math.min(10, optimalZoom)));
    }
  }, [videoDuration]);

  // Multi-selection logic
  const toggleItemSelection = useCallback((item, event) => {
    if (event.ctrlKey || event.metaKey) {
      setSelectedItems(prev => {
        const newSet = new Set(prev);
        if (newSet.has(item.id)) {
          newSet.delete(item.id);
        } else {
          newSet.add(item.id);
        }
        return newSet;
      });
    } else {
      setSelectedItems(new Set([item.id]));
      setSelectedTimelineItem(item);
    }
  }, [setSelectedTimelineItem]);

  // Professional trimming with preview
  const handleResizeStart = useCallback((e, item, handle) => {
    e.preventDefault();
    e.stopPropagation();
    
    if (editMode !== 'select') return;
    
    setIsResizing(true);
    setResizeHandle(handle);
    setResizeItem(item);
    setOriginalItemData({
      startTime: item.startTime,
      duration: item.duration,
    });
    setIsTimelineDragging(false);
    setDraggedTimelineItem(null);
    
    // Show resize cursor
    document.body.style.cursor = 'ew-resize';
  }, [editMode, setIsResizing, setIsTimelineDragging, setDraggedTimelineItem]);

  const handleResizeEnd = useCallback(() => {
    setIsResizing(false);
    setResizeHandle(null);
    setResizeItem(null);
    setOriginalItemData(null);
    setSnapIndicators([]);
    stopAutoScroll();
    document.body.style.cursor = 'default';
  }, [setIsResizing, stopAutoScroll]);

  // Enhanced Track Operations
  const addVideoTrack = useCallback(() => {
    const newTrackId = `video-${Date.now()}`;
    setTracks(prev => ({
      ...prev,
      video: [...prev.video, {
        id: newTrackId,
        name: `V${prev.video.length + 1}`,
        type: "video",
        visible: true,
        locked: false,
        muted: false,
        solo: false,
        height: trackHeight,
      }],
    }));
  }, [setTracks, trackHeight]);

  const addAudioTrack = useCallback(() => {
    const newTrackId = `audio-${Date.now()}`;
    setTracks(prev => ({
      ...prev,
      audio: [...prev.audio, {
        id: newTrackId,
        name: `A${prev.audio.length + 1}`,
        type: "audio",
        visible: true,
        locked: false,
        muted: false,
        solo: false,
        height: trackHeight,
      }],
    }));
  }, [setTracks, trackHeight]);

  const addTextTrack = useCallback(() => {
    const newTrackId = `text-${Date.now()}`;
    setTracks(prev => ({
      ...prev,
      text: [...prev.text, {
        id: newTrackId,
        name: `T${prev.text.length + 1}`,
        type: "text",
        visible: true,
        locked: false,
        muted: false,
        solo: false,
        height: trackHeight,
      }],
    }));
  }, [setTracks, trackHeight]);

  const toggleTrackProperty = useCallback((trackId, property) => {
    setTracks(prev => {
      const newTracks = { ...prev };
      Object.keys(newTracks).forEach(trackType => {
        newTracks[trackType] = newTracks[trackType].map(track =>
          track.id === trackId ? { ...track, [property]: !track[property] } : track
        );
      });
      return newTracks;
    });
  }, [setTracks]);

  // Helper functions
  const getAllTracks = useCallback(() => [
    ...(tracks.video || []),
    ...(tracks.audio || []),
    ...(tracks.text || []),
  ], [tracks]);

  const getTimelineWidth = useCallback(() => {
    const baseWidth = videoDuration * 50 * timelineZoom;
    return Math.max(baseWidth, 3000);
  }, [videoDuration, timelineZoom]);

  const getTrackColor = useCallback((trackType, isSelected = false) => {
    const colors = {
      video: isSelected ? "bg-blue-600 border-blue-500" : "bg-blue-500 border-blue-400",
      audio: isSelected ? "bg-green-600 border-green-500" : "bg-green-500 border-green-400", 
      text: isSelected ? "bg-purple-600 border-purple-500" : "bg-purple-500 border-purple-400",
    };
    return colors[trackType] || "bg-gray-500 border-gray-400";
  }, []);

  const getTrackIcon = useCallback((trackType) => {
    const icons = {
      video: <Video size={14} />,
      audio: <Music size={14} />,
      text: <Type size={14} />,
    };
    return icons[trackType] || <Layers size={14} />;
  }, []);

  // Professional editing modes
  const handleEditModeChange = useCallback((mode) => {
    setEditMode(mode);
    setSelectedItems(new Set());
    setSelectionBox(null);
    document.body.style.cursor = mode === 'hand' ? 'grab' : 'default';
  }, []);

  // Timeline event handlers
  const handleTimelineClick = useCallback((e) => {
    if (isDragging || isTimelineDragging || isResizing) return;
    
    if (editMode === 'razor') {
      // Split clip at cursor position
      const rect = timelineScrollRef.current.getBoundingClientRect();
      const clickX = e.clientX - rect.left + timelineScrollRef.current.scrollLeft;
      const clickTime = clickX / getPixelsPerSecond();
      
      // Find clip to split
      const clipToSplit = timelineItems.find(item => 
        clickTime >= item.startTime && clickTime <= item.startTime + item.duration
      );
      
      if (clipToSplit) {
        splitTimelineItem(clipToSplit, clickTime);
      }
    } else if (editMode === 'select') {
      const rect = timelineScrollRef.current.getBoundingClientRect();
      const clickX = e.clientX - rect.left + timelineScrollRef.current.scrollLeft;
      const clickTime = clickX / getPixelsPerSecond();
      seekTo(clickTime);
      
      if (!e.ctrlKey && !e.metaKey) {
        setSelectedItems(new Set());
        setSelectedTimelineItem(null);
      }
    }
  }, [isDragging, isTimelineDragging, isResizing, editMode, getPixelsPerSecond, timelineItems, splitTimelineItem, seekTo, setSelectedTimelineItem]);

  // Enhanced drag and drop
  const handleTimelineItemDragStart = useCallback((e, timelineItem) => {
    if (isResizing || editMode !== 'select') {
      e.preventDefault();
      return;
    }

    const track = getAllTracks().find(t => t.id === timelineItem.trackId);
    if (track?.locked) {
      e.preventDefault();
      return;
    }

    e.stopPropagation();
    setDraggedTimelineItem(timelineItem);
    setIsTimelineDragging(true);

    const rect = timelineScrollRef.current.getBoundingClientRect();
    const itemRect = e.target.getBoundingClientRect();
    setDragOffset(e.clientX - itemRect.left + timelineScrollRef.current.scrollLeft);

    e.dataTransfer.effectAllowed = "move";
    e.dataTransfer.setData("text/plain", "");
    
    // If item is not selected, select it
    if (!selectedItems.has(timelineItem.id)) {
      setSelectedItems(new Set([timelineItem.id]));
    }
  }, [isResizing, editMode, getAllTracks, setDraggedTimelineItem, setIsTimelineDragging, selectedItems]);

  // Keyboard shortcuts
  useEffect(() => {
    const handleKeyDown = (e) => {
      if (e.target.tagName === 'INPUT' || e.target.tagName === 'TEXTAREA') return;
      
      switch (e.key.toLowerCase()) {
        case 'v':
          if (!e.ctrlKey && !e.metaKey) handleEditModeChange('select');
          break;
        case 'c':
          if (!e.ctrlKey && !e.metaKey) handleEditModeChange('razor');
          break;
        case 'h':
          if (!e.ctrlKey && !e.metaKey) handleEditModeChange('hand');
          break;
        case 's':
          if (!e.ctrlKey && !e.metaKey && selectedTimelineItem) {
            e.preventDefault();
            splitTimelineItem(selectedTimelineItem, currentTime);
          }
          break;
        case 'delete':
        case 'backspace':
          if (selectedItems.size > 0) {
            e.preventDefault();
            deleteSelectedItem();
          }
          break;
        case ' ':
          e.preventDefault();
          setIsPlaying(!isPlaying);
          break;
        case '+':
        case '=':
          if (e.ctrlKey || e.metaKey) {
            e.preventDefault();
            zoomIn();
          }
          break;
        case '-':
          if (e.ctrlKey || e.metaKey) {
            e.preventDefault();
            zoomOut();
          }
          break;
        case '0':
          if (e.ctrlKey || e.metaKey) {
            e.preventDefault();
            resetZoom();
          }
          break;
        case 'f':
          if (e.ctrlKey || e.metaKey) {
            e.preventDefault();
            fitToTimeline();
          }
          break;
      }
    };

    document.addEventListener('keydown', handleKeyDown);
    return () => document.removeEventListener('keydown', handleKeyDown);
  }, [handleEditModeChange, selectedTimelineItem, selectedItems, currentTime, splitTimelineItem, deleteSelectedItem, isPlaying, zoomIn, zoomOut, resetZoom, fitToTimeline]);

  // Timeline rendering
  const currentTrackHeight = getTrackHeightAdjusted();
  const allTracks = getAllTracks();

  return (
    <div className={`h-[600px] bg-white rounded-2xl shadow-lg border-2 transition-all duration-200 overflow-hidden flex flex-col ${
      isDragging || isTimelineDragging || isResizing ? "border-blue-400 bg-blue-50/20" : "border-gray-200"
    }`}>
      
      {/* Professional Header */}
      <div className="bg-gradient-to-r from-gray-50 to-gray-100 p-4 border-b border-gray-200 space-y-4">
        <div className="flex items-center justify-between">
          <div className="flex items-center space-x-3">
            <h4 className="text-gray-800 font-bold flex items-center space-x-2">
              <Film size={20} className="text-blue-600" />
              <span>CapCut Professional Timeline</span>
            </h4>
            
            {/* Status indicators */}
            <div className="flex items-center space-x-2 text-xs">
              {isRippleMode && <span className="bg-green-100 text-green-700 px-2 py-1 rounded">RIPPLE</span>}
              {isSnappingEnabled && <span className="bg-blue-100 text-blue-700 px-2 py-1 rounded">SNAP</span>}
              {isAutoScrolling && <span className="bg-orange-100 text-orange-700 px-2 py-1 rounded">AUTO-SCROLL</span>}
              <span className="bg-gray-100 text-gray-700 px-2 py-1 rounded">{editMode.toUpperCase()}</span>
            </div>
          </div>

          {/* Playback Controls */}
          <div className="flex items-center space-x-2">
            <button onClick={() => seekTo(Math.max(0, currentTime - 1))} className="p-2 hover:bg-gray-200 rounded">
              <SkipBack size={16} />
            </button>
            <button 
              onClick={() => setIsPlaying(!isPlaying)} 
              className={`p-2 rounded ${isPlaying ? 'bg-red-500 text-white' : 'hover:bg-gray-200'}`}
            >
              {isPlaying ? <Pause size={16} /> : <Play size={16} />}
            </button>
            <button onClick={() => seekTo(Math.min(videoDuration, currentTime + 1))} className="p-2 hover:bg-gray-200 rounded">
              <SkipForward size={16} />
            </button>
            
            <select 
              value={playbackSpeed} 
              onChange={(e) => setPlaybackSpeed(Number(e.target.value))}
              className="text-xs border rounded px-2 py-1"
            >
              <option value={0.25}>0.25x</option>
              <option value={0.5}>0.5x</option>
              <option value={1}>1x</option>
              <option value={1.5}>1.5x</option>
              <option value={2}>2x</option>
            </select>
          </div>
        </div>

        {/* Tool Controls */}
        <div className="flex items-center justify-between">
          <div className="flex items-center space-x-6">
            
            {/* Edit Mode Tools */}
            <div className="flex items-center space-x-1 bg-white rounded-lg p-1 border">
              <button
                onClick={() => handleEditModeChange('select')}
                className={`p-2 rounded transition-all ${editMode === 'select' ? 'bg-blue-500 text-white' : 'hover:bg-gray-100'}`}
                title="Selection Tool (V)"
              >
                <MousePointer size={16} />
              </button>
              <button
                onClick={() => handleEditModeChange('razor')}
                className={`p-2 rounded transition-all ${editMode === 'razor' ? 'bg-red-500 text-white' : 'hover:bg-gray-100'}`}
                title="Razor Tool (C)"
              >
                <Scissors size={16} />
              </button>
              <button
                onClick={() => handleEditModeChange('hand')}
                className={`p-2 rounded transition-all ${editMode === 'hand' ? 'bg-green-500 text-white' : 'hover:bg-gray-100'}`}
                title="Hand Tool (H)"
              >
                <Hand size={16} />
              </button>
            </div>

            {/* Track Controls */}
            <div className="flex items-center space-x-2">
              <button onClick={addVideoTrack} className="p-2 text-blue-600 hover:bg-blue-50 rounded" title="Add Video Track">
                <Video size={16} />
              </button>
              <button onClick={addAudioTrack} className="p-2 text-green-600 hover:bg-green-50 rounded" title="Add Audio Track">
                <Music size={16} />
              </button>
              <button onClick={addTextTrack} className="p-2 text-purple-600 hover:bg-purple-50 rounded" title="Add Text Track">
                <Type size={16} />
              </button>
            </div>

            {/* Professional Controls */}
            <div className="flex items-center space-x-2">
              <button
                onClick={() => setIsRippleMode(!isRippleMode)}
                className={`p-2 rounded transition-all ${isRippleMode ? 'bg-green-500 text-white' : 'text-gray-600 hover:bg-gray-100'}`}
                title="Ripple Edit Mode"
              >
                <Move size={16} />
              </button>
              <button
                onClick={() => setIsSnappingEnabled(!isSnappingEnabled)}
                className={`p-2 rounded transition-all ${isSnappingEnabled ? 'bg-blue-500 text-white' : 'text-gray-600 hover:bg-gray-100'}`}
                title="Magnetic Timeline"
              >
                <Magnet size={16} />
              </button>
            </div>
          </div>

          {/* Advanced Controls */}
          <div className="flex items-center space-x-4">
            
            {/* View Options */}
            <div className="flex items-center space-x-2">
              <button
                onClick={() => setShowWaveforms(!showWaveforms)}
                className={`p-1 rounded ${showWaveforms ? 'bg-green-100 text-green-700' : 'text-gray-500'}`}
                title="Show Waveforms"
              >
                <Volume2 size={14} />
              </button>
              <button
                onClick={() => setShowThumbnails(!showThumbnails)}
                className={`p-1 rounded ${showThumbnails ? 'bg-blue-100 text-blue-700' : 'text-gray-500'}`}
                title="Show Thumbnails"
              >
                <Grid size={14} />
              </button>
            </div>

            {/* Track Height Controls */}
            <div className="flex items-center space-x-1 bg-white rounded-lg p-1 border">
              <button onClick={() => adjustTrackHeight(-10)} className="p-1 hover:bg-gray-100 rounded" title="Decrease Track Height">
                <ChevronDown size={14} />
              </button>
              <span className="text-xs px-2">{Math.round(currentTrackHeight)}px</span>
              <button onClick={() => adjustTrackHeight(10)} className="p-1 hover:bg-gray-100 rounded" title="Increase Track Height">
                <ChevronUp size={14} />
              </button>
            </div>

            {/* Zoom Controls */}
            <div className="flex items-center space-x-2 bg-white rounded-lg p-2 border">
              <button onClick={zoomOut} className="p-1 hover:bg-gray-100 rounded" title="Zoom Out (Ctrl+-)">
                <ZoomOut size={16} />
              </button>
              <span className="text-xs min-w-16 text-center font-mono">
                {(timelineZoom * 100).toFixed(0)}%
              </span>
              <button onClick={zoomIn} className="p-1 hover:bg-gray-100 rounded" title="Zoom In (Ctrl++)">
                <ZoomIn size={16} />
              </button>
              <button onClick={resetZoom} className="p-1 hover:bg-gray-100 rounded" title="Reset Zoom (Ctrl+0)">
                <RotateCcw size={16} />
              </button>
              <button onClick={fitToTimeline} className="p-1 hover:bg-gray-100 rounded" title="Fit to Timeline (Ctrl+F)">
                <Maximize2 size={16} />
              </button>
            </div>
          </div>
        </div>
      </div>

      {/* Timeline Main Area */}
      <div className="flex-1 flex min-h-0">
        
        {/* Enhanced Track Labels */}
        <div className="w-56 bg-gradient-to-b from-gray-50 to-gray-100 border-r border-gray-200 flex-shrink-0 flex flex-col">
          <div className="h-14 border-b border-gray-200 flex items-center px-4 bg-gray-100">
            <span className="text-xs font-semibold text-gray-600">TRACKS</span>
          </div>
          
          <div
            ref={trackLabelsRef}
            className="flex-1 overflow-y-auto overflow-x-hidden"
            onScroll={(e) => {
              if (timelineScrollRef.current) {
                timelineScrollRef.current.scrollTop = e.target.scrollTop;
              }
            }}
          >
            {allTracks.map((track) => (
              <div
                key={track.id}
                className={`flex items-center px-3 border-b border-gray-100 transition-colors ${
                  track.type === 'video' ? 'bg-blue-50/50 hover:bg-blue-50' :
                  track.type === 'audio' ? 'bg-green-50/50 hover:bg-green-50' :
                  'bg-purple-50/50 hover:bg-purple-50'
                }`}
                style={{ height: `${currentTrackHeight}px`, minHeight: `${currentTrackHeight}px` }}
              >
                <div className="flex items-center space-x-2 flex-1">
                  <div className={`p-1 rounded ${
                    track.type === 'video' ? 'bg-blue-500 text-white' :
                    track.type === 'audio' ? 'bg-green-500 text-white' :
                    'bg-purple-500 text-white'
                  }`}>
                    {getTrackIcon(track.type)}
                  </div>
                  <span className="text-sm font-medium truncate flex-1 text-gray-700">
                    {track.name}
                  </span>
                </div>
                
                {/* Track Controls */}
                <div className="flex items-center space-x-1">
                  <button
                    onClick={() => toggleTrackProperty(track.id, 'visible')}
                    className={`p-1 rounded transition-colors ${
                      track.visible ? 'text-gray-600 hover:text-gray-800' : 'text-red-500'
                    }`}
                    title={track.visible ? "Hide track" : "Show track"}
                  >
                    {track.visible ? <Eye size={12} /> : <EyeOff size={12} />}
                  </button>
                  <button
                    onClick={() => toggleTrackProperty(track.id, 'locked')}
                    className={`p-1 rounded transition-colors ${
                      track.locked ? 'text-red-500' : 'text-gray-600 hover:text-gray-800'
                    }`}
                    title={track.locked ? "Unlock track" : "Lock track"}
                  >
                    {track.locked ? <Lock size={12} /> : <Unlock size={12} />}
                  </button>
                  <button
                    onClick={() => toggleTrackProperty(track.id, 'muted')}
                    className={`p-1 rounded transition-colors ${
                      track.muted ? 'text-red-500' : 'text-gray-600 hover:text-gray-800'
                    }`}
                    title={track.muted ? "Unmute track" : "Mute track"}
                  >
                    <Volume2 size={12} />
                  </button>
                </div>
              </div>
            ))}
          </div>
        </div>

        {/* Enhanced Timeline Scroll Area */}
        <div
          ref={(el) => {
            timelineRef.current = el;
            timelineScrollRef.current = el;
          }}
          className="flex-1 relative overflow-auto bg-white"
          onScroll={(e) => {
            if (trackLabelsRef.current) {
              trackLabelsRef.current.scrollTop = e.target.scrollTop;
            }
          }}
          onDragOver={(e) => {
            if (!draggedItem && !draggedTimelineItem) return;
            e.preventDefault();
            checkAutoScroll(e.clientX);
            // Enhanced drag over logic would go here
          }}
          onClick={handleTimelineClick}
          style={{ 
            overflowX: "auto", 
            overflowY: "auto", 
            scrollbarWidth: "thin",
            cursor: editMode === 'hand' ? 'grab' : editMode === 'razor' ? 'crosshair' : 'default'
          }}
        >
          <div
            style={{
              width: `${getTimelineWidth()}px`,
              minHeight: "100%",
              position: "relative",
            }}
          >
            {/* Enhanced Time Ruler */}
            <div className="h-14 bg-gradient-to-b from-gray-100 to-gray-50 border-b border-gray-200 relative">
              
              {/* Major time markers */}
              {Array.from({ length: Math.ceil((videoDuration * timelineZoom) / 5) + 1 }, (_, i) => {
                const timeValue = (i * 5) / timelineZoom;
                const pixelsPerSecond = getPixelsPerSecond();
                return (
                  <div
                    key={i}
                    className="absolute top-0 h-full border-l border-gray-300"
                    style={{ left: `${timeValue * pixelsPerSecond}px` }}
                  >
                    <span className="text-xs text-gray-600 ml-2 mt-2 inline-block font-mono font-medium">
                      {formatTime(timeValue)}
                    </span>
                  </div>
                );
              })}

              {/* Minor time markers for high zoom */}
              {timelineZoom > 2 && Array.from({ length: Math.ceil(videoDuration * timelineZoom) + 1 }, (_, i) => {
                const timeValue = i / timelineZoom;
                const pixelsPerSecond = getPixelsPerSecond();
                return (
                  <div
                    key={`sub-${i}`}
                    className="absolute top-10 w-0 h-4 border-l border-gray-200"
                    style={{ left: `${timeValue * pixelsPerSecond}px` }}
                  />
                );
              })}

              {/* Enhanced Playhead */}
              <div
                className="absolute top-0 w-1 h-full bg-red-500 z-30 shadow-lg cursor-ew-resize"
                style={{ left: `${currentTime * getPixelsPerSecond()}px` }}
                onMouseDown={(e) => {
                  // Allow dragging playhead
                  const startX = e.clientX;
                  const startTime = currentTime;
                  
                  const handleMouseMove = (e) => {
                    const deltaX = e.clientX - startX;
                    const deltaTime = deltaX / getPixelsPerSecond();
                    const newTime = Math.max(0, Math.min(videoDuration, startTime + deltaTime));
                    seekTo(newTime);
                  };
                  
                  const handleMouseUp = () => {
                    document.removeEventListener('mousemove', handleMouseMove);
                    document.removeEventListener('mouseup', handleMouseUp);
                  };
                  
                  document.addEventListener('mousemove', handleMouseMove);
                  document.addEventListener('mouseup', handleMouseUp);
                }}
              >
                <div className="absolute top-0 left-0 w-0 h-0 border-l-4 border-r-4 border-b-8 border-transparent border-b-red-500 transform -translate-x-1/2" />
              </div>

              {/* Snap indicators in ruler */}
              {snapIndicators.map((indicator, index) => (
                <div
                  key={index}
                  className={`absolute top-0 w-0.5 h-full z-20 ${indicator.color || 'bg-yellow-400'} opacity-75`}
                  style={{ left: `${indicator.time * getPixelsPerSecond()}px` }}
                />
              ))}
            </div>

            {/* Enhanced Tracks Area */}
            <div
              className="relative"
              style={{
                height: `${allTracks.length * currentTrackHeight}px`,
                minHeight: "100%",
              }}
            >
              {/* Timeline clips with enhanced features */}
              {timelineItems.map((item) => {
                const track = allTracks.find(t => t.id === item.trackId);
                const trackIndex = allTracks.findIndex(t => t.id === item.trackId);
                const isSelected = selectedItems.has(item.id);
                
                return (
                  <div
                    key={item.id}
                    className={`absolute rounded-lg border-2 transition-all shadow-lg hover:shadow-xl group ${
                      getTrackColor(item.trackType, isSelected)
                    } ${
                      draggedTimelineItem?.id === item.id ? "opacity-50 z-40" : "z-10"
                    } ${
                      !track?.visible ? "opacity-40" : ""
                    } ${
                      track?.locked ? "cursor-not-allowed" : editMode === 'select' ? "cursor-grab active:cursor-grabbing" : ""
                    }`}
                    style={{
                      left: `${item.startTime * getPixelsPerSecond()}px`,
                      width: `${item.duration * getPixelsPerSecond()}px`,
                      top: `${trackIndex * currentTrackHeight + 8}px`,
                      height: `${currentTrackHeight - 16}px`,
                    }}
                    onClick={(e) => {
                      if (editMode === 'select') {
                        e.stopPropagation();
                        toggleItemSelection(item, e);
                      }
                    }}
                    onMouseDown={(e) => {
                      if (editMode === 'select' && !track?.locked) {
                        handleTimelineItemDragStart(e, item);
                      }
                    }}
                  >
                    
                    {/* Left trim handle - Enhanced */}
                    {!track?.locked && editMode === 'select' && (
                      <div
                        className="absolute left-0 top-0 w-4 h-full cursor-ew-resize opacity-0 group-hover:opacity-100 bg-gradient-to-r from-white/80 to-transparent rounded-l-lg z-20 flex items-center justify-center transition-opacity"
                        onMouseDown={(e) => handleResizeStart(e, item, "left")}
                        title="Trim start"
                      >
                        <div className="w-1 h-8 bg-white/90 rounded-full shadow-sm"></div>
                      </div>
                    )}

                    {/* Clip content with thumbnails/waveforms */}
                    <div className="h-full px-4 flex items-center relative overflow-hidden rounded-lg">
                      
                      {/* Background pattern for video */}
                      {item.trackType === 'video' && showThumbnails && (
                        <div className="absolute inset-0 opacity-20">
                          <div className="w-full h-full bg-gradient-to-r from-blue-300 to-blue-400 rounded-lg"></div>
                        </div>
                      )}
                      
                      {/* Waveform for audio */}
                      {item.trackType === 'audio' && showWaveforms && (
                        <div className="absolute inset-0 flex items-center opacity-30">
                          {Array.from({ length: Math.floor(item.duration * 10) }, (_, i) => (
                            <div
                              key={i}
                              className="bg-white/80 mx-px rounded-full"
                              style={{
                                width: '2px',
                                height: `${20 + Math.random() * (currentTrackHeight - 40)}px`
                              }}
                            />
                          ))}
                        </div>
                      )}
                      
                      {/* Clip label */}
                      <span className="text-white text-sm font-medium truncate relative z-10 drop-shadow-sm">
                        {item.name}
                      </span>
                      
                      {/* Status indicators */}
                      <div className="absolute top-2 right-2 flex space-x-1">
                        {track?.locked && <Lock size={12} className="text-white/80" />}
                        {track?.muted && <Volume2 size={12} className="text-white/80" />}
                        {isSelected && <Square size={8} className="text-yellow-300 fill-current" />}
                      </div>
                    </div>

                    {/* Right trim handle - Enhanced */}
                    {!track?.locked && editMode === 'select' && (
                      <div
                        className="absolute right-0 top-0 w-4 h-full cursor-ew-resize opacity-0 group-hover:opacity-100 bg-gradient-to-l from-white/80 to-transparent rounded-r-lg z-20 flex items-center justify-center transition-opacity"
                        onMouseDown={(e) => handleResizeStart(e, item, "right")}
                        title="Trim end"
                      >
                        <div className="w-1 h-8 bg-white/90 rounded-full shadow-sm"></div>
                      </div>
                    )}
                  </div>
                );
              })}

              {/* Enhanced snap indicators */}
              {snapIndicators.map((indicator, index) => (
                <div
                  key={`timeline-snap-${index}`}
                  className={`absolute w-0.5 z-30 opacity-80 ${indicator.color || 'bg-yellow-400'}`}
                  style={{
                    left: `${indicator.time * getPixelsPerSecond()}px`,
                    top: '0px',
                    height: `${allTracks.length * currentTrackHeight}px`
                  }}
                />
              ))}

              {/* Track separators */}
              {allTracks.map((_, index) => (
                <div
                  key={`separator-${index}`}
                  className="absolute w-full border-b border-gray-100"
                  style={{
                    top: `${(index + 1) * currentTrackHeight}px`,
                  }}
                />
              ))}
            </div>
          </div>
        </div>
      </div>

      {/* Professional Status Bar */}
      <div className="bg-gray-50 px-4 py-2 border-t border-gray-200 flex items-center justify-between text-xs text-gray-600">
        <div className="flex items-center space-x-4">
          <span>Timeline: {formatTime(videoDuration)}</span>
          <span>Current: {formatTime(currentTime)}</span>
          <span>Selected: {selectedItems.size} items</span>
          {isAutoScrolling && <span className="text-orange-600">Auto-scrolling...</span>}
        </div>
        <div className="flex items-center space-x-4">
          <span>Tracks: {allTracks.length}</span>
          <span>Zoom: {(timelineZoom * 100).toFixed(0)}%</span>
          <span>Height: {Math.round(currentTrackHeight)}px</span>
        </div>
      </div>
    </div>
  );
};
