// src/hooks/useRoutes.js (обновленный)
import { lazy } from "react";
import { ROUTES } from "../constants/routes";

// Ленивая загрузка компонентов для оптимизации
const VideoEditorApp = lazy(() => import("../../pages/VideoEditorAppPage"));
const TermsOfUsePage = lazy(() => import("../../pages/TermsOfUsePage"));
const PrivacyPolicyPage = lazy(() => import("../../pages/PrivacyPolicyPage"));
const NotFoundPage = lazy(() => import("../../pages/NotFoundPage"));
const ScriptifyLanding = lazy(() => import("../../pages/ScriptifyLanding"));

export const useAppRoutes = () => {
  return [
    {
      path: ROUTES.HOME,
      element: ScriptifyLanding,
      title: "Scriptify - AI Video Creator",
      requiresAuth: false,
    },
    {
      path: ROUTES.VIDEO_MAKER,
      element: VideoEditorApp,
      title: "Video Maker - Scriptify",
      requiresAuth: true, // Защищенный роут
    },
    {
      path: ROUTES.TERMS_OF_USE,
      element: TermsOfUsePage,
      title: "Terms of Use - Scriptify",
      requiresAuth: false,
    },
    {
      path: ROUTES.PRIVACY_POLICY,
      element: PrivacyPolicyPage,
      title: "Privacy Policy - Scriptify",
      requiresAuth: false,
    },
    {
      path: ROUTES.NOT_FOUND,
      element: NotFoundPage,
      title: "Page Not Found - Scriptify",
      requiresAuth: false,
    },
  ];
};
