import { useState, useRef, useEffect } from "react";

export const TeleprompterModal = ({
  isOpen = true,
  onClose = () => {},
  script = "Ð”Ð¾Ð±Ñ€Ð¾ Ð¿Ð¾Ð¶Ð°Ð»Ð¾Ð²Ð°Ñ‚ÑŒ Ð² Ñ‚ÐµÐ»ÐµÐ¿Ñ€Ð¾Ð¼Ð¿Ñ‚ÐµÑ€! Ð­Ñ‚Ð¾ Ð´ÐµÐ¼Ð¾Ð½ÑÑ‚Ñ€Ð°Ñ†Ð¸Ñ Ñ€Ð°Ð±Ð¾Ñ‚Ñ‹ ÑÑƒÑ„Ð»ÐµÑ€Ð° Ñ Ð²Ð¾Ð·Ð¼Ð¾Ð¶Ð½Ð¾ÑÑ‚ÑŒÑŽ Ð·Ð°Ð¿Ð¸ÑÐ¸. Ð—Ð´ÐµÑÑŒ Ð²Ñ‹ Ð¼Ð¾Ð¶ÐµÑ‚Ðµ Ð½Ð°ÑÑ‚Ñ€Ð¾Ð¸Ñ‚ÑŒ ÑÐºÐ¾Ñ€Ð¾ÑÑ‚ÑŒ Ð¿Ñ€Ð¾ÐºÑ€ÑƒÑ‚ÐºÐ¸, Ñ€Ð°Ð·Ð¼ÐµÑ€ ÑˆÑ€Ð¸Ñ„Ñ‚Ð° Ð¸ Ñ‚Ð¸Ð¿ Ð·Ð°Ð¿Ð¸ÑÐ¸. ÐŸÑ€Ð¾ÐºÑ€ÑƒÑ‚ÐºÐ° Ð±ÑƒÐ´ÐµÑ‚ Ð°Ð²Ñ‚Ð¾Ð¼Ð°Ñ‚Ð¸Ñ‡ÐµÑÐºÐ¸ Ð·Ð°Ð¿ÑƒÑ‰ÐµÐ½Ð° Ð¿Ñ€Ð¸ Ð½Ð°Ñ‡Ð°Ð»Ðµ Ð·Ð°Ð¿Ð¸ÑÐ¸. Ð˜ÑÐ¿Ð¾Ð»ÑŒÐ·ÑƒÐ¹Ñ‚Ðµ ÐºÐ½Ð¾Ð¿ÐºÐ¸ ÑƒÐ¿Ñ€Ð°Ð²Ð»ÐµÐ½Ð¸Ñ Ð´Ð»Ñ Ð¿Ð°ÑƒÐ·Ñ‹, Ð¿Ñ€Ð¾Ð´Ð¾Ð»Ð¶ÐµÐ½Ð¸Ñ Ð¸Ð»Ð¸ ÑÐ±Ñ€Ð¾ÑÐ° Ñ‚ÐµÐºÑÑ‚Ð°. ÐŸÑ€Ð¸ Ð·Ð°Ð¿Ð¸ÑÐ¸ Ð²Ð¸Ð´ÐµÐ¾ Ð²Ñ‹ ÑƒÐ²Ð¸Ð´Ð¸Ñ‚Ðµ Ð¾ÐºÐ½Ð¾ Ð¿Ñ€ÐµÐ´Ð²Ð°Ñ€Ð¸Ñ‚ÐµÐ»ÑŒÐ½Ð¾Ð³Ð¾ Ð¿Ñ€Ð¾ÑÐ¼Ð¾Ñ‚Ñ€Ð° Ð² Ð¿Ñ€Ð°Ð²Ð¾Ð¼ Ð²ÐµÑ€Ñ…Ð½ÐµÐ¼ ÑƒÐ³Ð»Ñƒ. Ð­Ñ‚Ð¾ Ð¿Ð¾Ð¼Ð¾Ð¶ÐµÑ‚ Ð²Ð°Ð¼ Ð²Ð¸Ð´ÐµÑ‚ÑŒ, Ñ‡Ñ‚Ð¾ Ð¸Ð¼ÐµÐ½Ð½Ð¾ Ð·Ð°Ð¿Ð¸ÑÑ‹Ð²Ð°ÐµÑ‚ÑÑ. Ð¢ÐµÐ»ÐµÐ¿Ñ€Ð¾Ð¼Ð¿Ñ‚ÐµÑ€ Ð¿Ð¾Ð´Ð´ÐµÑ€Ð¶Ð¸Ð²Ð°ÐµÑ‚ ÐºÐ°Ðº Ð°ÑƒÐ´Ð¸Ð¾, Ñ‚Ð°Ðº Ð¸ Ð²Ð¸Ð´ÐµÐ¾ Ð·Ð°Ð¿Ð¸ÑÑŒ. ÐÐ°ÑÑ‚Ñ€Ð¾Ð¹Ñ‚Ðµ Ð¿Ð°Ñ€Ð°Ð¼ÐµÑ‚Ñ€Ñ‹ Ð² ÑÐ¾Ð¾Ñ‚Ð²ÐµÑ‚ÑÑ‚Ð²Ð¸Ð¸ Ñ Ð²Ð°ÑˆÐ¸Ð¼Ð¸ Ð¿Ð¾Ñ‚Ñ€ÐµÐ±Ð½Ð¾ÑÑ‚ÑÐ¼Ð¸ Ð¸ Ð½Ð°Ñ‡Ð¸Ð½Ð°Ð¹Ñ‚Ðµ Ð·Ð°Ð¿Ð¸ÑÑŒ!",
  onRecordingComplete = () => {},
})  => {
  const [isRecording, setIsRecording] = useState(false);
  const [isPaused, setIsPaused] = useState(false);
  const [recordingType, setRecordingType] = useState("audio");
  const [speed, setSpeed] = useState(50);
  const [fontSize, setFontSize] = useState(24);
  const [isScrolling, setIsScrolling] = useState(false);
  const [showSettings, setShowSettings] = useState(false);
  const [recordedBlob, setRecordedBlob] = useState(null);
  const [recordingTime, setRecordingTime] = useState(0);
  const [videoReady, setVideoReady] = useState(false);

  const scrollContainerRef = useRef(null);
  const mediaRecorderRef = useRef(null);
  const scrollIntervalRef = useRef(null);
  const timerRef = useRef(null);
  const streamRef = useRef(null);
  const videoPreviewRef = useRef(null);

  const startScrolling = () => {
    if (scrollIntervalRef.current) return;

    setIsScrolling(true);
    scrollIntervalRef.current = setInterval(() => {
      if (scrollContainerRef.current && isScrolling && !isPaused) {
        const container = scrollContainerRef.current;
        const scrollStep = (speed / 50) * 2;
        container.scrollTop += scrollStep;

        if (
          container.scrollTop >=
          container.scrollHeight - container.clientHeight
        ) {
          stopScrolling();
        }
      }
    }, 50);
  };

  const stopScrolling = () => {
    if (scrollIntervalRef.current) {
      clearInterval(scrollIntervalRef.current);
      scrollIntervalRef.current = null;
    }
    setIsScrolling(false);
  };

  const resetScroll = () => {
    if (scrollContainerRef.current) {
      scrollContainerRef.current.scrollTop = 0;
    }
    stopScrolling();
  };

  const startTimer = () => {
    timerRef.current = setInterval(() => {
      setRecordingTime((prev) => prev + 1);
    }, 1000);
  };

  const stopTimer = () => {
    if (timerRef.current) {
      clearInterval(timerRef.current);
      timerRef.current = null;
    }
  };

  const resetTimer = () => {
    setRecordingTime(0);
    stopTimer();
  };

  const startRecording = async () => {
    try {
      const constraints =
        recordingType === "video"
          ? { video: true, audio: true }
          : { audio: true };

      const stream = await navigator.mediaDevices.getUserMedia(constraints);
      streamRef.current = stream;

      // ÐÐ°ÑÑ‚Ñ€Ð¾Ð¹ÐºÐ° Ð¿Ñ€ÐµÐ´Ð²Ð°Ñ€Ð¸Ñ‚ÐµÐ»ÑŒÐ½Ð¾Ð³Ð¾ Ð¿Ñ€Ð¾ÑÐ¼Ð¾Ñ‚Ñ€Ð° Ð²Ð¸Ð´ÐµÐ¾
      if (recordingType === "video" && videoPreviewRef.current) {
        const videoElement = videoPreviewRef.current;
        setVideoReady(false);
        
        // ÐžÑ‡Ð¸Ñ‰Ð°ÐµÐ¼ Ð¿Ñ€ÐµÐ´Ñ‹Ð´ÑƒÑ‰Ð¸Ð¹ Ð¿Ð¾Ñ‚Ð¾Ðº
        if (videoElement.srcObject) {
          videoElement.srcObject = null;
        }
        
        // Ð£ÑÑ‚Ð°Ð½Ð°Ð²Ð»Ð¸Ð²Ð°ÐµÐ¼ Ð½Ð¾Ð²Ñ‹Ð¹ Ð¿Ð¾Ñ‚Ð¾Ðº
        videoElement.srcObject = stream;
        
        // ÐŸÑ€Ð¸Ð½ÑƒÐ´Ð¸Ñ‚ÐµÐ»ÑŒÐ½Ð¾ Ð¿Ð¾ÐºÐ°Ð·Ñ‹Ð²Ð°ÐµÐ¼ Ð²Ð¸Ð´ÐµÐ¾ Ñ‡ÐµÑ€ÐµÐ· 1 ÑÐµÐºÑƒÐ½Ð´Ñƒ ÐµÑÐ»Ð¸ ÑÐ¾Ð±Ñ‹Ñ‚Ð¸Ñ Ð½Ðµ ÑÑ€Ð°Ð±Ð¾Ñ‚Ð°Ð»Ð¸
        setTimeout(() => {
          console.log("ðŸ”„ ÐŸÑ€Ð¸Ð½ÑƒÐ´Ð¸Ñ‚ÐµÐ»ÑŒÐ½Ð¾Ðµ Ð²ÐºÐ»ÑŽÑ‡ÐµÐ½Ð¸Ðµ Ð²Ð¸Ð´ÐµÐ¾ Ñ‡ÐµÑ€ÐµÐ· 1 ÑÐµÐº");
          setVideoReady(true);
        }, 1000);
        
        // Ð”Ð¾Ð±Ð°Ð²Ð»ÑÐµÐ¼ Ð¾Ð±Ñ€Ð°Ð±Ð¾Ñ‚Ñ‡Ð¸ÐºÐ¸ ÑÐ¾Ð±Ñ‹Ñ‚Ð¸Ð¹ Ð´Ð»Ñ Ð¾Ñ‚Ð»Ð°Ð´ÐºÐ¸
        videoElement.onloadedmetadata = () => {
          console.log("âœ… Ð’Ð¸Ð´ÐµÐ¾ Ð¼ÐµÑ‚Ð°Ð´Ð°Ð½Ð½Ñ‹Ðµ Ð·Ð°Ð³Ñ€ÑƒÐ¶ÐµÐ½Ñ‹, Ñ€Ð°Ð·Ð¼ÐµÑ€:", videoElement.videoWidth, "x", videoElement.videoHeight);
          setVideoReady(true); // Ð£ÑÑ‚Ð°Ð½Ð°Ð²Ð»Ð¸Ð²Ð°ÐµÐ¼ ÑÑ€Ð°Ð·Ñƒ Ð¿Ñ€Ð¸ Ð·Ð°Ð³Ñ€ÑƒÐ·ÐºÐµ Ð¼ÐµÑ‚Ð°Ð´Ð°Ð½Ð½Ñ‹Ñ…
          videoElement.play().catch(e => console.log("âŒ ÐžÑˆÐ¸Ð±ÐºÐ° play Ð¿Ð¾ÑÐ»Ðµ metadata:", e));
        };
        
        videoElement.onloadeddata = () => {
          console.log("âœ… Ð’Ð¸Ð´ÐµÐ¾ Ð´Ð°Ð½Ð½Ñ‹Ðµ Ð·Ð°Ð³Ñ€ÑƒÐ¶ÐµÐ½Ñ‹");
          setVideoReady(true);
        };
        
        videoElement.oncanplay = () => {
          console.log("âœ… Ð’Ð¸Ð´ÐµÐ¾ Ð³Ð¾Ñ‚Ð¾Ð²Ð¾ Ðº Ð²Ð¾ÑÐ¿Ñ€Ð¾Ð¸Ð·Ð²ÐµÐ´ÐµÐ½Ð¸ÑŽ");
          setVideoReady(true);
        };
        
        videoElement.onplaying = () => {
          console.log("âœ… Ð’Ð¸Ð´ÐµÐ¾ Ð²Ð¾ÑÐ¿Ñ€Ð¾Ð¸Ð·Ð²Ð¾Ð´Ð¸Ñ‚ÑÑ!");
          setVideoReady(true);
        };
        
        videoElement.onstalled = () => {
          console.log("âš ï¸ Ð’Ð¸Ð´ÐµÐ¾ Ð·Ð°ÑÑ‚Ñ€ÑÐ»Ð¾");
        };
        
        videoElement.onerror = (e) => {
          console.log("âŒ ÐžÑˆÐ¸Ð±ÐºÐ° Ð²Ð¸Ð´ÐµÐ¾:", e, videoElement.error);
        };
        
        // ÐŸÑ€Ð¸Ð½ÑƒÐ´Ð¸Ñ‚ÐµÐ»ÑŒÐ½Ñ‹Ðµ Ð¿Ð¾Ð¿Ñ‹Ñ‚ÐºÐ¸ Ð·Ð°Ð¿ÑƒÑÐºÐ°
        const tryPlay = async () => {
          try {
            console.log("ðŸ”„ ÐŸÐ¾Ð¿Ñ‹Ñ‚ÐºÐ° Ð²Ð¾ÑÐ¿Ñ€Ð¾Ð¸Ð·Ð²ÐµÐ´ÐµÐ½Ð¸Ñ...");
            await videoElement.play();
            console.log("âœ… Ð’Ð¸Ð´ÐµÐ¾ Ð·Ð°Ð¿ÑƒÑ‰ÐµÐ½Ð¾ ÑƒÑÐ¿ÐµÑˆÐ½Ð¾");
            setVideoReady(true);
          } catch (e) {
            console.log("âŒ ÐžÑˆÐ¸Ð±ÐºÐ° Ð°Ð²Ñ‚Ð¾Ð·Ð°Ð¿ÑƒÑÐºÐ°:", e.message);
            // Ð”Ð°Ð¶Ðµ ÐµÑÐ»Ð¸ Ð°Ð²Ñ‚Ð¾Ð·Ð°Ð¿ÑƒÑÐº Ð½Ðµ ÑÑ€Ð°Ð±Ð¾Ñ‚Ð°Ð», Ð¿Ð¾ÐºÐ°Ð·Ñ‹Ð²Ð°ÐµÐ¼ Ð²Ð¸Ð´ÐµÐ¾
            setVideoReady(true);
          }
        };
        
        // ÐÐµÑÐºÐ¾Ð»ÑŒÐºÐ¾ Ð¿Ð¾Ð¿Ñ‹Ñ‚Ð¾Ðº Ð·Ð°Ð¿ÑƒÑÐºÐ° Ñ Ñ€Ð°Ð·Ð½Ñ‹Ð¼Ð¸ Ð·Ð°Ð´ÐµÑ€Ð¶ÐºÐ°Ð¼Ð¸
        setTimeout(tryPlay, 50);
        setTimeout(tryPlay, 200);
        setTimeout(tryPlay, 500);
      }

      const mediaRecorder = new MediaRecorder(stream);
      mediaRecorderRef.current = mediaRecorder;

      const chunks = [];
      mediaRecorder.ondataavailable = (event) => {
        if (event.data.size > 0) {
          chunks.push(event.data);
        }
      };

      mediaRecorder.onstop = () => {
        const blob = new Blob(chunks, {
          type: recordingType === "video" ? "video/webm" : "audio/webm",
        });
        setRecordedBlob(blob);

        if (onRecordingComplete) {
          onRecordingComplete(blob, recordingType);
        }

        // ÐžÑÑ‚Ð°Ð½Ð¾Ð²ÐºÐ° Ð¿Ñ€ÐµÐ´Ð²Ð°Ñ€Ð¸Ñ‚ÐµÐ»ÑŒÐ½Ð¾Ð³Ð¾ Ð¿Ñ€Ð¾ÑÐ¼Ð¾Ñ‚Ñ€Ð°
        if (videoPreviewRef.current) {
          videoPreviewRef.current.srcObject = null;
        }

        stream.getTracks().forEach((track) => track.stop());
      };

      mediaRecorder.start();
      setIsRecording(true);
      startTimer();
      
      // ÐÐ²Ñ‚Ð¾Ð¼Ð°Ñ‚Ð¸Ñ‡ÐµÑÐºÐ¸ Ð½Ð°Ñ‡Ð¸Ð½Ð°ÐµÐ¼ Ð¿Ñ€Ð¾ÐºÑ€ÑƒÑ‚ÐºÑƒ Ð¿Ñ€Ð¸ Ð·Ð°Ð¿Ð¸ÑÐ¸
      if (!isScrolling) {
        startScrolling();
      }
    } catch (error) {
      console.error("ÐžÑˆÐ¸Ð±ÐºÐ° Ð´Ð¾ÑÑ‚ÑƒÐ¿Ð° Ðº Ð¼ÐµÐ´Ð¸Ð°:", error);
      alert("ÐÐµ ÑƒÐ´Ð°Ð»Ð¾ÑÑŒ Ð¿Ð¾Ð»ÑƒÑ‡Ð¸Ñ‚ÑŒ Ð´Ð¾ÑÑ‚ÑƒÐ¿ Ðº Ð¼Ð¸ÐºÑ€Ð¾Ñ„Ð¾Ð½Ñƒ/ÐºÐ°Ð¼ÐµÑ€Ðµ");
    }
  };

  const stopRecording = () => {
    if (mediaRecorderRef.current && isRecording) {
      mediaRecorderRef.current.stop();
      setIsRecording(false);
      stopTimer();

      // ÐžÑÑ‚Ð°Ð½Ð¾Ð²ÐºÐ° Ð¿Ñ€ÐµÐ´Ð²Ð°Ñ€Ð¸Ñ‚ÐµÐ»ÑŒÐ½Ð¾Ð³Ð¾ Ð¿Ñ€Ð¾ÑÐ¼Ð¾Ñ‚Ñ€Ð°
      if (videoPreviewRef.current) {
        videoPreviewRef.current.srcObject = null;
      }
      setVideoReady(false);

      if (streamRef.current) {
        streamRef.current.getTracks().forEach((track) => track.stop());
      }
    }
  };

  const downloadRecording = () => {
    if (recordedBlob) {
      const url = URL.createObjectURL(recordedBlob);
      const a = document.createElement("a");
      a.href = url;
      a.download = `recording-${Date.now()}.${
        recordingType === "video" ? "webm" : "webm"
      }`;
      a.click();
      URL.revokeObjectURL(url);
    }
  };

  const formatTime = (seconds) => {
    const mins = Math.floor(seconds / 60);
    const secs = seconds % 60;
    return `${mins.toString().padStart(2, "0")}:${secs
      .toString()
      .padStart(2, "0")}`;
  };

  // Ð˜ÑÐ¿Ñ€Ð°Ð²Ð»ÐµÐ½Ð½Ñ‹Ð¹ useEffect Ð´Ð»Ñ ÐºÐ¾Ñ€Ñ€ÐµÐºÑ‚Ð½Ð¾Ð³Ð¾ Ð¾Ð±Ð½Ð¾Ð²Ð»ÐµÐ½Ð¸Ñ Ð¿Ñ€Ð¾ÐºÑ€ÑƒÑ‚ÐºÐ¸
  useEffect(() => {
    if (isScrolling && !isPaused && scrollIntervalRef.current) {
      // ÐŸÐµÑ€ÐµÐ·Ð°Ð¿ÑƒÑÐºÐ°ÐµÐ¼ Ð¸Ð½Ñ‚ÐµÑ€Ð²Ð°Ð» Ð¿Ñ€Ð¸ Ð¸Ð·Ð¼ÐµÐ½ÐµÐ½Ð¸Ð¸ ÑÐºÐ¾Ñ€Ð¾ÑÑ‚Ð¸
      clearInterval(scrollIntervalRef.current);
      scrollIntervalRef.current = setInterval(() => {
        if (scrollContainerRef.current && isScrolling && !isPaused) {
          const container = scrollContainerRef.current;
          const scrollStep = (speed / 50) * 2;
          container.scrollTop += scrollStep;

          if (
            container.scrollTop >=
            container.scrollHeight - container.clientHeight
          ) {
            stopScrolling();
          }
        }
      }, 50);
    }
  }, [speed, isScrolling, isPaused]);

  useEffect(() => {
    return () => {
      stopScrolling();
      stopTimer();
      setVideoReady(false);
      if (streamRef.current) {
        streamRef.current.getTracks().forEach((track) => track.stop());
      }
    };
  }, []);

  if (!isOpen) return null;

  return (
    <div className="fixed inset-0 bg-black/50 backdrop-blur-md flex items-center justify-center z-50 p-4">
      <div className="bg-white/95 backdrop-blur-xl rounded-2xl w-full max-w-7xl h-[90vh] flex flex-col overflow-hidden border border-slate-200/50 shadow-2xl">
        {/* Ð¨Ð°Ð¿ÐºÐ° Ð¼Ð¾Ð´Ð°Ð»Ð° */}
        <div className="flex items-center justify-between p-6 border-b border-slate-200/50 bg-gradient-to-r from-slate-50 to-blue-50">
          <div className="flex items-center space-x-4">
            <div className="w-12 h-12 bg-gradient-to-r from-slate-600 to-blue-600 rounded-2xl flex items-center justify-center shadow-lg">
              <span className="text-white text-2xl">ðŸ‘ï¸</span>
            </div>
            <div>
              <h2 className="text-2xl font-bold bg-gradient-to-r from-slate-700 to-blue-700 bg-clip-text text-transparent">
                Ð¢ÐµÐ»ÐµÐ¿Ñ€Ð¾Ð¼Ð¿Ñ‚ÐµÑ€
              </h2>
              <p className="text-sm text-gray-600 font-medium">
                ÐŸÑ€Ð¾Ñ„ÐµÑÑÐ¸Ð¾Ð½Ð°Ð»ÑŒÐ½Ð°Ñ Ð·Ð°Ð¿Ð¸ÑÑŒ Ñ ÑÑƒÑ„Ð»ÐµÑ€Ð¾Ð¼
              </p>
            </div>
          </div>

          <div className="flex items-center space-x-2">
            <button
              onClick={() => setShowSettings(!showSettings)}
              className="p-3 hover:bg-slate-100/80 rounded-xl transition-all duration-300 backdrop-blur-sm border border-slate-200/50"
            >
              âš™ï¸
            </button>
            <button
              onClick={onClose}
              className="p-3 hover:bg-slate-100/80 rounded-xl transition-all duration-300 backdrop-blur-sm border border-slate-200/50"
            >
              âœ•
            </button>
          </div>
        </div>

        <div className="flex flex-1 overflow-hidden">
          {/* ÐžÑÐ½Ð¾Ð²Ð½Ð°Ñ Ð¾Ð±Ð»Ð°ÑÑ‚ÑŒ Ñ Ñ‚ÐµÐ»ÐµÐ¿Ñ€Ð¾Ð¼Ð¿Ñ‚ÐµÑ€Ð¾Ð¼ */}
          <div className="flex-1 flex flex-col">
            {/* ÐÐ°ÑÑ‚Ñ€Ð¾Ð¹ÐºÐ¸ */}
            {showSettings && (
              <div className="bg-gradient-to-r from-slate-50/90 to-blue-50/90 backdrop-blur-xl p-6 border-b border-slate-200/50">
                <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
                  <div>
                    <label className="block text-sm font-semibold text-slate-700 mb-3">
                      Ð¡ÐºÐ¾Ñ€Ð¾ÑÑ‚ÑŒ Ð¿Ñ€Ð¾ÐºÑ€ÑƒÑ‚ÐºÐ¸: {speed}%
                    </label>
                    <input
                      type="range"
                      min="10"
                      max="200"
                      value={speed}
                      onChange={(e) => setSpeed(parseInt(e.target.value))}
                      className="w-full h-2 bg-slate-200 rounded-lg appearance-none cursor-pointer"
                    />
                    <div className="flex justify-between text-xs text-slate-500 mt-1">
                      <span>ÐœÐµÐ´Ð»ÐµÐ½Ð½Ð¾</span>
                      <span>Ð‘Ñ‹ÑÑ‚Ñ€Ð¾</span>
                    </div>
                  </div>
                  <div>
                    <label className="block text-sm font-semibold text-slate-700 mb-3">
                      Ð Ð°Ð·Ð¼ÐµÑ€ ÑˆÑ€Ð¸Ñ„Ñ‚Ð°: {fontSize}px
                    </label>
                    <input
                      type="range"
                      min="16"
                      max="48"
                      value={fontSize}
                      onChange={(e) => setFontSize(parseInt(e.target.value))}
                      className="w-full h-2 bg-slate-200 rounded-lg appearance-none cursor-pointer"
                    />
                    <div className="flex justify-between text-xs text-slate-500 mt-1">
                      <span>ÐœÐµÐ»ÐºÐ¸Ð¹</span>
                      <span>ÐšÑ€ÑƒÐ¿Ð½Ñ‹Ð¹</span>
                    </div>
                  </div>
                  <div>
                    <label className="block text-sm font-semibold text-slate-700 mb-3">
                      Ð¢Ð¸Ð¿ Ð·Ð°Ð¿Ð¸ÑÐ¸
                    </label>
                    <select
                      value={recordingType}
                      onChange={(e) => setRecordingType(e.target.value)}
                      className="w-full p-3 bg-white/90 backdrop-blur-sm border-2 border-slate-200 rounded-xl text-slate-900 focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-blue-500 transition-all duration-300 shadow-lg font-medium"
                    >
                      <option value="audio">Ð¢Ð¾Ð»ÑŒÐºÐ¾ Ð°ÑƒÐ´Ð¸Ð¾</option>
                      <option value="video">Ð’Ð¸Ð´ÐµÐ¾ + Ð°ÑƒÐ´Ð¸Ð¾</option>
                    </select>
                  </div>
                </div>
              </div>
            )}

            {/* ÐžÐ±Ð»Ð°ÑÑ‚ÑŒ Ñ‚ÐµÐ»ÐµÐ¿Ñ€Ð¾Ð¼Ð¿Ñ‚ÐµÑ€Ð° */}
            <div className="flex-1 relative bg-black overflow-hidden">
              <div
                ref={scrollContainerRef}
                className="h-full overflow-y-scroll px-12 py-20"
                style={{
                  scrollbarWidth: "none",
                  msOverflowStyle: "none",
                }}
              >
                <style jsx>{`
                  .scrollbar-hide::-webkit-scrollbar {
                    display: none;
                  }
                `}</style>
                <div
                  className="text-white leading-relaxed max-w-4xl mx-auto text-center font-medium"
                  style={{
                    fontSize: `${fontSize}px`,
                    lineHeight: "1.8",
                    textShadow: "0 2px 4px rgba(0,0,0,0.5)",
                  }}
                >
                  {script}
                </div>
                <div className="h-screen"></div>
              </div>

              {/* Ð˜Ð½Ð´Ð¸ÐºÐ°Ñ‚Ð¾Ñ€ Ð·Ð°Ð¿Ð¸ÑÐ¸ */}
              {isRecording && (
                <div className="absolute top-6 left-6 flex items-center space-x-3 bg-gradient-to-r from-red-500 to-pink-500 text-white px-6 py-3 rounded-2xl shadow-2xl backdrop-blur-xl border border-red-400/30">
                  <div className="w-4 h-4 bg-white rounded-full animate-pulse"></div>
                  <span className="font-mono font-bold text-lg">
                    {formatTime(recordingTime)}
                  </span>
                  <span className="text-sm opacity-90">REC</span>
                </div>
              )}

              {/* Ð˜Ð½Ð´Ð¸ÐºÐ°Ñ‚Ð¾Ñ€ Ð¿Ñ€Ð¾ÐºÑ€ÑƒÑ‚ÐºÐ¸ */}
              {isScrolling && (
                <div className="absolute bottom-6 left-1/2 transform -translate-x-1/2 bg-blue-500/80 text-white px-4 py-2 rounded-xl backdrop-blur-xl border border-blue-400/30">
                  <div className="flex items-center space-x-2">
                    <div className="w-2 h-2 bg-white rounded-full animate-pulse"></div>
                    <span className="text-sm font-medium">
                      {isPaused ? "ÐŸÐ°ÑƒÐ·Ð°" : "ÐŸÑ€Ð¾ÐºÑ€ÑƒÑ‚ÐºÐ° Ð°ÐºÑ‚Ð¸Ð²Ð½Ð°"}
                    </span>
                  </div>
                </div>
              )}
            </div>
          </div>

          {/* ÐŸÐ°Ð½ÐµÐ»ÑŒ ÑƒÐ¿Ñ€Ð°Ð²Ð»ÐµÐ½Ð¸Ñ */}
          <div className="w-96 bg-gradient-to-br from-white/90 to-slate-50/90 backdrop-blur-xl border-l border-slate-200/50 p-6 flex flex-col">
            
