// components/pages/VideoEditorAppPage.jsx - с интегрированной системой подписок
import { useState, useEffect } from "react";
import { usePaywall } from "../common/Paywall";
import { useSubscription } from "../../contexts/SubscriptionContext";
import { 
  Play, 
  Pause, 
  Settings, 
  Download, 
  Mic,
  Volume2,
  SkipBack,
  SkipForward,
  Zap,
  Crown,
  Brain,
  FileText,
  AlertCircle,
  CheckCircle,
  Sparkles
} from "lucide-react";

const VideoEditorAppPage = () => {
  const { 
    PaywallComponent, 
    checkFeatureAccess, 
    checkUsageLimit 
  } = usePaywall();
  
  const { 
    updateUsage, 
    getCurrentPlan, 
    usage, 
    checkLimit,
    hasFeatureAccess 
  } = useSubscription();

  // Состояния компонента
  const [teleprompterSpeed, setTeleprompterSpeed] = useState(1);
  const [isRecording, setIsRecording] = useState(false);
  const [exportQuality, setExportQuality] = useState("HD");
  const [script, setScript] = useState("");
  const [isGeneratingScript, setIsGeneratingScript] = useState(false);
  const [scriptTopic, setScriptTopic] = useState("");

  const currentPlan = getCurrentPlan();

  // Проверка лимитов при загрузке
  useEffect(() => {
    // Можно добавить проверки при инициализации
  }, []);

  // Обработка изменения скорости телесуфлёра
  const handleSpeedChange = (newSpeed) => {
    if (newSpeed > 5 && !checkFeatureAccess("advanced_teleprompter", {
      title: "Advanced Teleprompter",
      description: "Unlock teleprompter speeds up to 12x for professional video creation",
      requiredPlan: "creator"
    })) {
      return;
    }
    setTeleprompterSpeed(newSpeed);
  };

  // Обработка начала записи
  const handleStartRecording = () => {
    if (!checkUsageLimit("videos", 1, {
      title: "Video Creation Limit",
      description: "You've reached your monthly video creation limit",
      requiredPlan: "creator"
    })) {
      return;
    }

    setIsRecording(true);
    console.log("Recording started...");
  };

  // Обработка остановки записи
  const handleStopRecording = async () => {
    setIsRecording(false);
    
    // Обновляем статистику использования
    try {
      await updateUsage("videosThisMonth", 1);
      console.log("Video created, usage updated");
    } catch (error) {
      console.error("Failed to update usage:", error);
    }
  };

  // Обработка экспорта видео
  const handleExport = (quality) => {
    if (quality === "4K" && !checkFeatureAccess("4k_export", {
      title: "4K Export",
      description: "Export your videos in stunning 4K quality",
      requiredPlan: "creator"
    })) {
      return;
    }

    setExportQuality(quality);
    console.log(`Exporting in ${quality} quality...`);
  };

  // Генерация AI скрипта
  const handleGenerateScript = async () => {
    if (!scriptTopic.trim()) {
      alert("Please enter a video topic");
      return;
    }

    // Проверяем лимит по словам
    const maxWords = currentPlan.limits.scriptWords;
    if (scriptTopic.length > maxWords) {
      checkFeatureAccess("unlimited_script_words", {
        title: "Script Word Limit",
        description: `Your current plan allows scripts up to ${maxWords} words`,
        requiredPlan: "creator"
      });
      return;
    }

    setIsGeneratingScript(true);
    
    try {
      // Имитация генерации скрипта
      await new Promise(resolve => setTimeout(resolve, 2000));
      
      const generatedScript = `Hey everyone! Welcome back to my channel. Today we're talking about ${scriptTopic}. 
      
This is going to be an amazing video where I'll share some incredible insights about ${scriptTopic} that will completely change how you think about this topic.

Let's dive right in...`;

      setScript(generatedScript);
      
      // Обновляем статистику
      await updateUsage("scriptsGenerated", 1);
      
    } catch (error) {
      console.error("Failed to generate script:", error);
      alert("Failed to generate script. Please try again.");
    } finally {
      setIsGeneratingScript(false);
    }
  };

  // Получение процента использования
  const getUsagePercentage = (used, limit) => {
    if (limit === -1) return 0; // Unlimited
    return Math.min((used / limit) * 100, 100);
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-slate-50 via-blue-50 to-indigo-50">
      <div className="max-w-7xl mx-auto py-8 px-6">
        
        {/* Header */}
        <div className="flex items-center justify-between mb-8">
          <div>
            <h1 className="text-3xl font-bold text-gray-900 mb-2">AI Video Studio</h1>
            <p className="text-gray-600">Create professional videos with AI assistance</p>
          </div>
          
          <div className="flex items-center space-x-4">
            {/* Current Plan Badge */}
            <div className="bg-white/80 backdrop-blur-sm rounded-xl px-4 py-2 border border-slate-200/50 flex items-center space-x-2">
              <Crown size={16} className={currentPlan.id === 'starter' ? 'text-gray-400' : 'text-yellow-500'} />
              <span className="text-sm text-gray-600">Plan: </span>
              <span className="font-semibold text-gray-900">{currentPlan.name}</span>
              {currentPlan.id === 'starter' && (
                <button
                  onClick={() => checkFeatureAccess("upgrade")}
                  className="ml-2 bg-gradient-to-r from-blue-500 to-purple-500 text-white px-3 py-1 rounded-lg text-xs font-medium hover:from-blue-600 hover:to-purple-600 transition-colors"
                >
                  Upgrade
                </button>
              )}
            </div>
          </div>
        </div>

        <div className="grid grid-cols-1 xl:grid-cols-4 gap-8">
          
          {/* Main Video Area */}
          <div className="xl:col-span-3">
            
            {/* Video Preview */}
            <div className="bg-white/80 backdrop-blur-sm rounded-2xl p-6 shadow-xl border border-slate-200/50 mb-6">
              <div className="aspect-video bg-black rounded-xl mb-4 flex items-center justify-center relative overflow-hidden">
                <div className="text-white text-center">
                  <Play size={48} className="mx-auto mb-4 opacity-50" />
                  <p className="text-lg opacity-70">Video Preview</p>
                </div>
                
                {/* Recording Indicator */}
                {isRecording && (
                  <div className="absolute top-4 right-4 flex items-center space-x-2 bg-red-500 px-3 py-1 rounded-full">
                    <div className="w-2 h-2 bg-white rounded-full animate-pulse"></div>
                    <span className="text-white text-sm font-semibold">REC</span>
                  </div>
                )}
              </div>

              {/* Video Controls */}
              <div className="flex items-center justify-between">
                <div className="flex items-center space-x-4">
                  <button
                    onClick={isRecording ? handleStopRecording : handleStartRecording}
                    className={`flex items-center space-x-2 px-4 py-2 rounded-xl font-semibold transition-all ${
                      isRecording
                        ? "bg-red-500 hover:bg-red-600 text-white"
                        : "bg-gradient-to-r from-blue-500 to-purple-500 hover:from-blue-600 hover:to-purple-600 text-white"
                    }`}
                  >
                    <Mic size={20} />
                    <span>{isRecording ? "Stop Recording" : "Start Recording"}</span>
                  </button>

                  <div className="flex items-center space-x-2">
                    <SkipBack size={20} className="text-gray-600 cursor-pointer hover:text-gray-800" />
                    <button className="p-2 bg-gray-100 hover:bg-gray-200 rounded-lg transition-colors">
                      <Play size={20} className="text-gray-700" />
                    </button>
                    <SkipForward size={20} className="text-gray-600 cursor-pointer hover:text-gray-800" />
                  </div>
                </div>

                <div className="flex items-center space-x-4">
                  <Volume2 size={20} className="text-gray-600" />
                  
                  {/* Export Quality Selector */}
                  <div className="relative group">
                    <button className="flex items-center space-x-2 bg-green-500 hover:bg-green-600 text-white px-4 py-2 rounded-xl font-semibold transition-colors">
                      <Download size={20} />
                      <span>Export {exportQuality}</span>
                    </button>
                    
                    <div className="absolute bottom-full mb-2 right-0 bg-white rounded-lg shadow-lg border border-gray-200 p-2 opacity-0 group-hover:opacity-100 transition-opacity pointer-events-none group-hover:pointer-events-auto z-10">
                      <button
                        onClick={() => handleExport("HD")}
                        className="block w-full text-left px-3 py-2 text-sm hover:bg-gray-100 rounded"
                      >
                        HD (1080p)
                      </button>
                      <button
                        onClick={() => handleExport("4K")}
                        className="block w-full text-left px-3 py-2 text-sm hover:bg-gray-100 rounded flex items-center justify-between"
                      >
                        4K (2160p)
                        {currentPlan.id === 'starter' && <Crown size={16} className="text-blue-500" />}
                      </button>
                    </div>
                  </div>
                </div>
              </div>
            </div>

            {/* Teleprompter */}
            <div className="bg-white/80 backdrop-blur-sm rounded-2xl p-6 shadow-xl border border-slate-200/50">
              <div className="flex items-center justify-between mb-4">
                <h3 className="text-xl font-bold text-gray-900 flex items-center space-x-2">
                  <FileText size={20} />
                  <span>Smart Teleprompter</span>
                </h3>
                
                <div className="flex items-center space-x-4">
                  <div className="flex items-center space-x-2">
                    <Zap size={16} className="text-blue-500" />
                    <span className="text-sm text-gray-600">Speed:</span>
                    <select
                      value={teleprompterSpeed}
                      onChange={(e) => handleSpeedChange(parseInt(e.target.value))}
                      className="border border-gray-300 rounded-lg px-2 py-1 text-sm focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                    >
                      {[1, 2, 3, 4, 5].map(speed => (
                        <option key={speed} value={speed}>{speed}x</option>
                      ))}
                      {currentPlan.limits.maxSpeed >= 12 && (
                        [6, 7, 8, 9, 10, 11, 12].map(speed => (
                          <option key={speed} value={speed}>{speed}x</option>
                        ))
                      )}
                    </select>
                  </div>
                  
                  {currentPlan.id === 'starter' && (
                    <button
                      onClick={() => checkFeatureAccess("advanced_teleprompter")}
                      className="flex items-center space-x-1 text-blue-600 hover:text-blue-700 text-sm font-medium bg-blue-50 px-3 py-1 rounded-lg border border-blue-200 hover:bg-blue-100 transition-colors"
                    >
                      <Crown size={16} />
                      <span>Unlock 12x Speed</span>
                    </button>
                  )}
                </div>
              </div>

              <div className="bg-black rounded-xl p-6 min-h-[200px] text-white text-lg leading-relaxed relative">
                {script ? (
                  <div className="text-center">
                    {script}
                    <div className="h-0.5 bg-red-500 mt-4 rounded-full opacity-60 w-full"></div>
                  </div>
                ) : (
                  <div className="flex items-center justify-center h-full text-gray-500">
                    <div className="text-center">
                      <FileText size={48} className="mx-auto mb-4 opacity-50" />
                      <p>Generate an AI script to start using the teleprompter</p>
                    </div>
                  </div>
                )}
              </div>
            </div>
          </div>

          {/* Sidebar */}
          <div className="space-y-6">
            
            {/* Plan Usage */}
            <div className="bg-white/80 backdrop-blur-sm rounded-2xl p-6 shadow-xl border border-slate-200/50">
              <h3 className="text-lg font-bold text-gray-900 mb-4 flex items-center space-x-2">
                <CheckCircle size={20} className="text-green-500" />
                <span>Monthly Usage</span>
              </h3>
              
              <div className="space-y-4">
                {/* Videos Created */}
                <div>
                  <div className="flex justify-between text-sm mb-1">
                    <span className="text-gray-600">Videos Created</span>
                    <span className="font-medium">
                      {usage.videosThisMonth} / {currentPlan.limits.videos === -1 ? '∞' : currentPlan.limits.videos}
                    </span>
                  </div>
                  {currentPlan.limits.videos !== -1 && (
                    <div className="w-full bg-gray-200 rounded-full h-2">
                      <div 
                        className="bg-gradient-to-r from-blue-500 to-purple-500 h-2 rounded-full transition-all duration-300" 
                        style={{ width: `${getUsagePercentage(usage.videosThisMonth, currentPlan.limits.videos)}%` }}
                      ></div>
                    </div>
                  )}
                  {currentPlan.limits.videos === -1 && (
                    <div className="text-sm text-green-600 font-semibold flex items-center space-x-1">
                      <Sparkles size={16} />
                      <span>Unlimited</span>
                    </div>
                  )}
                </div>

                {/* Storage Used */}
                <div>
                  <div className="flex justify-between text-sm mb-1">
                    <span className="text-gray-600">Storage Used</span>
                    <span className="font-medium">
                      {(usage.storageUsed / 1024).toFixed(1)} GB / {(currentPlan.limits.storage / 1024).toFixed(0)} GB
                    </span>
                  </div>
                  <div className="w-full bg-gray-200 rounded-full h-2">
                    <div 
                      className="bg-gradient-to-r from-green-500 to-teal-500 h-2 rounded-full transition-all duration-300" 
                      style={{ width: `${getUsagePercentage(usage.storageUsed, currentPlan.limits.storage)}%` }}
                    ></div>
                  </div>
                </div>

                {/* Usage Warning */}
                {currentPlan.limits.videos !== -1 && usage.videosThisMonth >= currentPlan.limits.videos * 0.8 && (
                  <div className="bg-orange-50 border border-orange-200 rounded-lg p-3 flex items-start space-x-2">
                    <AlertCircle size={16} className="text-orange-600 mt-0.5 flex-shrink-0" />
                    <div className="text-sm">
                      <p className="text-orange-800 font-medium">Usage Warning</p>
                      <p className="text-orange-700">You're approaching your monthly limit</p>
                    </div>
                  </div>
                )}
              </div>
            </div>

            {/* AI Script Generator */}
            <div className="bg-white/80 backdrop-blur-sm rounded-2xl p-6 shadow-xl border border-slate-200/50">
              <h3 className="text-lg font-bold text-gray-900 mb-4 flex items-center space-x-2">
                <Brain size={20} className="text-purple-500" />
                <span>AI Script Generator</span>
              </h3>
              
              <div className="space-y-4">
                <input
                  type="text"
                  placeholder="Enter your video topic..."
                  value={scriptTopic}
                  onChange={(e) => setScriptTopic(e.target.value)}
                  className="w-full p-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-purple-500 focus:border-transparent"
                  maxLength={100}
                />
                
                <div className="flex justify-between items-center text-xs text-gray-500">
                  <span>Max {currentPlan.limits.scriptWords} words per script</span>
                  <span>{scriptTopic.length}/100</span>
                </div>
                
                <button
                  onClick={handleGenerateScript}
                  disabled={isGeneratingScript || !scriptTopic.trim()}
                  className="w-full bg-gradient-to-r from-purple-500 to-pink-500 hover:from-purple-600 hover:to-pink-600 text-white px-4 py-3 rounded-lg font-semibold transition-colors disabled:opacity-50 disabled:cursor-not-allowed flex items-center justify-center space-x-2"
                >
                  {isGeneratingScript ? (
                    <>
                      <div className="animate-spin rounded-full h-4 w-4 border-b-2 border-white"></div>
                      <span>Generating...</span>
                    </>
                  ) : (
                    <>
                      <Brain size={16} />
                      <span>Generate AI Script</span>
                    </>
                  )}
                </button>
                
                {script && (
                  <div className="mt-4 p-3 bg-green-50 border border-green-200 rounded-lg">
                    <div className="flex items-center space-x-2 text-green-800 font-medium text-sm mb-2">
                      <CheckCircle size={16} />
                      <span>Script Generated Successfully!</span>
                    </div>
                    <p className="text-green-700 text-sm">Your script is now loaded in the teleprompter above.</p>
                  </div>
                )}
              </div>
            </div>

            {/* Upgrade Prompt for Starter Plan */}
            {currentPlan.id === 'starter' && (
              <div className="bg-gradient-to-r from-blue-500 to-purple-500 rounded-2xl p-6 text-white">
                <div className="text-center">
                  <Crown size={32} className="mx-auto mb-4 text-yellow-300" />
                  <h3 className="text-lg font-bold mb-2">Unlock Full Potential</h3>
                  <p className="text-blue-100 text-sm mb-4">
                    Upgrade to Creator plan for unlimited videos, 4K export, and advanced features.
                  </p>
                  <button
                    onClick={() => checkFeatureAccess("upgrade")}
                    className="bg-white text-blue-600 font-semibold px-4 py-2 rounded-lg hover:bg-gray-100 transition-colors"
                  >
                    Upgrade Now
                  </button>
                </div>
              </div>
            )}
          </div>
        </div>
      </div>

      {/* Paywall Component */}
      <PaywallComponent />
    </div>
  );
};

export default VideoEditorAppPage;
