import { useState, useEffect } from "react";
import { X, Download, Check, AlertCircle, FolderOpen } from "lucide-react";

export const VideoExportModal = ({
  isOpen,
  onClose,
  timelineItems,
  tracks,
  videoDuration,
  onExport,
}) => {
  const [exportSettings, setExportSettings] = useState({
    resolution: "1920x1080",
    fps: 30,
    bitrate: 5000,
    format: "mp4",
    quality: "high",
    saveDirectory: null,
    filename: `video_${Date.now()}`
  });

  const [isExporting, setIsExporting] = useState(false);
  const [exportProgress, setExportProgress] = useState(0);
  const [exportStatus, setExportStatus] = useState("idle");
  const [estimatedTime, setEstimatedTime] = useState(null);

  // Предустановки для быстрого выбора
  const presets = [
    { name: "YouTube 1080p", resolution: "1920x1080", fps: 30, quality: "high", format: "mp4" },
    { name: "Instagram", resolution: "1080x1080", fps: 30, quality: "medium", format: "mp4" },
    { name: "TikTok", resolution: "1080x1920", fps: 30, quality: "medium", format: "mp4" },
    { name: "Web", resolution: "1280x720", fps: 30, quality: "medium", format: "webm" },
  ];

  const qualityOptions = [
    { value: "low", label: "Низкое", bitrate: 1000 },
    { value: "medium", label: "Среднее", bitrate: 3000 },
    { value: "high", label: "Высокое", bitrate: 5000 },
    { value: "ultra", label: "Ультра", bitrate: 10000 },
  ];

  const formatOptions = [
    { value: "mp4", label: "MP4" },
    { value: "webm", label: "WebM" },
    { value: "mov", label: "MOV" },
  ];

  // Быстрый расчет времени экспорта
  useEffect(() => {
    if (videoDuration && timelineItems) {
      const complexity = timelineItems.filter(item => item.trackType === "overlay").length;
      const baseTime = Math.max(2, videoDuration * 0.1); // Минимум 2 секунды
      const complexityMultiplier = 1 + (complexity * 0.1);
      setEstimatedTime(Math.ceil(baseTime * complexityMultiplier));
    }
  }, [videoDuration, timelineItems]);

  // Синхронизация битрейта с качеством
  useEffect(() => {
    const quality = qualityOptions.find(q => q.value === exportSettings.quality);
    if (quality && quality.bitrate !== exportSettings.bitrate) {
      setExportSettings(prev => ({ ...prev, bitrate: quality.bitrate }));
    }
  }, [exportSettings.quality]);

  // Выбор папки для сохранения
  const handleSelectDirectory = async () => {
    try {
      if ('showDirectoryPicker' in window) {
        const directoryHandle = await window.showDirectoryPicker();
        setExportSettings(prev => ({ 
          ...prev, 
          saveDirectory: directoryHandle 
        }));
      } else {
        // Fallback для браузеров без поддержки File System Access API
        alert('Ваш браузер не поддерживает выбор папки. Файл будет сохранен в папку загрузок.');
      }
    } catch (error) {
      if (error.name !== 'AbortError') {
        console.error('Ошибка выбора папки:', error);
      }
    }
  };

  // Применение пресета
  const applyPreset = (preset) => {
    setExportSettings(prev => ({
      ...prev,
      ...preset
    }));
  };

  const getFileSize = () => {
    if (!videoDuration) return "~";
    const sizeInMB = (exportSettings.bitrate * videoDuration * 1000) / (8 * 1024 * 1024);
    return sizeInMB < 1024 ? `~${sizeInMB.toFixed(0)} МБ` : `~${(sizeInMB / 1024).toFixed(1)} ГБ`;
  };

  const handleExport = async () => {
    if (!timelineItems || timelineItems.length === 0) {
      alert('Нет элементов для экспорта!');
      return;
    }

    // Детальная диагностика элементов
    console.log('=== ДИАГНОСТИКА ЭЛЕМЕНТОВ TIMELINE ===');
    timelineItems.forEach((item, index) => {
      console.log(`Элемент ${index}:`, {
        id: item.id,
        name: item.name,
        type: item.type,
        trackType: item.trackType,
        startTime: item.startTime,
        duration: item.duration,
        src: item.src,
        file: item.file,
        url: item.url,
        data: item.data,
        content: item.content,
        text: item.text,
        videoElement: item.videoElement,
        imageElement: item.imageElement,
        allKeys: Object.keys(item)
      });
    });
    console.log('=====================================');

    setIsExporting(true);
    setExportStatus("processing");
    setExportProgress(0);

    try {
      await simulateVideoExport();
      setExportStatus("success");
    } catch (error) {
      console.error("Export failed:", error);
      setExportStatus("error");
    } finally {
      setIsExporting(false);
    }
  };

  const simulateVideoExport = async () => {
    const totalSteps = 50; // Уменьшили количество шагов для быстрой подготовки
    const stepDuration = Math.min(100, (estimatedTime * 1000) / totalSteps); // Максимум 100мс на шаг

    for (let i = 0; i <= totalSteps; i++) {
      setExportProgress(Math.round((i / totalSteps) * 100));
      await new Promise(resolve => setTimeout(resolve, stepDuration));
    }

    await actualVideoExport();
  };

  const actualVideoExport = async () => {
    try {
      const videoExporter = new VideoExporter({
        timelineItems,
        tracks,
        videoDuration,
        settings: exportSettings,
      });

      const exportedVideoBlob = await videoExporter.render();
      await downloadVideo(exportedVideoBlob);
    } catch (error) {
      console.error('Ошибка при экспорте:', error);
      throw error;
    }
  };

  const downloadVideo = async (blob) => {
    const filename = `${exportSettings.filename}.${exportSettings.format}`;
    
    try {
      if (exportSettings.saveDirectory && 'showDirectoryPicker' in window) {
        // Сохранение в выбранную папку
        const fileHandle = await exportSettings.saveDirectory.getFileHandle(filename, {
          create: true,
        });
        const writable = await fileHandle.createWritable();
        await writable.write(blob);
        await writable.close();
      } else {
        // Стандартное скачивание
        const url = URL.createObjectURL(blob);
        const a = document.createElement("a");
        a.href = url;
        a.download = filename;
        document.body.appendChild(a);
        a.click();
        document.body.removeChild(a);
        URL.revokeObjectURL(url);
      }
    } catch (error) {
      console.error('Ошибка сохранения:', error);
      throw error;
    }
  };

  const handleClose = () => {
    if (!isExporting) {
      setExportStatus("idle");
      setExportProgress(0);
      onClose();
    }
  };

  if (!isOpen) return null;

  return (
    <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50 p-4">
      <div className="bg-white rounded-2xl shadow-xl max-w-lg w-full max-h-[90vh] overflow-hidden">
        <div className="flex items-center justify-between p-4 border-b border-gray-200">
          <h2 className="text-lg font-semibold text-gray-800 flex items-center">
            <Download size={20} className="mr-2 text-blue-600" />
            Экспорт видео
          </h2>
          <button
            onClick={handleClose}
            className="p-1 hover:bg-gray-100 rounded-full transition-colors"
            disabled={isExporting}
          >
            <X size={18} />
          </button>
        </div>

        <div className="p-4 space-y-4 max-h-[calc(90vh-120px)] overflow-y-auto">
          {/* Статус экспорта */}
          {exportStatus !== "idle" && (
            <div className={`p-3 rounded-lg ${
              exportStatus === "processing" ? "bg-blue-50 border border-blue-200" :
              exportStatus === "success" ? "bg-green-50 border border-green-200" :
              "bg-red-50 border border-red-200"
            }`}>
              <div className="flex items-center">
                {exportStatus === "processing" && (
                  <>
                    <div className="animate-spin rounded-full h-4 w-4 border-b-2 border-blue-600 mr-2"></div>
                    <div className="flex-1">
                      <div className="flex justify-between text-sm text-blue-700 mb-1">
                        <span>Экспорт...</span>
                        <span>{exportProgress}%</span>
                      </div>
                      <div className="w-full bg-blue-200 rounded-full h-1.5">
                        <div
                          className="bg-blue-600 h-1.5 rounded-full transition-all duration-300"
                          style={{ width: `${exportProgress}%` }}
                        />
                      </div>
                    </div>
                  </>
                )}
                {exportStatus === "success" && (
                  <>
                    <Check size={16} className="text-green-600 mr-2" />
                    <span className="text-green-700 text-sm font-medium">Готово!</span>
                  </>
                )}
                {exportStatus === "error" && (
                  <>
                    <AlertCircle size={16} className="text-red-600 mr-2" />
                    <span className="text-red-700 text-sm font-medium">Ошибка экспорта</span>
                  </>
                )}
              </div>
            </div>
          )}

          {/* Быстрые пресеты */}
          <div>
            <label className="block text-sm font-medium text-gray-700 mb-2">
              Быстрые настройки
            </label>
            <div className="grid grid-cols-2 gap-2">
              {presets.map((preset) => (
                <button
                  key={preset.name}
                  onClick={() => applyPreset(preset)}
                  className="p-2 text-sm border border-gray-300 rounded-lg hover:bg-gray-50 transition-colors"
                  disabled={isExporting}
                >
                  {preset.name}
                </button>
              ))}
            </div>
          </div>

          {/* Настройки экспорта */}
          <div className="grid grid-cols-2 gap-3">
            <div>
              <label className="block text-xs font-medium text-gray-700 mb-1">
                Разрешение
              </label>
              <select
                value={exportSettings.resolution}
                onChange={(e) => setExportSettings(prev => ({ ...prev, resolution: e.target.value }))}
                className="w-full p-2 text-sm border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                disabled={isExporting}
              >
                <option value="3840x2160">4K</option>
                <option value="1920x1080">1080p</option>
                <option value="1280x720">720p</option>
                <option value="854x480">480p</option>
                <option value="1080x1920">9:16 (TikTok)</option>
                <option value="1080x1080">1:1 (Instagram)</option>
              </select>
            </div>

            <div>
              <label className="block text-xs font-medium text-gray-700 mb-1">
                FPS
              </label>
              <select
                value={exportSettings.fps}
                onChange={(e) => setExportSettings(prev => ({ ...prev, fps: parseInt(e.target.value) }))}
                className="w-full p-2 text-sm border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                disabled={isExporting}
              >
                <option value="24">24</option>
                <option value="30">30</option>
                <option value="60">60</option>
              </select>
            </div>

            <div>
              <label className="block text-xs font-medium text-gray-700 mb-1">
                Качество
              </label>
              <select
                value={exportSettings.quality}
                onChange={(e) => setExportSettings(prev => ({ ...prev, quality: e.target.value }))}
                className="w-full p-2 text-sm border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                disabled={isExporting}
              >
                {qualityOptions.map((quality) => (
                  <option key={quality.value} value={quality.value}>
                    {quality.label}
                  </option>
                ))}
              </select>
            </div>

            <div>
              <label className="block text-xs font-medium text-gray-700 mb-1">
                Формат
              </label>
              <select
                value={exportSettings.format}
                onChange={(e) => setExportSettings(prev => ({ ...prev, format: e.target.value }))}
                className="w-full p-2 text-sm border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                disabled={isExporting}
              >
                {formatOptions.map((format) => (
                  <option key={format.value} value={format.value}>
                    {format.label}
                  </option>
                ))}
              </select>
            </div>
          </div>

          {/* Имя файла и папка сохранения */}
          <div className="space-y-2">
            <div>
              <label className="block text-xs font-medium text-gray-700 mb-1">
                Имя файла
              </label>
              <input
                type="text"
                value={exportSettings.filename}
                onChange={(e) => setExportSettings(prev => ({ ...prev, filename: e.target.value }))}
                className="w-full p-2 text-sm border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                disabled={isExporting}
                placeholder="Имя файла"
              />
            </div>

            <div>
              <label className="block text-xs font-medium text-gray-700 mb-1">
                Папка для сохранения
              </label>
              <button
                onClick={handleSelectDirectory}
                className="w-full p-2 text-sm border border-gray-300 rounded-lg hover:bg-gray-50 transition-colors flex items-center justify-between"
                disabled={isExporting}
              >
                <span className="text-gray-600">
                  {exportSettings.saveDirectory ? exportSettings.saveDirectory.name : 'Выберите папку'}
                </span>
                <FolderOpen size={16} />
              </button>
            </div>
          </div>

          {/* Информация */}
          <div className="bg-gray-50 rounded-lg p-3">
            <div className="grid grid-cols-2 gap-2 text-xs">
              <div>
                <span className="text-gray-600">Длительность:</span>
                <span className="ml-1 font-medium">{Math.round(videoDuration)}с</span>
              </div>
              <div>
                <span className="text-gray-600">Размер:</span>
                <span className="ml-1 font-medium">{getFileSize()}</span>
              </div>
              <div>
                <span className="text-gray-600">Битрейт:</span>
                <span className="ml-1 font-medium">{exportSettings.bitrate} kbps</span>
              </div>
              <div>
                <span className="text-gray-600">Время:</span>
                <span className="ml-1 font-medium">~{estimatedTime}с</span>
              </div>
            </div>
          </div>

          {/* Элементы для экспорта */}
          <div className="bg-gray-50 rounded-lg p-3">
            <div className="flex justify-between items-center mb-2">
              <span className="text-xs font-medium text-gray-800">Элементы</span>
              <span className="text-xs text-gray-600">{timelineItems?.length || 0}</span>
            </div>
            {timelineItems && timelineItems.length > 0 ? (
              <div className="space-y-1 max-h-20 overflow-y-auto">
                {timelineItems.slice(0, 3).map((item) => (
                  <div key={item.id} className="flex items-center justify-between text-xs">
                    <span className="text-gray-700 truncate">{item.name}</span>
                    <span className={`px-1 py-0.5 rounded text-xs ${
                      item.trackType === "main" ? "bg-blue-100 text-blue-700" :
                      item.trackType === "audio" ? "bg-green-100 text-green-700" :
                      "bg-purple-100 text-purple-700"
                    }`}>
                      {item.trackType}
                    </span>
                  </div>
                ))}
                {timelineItems.length > 3 && (
                  <div className="text-xs text-gray-500 text-center">
                    +{timelineItems.length - 3} ещё
                  </div>
                )}
              </div>
            ) : (
              <div className="text-xs text-gray-500 text-center py-2">
                Нет элементов
              </div>
            )}
          </div>
        </div>

        {/* Кнопки */}
        <div className="flex justify-end space-x-3 p-4 border-t border-gray-200">
          <button
            onClick={handleClose}
            className="px-4 py-2 text-sm text-gray-600 hover:text-gray-800 transition-colors"
            disabled={isExporting}
          >
            Отмена
          </button>
          <button
            onClick={handleExport}
            disabled={isExporting || !timelineItems || timelineItems.length === 0}
            className={`px-4 py-2 text-sm rounded-lg font-medium transition-all flex items-center ${
              isExporting || !timelineItems || timelineItems.length === 0
                ? "bg-gray-300 text-gray-500 cursor-not-allowed"
                : "bg-blue-600 text-white hover:bg-blue-700"
            }`}
          >
            {isExporting ? (
              <>
                <div className="animate-spin rounded-full h-3 w-3 border-b-2 border-white mr-2"></div>
                Экспорт...
              </>
            ) : (
              <>
                <Download size={14} className="mr-2" />
                Экспортировать
              </>
            )}
          </button>
        </div>
      </div>
    </div>
  );
};

// Улучшенный VideoExporter класс
class VideoExporter {
  constructor({ timelineItems, tracks, videoDuration, settings }) {
    this.timelineItems = timelineItems;
    this.tracks = tracks;
    this.videoDuration = videoDuration;
    this.settings = settings;

    this.canvas = document.createElement("canvas");
    this.ctx = this.canvas.getContext("2d");

    const [width, height] = settings.resolution.split("x").map(Number);
    this.canvas.width = width;
    this.canvas.height = height;
  }

  async render() {
    try {
      console.log('Начинаем экспорт видео:', {
        элементов: this.timelineItems.length,
        длительность: this.videoDuration,
        разрешение: this.settings.resolution,
        fps: this.settings.fps
      });

      // Подготавливаем элементы
      await this.prepareElements();

      // Создаем аудио контекст для микширования звука
      const audioContext = new (window.AudioContext || window.webkitAudioContext)();
      const audioDestination = audioContext.createMediaStreamDestination();
      
      // Микшируем аудио треки
      await this.setupAudioTracks(audioContext, audioDestination);

      // Создаем видео поток
      const videoStream = this.canvas.captureStream(this.settings.fps);
      
      // Объединяем видео и аудио потоки
      const combinedStream = new MediaStream([
        ...videoStream.getVideoTracks(),
        ...audioDestination.stream.getAudioTracks()
      ]);

      const options = {
        mimeType: this.getMimeType(),
        videoBitsPerSecond: this.settings.bitrate * 1000,
        audioBitsPerSecond: 128000, // 128 kbps для аудио
      };

      console.log('Настройки записи:', options);

      const mediaRecorder = new MediaRecorder(combinedStream, options);
      const chunks = [];

      mediaRecorder.ondataavailable = (event) => {
        if (event.data.size > 0) {
          chunks.push(event.data);
        }
      };

      return new Promise((resolve, reject) => {
        mediaRecorder.onstop = () => {
          console.log(`Запись остановлена. Всего чанков: ${chunks.length}`);
          audioContext.close(); // Закрываем аудио контекст
          
          const blob = new Blob(chunks, {
            type: `video/${this.settings.format}`,
          });
          console.log(`Создан blob: ${blob.size} байт, тип: ${blob.type}`);
          resolve(blob);
        };

        mediaRecorder.onerror = (error) => {
          console.error('Ошибка MediaRecorder:', error);
          audioContext.close();
          reject(error);
        };

        mediaRecorder.start(100);
        console.log('MediaRecorder запущен');

        this.renderFrames()
          .then(() => {
            console.log('Рендеринг кадров завершен');
            setTimeout(() => {
              mediaRecorder.stop();
            }, 500); // Увеличили задержку для стабильности
          })
          .catch(reject);
      });
    } catch (error) {
      console.error('Ошибка настройки записи:', error);
      throw error;
    }
  }

  async prepareElements() {
    console.log('=== ПОДГОТОВКА ЭЛЕМЕНТОВ ===');
    
    for (const item of this.timelineItems) {
      console.log(`Подготавливаем элемент: ${item.name}`, {
        type: item.type,
        trackType: item.trackType,
        hasFile: !!item.file,
        hasSrc: !!item.src,
        hasUrl: !!item.url,
        hasData: !!item.data,
        allProps: Object.keys(item)
      });

      // Определяем тип элемента по различным признакам
      const isVideo = this.isVideoElement(item);
      const isImage = this.isImageElement(item);
      const isAudio = this.isAudioElement(item);

      if (isVideo) {
        await this.prepareVideoElement(item);
      } else if (isImage) {
        await this.prepareImageElement(item);
      } else if (isAudio) {
        console.log(`Аудио элемент (пропускаем): ${item.name}`);
      } else {
        console.log(`Неопознанный элемент: ${item.name}, будет показан как текст`);
      }
    }
    
    console.log('=== ПОДГОТОВКА ЗАВЕРШЕНА ===');
  }

  isVideoElement(item) {
    // Проверяем различные признаки видео
    if (item.type === "video" || item.trackType === "main") return true;
    if (item.file && item.file.type && item.file.type.startsWith('video/')) return true;
    if (item.src && (item.src.includes('.mp4') || item.src.includes('.webm') || item.src.includes('.mov'))) return true;
    if (item.url && (item.url.includes('.mp4') || item.url.includes('.webm') || item.url.includes('.mov'))) return true;
    if (item.name && (item.name.includes('.mp4') || item.name.includes('.webm') || item.name.includes('.mov'))) return true;
    return false;
  }

  isImageElement(item) {
    // Проверяем различные признаки изображения
    if (item.type === "image") return true;
    if (item.file && item.file.type && item.file.type.startsWith('image/')) return true;
    if (item.src && (item.src.includes('.jpg') || item.src.includes('.png') || item.src.includes('.jpeg') || item.src.includes('.gif'))) return true;
    if (item.url && (item.url.includes('.jpg') || item.url.includes('.png') || item.url.includes('.jpeg') || item.url.includes('.gif'))) return true;
    if (item.name && (item.name.includes('.jpg') || item.name.includes('.png') || item.name.includes('.jpeg') || item.name.includes('.gif'))) return true;
    return false;
  }

  isAudioElement(item) {
    // Проверяем различные признаки аудио
    if (item.type === "audio" || item.trackType === "audio") return true;
    if (item.file && item.file.type && item.file.type.startsWith('audio/')) return true;
    if (item.src && (item.src.includes('.mp3') || item.src.includes('.wav') || item.src.includes('.ogg'))) return true;
    if (item.url && (item.url.includes('.mp3') || item.url.includes('.wav') || item.url.includes('.ogg'))) return true;
    if (item.name && (item.name.includes('.mp3') || item.name.includes('.wav') || item.name.includes('.ogg'))) return true;
    return false;
  }

  async prepareVideoElement(item) {
    if (item.videoElement) {
      console.log(`Видео элемент уже подготовлен: ${item.name}`);
      return;
    }

    try {
      const video = document.createElement("video");
      video.crossOrigin = "anonymous";
      video.muted = true;
      video.preload = "metadata";
      
      // Получаем URL из различных источников
      let videoUrl = null;
      
      if (item.file && item.file instanceof File) {
        videoUrl = URL.createObjectURL(item.file);
        console.log(`Создан URL для файла: ${videoUrl}`);
      } else if (item.src) {
        videoUrl = item.src;
        console.log(`Используем src: ${videoUrl}`);
      } else if (item.url) {
        videoUrl = item.url;
        console.log(`Используем url: ${videoUrl}`);
      } else if (item.data) {
        videoUrl = item.data;
        console.log(`Используем data: ${videoUrl}`);
      }

      if (!videoUrl) {
        console.warn(`Не найден URL для видео: ${item.name}`);
        return;
      }

      video.src = videoUrl;
      item.videoElement = video;
      
      // Ждем загрузки метаданных
      await new Promise((resolve, reject) => {
        const timeoutId = setTimeout(() => {
          console.warn(`Таймаут загрузки видео: ${item.name}`);
          reject(new Error('Timeout'));
        }, 5000);

        video.onloadedmetadata = () => {
          clearTimeout(timeoutId);
          console.log(`Видео загружено: ${item.name}, длительность: ${video.duration}с`);
          resolve();
        };
        
        video.onerror = (e) => {
          clearTimeout(timeoutId);
          console.error(`Ошибка загрузки видео: ${item.name}`, e);
          reject(e);
        };
      }).catch(() => {
        console.warn(`Не удалось загрузить видео: ${item.name}`);
      });
      
    } catch (error) {
      console.error(`Ошибка подготовки видео ${item.name}:`, error);
    }
  }

  async prepareImageElement(item) {
    if (item.imageElement) {
      console.log(`Изображение уже подготовлено: ${item.name}`);
      return;
    }

    try {
      const img = new Image();
      img.crossOrigin = "anonymous";
      
      // Получаем URL из различных источников
      let imageUrl = null;
      
      if (item.file && item.file instanceof File) {
        imageUrl = URL.createObjectURL(item.file);
        console.log(`Создан URL для изображения: ${imageUrl}`);
      } else if (item.src) {
        imageUrl = item.src;
        console.log(`Используем src для изображения: ${imageUrl}`);
      } else if (item.url) {
        imageUrl = item.url;
        console.log(`Используем url для изображения: ${imageUrl}`);
      } else if (item.data) {
        imageUrl = item.data;
        console.log(`Используем data для изображения: ${imageUrl}`);
      }

      if (!imageUrl) {
        console.warn(`Не найден URL для изображения: ${item.name}`);
        return;
      }

      img.src = imageUrl;
      item.imageElement = img;
      
      // Ждем загрузки изображения
      await new Promise((resolve, reject) => {
        const timeoutId = setTimeout(() => {
          console.warn(`Таймаут загрузки изображения: ${item.name}`);
          reject(new Error('Timeout'));
        }, 3000);

        img.onload = () => {
          clearTimeout(timeoutId);
          console.log(`Изображение загружено: ${item.name}, размер: ${img.naturalWidth}x${img.naturalHeight}`);
          resolve();
        };
        
        img.onerror = (e) => {
          clearTimeout(timeoutId);
          console.error(`Ошибка загрузки изображения: ${item.name}`, e);
          reject(e);
        };
      }).catch(() => {
        console.warn(`Не удалось загрузить изображение: ${item.name}`);
      });
      
    } catch (error) {
      console.error(`Ошибка подготовки изображения ${item.name}:`, error);
    }
  }

  async setupAudioTracks(audioContext, audioDestination) {
    console.log('Настройка аудио треков...');
    
    const audioElements = this.timelineItems.filter(item => this.isAudioElement(item) || this.isVideoElement(item));
    
    for (const item of audioElements) {
      try {
        let audioElement = null;
        
        if (this.isVideoElement(item) && item.videoElement) {
          // Для видео используем его аудио дорожку
          audioElement = item.videoElement;
        } else if (this.isAudioElement(item)) {
          // Создаем аудио элемент для аудио файлов
          audioElement = document.createElement('audio');
          audioElement.crossOrigin = 'anonymous';
          
          if (item.file && item.file instanceof File) {
            audioElement.src = URL.createObjectURL(item.file);
          } else if (item.src) {
            audioElement.src = item.src;
          } else if (item.url) {
            audioElement.src = item.url;
          } else if (item.data) {
            audioElement.src = item.data;
          }
          
          item.audioElement = audioElement;
        }
        
        if (audioElement) {
          // Подключаем к аудио контексту
          const source = audioContext.createMediaElementSource(audioElement);
          const gainNode = audioContext.createGain();
          
          source.connect(gainNode);
          gainNode.connect(audioDestination);
          
          // Настройки громкости (можно добавить в настройки)
          gainNode.gain.value = 0.8;
          
          console.log(`Подключен аудио для: ${item.name}`);
        }
      } catch (error) {
        console.warn(`Ошибка настройки аудио для ${item.name}:`, error);
      }
    }
  }

  getMimeType() {
    const testTypes = [
      'video/webm;codecs=vp9',
      'video/webm;codecs=vp8', 
      'video/webm'
    ];
    
    for (const type of testTypes) {
      if (MediaRecorder.isTypeSupported(type)) {
        console.log(`Используем MIME тип: ${type}`);
        return type;
      }
    }
    
    console.log('Используем fallback: video/webm');
    return 'video/webm';
  }

  async renderFrames() {
    const frameCount = Math.ceil(this.videoDuration * this.settings.fps);
    console.log(`Рендерим ${frameCount} кадров со скоростью ${this.settings.fps} FPS`);

    // Стабильный интервал рендеринга
    const frameInterval = 1000 / this.settings.fps;
    let lastFrameTime = performance.now();

    for (let frame = 0; frame < frameCount; frame++) {
      const currentTime = frame / this.settings.fps;
      
      // Синхронизируем аудио элементы с текущим временем
      await this.syncAudioElements(currentTime);
      
      // Рендерим кадр
      await this.renderFrame(currentTime);
      
      // Стабилизируем частоту кадров
      const now = performance.now();
      const elapsed = now - lastFrameTime;
      const targetDelay = frameInterval - elapsed;
      
      if (targetDelay > 0) {
        await new Promise(resolve => setTimeout(resolve, targetDelay));
      }
      
      lastFrameTime = performance.now();
      
      // Показываем прогресс каждые 30 кадров
      if (frame % 30 === 0) {
        console.log(`Прогресс: ${frame}/${frameCount} кадров (${(frame/frameCount*100).toFixed(1)}%)`);
      }
    }
    
    console.log('Все кадры отрендерены');
  }

  async renderFrame(currentTime) {
    // Очищаем canvas
    this.ctx.fillStyle = "#000000";
    this.ctx.fillRect(0, 0, this.canvas.width, this.canvas.height);

    // Получаем активные элементы для текущего времени
    const activeItems = this.timelineItems.filter(item => 
      this.isItemActive(item, currentTime)
    );

    if (activeItems.length === 0) {
      // Если нет активных элементов, показываем информационный кадр
      this.renderPlaceholder(`${currentTime.toFixed(1)}с`);
      return;
    }

    // Сортируем элементы по слоям
    const sortedItems = [...activeItems].sort((a, b) => {
      const order = { 
        main: 1, 
        video: 1, 
        background: 1,
        audio: 10, // аудио не рендерится, но ставим в конец
        overlay: 3, 
        text: 4,
        title: 4
      };
      return (order[a.trackType] || order[a.type] || 2) - (order[b.trackType] || order[b.type] || 2);
    });

    // Рендерим только первый визуальный элемент (главный слой)
    let hasRenderedMainContent = false;
    
    for (const item of sortedItems) {
      const relativeTime = currentTime - (item.startTime || 0);
      
      // Рендерим основной контент (видео/изображение)
      if (!hasRenderedMainContent && (this.isVideoElement(item) || this.isImageElement(item))) {
        await this.renderItem(item, relativeTime);
        hasRenderedMainContent = true;
      }
      // Рендерим оверлеи поверх основного контента
      else if (hasRenderedMainContent && (item.trackType === 'overlay' || item.type === 'text')) {
        await this.renderItem(item, relativeTime);
      }
    }
    
    // Если ничего не отрендерили, показываем заглушку
    if (!hasRenderedMainContent) {
      this.renderPlaceholder(`Нет контента: ${currentTime.toFixed(1)}с`);
    }
  }

  isItemActive(item, currentTime) {
    const startTime = item.startTime || 0;
    const duration = item.duration || 0;
    const endTime = startTime + duration;
    
    const isActive = currentTime >= startTime && currentTime < endTime;
    
    if (isActive) {
      console.log(`Элемент ${item.name} активен в ${currentTime.toFixed(2)}с (${startTime.toFixed(2)}-${endTime.toFixed(2)})`);
    }
    
    return isActive;
  }

  async renderItem(item, relativeTime) {
    try {
      this.ctx.save();
      
      console.log(`>>> Рендерим элемент: ${item.name}, тип: ${item.type}, trackType: ${item.trackType}, время: ${relativeTime.toFixed(2)}с`);
      
      // Определяем что рендерить на основе содержимого элемента
      if (item.videoElement) {
        console.log(`Рендерим как видео: ${item.name}`);
        await this.renderVideo(item, relativeTime);
      } else if (item.imageElement) {
        console.log(`Рендерим как изображение: ${item.name}`);
        await this.renderImage(item);
      } else if (this.isVideoElement(item)) {
        console.log(`Элемент должен быть видео, но не подготовлен: ${item.name}`);
        this.renderPlaceholder(`Видео: ${item.name}`);
      } else if (this.isImageElement(item)) {
        console.log(`Элемент должен быть изображением, но не подготовлен: ${item.name}`);
        this.renderPlaceholder(`Изображение: ${item.name}`);
      } else if (this.isAudioElement(item)) {
        console.log(`Рендерим аудио индикатор: ${item.name}`);
        this.renderAudioIndicator(item.name || 'Audio');
      } else if (item.text || item.content || item.title) {
        console.log(`Рендерим как текст: ${item.name}`);
        this.renderText(item.text || item.content || item.title || item.name || 'Text');
      } else {
        console.log(`Рендерим как заглушку: ${item.name}`);
        this.renderPlaceholder(item.name || 'Неизвестный элемент');
      }
    } catch (error) {
      console.warn('Ошибка рендеринга элемента:', error);
      this.renderPlaceholder(`Ошибка: ${item.name || 'Элемент'}`);
    } finally {
      this.ctx.restore();
    }
  }

  async renderVideo(item, relativeTime) {
    if (!item.videoElement) {
      console.warn(`Нет videoElement для ${item.name}`);
      this.renderPlaceholder(`Видео не загружено: ${item.name}`);
      return;
    }

    try {
      const video = item.videoElement;
      console.log(`Рендерим видео: ${item.name}, текущее время видео: ${video.currentTime}, целевое время: ${relativeTime}, длительность: ${video.duration}`);
      
      // Устанавливаем время с учетом обрезки
      const targetTime = Math.max(0, relativeTime);
      
      if (video.duration && targetTime <= video.duration) {
        // Устанавливаем время только если разница существенная
        if (Math.abs(video.currentTime - targetTime) > 0.1) {
          video.currentTime = targetTime;
          
          // Ждем готовности кадра
          let attempts = 0;
          while (video.readyState < 2 && attempts < 50) {
            await new Promise(resolve => setTimeout(resolve, 10));
            attempts++;
          }
        }
        
        console.log(`Отрисовываем видео кадр: время ${video.currentTime.toFixed(2)}с, размер ${video.videoWidth}x${video.videoHeight}`);
        this.drawScaled(video);
      } else {
        console.warn(`Время ${targetTime}с вне диапазона видео ${item.name} (длительность: ${video.duration}с)`);
        this.renderPlaceholder(`Вне диапазона: ${item.name}`);
      }
    } catch (error) {
      console.warn('Ошибка рендеринга видео:', error);
      this.renderPlaceholder(`Ошибка видео: ${item.name}`);
    }
  }

  async renderImage(item) {
    if (!item.imageElement) {
      console.warn(`Нет imageElement для ${item.name}`);
      this.renderPlaceholder(`Изображение не загружено: ${item.name}`);
      return;
    }

    try {
      const img = item.imageElement;
      console.log(`Рендерим изображение: ${item.name}, размер: ${img.naturalWidth}x${img.naturalHeight}, загружено: ${img.complete}`);
      
      if (img.complete && img.naturalWidth > 0) {
        this.drawScaled(img);
      } else {
        console.warn(`Изображение не готово: ${item.name}`);
        this.renderPlaceholder(`Загрузка: ${item.name}`);
      }
    } catch (error) {
      console.warn('Ошибка рендеринга изображения:', error);
      this.renderPlaceholder(`Ошибка изображения: ${item.name}`);
    }
  }

  renderText(text) {
    this.ctx.fillStyle = "#ffffff";
    this.ctx.font = `${Math.min(48, this.canvas.width / 20)}px Arial`;
    this.ctx.textAlign = "center";
    this.ctx.textBaseline = "middle";
    
    const x = this.canvas.width / 2;
    const y = this.canvas.height / 2;
    
    this.ctx.strokeStyle = "#000000";
    this.ctx.lineWidth = 2;
    this.ctx.strokeText(text, x, y);
    this.ctx.fillText(text, x, y);
  }

  renderPlaceholder(text) {
    this.ctx.fillStyle = "#333333";
    this.ctx.fillRect(0, 0, this.canvas.width, this.canvas.height);
    
    this.ctx.fillStyle = "#ffffff";
    this.ctx.font = `${Math.min(24, this.canvas.width / 40)}px Arial`;
    this.ctx.textAlign = "center";
    this.ctx.textBaseline = "middle";
    this.ctx.fillText(text, this.canvas.width / 2, this.canvas.height / 2);
  }

  renderAudioIndicator(text) {
    // Рендерим небольшой индикатор аудио в углу
    this.ctx.fillStyle = "rgba(0, 0, 0, 0.7)";
    this.ctx.fillRect(10, 10, 150, 30);
    
    this.ctx.fillStyle = "#00ff00";
    this.ctx.font = "14px Arial";
    this.ctx.textAlign = "left";
    this.ctx.textBaseline = "middle";
    this.ctx.fillText(`🎵 ${text}`, 20, 25);
  }

  async syncAudioElements(currentTime) {
    // Синхронизируем все аудио/видео элементы с текущим временем
    for (const item of this.timelineItems) {
      if (this.isItemActive(item, currentTime)) {
        const relativeTime = currentTime - (item.startTime || 0);
        
        if (item.videoElement && item.videoElement.duration) {
          const targetTime = Math.max(0, Math.min(relativeTime, item.videoElement.duration));
          if (Math.abs(item.videoElement.currentTime - targetTime) > 0.1) {
            item.videoElement.currentTime = targetTime;
          }
        }
        
        if (item.audioElement && item.audioElement.duration) {
          const targetTime = Math.max(0, Math.min(relativeTime, item.audioElement.duration));
          if (Math.abs(item.audioElement.currentTime - targetTime) > 0.1) {
            item.audioElement.currentTime = targetTime;
          }
        }
      }
    }
  }

  drawScaled(element) {
    const canvasRatio = this.canvas.width / this.canvas.height;
    const elementWidth = element.videoWidth || element.naturalWidth || element.width;
    const elementHeight = element.videoHeight || element.naturalHeight || element.height;
    
    if (!elementWidth || !elementHeight) {
      console.warn('Элемент не имеет размеров:', element);
      return;
    }
    
    const elementRatio = elementWidth / elementHeight;
    let drawWidth, drawHeight, drawX, drawY;

    // Используем режим "contain" - элемент полностью помещается в canvas
    if (elementRatio > canvasRatio) {
      // Элемент шире - ограничиваем по ширине
      drawWidth = this.canvas.width;
      drawHeight = this.canvas.width / elementRatio;
      drawX = 0;
      drawY = (this.canvas.height - drawHeight) / 2;
    } else {
      // Элемент выше - ограничиваем по высоте
      drawHeight = this.canvas.height;
      drawWidth = this.canvas.height * elementRatio;
      drawX = (this.canvas.width - drawWidth) / 2;
      drawY = 0;
    }

    // Очищаем фон вокруг элемента черным цветом
    this.ctx.fillStyle = "#000000";
    this.ctx.fillRect(0, 0, this.canvas.width, this.canvas.height);
    
    try {
      this.ctx.drawImage(element, drawX, drawY, drawWidth, drawHeight);
      console.log(`✓ Элемент отрисован: ${elementWidth}x${elementHeight} -> ${drawWidth.toFixed(0)}x${drawHeight.toFixed(0)} в позиции (${drawX.toFixed(0)}, ${drawY.toFixed(0)})`);
    } catch (error) {
      console.error('Ошибка отрисовки:', error);
      this.renderPlaceholder('Ошибка отрисовки');
    }
  }
}

export const useVideoExporter = () => {
  const [isExportModalOpen, setIsExportModalOpen] = useState(false);

  const openExportModal = () => setIsExportModalOpen(true);
  const closeExportModal = () => setIsExportModalOpen(false);

  return {
    isExportModalOpen,
    openExportModal,
    closeExportModal,
  };
};
