// Ваш обновленный ScriptEditor с кнопкой суфлёра
import React, { useState } from 'react';
import { Skeleton } from '@mui/material';

// Скелетон (оставляем как есть)
const ScriptEditorSkeleton = () => {
  return (
    <div className="bg-white rounded-lg shadow-md p-4">
      <div className="flex items-center justify-between mb-4">
        <div className="flex items-center space-x-2">
          <Skeleton variant="text" width={200} height={20} />
          <span className="text-yellow-500">✨</span>
        </div>
        <div className="bg-slate-100 rounded-full px-4 py-2 text-sm text-slate-600">
          <Skeleton variant="text" width={180} height={20} />
        </div>
      </div>

      <div className="border border-slate-200 rounded-lg overflow-hidden">
        <div className="p-4 bg-slate-50">
          <Skeleton variant="text" width="100%" height={20} />
          <Skeleton variant="text" width="80%" height={20} className="mt-2" />
          <Skeleton variant="text" width="70%" height={20} className="mt-2" />
          <Skeleton variant="text" width="90%" height={20} className="mt-2" />
        </div>

        <div className="absolute inset-y-0 right-0 w-1 bg-slate-200 rounded-r-lg">
          <div className="h-1/2 bg-slate-300 rounded-full animate-pulse"/>
        </div>
      </div>
    </div>
  );
};

// Основной редактор сценария с суфлёром
const ScriptEditor = ({ 
  script = '', 
  onSave, 
  title = 'Редактор сценария',
  isLoading = false 
}) => {
  const [content, setContent] = useState(script);
  const [showTeleprompter, setShowTeleprompter] = useState(false);
  const [wordCount, setWordCount] = useState(0);
  const [charCount, setCharCount] = useState(0);

  // Подсчет статистики
  React.useEffect(() => {
    const words = content.trim().split(/\s+/).filter(word => word.length > 0);
    setWordCount(words.length);
    setCharCount(content.length);
  }, [content]);

  // Если загружается - показываем скелетон
  if (isLoading) {
    return <ScriptEditorSkeleton />;
  }

  const handleSave = () => {
    if (onSave) {
      onSave(content);
    }
  };

  const handleOpenTeleprompter = () => {
    if (!content.trim()) {
      alert('Сначала напишите текст сценария');
      return;
    }
    setShowTeleprompter(true);
  };

  const estimatedTime = Math.ceil(wordCount / 150); // 150 слов в минуту

  return (
    <>
      <div className="bg-white rounded-lg shadow-md p-4">
        {/* Заголовок с кнопками */}
        <div className="flex items-center justify-between mb-4">
          <div className="flex items-center space-x-2">
            <h2 className="text-lg font-semibold text-gray-800">{title}</h2>
            <span className="text-yellow-500">✨</span>
          </div>
          
          {/* Статистика и кнопки */}
          <div className="flex items-center space-x-3">
            <div className="bg-slate-100 rounded-full px-4 py-2 text-sm text-slate-600">
              {wordCount} слов • {charCount} символов • ~{estimatedTime} мин
            </div>
            
            {/* Кнопка суфлёра */}
            {content.trim() && (
              <button
                onClick={handleOpenTeleprompter}
                className="bg-blue-500 hover:bg-blue-600 text-white px-4 py-2 rounded-lg text-sm font-medium flex items-center space-x-2 transition-colors"
                title="Запустить суфлёр для чтения сценария"
              >
                <span>🎬</span>
                <span>Суфлёр</span>
              </button>
            )}
          </div>
        </div>

        {/* Редактор текста */}
        <div className="border border-slate-200 rounded-lg overflow-hidden">
          <textarea
            value={content}
            onChange={(e) => setContent(e.target.value)}
            className="w-full h-64 p-4 bg-slate-50 border-none outline-none resize-none text-gray-800 placeholder-gray-500"
            placeholder="Начните писать ваш сценарий здесь...

Советы:
• Пишите короткими предложениями для лучшего чтения
• Отмечайте места для пауз знаками препинания
• Используйте простые слова для естественной речи"
          />
          
          {/* Полоса прокрутки (декоративная) */}
          <div className="absolute inset-y-0 right-0 w-1 bg-slate-200 rounded-r-lg">
            <div 
              className="bg-blue-400 rounded-full transition-all duration-300"
              style={{ 
                height: content ? '30%' : '10%',
                marginTop: content ? '20%' : '0%'
              }}
            />
          </div>
        </div>

        {/* Нижняя панель */}
        <div className="flex items-center justify-between mt-4">
          <div className="text-sm text-gray-500">
            {content.trim() ? (
              <span className="text-green-600">✓ Сценарий готов к использованию</span>
            ) : (
              <span>Начните писать сценарий</span>
            )}
          </div>
          
          <div className="flex space-x-2">
            {/* Кнопка сохранения */}
            <button
              onClick={handleSave}
              disabled={!content.trim()}
              className={`px-4 py-2 rounded-lg text-sm font-medium transition-colors ${
                content.trim()
                  ? 'bg-green-500 hover:bg-green-600 text-white'
                  : 'bg-gray-200 text-gray-400 cursor-not-allowed'
              }`}
            >
              💾 Сохранить
            </button>
            
            {/* Дополнительная кнопка суфлёра */}
            {content.trim() && (
              <button
                onClick={handleOpenTeleprompter}
                className="bg-blue-500 hover:bg-blue-600 text-white px-4 py-2 rounded-lg text-sm font-medium transition-colors"
              >
                🎬 Открыть суфлёр
              </button>
            )}
          </div>
        </div>

        {/* Подсказки */}
        {!content.trim() && (
          <div className="mt-4 p-3 bg-blue-50 border border-blue-200 rounded-lg">
            <div className="text-sm text-blue-700">
              <strong>💡 Совет:</strong> После написания сценария вы сможете использовать суфлёр для комфортного чтения во время записи видео
            </div>
          </div>
        )}
      </div>

      {/* Суфлёр */}
      <SimpleTeleprompter
        isOpen={showTeleprompter}
        onClose={() => setShowTeleprompter(false)}
        scriptText={content}
        scriptTitle={title}
      />
    </>
  );
};


export default ScriptEditor;
