import { useState, useRef, useEffect, useCallback } from "react";
import {
  Video,
  Music,
  Copy,
  Trash2,
  Scissors,
  Film,
  Plus,
  Layers,
  ZoomIn,
  ZoomOut,
  RotateCcw,
  Type,
  Magnet,
  Eye,
  EyeOff,
  Lock,
  Unlock,
} from "lucide-react";

export const Timeline = ({
  timelineItems,
  setTimelineItems,
  currentTime,
  videoDuration,
  setVideoDuration,
  selectedTimelineItem,
  setSelectedTimelineItem,
  timelineZoom,
  setTimelineZoom,
  tracks,
  setTracks,
  draggedItem,
  draggedTimelineItem,
  setDraggedTimelineItem,
  dropPreview,
  setDropPreview,
  isDragging,
  isTimelineDragging,
  setIsTimelineDragging,
  isResizing,
  setIsResizing,
  copiedItem,
  formatTime,
  seekTo,
  splitTimelineItem,
  copyTimelineItem,
  pasteTimelineItem,
  deleteSelectedItem,
  addToTimeline,
  insertWithRipple,
  applyRippleEffect,
  removeFromTimeline,
}) => {
  const timelineRef = useRef(null);
  const timelineScrollRef = useRef(null);
  const trackLabelsRef = useRef(null);
  const autoScrollIntervalRef = useRef(null);

  // CapCut-style states
  const [isSnappingEnabled, setIsSnappingEnabled] = useState(true);
  const [snapThreshold] = useState(10); // pixels
  const [dragOffset, setDragOffset] = useState(0);
  const [resizeHandle, setResizeHandle] = useState(null);
  const [resizeItem, setResizeItem] = useState(null);
  const [originalItemData, setOriginalItemData] = useState(null);
  const [snapIndicators, setSnapIndicators] = useState([]);

  // Snap helpers
  const getSnapPoints = useCallback(() => {
    const snapPoints = [];
    const pixelsPerSecond = getPixelsPerSecond();
    
    // Playhead snap point
    snapPoints.push({
      time: currentTime,
      pixel: currentTime * pixelsPerSecond,
      type: 'playhead'
    });
    
    // Clip edges snap points
    timelineItems.forEach(item => {
      if (draggedTimelineItem?.id !== item.id) {
        snapPoints.push({
          time: item.startTime,
          pixel: item.startTime * pixelsPerSecond,
          type: 'clip-start',
          clipId: item.id
        });
        snapPoints.push({
          time: item.startTime + item.duration,
          pixel: (item.startTime + item.duration) * pixelsPerSecond,
          type: 'clip-end',
          clipId: item.id
        });
      }
    });
    
    // Timeline start
    snapPoints.push({
      time: 0,
      pixel: 0,
      type: 'timeline-start'
    });
    
    return snapPoints;
  }, [currentTime, timelineItems, draggedTimelineItem, getPixelsPerSecond]);

  const findNearestSnap = useCallback((targetPixel) => {
    if (!isSnappingEnabled) return null;
    
    const snapPoints = getSnapPoints();
    let nearestSnap = null;
    let minDistance = snapThreshold;
    
    snapPoints.forEach(point => {
      const distance = Math.abs(point.pixel - targetPixel);
      if (distance < minDistance) {
        minDistance = distance;
        nearestSnap = point;
      }
    });
    
    return nearestSnap;
  }, [isSnappingEnabled, getSnapPoints, snapThreshold]);

  // Auto-scroll logic
  const stopAutoScroll = useCallback(() => {
    if (autoScrollIntervalRef.current) {
      clearInterval(autoScrollIntervalRef.current);
      autoScrollIntervalRef.current = null;
    }
  }, []);

  const startAutoScroll = useCallback(
    (direction, speed = 1) => {
      stopAutoScroll();
      autoScrollIntervalRef.current = setInterval(() => {
        if (timelineScrollRef.current) {
          const scrollAmount = 15 * speed;
          const currentScrollLeft = timelineScrollRef.current.scrollLeft;
          const maxScroll =
            timelineScrollRef.current.scrollWidth -
            timelineScrollRef.current.clientWidth;

          if (direction === "left" && currentScrollLeft > 0) {
            timelineScrollRef.current.scrollLeft = Math.max(
              0,
              currentScrollLeft - scrollAmount
            );
          } else if (direction === "right" && currentScrollLeft < maxScroll) {
            timelineScrollRef.current.scrollLeft = Math.min(
              maxScroll,
              currentScrollLeft + scrollAmount
            );
          }
        }
      }, 16);
    },
    [stopAutoScroll]
  );

  const checkAutoScroll = useCallback(
    (clientX) => {
      if (!timelineScrollRef.current) return;
      const rect = timelineScrollRef.current.getBoundingClientRect();
      const scrollZoneWidth = 120;
      const relativeX = clientX - rect.left;

      if (
        relativeX < scrollZoneWidth &&
        timelineScrollRef.current.scrollLeft > 0
      ) {
        const speed = ((scrollZoneWidth - relativeX) / scrollZoneWidth) * 3;
        startAutoScroll("left", speed);
      } else if (relativeX > rect.width - scrollZoneWidth) {
        const maxScroll =
          timelineScrollRef.current.scrollWidth -
          timelineScrollRef.current.clientWidth;
        if (timelineScrollRef.current.scrollLeft < maxScroll) {
          const speed =
            ((relativeX - (rect.width - scrollZoneWidth)) / scrollZoneWidth) *
            3;
          startAutoScroll("right", speed);
        }
      } else {
        stopAutoScroll();
      }
    },
    [startAutoScroll, stopAutoScroll]
  );

  // Zoom functions
  const zoomIn = () => setTimelineZoom((prev) => Math.min(8, prev * 1.5));
  const zoomOut = () => setTimelineZoom((prev) => Math.max(0.25, prev / 1.5));
  const resetZoom = () => setTimelineZoom(1);

  // Track management
  const addVideoTrack = () => {
    const newTrackId = `video-${Date.now()}`;
    setTracks((prev) => ({
      ...prev,
      video: [
        ...prev.video,
        {
          id: newTrackId,
          name: `Video ${prev.video.length + 1}`,
          type: "video",
          visible: true,
          locked: false,
        },
      ],
    }));
  };

  const addAudioTrack = () => {
    const newTrackId = `audio-${Date.now()}`;
    setTracks((prev) => ({
      ...prev,
      audio: [
        ...prev.audio,
        {
          id: newTrackId,
          name: `Audio ${prev.audio.length + 1}`,
          type: "audio",
          visible: true,
          locked: false,
        },
      ],
    }));
  };

  const addTextTrack = () => {
    const newTrackId = `text-${Date.now()}`;
    setTracks((prev) => ({
      ...prev,
      text: [
        ...prev.text,
        {
          id: newTrackId,
          name: `Text ${prev.text.length + 1}`,
          type: "text",
          visible: true,
          locked: false,
        },
      ],
    }));
  };

  const toggleTrackVisibility = (trackId) => {
    setTracks((prev) => {
      const newTracks = { ...prev };
      Object.keys(newTracks).forEach(trackType => {
        newTracks[trackType] = newTracks[trackType].map(track =>
          track.id === trackId ? { ...track, visible: !track.visible } : track
        );
      });
      return newTracks;
    });
  };

  const toggleTrackLock = (trackId) => {
    setTracks((prev) => {
      const newTracks = { ...prev };
      Object.keys(newTracks).forEach(trackType => {
        newTracks[trackType] = newTracks[trackType].map(track =>
          track.id === trackId ? { ...track, locked: !track.locked } : track
        );
      });
      return newTracks;
    });
  };

  // Helper functions
  const getTimelineWidth = () => {
    const baseWidth = videoDuration * 50 * timelineZoom;
    return Math.max(baseWidth, 2000);
  };

  const getPixelsPerSecond = () => 50 * timelineZoom;

  const getAllTracks = () => [
    ...(tracks.video || []),
    ...(tracks.audio || []),
    ...(tracks.text || []),
  ];

  const getTrackColor = (trackType) => {
    switch (trackType) {
      case "video":
        return "bg-blue-500 border-blue-400";
      case "audio":
        return "bg-green-500 border-green-400";
      case "text":
        return "bg-purple-500 border-purple-400";
      default:
        return "bg-gray-500 border-gray-400";
    }
  };

  const getTrackIcon = (trackType) => {
    switch (trackType) {
      case "video":
        return <Video size={16} />;
      case "audio":
        return <Music size={16} />;
      case "text":
        return <Type size={16} />;
      default:
        return <Layers size={16} />;
    }
  };

  // Resize handlers
  const handleResizeStart = useCallback(
    (e, item, handle) => {
      e.preventDefault();
      e.stopPropagation();
      setIsResizing(true);
      setResizeHandle(handle);
      setResizeItem(item);
      setOriginalItemData({
        startTime: item.startTime,
        duration: item.duration,
      });
      setIsTimelineDragging(false);
      setDraggedTimelineItem(null);
    },
    [setIsResizing, setIsTimelineDragging, setDraggedTimelineItem]
  );

  const handleResizeEnd = useCallback(() => {
    setIsResizing(false);
    setResizeHandle(null);
    setResizeItem(null);
    setOriginalItemData(null);
    setSnapIndicators([]);
    stopAutoScroll();
  }, [setIsResizing, stopAutoScroll]);

  // Sync scroll
  const handleTrackAreaScroll = (e) => {
    if (trackLabelsRef.current) {
      trackLabelsRef.current.scrollTop = e.target.scrollTop;
    }
  };

  const handleTrackLabelsScroll = (e) => {
    if (timelineScrollRef.current) {
      timelineScrollRef.current.scrollTop = e.target.scrollTop;
    }
  };

  // Event handlers
  const handleTimelineClick = (e) => {
    if (isDragging || isTimelineDragging || isResizing) return;
    const rect = timelineScrollRef.current.getBoundingClientRect();
    const clickX = e.clientX - rect.left + timelineScrollRef.current.scrollLeft;
    const pixelsPerSecond = getPixelsPerSecond();
    const clickTime = clickX / pixelsPerSecond;
    seekTo(clickTime);
  };

  const handleTimelineItemDragStart = (e, timelineItem) => {
    if (isResizing) {
      e.preventDefault();
      return;
    }

    const track = getAllTracks().find(t => t.id === timelineItem.trackId);
    if (track?.locked) {
      e.preventDefault();
      return;
    }

    e.stopPropagation();
    setDraggedTimelineItem(timelineItem);
    setIsTimelineDragging(true);

    const rect = timelineScrollRef.current.getBoundingClientRect();
    const itemRect = e.target.getBoundingClientRect();
    setDragOffset(
      e.clientX - itemRect.left + timelineScrollRef.current.scrollLeft
    );

    e.dataTransfer.effectAllowed = "move";
    e.dataTransfer.setData("text/plain", "");
  };

  const handleTimelineItemDragEnd = () => {
    setDraggedTimelineItem(null);
    setDropPreview(null);
    setIsTimelineDragging(false);
    setDragOffset(0);
    setSnapIndicators([]);
    stopAutoScroll();
  };

  const handleDragOver = (e) => {
    if (!draggedItem && !draggedTimelineItem) return;
    e.preventDefault();
    e.dataTransfer.dropEffect = draggedItem ? "copy" : "move";

    if (draggedTimelineItem || isResizing) {
      checkAutoScroll(e.clientX);
    }

    const timelineRect = timelineScrollRef.current.getBoundingClientRect();
    let dragX = e.clientX - timelineRect.left + timelineScrollRef.current.scrollLeft;
    
    if (draggedTimelineItem) {
      dragX -= dragOffset;
    }

    const pixelsPerSecond = getPixelsPerSecond();
    let dropTime = Math.max(0, dragX / pixelsPerSecond);

    // Apply snapping
    if (isSnappingEnabled) {
      const nearestSnap = findNearestSnap(dragX);
      if (nearestSnap) {
        dropTime = nearestSnap.time;
        setSnapIndicators([{
          time: nearestSnap.time,
          type: nearestSnap.type
        }]);
      } else {
        setSnapIndicators([]);
      }
    }

    const dragY = e.clientY - timelineRect.top + timelineScrollRef.current.scrollTop;
    const trackHeight = 60;
    const timeRulerHeight = 48;
    const relativeY = dragY - timeRulerHeight;
    const trackIndex = Math.floor(relativeY / trackHeight);

    const allTracks = getAllTracks();
    const targetTrack = allTracks[trackIndex];

    if (draggedItem && targetTrack && !targetTrack.locked) {
      const duration = draggedItem.duration || (draggedItem.type === "image" ? 5 : 10);
      const insertResult = insertWithRipple(targetTrack.id, dropTime, duration);

      setDropPreview({
        time: insertResult.time,
        duration: duration,
        trackId: targetTrack.id,
        trackType: targetTrack.type,
        isValid: true,
        shouldRipple: insertResult.shouldRipple,
        affectedItems: insertResult.affectedItems || [],
      });
    } else if (draggedTimelineItem && targetTrack && !targetTrack.locked) {
      const insertResult = insertWithRipple(
        targetTrack.id,
        dropTime,
        draggedTimelineItem.duration,
        draggedTimelineItem.id
      );

      setDropPreview({
        time: insertResult.time,
        duration: draggedTimelineItem.duration,
        trackId: targetTrack.id,
        trackType: targetTrack.type,
        isValid: true,
        shouldRipple: insertResult.shouldRipple,
        affectedItems: insertResult.affectedItems || [],
      });
    }
  };

  const handleDrop = (e) => {
    if ((!draggedItem && !draggedTimelineItem) || !dropPreview) return;
    e.preventDefault();
    stopAutoScroll();
    setSnapIndicators([]);

    if (dropPreview.isValid) {
      if (draggedItem) {
        if (dropPreview.shouldRipple && dropPreview.affectedItems?.length > 0) {
          applyRippleEffect(dropPreview.affectedItems, dropPreview.duration);
        }
        addToTimeline(draggedItem, dropPreview.time, dropPreview.trackId);
      } else if (draggedTimelineItem) {
        if (dropPreview.shouldRipple && dropPreview.affectedItems?.length > 0) {
          applyRippleEffect(
            dropPreview.affectedItems,
            draggedTimelineItem.duration
          );
        }

        setTimelineItems((prev) =>
          prev.map((item) =>
            item.id === draggedTimelineItem.id
              ? {
                  ...item,
                  startTime: dropPreview.time,
                  trackId: dropPreview.trackId,
                  trackType: dropPreview.trackType,
                }
              : item
          )
        );

        const newEndTime = dropPreview.time + draggedTimelineItem.duration;
        if (newEndTime > videoDuration) {
          setVideoDuration(newEndTime + 10);
        }
      }
    }

    setDropPreview(null);
    setIsTimelineDragging(false);
  };

  const handleDragLeave = (e) => {
    if (!timelineRef.current?.contains(e.relatedTarget)) {
      setDropPreview(null);
      setSnapIndicators([]);
      stopAutoScroll();
    }
  };

  const getTimelineItemStyle = (item) => {
    const allTracks = getAllTracks();
    const trackIndex = allTracks.findIndex((track) => track.id === item.trackId);
    const pixelsPerSecond = getPixelsPerSecond();

    return {
      left: `${item.startTime * pixelsPerSecond}px`,
      width: `${item.duration * pixelsPerSecond}px`,
      top: `${trackIndex * 60 + 10}px`,
    };
  };

  const getDropPreviewStyle = () => {
    if (!dropPreview) return {};
    const allTracks = getAllTracks();
    const trackIndex = allTracks.findIndex((track) => track.id === dropPreview.trackId);
    const pixelsPerSecond = getPixelsPerSecond();

    return {
      left: `${dropPreview.time * pixelsPerSecond}px`,
      width: `${dropPreview.duration * pixelsPerSecond}px`,
      top: `${trackIndex * 60 + 10}px`,
    };
  };

  // Resize effect
  useEffect(() => {
    const handleMouseMove = (e) => {
      if (!isResizing || !resizeItem || !timelineScrollRef.current) return;
      checkAutoScroll(e.clientX);

      const rect = timelineScrollRef.current.getBoundingClientRect();
      let mouseX = e.clientX - rect.left + timelineScrollRef.current.scrollLeft;
      const pixelsPerSecond = getPixelsPerSecond();
      let newTime = mouseX / pixelsPerSecond;

      // Apply snapping for resize
      if (isSnappingEnabled) {
        const nearestSnap = findNearestSnap(mouseX);
        if (nearestSnap) {
          newTime = nearestSnap.time;
          setSnapIndicators([{
            time: nearestSnap.time,
            type: nearestSnap.type
          }]);
        } else {
          setSnapIndicators([]);
        }
      }

      setTimelineItems((prev) =>
        prev.map((item) => {
          if (item.id !== resizeItem.id) return item;
          const minDuration = 0.1;

          if (resizeHandle === "left") {
            const rightEdge = originalItemData.startTime + originalItemData.duration;
            const newStartTime = Math.max(0, Math.min(rightEdge - minDuration, newTime));
            const newDuration = rightEdge - newStartTime;
            return { ...item, startTime: newStartTime, duration: newDuration };
          } else if (resizeHandle === "right") {
            const leftEdge = originalItemData.startTime;
            const newEndTime = Math.max(leftEdge + minDuration, newTime);
            const newDuration = newEndTime - leftEdge;
            return { ...item, duration: newDuration };
          }
          return item;
        })
      );
    };

    const handleMouseUp = () => {
      if (isResizing) handleResizeEnd();
    };

    if (isResizing) {
      document.addEventListener("mousemove", handleMouseMove);
      document.addEventListener("mouseup", handleMouseUp);
    }

    return () => {
      document.removeEventListener("mousemove", handleMouseMove);
      document.removeEventListener("mouseup", handleMouseUp);
    };
  }, [
    isResizing,
    resizeItem,
    resizeHandle,
    originalItemData,
    getPixelsPerSecond,
    checkAutoScroll,
    handleResizeEnd,
    setTimelineItems,
    isSnappingEnabled,
    findNearestSnap,
  ]);

  useEffect(() => {
    return () => stopAutoScroll();
  }, [stopAutoScroll]);

  return (
    <div
      className={`h-80 bg-white rounded-2xl shadow-sm border-2 transition-all duration-200 overflow-hidden flex flex-col flex-shrink-0 ${
        isDragging || isTimelineDragging || isResizing
          ? "border-blue-400 bg-blue-50/20"
          : "border-gray-200"
      }`}
    >
      {/* Header */}
      <div className="bg-gray-50 p-4 border-b border-gray-200 space-y-3">
        <div className="flex items-center justify-between">
          <h4 className="text-gray-800 font-semibold flex items-center space-x-2">
            <Film size={18} />
            <span>Timeline Pro - CapCut Style</span>
            {isDragging && dropPreview && (
              <span className="text-green-600">- Adding "{draggedItem?.name}"</span>
            )}
            {isTimelineDragging && dropPreview && (
              <span className="text-blue-600">- Moving "{draggedTimelineItem?.name}"</span>
            )}
            {isResizing && resizeItem && (
              <span className="text-orange-600">- Trimming "{resizeItem.name}"</span>
            )}
          </h4>

          <div className="flex items-center space-x-3">
            {/* Snapping toggle */}
            <button
              onClick={() => setIsSnappingEnabled(!isSnappingEnabled)}
              className={`p-2 rounded-xl transition-all duration-200 ${
                isSnappingEnabled
                  ? "text-blue-600 bg-blue-50"
                  : "text-gray-400 hover:text-gray-600"
              }`}
              title="Toggle Snapping (Magnet)"
            >
              <Magnet size={16} />
            </button>

            {/* Zoom controls */}
            <div className="flex items-center space-x-2 bg-white rounded-xl p-2 border border-gray-200">
              <button onClick={zoomOut} className="p-1 text-gray-600 hover:text-blue-600 transition-colors" title="Zoom Out">
                <ZoomOut size={16} />
              </button>
              <span className="text-xs text-gray-500 min-w-12 text-center">
                {(timelineZoom * 100).toFixed(0)}%
              </span>
              <button onClick={zoomIn} className="p-1 text-gray-600 hover:text-blue-600 transition-colors" title="Zoom In">
                <ZoomIn size={16} />
              </button>
              <button onClick={resetZoom} className="p-1 text-gray-600 hover:text-blue-600 transition-colors" title="Reset Zoom">
                <RotateCcw size={16} />
              </button>
            </div>
          </div>
        </div>

        {/* Controls */}
        <div className="flex items-center justify-between">
          <div className="flex items-center space-x-6">
            {/* Add tracks */}
            <div className="flex items-center space-x-2">
              <button onClick={addVideoTrack} className="p-2 text-blue-600 hover:text-blue-800 hover:bg-blue-50 rounded-xl transition-all duration-200" title="Add Video Track">
                <Video size={16} />
              </button>
              <button onClick={addAudioTrack} className="p-2 text-green-600 hover:text-green-800 hover:bg-green-50 rounded-xl transition-all duration-200" title="Add Audio Track">
                <Music size={16} />
              </button>
              <button onClick={addTextTrack} className="p-2 text-purple-600 hover:text-purple-800 hover:bg-purple-50 rounded-xl transition-all duration-200" title="Add Text Track">
                <Type size={16} />
              </button>
            </div>

            {/* Timeline controls */}
            <div className="flex items-center space-x-2">
              <button
                onClick={splitTimelineItem}
                disabled={!selectedTimelineItem}
                className={`p-2 rounded-xl transition-all duration-200 ${
                  selectedTimelineItem
                    ? "text-red-600 hover:text-red-800 hover:bg-red-50"
                    : "text-gray-400 cursor-not-allowed"
                }`}
                title="Split Selected Item (S)"
              >
                <Scissors size={16} />
              </button>
              <button
                onClick={copyTimelineItem}
                disabled={!selectedTimelineItem}
                className={`p-2 rounded-xl transition-all duration-200 ${
                  selectedTimelineItem
                    ? "text-blue-600 hover:text-blue-800 hover:bg-blue-50"
                    : "text-gray-400 cursor-not-allowed"
                }`}
                title="Copy Selected Item (Ctrl+C)"
              >
                <Copy size={16} />
              </button>
              <button
                onClick={pasteTimelineItem}
                disabled={!copiedItem}
                className={`p-2 rounded-xl transition-all duration-200 ${
                  copiedItem
                    ? "text-green-600 hover:text-green-800 hover:bg-green-50"
                    : "text-gray-400 cursor-not-allowed"
                }`}
                title="Paste Item (Ctrl+V)"
              >
                <Copy size={16} className="rotate-180" />
              </button>
              <button
                onClick={deleteSelectedItem}
                disabled={!selectedTimelineItem}
                className={`p-2 rounded-xl transition-all duration-200 ${
                  selectedTimelineItem
                    ? "text-red-600 hover:text-red-800 hover:bg-red-50"
                    : "text-gray-400 cursor-not-allowed"
                }`}
                title="Delete Selected Item (Del)"
              >
                <Trash2 size={16} />
              </button>
            </div>
          </div>
        </div>
      </div>

      {/* Timeline area */}
      <div className="flex-1 flex min-h-0">
        {/* Track labels */}
        <div className="w-48 bg-gray-50 border-r border-gray-200 flex-shrink-0 flex flex-col">
          <div className="h-12 border-b border-gray-200 flex-shrink-0"></div>
          <div
            ref={trackLabelsRef}
            className="flex-1 overflow-y-auto overflow-x-hidden"
            onScroll={handleTrackLabelsScroll}
          >
            {getAllTracks().map((track) => (
              <div
                key={track.id}
                className={`h-15 flex items-center px-2 border-b border-gray-100 text-sm font-medium flex-shrink-0 ${
                  track.type === 'video' ? 'text-blue-700 bg-blue-50' :
                  track.type === 'audio' ? 'text-green-700 bg-green-50' :
                  'text-purple-700 bg-purple-50'
                }`}
                style={{ height: "60px" }}
              >
                <div className="flex items-center space-x-2 flex-1">
                  {getTrackIcon(track.type)}
                  <span className="truncate flex-1">{track.name}</span>
                </div>
                <div className="flex items-center space-x-1">
                  <button
                    onClick={() => toggleTrackVisibility(track.id)}
                    className="p-1 text-gray-500 hover:text-gray-700"
                    title={track.visible ? "Hide track" : "Show track"}
                  >
                    {track.visible ? <Eye size={14} /> : <EyeOff size={14} />}
                  </button>
                  <button
                    onClick={() => toggleTrackLock(track.id)}
                    className="p-1 text-gray-500 hover:text-gray-700"
                    title={track.locked ? "Unlock track" : "Lock track"}
                  >
                    {track.locked ? <Lock size={14} /> : <Unlock size={14} />}
                  </button>
                </div>
              </div>
            ))}
          </div>
        </div>

        {/* Timeline scroll area */}
        <div
          ref={(el) => {
            timelineRef.current = el;
            timelineScrollRef.current = el;
          }}
          className="flex-1 relative overflow-auto bg-white"
          onScroll={handleTrackAreaScroll}
          onDragOver={handleDragOver}
          onDrop={handleDrop}
          onDragLeave={handleDragLeave}
          style={{ overflowX: "auto", overflowY: "auto", scrollbarWidth: "thin" }}
        >
          <div
            style={{
              width: `${getTimelineWidth()}px`,
              minHeight: "100%",
              position: "relative",
            }}
            onClick={handleTimelineClick}
          >
            {/* Time ruler */}
            <div className="h-12 bg-gray-50 border-b border-gray-200 relative flex-shrink-0">
              {Array.from(
                { length: Math.ceil((videoDuration * timelineZoom) / 5) + 1 },
                (_, i) => {
                  const timeValue = (i * 5) / timelineZoom;
                  const pixelsPerSecond = getPixelsPerSecond();
                  return (
                    <div
                      key={i}
                      className="absolute top-0 h-full border-l border-gray-300"
                      style={{ left: `${timeValue * pixelsPerSecond}px` }}
                    >
                      <span className="text-xs text-gray-600 ml-2 mt-2 inline-block font-medium">
                        {formatTime(timeValue)}
                      </span>
                    </div>
                  );
                }
              )}

              {/* Sub-divisions for high zoom */}
              {timelineZoom > 2 &&
                Array.from(
                  { length: Math.ceil(videoDuration * timelineZoom) + 1 },
                  (_, i) => {
                    const timeValue = i / timelineZoom;
                    const pixelsPerSecond = getPixelsPerSecond();
                    return (
                      <div
                        key={`sub-${i}`}
                        className="absolute top-8 w-0 h-4 border-l border-gray-200"
                        style={{ left: `${timeValue * pixelsPerSecond}px` }}
                      />
                    );
                  }
                )}

              {/* Playhead */}
              <div
                className="absolute top-0 w-0.5 h-full bg-red-500 z-20 shadow-sm"
                style={{ left: `${currentTime * getPixelsPerSecond()}px` }}
              />

              {/* Snap indicators */}
              {snapIndicators.map((indicator, index) => (
                <div
                  key={index}
                  className={`absolute top-0 w-0.5 h-full z-10 ${
                    indicator.type === 'playhead' ? 'bg-red-300' : 'bg-yellow-400'
                  }`}
                  style={{ left: `${indicator.time * getPixelsPerSecond()}px` }}
                />
              ))}
            </div>

            {/* Tracks area */}
            <div
              className="relative flex-1"
              style={{
                height: `${getAllTracks().length * 60}px`,
                minHeight: "100%",
              }}
            >
              {/* Timeline items */}
              {timelineItems.map((item) => {
                const track = getAllTracks().find(t => t.id === item.trackId);
                return (
                  <div
                    key={item.id}
                    className={`absolute h-10 rounded-lg border-2 cursor-grab active:cursor-grabbing transition-all shadow-sm hover:shadow-md group ${getTrackColor(
                      item.trackType
                    )} ${
                      selectedTimelineItem?.id === item.id
                        ? "ring-2 ring-blue-300"
                        : ""
                    } ${
                      draggedTimelineItem?.id === item.id ? "opacity-50 z-30" : ""
                    } ${
                      copiedItem?.id === item.id
                        ? "ring-2 ring-green-300 ring-dashed"
                        : ""
                    } ${
                      !track?.visible ? "opacity-50" : ""
                    } ${
                      track?.locked ? "cursor-not-allowed" : ""
                    }`}
                    style={getTimelineItemStyle(item)}
                    onClick={(e) => {
                      e.stopPropagation();
                      setSelectedTimelineItem(item);
                    }}
                  >
                    {/* Left resize handle */}
                    {!track?.locked && (
                      <div
                        className="absolute left-0 top-0 w-3 h-full cursor-ew-resize opacity-0 group-hover:opacity-100 bg-white/50 rounded-l-lg z-10 flex items-center justify-center"
                        onMouseDown={(e) => handleResizeStart(e, item, "left")}
                        title="Trim start"
                      >
                        <div className="w-0.5 h-6 bg-white rounded-full opacity-80"></div>
                      </div>
                    )}

                    {/* Item content */}
                    <div
                      draggable={!track?.locked}
                      onDragStart={(e) => handleTimelineItemDragStart(e, item)}
                      onDragEnd={handleTimelineItemDragEnd}
                      className={`p-2 text-white text-xs truncate font-medium flex items-center h-full mx-3 ${
                        track?.locked ? 'cursor-not-allowed' : 'cursor-grab active:cursor-grabbing'
                      }`}
                      title={`${item.name} - ${track?.locked ? 'Track locked' : 'Drag to move'}`}
                    >
                      {item.name}
                      {track?.locked && <Lock size={12} className="ml-1" />}
                      {copiedItem?.id === item.id && (
                        <span className="ml-2 text-green-200">ðŸ“‹</span>
                      )}
                    </div>

                    {/* Right resize handle */}
                    {!track?.locked && (
                      <div
                        className="absolute right-0 top-0 w-3 h-full cursor-ew-resize opacity-0 group-hover:opacity-100 bg-white/50 rounded-r-lg z-10 flex items-center justify-center"
                        onMouseDown={(e) => handleResizeStart(e, item, "right")}
                        title="Trim end"
                      >
                        <div className="w-0.5 h-6 bg-white rounded-full opacity-80"></div>
                      </div>
                    )}
                  </div>
                );
              })}

              {/* Drop preview */}
              {dropPreview && (
                <div
                  className={`absolute h-10 rounded-lg border-2 transition-all ${
                    dropPreview.isValid
                      ? `${getTrackColor(dropPreview.trackType)} bg-opacity-60 border-dashed animate-pulse`
                      : "bg-red-300 border-red-500 border-dashed"
                  }`}
                  style={getDropPreviewStyle()}
                >
                  {dropPreview.isValid && (
                    <div className="flex items-center justify-center h-full text-white text-xs font-bold">
                      {isSnappingEnabled && snapIndicators.length > 0 ? "ðŸ§²" : "ðŸ“"}
                    </div>
                  )}
                </div>
              )}

              {/* Snap indicators in timeline */}
              {snapIndicators.map((indicator, index) => (
                <div
                  key={`timeline-snap-${index}`}
                  className={`absolute w-0.5 bg-yellow-400 z-30 opacity-75`}
                  style={{
                    left: `${indicator.time * getPixelsPerSecond()}px`,
                    top: '0px',
                    height: `${getAllTracks().length * 60}px`
                  }}
                />
              ))}
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};
