import React, { useState, useRef, useEffect } from "react";
import {
  X,
  Download,
  Check,
  AlertCircle,
  Play,
  Film,
  Zap,
  Settings,
  Volume2,
} from "lucide-react";

const FFmpegVideoExporter = ({
  isOpen,
  onClose,
  timelineItems = [],
  videoDuration = 60,
  overlayTransforms = {}, // ‚úÖ –î–û–ë–ê–í–õ–Ø–ï–ú overlay –∫–æ–æ—Ä–¥–∏–Ω–∞—Ç—ã
}) => {
  const [ffmpeg, setFfmpeg] = useState(null);
  const [isExporting, setIsExporting] = useState(false);
  const [exportProgress, setExportProgress] = useState(0);
  const [exportStage, setExportStage] = useState("idle");
  const [error, setError] = useState(null);
  const [exportedBlob, setExportedBlob] = useState(null);

  const [exportSettings, setExportSettings] = useState({
    resolution: "1280x720",
    fps: 24,
    quality: "fast",
    duration: Math.min(videoDuration, 30),
    includeAudio: true, // –ü—Ä–æ—Å—Ç–æ–π –ø–µ—Ä–µ–∫–ª—é—á–∞—Ç–µ–ª—å
  });

  const canvasRef = useRef(null);
  const mediaCache = useRef(new Map());

  // –ë—ã—Å—Ç—Ä–∞—è –∏–Ω–∏—Ü–∏–∞–ª–∏–∑–∞—Ü–∏—è (–ë–ï–ó –ò–ó–ú–ï–ù–ï–ù–ò–ô)
  useEffect(() => {
    const initFFmpeg = async () => {
      if (!isOpen || ffmpeg) return;

      try {
        setExportStage("loading");
        console.log("üöÄ –ë—ã—Å—Ç—Ä–∞—è –∏–Ω–∏—Ü–∏–∞–ª–∏–∑–∞—Ü–∏—è FFmpeg...");

        const { FFmpeg } = await import("@ffmpeg/ffmpeg");
        const { fetchFile } = await import("@ffmpeg/util");

        const ffmpegInstance = new FFmpeg();

        await ffmpegInstance.load({
          coreURL: "https://unpkg.com/@ffmpeg/core@0.12.6/dist/umd/ffmpeg-core.js",
          wasmURL: "https://unpkg.com/@ffmpeg/core@0.12.6/dist/umd/ffmpeg-core.wasm",
        });

        ffmpegInstance.on("progress", ({ progress }) => {
          if (isExporting) {
            setExportProgress(Math.min(90, Math.round(progress * 85) + 70));
          }
        });

        window.fetchFile = fetchFile;
        setFfmpeg(ffmpegInstance);
        setExportStage("ready");
        console.log("‚úÖ FFmpeg –≥–æ—Ç–æ–≤ –∫ –±—ã—Å—Ç—Ä–æ–º—É —ç–∫—Å–ø–æ—Ä—Ç—É");
      } catch (error) {
        console.error("‚ùå –û—à–∏–±–∫–∞ FFmpeg:", error);
        setError(error.message);
        setExportStage("error");
      }
    };

    initFFmpeg();
  }, [isOpen]);

  // –ë—ã—Å—Ç—Ä–æ–µ –∫—ç—à–∏—Ä–æ–≤–∞–Ω–∏–µ (–ë–ï–ó –ò–ó–ú–ï–ù–ï–ù–ò–ô)
  const loadMediaWithCache = async (item) => {
    const cacheKey = `${item.id}_${item.url}`;

    if (mediaCache.current.has(cacheKey)) {
      return mediaCache.current.get(cacheKey);
    }

    return new Promise((resolve, reject) => {
      const timeout = setTimeout(() => reject(new Error("Timeout")), 3000);

      const isVideo = item.type === "videos" || item.trackType === "main";

      if (isVideo) {
        const video = document.createElement("video");
        video.crossOrigin = "anonymous";
        video.muted = true;
        video.preload = "metadata";

        video.onloadedmetadata = () => {
          clearTimeout(timeout);
          mediaCache.current.set(cacheKey, video);
          resolve(video);
        };

        video.onerror = () => {
          clearTimeout(timeout);
          reject(new Error("Video load failed"));
        };

        video.src = item.url;
      } else {
        const img = new Image();
        img.crossOrigin = "anonymous";

        img.onload = () => {
          clearTimeout(timeout);
          mediaCache.current.set(cacheKey, img);
          resolve(img);
        };

        img.onerror = () => {
          clearTimeout(timeout);
          reject(new Error("Image load failed"));
        };

        img.src = item.url;
      }
    });
  };

  // –ò–°–ü–†–ê–í–õ–ï–ù–ù–´–ô —Ä–µ–Ω–¥–µ—Ä–∏–Ω–≥ —Å –∫–æ–æ—Ä–¥–∏–Ω–∞—Ç–∞–º–∏ overlay
  const renderFrameFast = async (ctx, currentTime, width, height) => {
    ctx.fillStyle = "#000000";
    ctx.fillRect(0, 0, width, height);

    // –î–∏–∞–≥–Ω–æ—Å—Ç–∏–∫–∞ –≤—Å–µ—Ö —ç–ª–µ–º–µ–Ω—Ç–æ–≤ timeline
    console.log(`\n‚è±Ô∏è –í—Ä–µ–º—è ${currentTime.toFixed(1)}s - –ø—Ä–æ–≤–µ—Ä—è–µ–º ${timelineItems.length} —ç–ª–µ–º–µ–Ω—Ç–æ–≤:`);
    
    timelineItems.forEach((item, index) => {
      const start = item.startTime || 0;
      const end = start + (item.duration || 0);
      const isActive = currentTime >= start && currentTime <= end;
      const isNotAudio = item.trackType !== "audio";
      
      console.log(`  ${index + 1}. ${item.name || 'Unnamed'}`);
      console.log(`     –¢–∏–ø: ${item.type}, –¢—Ä–µ–∫: ${item.trackType}`);
      console.log(`     –í—Ä–µ–º—è: ${start}s - ${end.toFixed(1)}s (–¥–ª–∏—Ç–µ–ª—å–Ω–æ—Å—Ç—å: ${item.duration}s)`);
      console.log(`     –ê–∫—Ç–∏–≤–µ–Ω: ${isActive}, –ù–µ –∞—É–¥–∏–æ: ${isNotAudio}`);
      
      // ‚úÖ –î–ò–ê–ì–ù–û–°–¢–ò–ö–ê overlay –∫–æ–æ—Ä–¥–∏–Ω–∞—Ç
      if (item.trackType === "overlay") {
        const overlayCoords = overlayTransforms[item.id];
        console.log(`     Overlay –∫–æ–æ—Ä–¥–∏–Ω–∞—Ç—ã:`, overlayCoords);
      }
      
      console.log(`     ‚úÖ –í–∫–ª—é—á–µ–Ω –≤ —Ä–µ–Ω–¥–µ—Ä: ${isActive && isNotAudio}`);
    });

    // –ò—Å–ø—Ä–∞–≤–ª–µ–Ω–Ω–∞—è —Ñ–∏–ª—å—Ç—Ä–∞—Ü–∏—è
    const activeItems = timelineItems.filter((item) => {
      const startTime = item.startTime ?? item.start ?? 0;
      const duration = item.duration ?? item.end ?? 10;
      const endTime = startTime + duration;
      
      const isInTimeRange = currentTime >= startTime && currentTime <= endTime;
      const isNotAudio = item.trackType !== "audio";
      
      return isInTimeRange && isNotAudio;
    });

    console.log(`üé¨ –ê–∫—Ç–∏–≤–Ω—ã—Ö —ç–ª–µ–º–µ–Ω—Ç–æ–≤ –¥–ª—è —Ä–µ–Ω–¥–µ—Ä–∞: ${activeItems.length}`);

    // –ü—Ä–∞–≤–∏–ª—å–Ω–∞—è —Å–æ—Ä—Ç–∏—Ä–æ–≤–∫–∞: main —Å–Ω–∞—á–∞–ª–∞, overlay –ø–æ—Ç–æ–º
    const sortedItems = activeItems.sort((a, b) => {
      const order = { main: 1, overlay: 2 };
      return (order[a.trackType] || 1) - (order[b.trackType] || 1);
    });

    for (const item of sortedItems) {
      try {
        const element = await loadMediaWithCache(item);
        if (!element) {
          console.log(`‚ùå –ù–µ —É–¥–∞–ª–æ—Å—å –∑–∞–≥—Ä—É–∑–∏—Ç—å —ç–ª–µ–º–µ–Ω—Ç: ${item.name}`);
          continue;
        }

        ctx.save();

        // –û–±—Ä–∞–±–æ—Ç–∫–∞ –≤–∏–¥–µ–æ —ç–ª–µ–º–µ–Ω—Ç–æ–≤
        if (element.tagName === "VIDEO") {
          const relativeTime = currentTime - (item.startTime || 0);
          const targetTime = Math.max(0, relativeTime);

          if (Math.abs(element.currentTime - targetTime) > 0.2) {
            element.currentTime = targetTime;
          }
        }

        // –û–ø—Ä–µ–¥–µ–ª—è–µ–º –ø–æ–∑–∏—Ü–∏–∏ –∏ —Ä–∞–∑–º–µ—Ä—ã
        let x, y, w, h;

        if (item.trackType === "main") {
          // –û—Å–Ω–æ–≤–Ω–æ–µ –≤–∏–¥–µ–æ - –Ω–∞ –≤–µ—Å—å —ç–∫—Ä–∞–Ω
          x = 0;
          y = 0;
          w = width;
          h = height;
          console.log(`üì∫ –†–µ–Ω–¥–µ—Ä–∏–º –æ—Å–Ω–æ–≤–Ω–æ–µ –≤–∏–¥–µ–æ: ${item.name} (${w}x${h})`);
        } else if (item.trackType === "overlay") {
          // ‚úÖ –ò–°–ü–û–õ–¨–ó–£–ï–ú –∫–æ–æ—Ä–¥–∏–Ω–∞—Ç—ã –∏–∑ overlayTransforms
          const overlayCoords = overlayTransforms[item.id] || { x: 0, y: 0, scale: 1, opacity: 1 };
          
          // –ë–∞–∑–æ–≤—ã–µ —Ä–∞–∑–º–µ—Ä—ã overlay –≤ VideoEditor
          const baseX = 200 + overlayCoords.x; // 200 - –±–∞–∑–æ–≤–∞—è –ø–æ–∑–∏—Ü–∏—è –≤ VideoEditor
          const baseY = 50 + overlayCoords.y;   // 50 - –±–∞–∑–æ–≤–∞—è –ø–æ–∑–∏—Ü–∏—è
          const baseWidth = 128;  // –†–∞–∑–º–µ—Ä overlay –≤ VideoEditor
          const baseHeight = 96;
          const scale = overlayCoords.scale || 1;
          const opacity = overlayCoords.opacity || 1;

          // –ú–∞—Å—à—Ç–∞–±–∏—Ä—É–µ–º –ø–æ–¥ —Ä–∞–∑–º–µ—Ä —ç–∫—Å–ø–æ—Ä—Ç–∞
          // –ü—Ä–µ–¥–ø–æ–ª–∞–≥–∞–µ–º —á—Ç–æ –ø—Ä–µ–≤—å—é –≤ VideoEditor –ø—Ä–∏–º–µ—Ä–Ω–æ 1920x1080
          const previewWidth = 1920;
          const previewHeight = 1080;
          
          const scaleX = width / previewWidth;
          const scaleY = height / previewHeight;

          x = baseX * scaleX;
          y = baseY * scaleY;
          w = baseWidth * scale * scaleX;
          h = baseHeight * scale * scaleY;

          ctx.globalAlpha = opacity;
          console.log(`üñºÔ∏è –†–µ–Ω–¥–µ—Ä–∏–º overlay: ${item.name}`);
          console.log(`     –ò—Å—Ö–æ–¥–Ω—ã–µ –∫–æ–æ—Ä–¥–∏–Ω–∞—Ç—ã:`, overlayCoords);
          console.log(`     –§–∏–Ω–∞–ª—å–Ω–∞—è –ø–æ–∑–∏—Ü–∏—è: pos(${x.toFixed(0)}, ${y.toFixed(0)}) size(${w.toFixed(0)}x${h.toFixed(0)}) opacity(${opacity})`);
        } else {
          // –ù–µ–∏–∑–≤–µ—Å—Ç–Ω—ã–π —Ç–∏–ø - —Ä–µ–Ω–¥–µ—Ä–∏–º –∫–∞–∫ overlay –ø–æ —É–º–æ–ª—á–∞–Ω–∏—é
          x = 100;
          y = 100;
          w = 200;
          h = 150;
          console.log(`üî∂ –†–µ–Ω–¥–µ—Ä–∏–º –Ω–µ–∏–∑–≤–µ—Å—Ç–Ω—ã–π —Ç–∏–ø: ${item.name} –∫–∞–∫ overlay`);
        }

        // –†–∏—Å—É–µ–º —ç–ª–µ–º–µ–Ω—Ç
        if (w > 0 && h > 0) {
          ctx.drawImage(element, x, y, w, h);
          console.log(`‚úÖ –≠–ª–µ–º–µ–Ω—Ç –æ—Ç—Ä–µ–Ω–¥–µ—Ä–µ–Ω: ${item.name}`);
        } else {
          console.log(`‚ùå –ù–µ–ø—Ä–∞–≤–∏–ª—å–Ω—ã–µ —Ä–∞–∑–º–µ—Ä—ã –¥–ª—è ${item.name}: ${w}x${h}`);
        }

        ctx.restore();
      } catch (error) {
        console.warn(`‚ö†Ô∏è –û—à–∏–±–∫–∞ —Ä–µ–Ω–¥–µ—Ä–∏–Ω–≥–∞ ${item.name}:`, error);
      }
    }

    console.log(`üèÅ –†–µ–Ω–¥–µ—Ä–∏–Ω–≥ –∫–∞–¥—Ä–∞ ${currentTime.toFixed(1)}s –∑–∞–≤–µ—Ä—à–µ–Ω\n`);
  };

  // –£–õ–£–ß–®–ï–ù–ù–ê–Ø –ø–æ–¥–≥–æ—Ç–æ–≤–∫–∞ –∞—É–¥–∏–æ (–≤—Å–µ —Ç—Ä–µ–∫–∏)
  const prepareOneAudioFile = async () => {
    if (!exportSettings.includeAudio) {
      return null;
    }

    try {
      // –ò—â–µ–º –í–°–ï –∞—É–¥–∏–æ —Ç—Ä–µ–∫–∏
      const audioItems = timelineItems.filter(
        (item) => item.trackType === "audio" || (item.type === "videos" && item.trackType === "main")
      );

      if (audioItems.length === 0) {
        console.log("üîá –ê—É–¥–∏–æ —Ç—Ä–µ–∫–æ–≤ –Ω–µ –Ω–∞–π–¥–µ–Ω–æ");
        return null;
      }

      console.log(`üéµ –ù–∞–π–¥–µ–Ω–æ ${audioItems.length} –∞—É–¥–∏–æ —Ç—Ä–µ–∫–æ–≤:`, audioItems.map(item => item.name));

      // –ë–µ—Ä–µ–º –ø–µ—Ä–≤—ã–π –¥–æ—Å—Ç—É–ø–Ω—ã–π –∞—É–¥–∏–æ —Ç—Ä–µ–∫
      for (const audioItem of audioItems) {
        try {
          console.log(`üì• –ü—Ä–æ–±—É–µ–º –∑–∞–≥—Ä—É–∑–∏—Ç—å –∞—É–¥–∏–æ: ${audioItem.name} (—Ç–∏–ø: ${audioItem.type}, —Ç—Ä–µ–∫: ${audioItem.trackType})`);
          
          const response = await fetch(audioItem.url);
          if (!response.ok) {
            console.warn(`‚ö†Ô∏è HTTP –æ—à–∏–±–∫–∞ ${response.status} –¥–ª—è ${audioItem.name}`);
            continue;
          }

          const blob = await response.blob();
          if (blob.size === 0) {
            console.warn(`‚ö†Ô∏è –ü—É—Å—Ç–æ–π —Ñ–∞–π–ª: ${audioItem.name}`);
            continue;
          }

          console.log(`‚úÖ –ê—É–¥–∏–æ –∑–∞–≥—Ä—É–∂–µ–Ω–æ: ${audioItem.name} (${(blob.size / 1024).toFixed(1)}KB)`);
          return { blob, name: audioItem.name, type: audioItem.type };
        } catch (error) {
          console.warn(`‚ö†Ô∏è –û—à–∏–±–∫–∞ –∑–∞–≥—Ä—É–∑–∫–∏ ${audioItem.name}:`, error);
          continue;
        }
      }

      console.log("‚ùå –ù–µ —É–¥–∞–ª–æ—Å—å –∑–∞–≥—Ä—É–∑–∏—Ç—å –Ω–∏ –æ–¥–Ω–æ–≥–æ –∞—É–¥–∏–æ —Ñ–∞–π–ª–∞");
      return null;
    } catch (error) {
      console.warn(`‚ö†Ô∏è –û–±—â–∞—è –æ—à–∏–±–∫–∞ –ø–æ–¥–≥–æ—Ç–æ–≤–∫–∏ –∞—É–¥–∏–æ:`, error);
      return null;
    }
  };

  // –ë—ã—Å—Ç—Ä–æ–µ —Å–æ–∑–¥–∞–Ω–∏–µ –∫–∞–¥—Ä–æ–≤ (–ë–ï–ó –ò–ó–ú–ï–ù–ï–ù–ò–ô)
  const createFramesBatch = async (duration) => {
    const canvas = canvasRef.current;
    const ctx = canvas.getContext("2d", { alpha: false });

    const [width, height] = exportSettings.resolution.split("x").map(Number);
    canvas.width = width;
    canvas.height = height;

    const maxFrames = Math.min(
      Math.ceil(duration * exportSettings.fps),
      300
    );

    const frames = [];
    const batchSize = 10;

    for (let i = 0; i < maxFrames; i += batchSize) {
      const batch = [];

      for (let j = 0; j < batchSize && i + j < maxFrames; j++) {
        const frameIndex = i + j;
        const currentTime = frameIndex / exportSettings.fps;

        await renderFrameFast(ctx, currentTime, width, height);

        const blob = await new Promise((resolve) => {
          canvas.toBlob(resolve, "image/jpeg", 0.7);
        });

        if (blob) batch.push(blob);
      }

      frames.push(...batch);

      const progress = Math.round((i / maxFrames) * 50);
      setExportProgress(progress);

      await new Promise((resolve) => setTimeout(resolve, 10));
    }

    return frames;
  };

  // –ë–´–°–¢–†–û–ï –∫–æ–¥–∏—Ä–æ–≤–∞–Ω–∏–µ (–≤–æ–∑–≤—Ä–∞—Ç –∫ ultrafast + –º–∏–Ω–∏–º–∞–ª—å–Ω–æ–µ –∞—É–¥–∏–æ)
  const encodeVideoFast = async (frames, audioFile, duration) => {
    if (!window.fetchFile || frames.length === 0) {
      throw new Error("–ù–µ—Ç –∫–∞–¥—Ä–æ–≤ –¥–ª—è –∫–æ–¥–∏—Ä–æ–≤–∞–Ω–∏—è");
    }

    try {
      setExportProgress(50);
      console.log(`üé¨ –ë—ã—Å—Ç—Ä–æ–µ –∫–æ–¥–∏—Ä–æ–≤–∞–Ω–∏–µ ${frames.length} –∫–∞–¥—Ä–æ–≤...`);

      // –ó–∞–ø–∏—Å—ã–≤–∞–µ–º –∫–∞–¥—Ä—ã
      for (let i = 0; i < frames.length; i++) {
        const frameData = await window.fetchFile(frames[i]);
        const filename = `f${i.toString().padStart(4, "0")}.jpg`;
        await ffmpeg.writeFile(filename, frameData);

        if (i % 20 === 0) {
          setExportProgress(50 + Math.round((i / frames.length) * 20));
        }
      }

      setExportProgress(70);

      // –ü—Ä–æ—Å—Ç–µ–π—à–∞—è –∫–æ–º–∞–Ω–¥–∞ FFmpeg (–≤–æ–∑–≤—Ä–∞—Ç –∫ –æ—Ä–∏–≥–∏–Ω–∞–ª—É)
      const args = [
        "-framerate", exportSettings.fps.toString(),
        "-i", "f%04d.jpg",
        "-t", Math.min(duration, frames.length / exportSettings.fps).toString(),
      ];

      // –¢–û–õ–¨–ö–û –µ—Å–ª–∏ –µ—Å—Ç—å –∞—É–¥–∏–æ - –¥–æ–±–∞–≤–ª—è–µ–º –µ–≥–æ –ø—Ä–æ—Å—Ç–æ
      if (audioFile) {
        const audioData = await window.fetchFile(audioFile.blob);
        await ffmpeg.writeFile("audio.mp3", audioData);
        
        args.push("-i", "audio.mp3");
        args.push("-c:a", "aac");
        args.push("-b:a", "128k");
        args.push("-shortest");
      }

      // –ë–´–°–¢–†–´–ï –Ω–∞—Å—Ç—Ä–æ–π–∫–∏ (–∫–∞–∫ –±—ã–ª–æ)
      args.push(
        "-c:v", "libx264",
        "-preset", "ultrafast", // –í–ï–†–ù–£–õ–ò ultrafast
        "-crf", "28",
        "-pix_fmt", "yuv420p",
        "-vf", "scale=trunc(iw/2)*2:trunc(ih/2)*2",
        "-movflags", "+faststart",
        "output.mp4"
      );

      console.log("üîß –ë—ã—Å—Ç—Ä–∞—è –∫–æ–º–∞–Ω–¥–∞ FFmpeg:", args.join(" "));

      await ffmpeg.exec(args);

      setExportProgress(90);

      const data = await ffmpeg.readFile("output.mp4");
      const videoBlob = new Blob([data.buffer], { type: "video/mp4" });

      // –ë—ã—Å—Ç—Ä–∞—è –æ—á–∏—Å—Ç–∫–∞
      for (let i = 0; i < frames.length; i++) {
        try {
          await ffmpeg.deleteFile(`f${i.toString().padStart(4, "0")}.jpg`);
        } catch (e) {}
      }
      
      if (audioFile) {
        try {
          await ffmpeg.deleteFile("audio.mp3");
        } catch (e) {}
      }

      try {
        await ffmpeg.deleteFile("output.mp4");
      } catch (e) {}

      return videoBlob;
    } catch (error) {
      throw new Error(`–û—à–∏–±–∫–∞ –∫–æ–¥–∏—Ä–æ–≤–∞–Ω–∏—è: ${error.message}`);
    }
  };

  // –£–õ–£–ß–®–ï–ù–ù–ê–Ø –æ—Å–Ω–æ–≤–Ω–∞—è —Ñ—É–Ω–∫—Ü–∏—è —Å –¥–∏–∞–≥–Ω–æ—Å—Ç–∏–∫–æ–π
  const startFastExport = async () => {
    if (!ffmpeg || isExporting || timelineItems.length === 0) return;

    setIsExporting(true);
    setExportProgress(0);
    setExportStage("processing");
    setError(null);
    setExportedBlob(null);

    try {
      console.log("üöÄ –ù–∞—á–∏–Ω–∞–µ–º –±—ã—Å—Ç—Ä—ã–π —ç–∫—Å–ø–æ—Ä—Ç...");
      console.log(`üìä Timeline —Å–æ–¥–µ—Ä–∂–∏—Ç ${timelineItems.length} —ç–ª–µ–º–µ–Ω—Ç–æ–≤:`);
      
      // –î–∏–∞–≥–Ω–æ—Å—Ç–∏–∫–∞ —ç–ª–µ–º–µ–Ω—Ç–æ–≤ timeline
      timelineItems.forEach((item, index) => {
        console.log(`  ${index + 1}. ${item.name} (—Ç–∏–ø: ${item.type}, —Ç—Ä–µ–∫: ${item.trackType}, –≤—Ä–µ–º—è: ${item.startTime}s-${(item.startTime + item.duration).toFixed(1)}s)`);
      });

      const actualDuration = Math.min(exportSettings.duration, 30);
      console.log(`‚è±Ô∏è –≠–∫—Å–ø–æ—Ä—Ç–∏—Ä—É–µ–º ${actualDuration}s –∏–∑ ${videoDuration}s`);

      setExportStage("rendering");
      const frames = await createFramesBatch(actualDuration);

      if (frames.length === 0) {
        throw new Error("–ù–µ —É–¥–∞–ª–æ—Å—å —Å–æ–∑–¥–∞—Ç—å –∫–∞–¥—Ä—ã");
      }

      console.log(`üéûÔ∏è –°–æ–∑–¥–∞–Ω–æ ${frames.length} –∫–∞–¥—Ä–æ–≤`);

      // –ü–æ–¥–≥–æ—Ç–æ–≤–∫–∞ –∞—É–¥–∏–æ —Å –¥–∏–∞–≥–Ω–æ—Å—Ç–∏–∫–æ–π
      const audioFile = await prepareOneAudioFile();
      if (audioFile) {
        console.log(`üéµ –ê—É–¥–∏–æ –ø–æ–¥–≥–æ—Ç–æ–≤–ª–µ–Ω–æ: ${audioFile.name}`);
      } else {
        console.log(`üîá –≠–∫—Å–ø–æ—Ä—Ç –±–µ–∑ –∞—É–¥–∏–æ`);
      }

      setExportStage("encoding");
      const videoBlob = await encodeVideoFast(frames, audioFile, actualDuration);

      setExportProgress(100);
      setExportStage("completed");
      setExportedBlob(videoBlob);

      console.log(`‚úÖ –ë—ã—Å—Ç—Ä—ã–π —ç–∫—Å–ø–æ—Ä—Ç –∑–∞–≤–µ—Ä—à–µ–Ω! –†–∞–∑–º–µ—Ä: ${(videoBlob.size / 1024 / 1024).toFixed(2)}MB`);
      console.log(`üì¶ –°–æ–¥–µ—Ä–∂–∏–º–æ–µ: –≤–∏–¥–µ–æ + ${audioFile ? '–∞—É–¥–∏–æ' : '–±–µ–∑ –∞—É–¥–∏–æ'} + overlay —ç–ª–µ–º–µ–Ω—Ç—ã`);
    } catch (error) {
      console.error("‚ùå –û—à–∏–±–∫–∞ –±—ã—Å—Ç—Ä–æ–≥–æ —ç–∫—Å–ø–æ—Ä—Ç–∞:", error);
      setError(error.message);
      setExportStage("error");
    } finally {
      setIsExporting(false);
    }
  };

  const downloadVideo = () => {
    if (!exportedBlob) return;

    const url = URL.createObjectURL(exportedBlob);
    const a = document.createElement("a");
    a.href = url;
    a.download = `fast_export_${Date.now()}.mp4`;
    document.body.appendChild(a);
    a.click();
    document.body.removeChild(a);
    URL.revokeObjectURL(url);
  };

  const getStageText = () => {
    switch (exportStage) {
      case "loading": return "–ó–∞–≥—Ä—É–∑–∫–∞ FFmpeg...";
      case "processing": return "–ü–æ–¥–≥–æ—Ç–æ–≤–∫–∞...";
      case "rendering": return "–†–µ–Ω–¥–µ—Ä–∏–Ω–≥ –∫–∞–¥—Ä–æ–≤...";
      case "encoding": return "–ë—ã—Å—Ç—Ä–æ–µ –∫–æ–¥–∏—Ä–æ–≤–∞–Ω–∏–µ...";
      case "completed": return "‚úÖ –ì–æ—Ç–æ–≤–æ!";
      case "error": return "‚ùå –û—à–∏–±–∫–∞";
      default: return "–ì–æ—Ç–æ–≤ –∫ –±—ã—Å—Ç—Ä–æ–º—É —ç–∫—Å–ø–æ—Ä—Ç—É";
    }
  };

  const audioTracksCount = timelineItems.filter(
    (item) => item.trackType === "audio" || (item.type === "videos" && item.trackType === "main")
  ).length;

  if (!isOpen) return null;

  return (
    <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
      <div className="bg-white rounded-2xl shadow-xl max-w-lg w-full p-6">
        <h2 className="text-xl font-bold mb-4 flex items-center">
          <Zap size={24} className="mr-3 text-yellow-600" />
          ‚ö° –ë—ã—Å—Ç—Ä—ã–π —ç–∫—Å–ø–æ—Ä—Ç + –∞—É–¥–∏–æ
        </h2>

        <div className="space-y-4">
          {/* –ü—Ä–æ—Å—Ç—ã–µ –Ω–∞—Å—Ç—Ä–æ–π–∫–∏ */}
          <div className="bg-yellow-50 p-4 rounded-lg">
            <h3 className="font-medium text-yellow-800 mb-2 flex items-center">
              <Settings size={16} className="mr-2" />
              –ù–∞—Å—Ç—Ä–æ–π–∫–∏ —Å–∫–æ—Ä–æ—Å—Ç–∏
            </h3>
            <div className="grid grid-cols-2 gap-3">
              <div>
                <label className="block text-xs font-medium mb-1">–†–∞–∑—Ä–µ—à–µ–Ω–∏–µ</label>
                <select
                  value={exportSettings.resolution}
                  onChange={(e) => setExportSettings(prev => ({ ...prev, resolution: e.target.value }))}
                  className="w-full p-2 text-sm border rounded"
                  disabled={isExporting}
                >
                  <option value="640x480">480p (–±—ã—Å—Ç—Ä–æ)</option>
                  <option value="1280x720">720p (—Å—Ä–µ–¥–Ω–µ)</option>
                </select>
              </div>

              <div>
                <label className="block text-xs font-medium mb-1">–î–ª–∏—Ç–µ–ª—å–Ω–æ—Å—Ç—å</label>
                <select
                  value={exportSettings.duration}
                  onChange={(e) => setExportSettings(prev => ({ ...prev, duration: parseInt(e.target.value) }))}
                  className="w-full p-2 text-sm border rounded"
                  disabled={isExporting}
                >
                  <option value="10">10 —Å–µ–∫</option>
                  <option value="20">20 —Å–µ–∫</option>
                  <option value="30">30 —Å–µ–∫</option>
                </select>
              </div>
            </div>

            {/* –ü—Ä–æ—Å—Ç–æ–π –ø–µ—Ä–µ–∫–ª—é—á–∞—Ç–µ–ª—å –∞—É–¥–∏–æ */}
            <div className="mt-3">
              <label className="flex items-center space-x-2">
                <input
                  type="checkbox"
                  checked={exportSettings.includeAudio}
                  onChange={(e) => setExportSettings(prev => ({ ...prev, includeAudio: e.target.checked }))}
                  disabled={isExporting}
                  className="rounded"
                />
                <span className="text-sm">
                  –í–∫–ª—é—á–∏—Ç—å –∞—É–¥–∏–æ (–ø–µ—Ä–≤—ã–π —Ç—Ä–µ–∫: {audioTracksCount > 0 ? "–µ—Å—Ç—å" : "–Ω–µ—Ç"})
                </span>
              </label>
            </div>
          </div>

          {/* –°—Ç–∞—Ç—É—Å */}
          <div className={`p-4 rounded-lg ${
            exportStage === "error" ? "bg-red-50" :
            exportStage === "completed" ? "bg-green-50" : "bg-blue-50"
          }`}>
            <div className="flex items-center justify-between mb-2">
              <span className="font-medium">{getStageText()}</span>
              {isExporting && <span className="text-sm">{exportProgress}%</span>}
            </div>
            {isExporting && (
              <div className="w-full bg-gray-200 rounded-full h-2">
                <div
                  className="bg-blue-600 h-2 rounded-full transition-all"
                  style={{ width: `${exportProgress}%` }}
                />
              </div>
            )}
          </div>

          {/* –û—à–∏–±–∫–∞ */}
          {error && (
            <div className="bg-red-50 p-4 rounded-lg">
              <div className="text-red-800 font-medium">–û—à–∏–±–∫–∞</div>
              <div className="text-red-600 text-sm">{error}</div>
            </div>
          )}

          {/* –†–µ–∑—É–ª—å—Ç–∞—Ç */}
          {exportedBlob && (
            <div className="bg-green-50 p-4 rounded-lg">
              <div className="text-green-800 font-medium mb-2">
                ‚úÖ –í–∏–¥–µ–æ –≥–æ—Ç–æ–≤–æ! –†–∞–∑–º–µ—Ä: {(exportedBlob.size / 1024 / 1024).toFixed(2)}MB
              </div>
              <button
                onClick={downloadVideo}
                className="bg-green-600 text-white px-4 py-2 rounded hover:bg-green-700 flex items-center"
              >
                <Download size={16} className="mr-2" />
                –°–∫–∞—á–∞—Ç—å
              </button>
            </div>
          )}

          {/* –ü–æ–¥—Ä–æ–±–Ω–∞—è —Å—Ç–∞—Ç–∏—Å—Ç–∏–∫–∞ */}
          <div className="text-xs text-gray-600 bg-gray-50 p-3 rounded">
            <div className="grid grid-cols-2 gap-2">
              <div>–≠–ª–µ–º–µ–Ω—Ç–æ–≤ timeline: {timelineItems.length}</div>
              <div>–î–ª–∏—Ç–µ–ª—å–Ω–æ—Å—Ç—å: {exportSettings.duration}s</div>
              <div>–í–∏–¥–µ–æ —Ç—Ä–µ–∫–æ–≤: {timelineItems.filter(item => item.trackType === "main").length}</div>
              <div>Overlay —ç–ª–µ–º–µ–Ω—Ç–æ–≤: {timelineItems.filter(item => item.trackType === "overlay").length}</div>
              <div>–ê—É–¥–∏–æ —Ç—Ä–µ–∫–æ–≤: {timelineItems.filter(item => item.trackType === "audio").length}</div>
              <div>–ê—É–¥–∏–æ: {exportSettings.includeAudio ? "–≤–∫–ª—é—á–µ–Ω–æ" : "–≤—ã–∫–ª—é—á–µ–Ω–æ"}</div>
            </div>
            <div className="mt-2 text-center">
              –ö–∞—á–µ—Å—Ç–≤–æ: –ë—ã—Å—Ç—Ä–æ–µ (ultrafast preset) + overlay –ø–æ–∑–∏—Ü–∏–æ–Ω–∏—Ä–æ–≤–∞–Ω–∏–µ
            </div>
          </div>
        </div>

        <div className="flex justify-between mt-6">
          <button
            onClick={onClose}
            className="px-4 py-2 text-gray-600 hover:text-gray-800"
            disabled={isExporting}
          >
            –ó–∞–∫—Ä—ã—Ç—å
          </button>

          <button
            onClick={startFastExport}
            disabled={!ffmpeg || isExporting || timelineItems.length === 0}
            className="bg-yellow-600 text-white px-6 py-2 rounded hover:bg-yellow-700 flex items-center disabled:bg-gray-400"
          >
            <Zap size={16} className="mr-2" />
            {isExporting ? `${exportProgress}%` : "–ë—ã—Å—Ç—Ä—ã–π —ç–∫—Å–ø–æ—Ä—Ç"}
          </button>
        </div>

        <canvas ref={canvasRef} style={{ display: "none" }} />
      </div>
    </div>
  );
};

export const useFFmpegExporter = () => {
  const [isExportModalOpen, setIsExportModalOpen] = useState(false);

  const openExportModal = () => setIsExportModalOpen(true);
  const closeExportModal = () => setIsExportModalOpen(false);

  return {
    isExportModalOpen,
    openExportModal,
    closeExportModal,
  };
};

export default FFmpegVideoExporter;
