import { useState, useRef, useEffect } from "react";

export const TeleprompterModal = ({
  isOpen = true,
  onClose = () => {},
  script = "–î–æ–±—Ä–æ –ø–æ–∂–∞–ª–æ–≤–∞—Ç—å –≤ —Ç–µ–ª–µ–ø—Ä–æ–º–ø—Ç–µ—Ä! –≠—Ç–æ –¥–µ–º–æ–Ω—Å—Ç—Ä–∞—Ü–∏—è —Ä–∞–±–æ—Ç—ã —Å—É—Ñ–ª–µ—Ä–∞ —Å –≤–æ–∑–º–æ–∂–Ω–æ—Å—Ç—å—é –∑–∞–ø–∏—Å–∏. –ó–¥–µ—Å—å –≤—ã –º–æ–∂–µ—Ç–µ –Ω–∞—Å—Ç—Ä–æ–∏—Ç—å —Å–∫–æ—Ä–æ—Å—Ç—å –ø—Ä–æ–∫—Ä—É—Ç–∫–∏, —Ä–∞–∑–º–µ—Ä —à—Ä–∏—Ñ—Ç–∞ –∏ —Ç–∏–ø –∑–∞–ø–∏—Å–∏. –ü—Ä–æ–∫—Ä—É—Ç–∫–∞ –±—É–¥–µ—Ç –∞–≤—Ç–æ–º–∞—Ç–∏—á–µ—Å–∫–∏ –∑–∞–ø—É—â–µ–Ω–∞ –ø—Ä–∏ –Ω–∞—á–∞–ª–µ –∑–∞–ø–∏—Å–∏. –ò—Å–ø–æ–ª—å–∑—É–π—Ç–µ –∫–Ω–æ–ø–∫–∏ —É–ø—Ä–∞–≤–ª–µ–Ω–∏—è –¥–ª—è –ø–∞—É–∑—ã, –ø—Ä–æ–¥–æ–ª–∂–µ–Ω–∏—è –∏–ª–∏ —Å–±—Ä–æ—Å–∞ —Ç–µ–∫—Å—Ç–∞. –ü—Ä–∏ –∑–∞–ø–∏—Å–∏ –≤–∏–¥–µ–æ –≤—ã —É–≤–∏–¥–∏—Ç–µ –æ–∫–Ω–æ –ø—Ä–µ–¥–≤–∞—Ä–∏—Ç–µ–ª—å–Ω–æ–≥–æ –ø—Ä–æ—Å–º–æ—Ç—Ä–∞ –≤ –ø—Ä–∞–≤–æ–º –≤–µ—Ä—Ö–Ω–µ–º —É–≥–ª—É. –≠—Ç–æ –ø–æ–º–æ–∂–µ—Ç –≤–∞–º –≤–∏–¥–µ—Ç—å, —á—Ç–æ –∏–º–µ–Ω–Ω–æ –∑–∞–ø–∏—Å—ã–≤–∞–µ—Ç—Å—è. –¢–µ–ª–µ–ø—Ä–æ–º–ø—Ç–µ—Ä –ø–æ–¥–¥–µ—Ä–∂–∏–≤–∞–µ—Ç –∫–∞–∫ –∞—É–¥–∏–æ, —Ç–∞–∫ –∏ –≤–∏–¥–µ–æ –∑–∞–ø–∏—Å—å. –ù–∞—Å—Ç—Ä–æ–π—Ç–µ –ø–∞—Ä–∞–º–µ—Ç—Ä—ã –≤ —Å–æ–æ—Ç–≤–µ—Ç—Å—Ç–≤–∏–∏ —Å –≤–∞—à–∏–º–∏ –ø–æ—Ç—Ä–µ–±–Ω–æ—Å—Ç—è–º–∏ –∏ –Ω–∞—á–∏–Ω–∞–π—Ç–µ –∑–∞–ø–∏—Å—å!",
  onRecordingComplete = () => {},
}) => {
  const [isRecording, setIsRecording] = useState(false);
  const [isPaused, setIsPaused] = useState(false);
  const [recordingType, setRecordingType] = useState("audio");
  const [speed, setSpeed] = useState(50);
  const [fontSize, setFontSize] = useState(24);
  const [isScrolling, setIsScrolling] = useState(false);
  const [showSettings, setShowSettings] = useState(false);
  const [recordedBlob, setRecordedBlob] = useState(null);
  const [recordingTime, setRecordingTime] = useState(0);
  const [videoLoading, setVideoLoading] = useState(true); // –î–æ–±–∞–≤–ª–µ–Ω–æ —Å–æ—Å—Ç–æ—è–Ω–∏–µ –∑–∞–≥—Ä—É–∑–∫–∏ –≤–∏–¥–µ–æ
  const [videoError, setVideoError] = useState(null); // –î–æ–±–∞–≤–ª–µ–Ω–æ —Å–æ—Å—Ç–æ—è–Ω–∏–µ –æ—à–∏–±–∫–∏ –≤–∏–¥–µ–æ
  const [cameraStatus, setCameraStatus] = useState('idle'); // idle, requesting, granted, denied, error
  const [videoDebugInfo, setVideoDebugInfo] = useState(null); // –û—Ç–ª–∞–¥–æ—á–Ω–∞—è –∏–Ω—Ñ–æ—Ä–º–∞—Ü–∏—è

  const scrollContainerRef = useRef(null);
  const mediaRecorderRef = useRef(null);
  const scrollIntervalRef = useRef(null);
  const timerRef = useRef(null);
  const streamRef = useRef(null);
  const videoPreviewRef = useRef(null);

  const startScrolling = () => {
    if (scrollIntervalRef.current) return;

    setIsScrolling(true);
    setIsPaused(false); // –Ø–≤–Ω–æ —Å–±—Ä–∞—Å—ã–≤–∞–µ–º –ø–∞—É–∑—É –ø—Ä–∏ —Å—Ç–∞—Ä—Ç–µ
    
    scrollIntervalRef.current = setInterval(() => {
      if (scrollContainerRef.current) {
        const container = scrollContainerRef.current;
        const scrollStep = (speed / 50) * 2;
        container.scrollTop += scrollStep;

        if (
          container.scrollTop >=
          container.scrollHeight - container.clientHeight
        ) {
          stopScrolling();
        }
      }
    }, 50);
  };

  const stopScrolling = () => {
    if (scrollIntervalRef.current) {
      clearInterval(scrollIntervalRef.current);
      scrollIntervalRef.current = null;
    }
    setIsScrolling(false);
    setIsPaused(false);
  };

  const pauseScrolling = () => {
    if (scrollIntervalRef.current) {
      clearInterval(scrollIntervalRef.current);
      scrollIntervalRef.current = null;
    }
    setIsPaused(true);
    // isScrolling –æ—Å—Ç–∞–µ—Ç—Å—è true, —á—Ç–æ–±—ã –ø–æ–∫–∞–∑–∞—Ç—å, —á—Ç–æ –ø—Ä–æ–∫—Ä—É—Ç–∫–∞ –∞–∫—Ç–∏–≤–Ω–∞, –Ω–æ –ø—Ä–∏–æ—Å—Ç–∞–Ω–æ–≤–ª–µ–Ω–∞
  };

  const resumeScrolling = () => {
    if (!isScrolling) return;
    
    setIsPaused(false);
    scrollIntervalRef.current = setInterval(() => {
      if (scrollContainerRef.current) {
        const container = scrollContainerRef.current;
        const scrollStep = (speed / 50) * 2;
        container.scrollTop += scrollStep;

        if (
          container.scrollTop >=
          container.scrollHeight - container.clientHeight
        ) {
          stopScrolling();
        }
      }
    }, 50);
  };

  const resetScroll = () => {
    if (scrollContainerRef.current) {
      scrollContainerRef.current.scrollTop = 0;
    }
    stopScrolling();
  };

  const startTimer = () => {
    timerRef.current = setInterval(() => {
      setRecordingTime((prev) => prev + 1);
    }, 1000);
  };

  const stopTimer = () => {
    if (timerRef.current) {
      clearInterval(timerRef.current);
      timerRef.current = null;
    }
  };

  const resetTimer = () => {
    setRecordingTime(0);
    stopTimer();
  };

  const startRecording = async () => {
    try {
      // –ü—Ä–æ–≤–µ—Ä—è–µ–º –¥–æ—Å—Ç—É–ø–Ω–æ—Å—Ç—å API
      if (!navigator.mediaDevices || !navigator.mediaDevices.getUserMedia) {
        setVideoError("API –Ω–µ–¥–æ—Å—Ç—É–ø–Ω–æ");
        alert("–í–∞—à –±—Ä–∞—É–∑–µ—Ä –Ω–µ –ø–æ–¥–¥–µ—Ä–∂–∏–≤–∞–µ—Ç –∑–∞–ø–∏—Å—å –º–µ–¥–∏–∞. –ò—Å–ø–æ–ª—å–∑—É–π—Ç–µ —Å–æ–≤—Ä–µ–º–µ–Ω–Ω—ã–π –±—Ä–∞—É–∑–µ—Ä (Chrome, Firefox, Safari).");
        return;
      }

      setCameraStatus('requesting');
      setVideoError(null);

      const constraints =
        recordingType === "video"
          ? { 
              video: { 
                width: { ideal: 640 }, 
                height: { ideal: 480 },
                facingMode: 'user'
              }, 
              audio: true 
            }
          : { audio: true };

      console.log("üé• –ó–∞–ø—Ä–∞—à–∏–≤–∞–µ–º –¥–æ—Å—Ç—É–ø –∫ –º–µ–¥–∏–∞:", constraints);
      const stream = await navigator.mediaDevices.getUserMedia(constraints);
      console.log("‚úÖ –ü–æ—Ç–æ–∫ –ø–æ–ª—É—á–µ–Ω:", stream);
      console.log("üìπ –í–∏–¥–µ–æ —Ç—Ä–µ–∫–∏:", stream.getVideoTracks());
      console.log("üé§ –ê—É–¥–∏–æ —Ç—Ä–µ–∫–∏:", stream.getAudioTracks());
      
      streamRef.current = stream;
      setCameraStatus('granted');

      // –ù–∞—Å—Ç—Ä–æ–π–∫–∞ –ø—Ä–µ–¥–≤–∞—Ä–∏—Ç–µ–ª—å–Ω–æ–≥–æ –ø—Ä–æ—Å–º–æ—Ç—Ä–∞ –≤–∏–¥–µ–æ
      if (recordingType === "video" && videoPreviewRef.current) {
        const videoElement = videoPreviewRef.current;
        
        // –ü—Ä–æ–≤–µ—Ä—è–µ–º —á—Ç–æ —ç–ª–µ–º–µ–Ω—Ç –¥–µ–π—Å—Ç–≤–∏—Ç–µ–ª—å–Ω–æ —è–≤–ª—è–µ—Ç—Å—è video —ç–ª–µ–º–µ–Ω—Ç–æ–º
        if (!videoElement || videoElement.tagName !== 'VIDEO') {
          console.error("‚ùå –≠–ª–µ–º–µ–Ω—Ç –Ω–µ —è–≤–ª—è–µ—Ç—Å—è VIDEO:", videoElement);
          setVideoError("–ù–µ–ø—Ä–∞–≤–∏–ª—å–Ω—ã–π —Ç–∏–ø –≤–∏–¥–µ–æ —ç–ª–µ–º–µ–Ω—Ç–∞");
          setVideoLoading(false);
          return;
        }
        
        console.log("üé¨ –í–∏–¥–µ–æ —ç–ª–µ–º–µ–Ω—Ç –ø—Ä–æ–≤–µ—Ä–µ–Ω:", {
          tagName: videoElement.tagName,
          hasPlay: typeof videoElement.play === 'function',
          readyState: videoElement.readyState
        });
        
        setVideoLoading(true);
        setVideoError(null);
        
        console.log("üé¨ –£—Å—Ç–∞–Ω–∞–≤–ª–∏–≤–∞–µ–º srcObject –¥–ª—è –≤–∏–¥–µ–æ");
        console.log("üé¨ –í–∏–¥–µ–æ —ç–ª–µ–º–µ–Ω—Ç:", videoElement);
        console.log("üé¨ Stream:", stream);
        console.log("üé¨ Video tracks:", stream.getVideoTracks());
        
        // –û—á–∏—â–∞–µ–º –ø—Ä–µ–¥—ã–¥—É—â–∏–µ –æ–±—Ä–∞–±–æ—Ç—á–∏–∫–∏
        videoElement.onloadedmetadata = null;
        videoElement.oncanplay = null;
        videoElement.onerror = null;
        
        // –ü–æ–ø—Ä–æ–±—É–µ–º —Ä–∞–∑–Ω—ã–µ —Å–ø–æ—Å–æ–±—ã —É—Å—Ç–∞–Ω–æ–≤–∫–∏ –ø–æ—Ç–æ–∫–∞
        try {
          videoElement.srcObject = stream;
          console.log("‚úÖ srcObject —É—Å—Ç–∞–Ω–æ–≤–ª–µ–Ω");
          
          // –û–±–Ω–æ–≤–ª—è–µ–º –æ—Ç–ª–∞–¥–æ—á–Ω—É—é –∏–Ω—Ñ–æ—Ä–º–∞—Ü–∏—é
          setVideoDebugInfo({
            hasStream: !!stream,
            videoTracks: stream.getVideoTracks().length,
            audioTracks: stream.getAudioTracks().length,
            readyState: videoElement.readyState,
            videoWidth: videoElement.videoWidth,
            videoHeight: videoElement.videoHeight,
            srcObject: !!videoElement.srcObject
          });
          
        } catch (error) {
          console.error("‚ùå –û—à–∏–±–∫–∞ —É—Å—Ç–∞–Ω–æ–≤–∫–∏ srcObject:", error);
          setVideoError(`–û—à–∏–±–∫–∞ srcObject: ${error.message}`);
          setVideoLoading(false);
          return;
        }
        
        // –§—É–Ω–∫—Ü–∏—è –¥–ª—è –ø—Ä–∏–Ω—É–¥–∏—Ç–µ–ª—å–Ω–æ–≥–æ –≤–æ—Å–ø—Ä–æ–∏–∑–≤–µ–¥–µ–Ω–∏—è
        const forcePlay = async () => {
          try {
            console.log("üéÆ –ü–æ–ø—ã—Ç–∫–∞ –≤–æ—Å–ø—Ä–æ–∏–∑–≤–µ–¥–µ–Ω–∏—è...");
            
            // –ü—Ä–æ–≤–µ—Ä—è–µ–º —á—Ç–æ —ç–ª–µ–º–µ–Ω—Ç —Å—É—â–µ—Å—Ç–≤—É–µ—Ç –∏ —è–≤–ª—è–µ—Ç—Å—è –≤–∏–¥–µ–æ —ç–ª–µ–º–µ–Ω—Ç–æ–º
            if (!videoElement || typeof videoElement.play !== 'function') {
              console.error("‚ùå videoElement –Ω–µ —è–≤–ª—è–µ—Ç—Å—è –≤–∏–¥–µ–æ —ç–ª–µ–º–µ–Ω—Ç–æ–º:", videoElement);
              setVideoError("–û—à–∏–±–∫–∞ –≤–∏–¥–µ–æ —ç–ª–µ–º–µ–Ω—Ç–∞");
              setVideoLoading(false);
              return;
            }
            
            await videoElement.play();
            console.log("‚úÖ –í–∏–¥–µ–æ –≤–æ—Å–ø—Ä–æ–∏–∑–≤–æ–¥–∏—Ç—Å—è");
            setVideoLoading(false);
            setVideoError(null);
            
            // –û–±–Ω–æ–≤–ª—è–µ–º –æ—Ç–ª–∞–¥–æ—á–Ω—É—é –∏–Ω—Ñ–æ—Ä–º–∞—Ü–∏—é
            setVideoDebugInfo(prev => ({
              ...prev,
              readyState: videoElement.readyState,
              videoWidth: videoElement.videoWidth,
              videoHeight: videoElement.videoHeight,
              paused: videoElement.paused,
              currentTime: videoElement.currentTime
            }));
            
          } catch (playError) {
            console.error("‚ùå –û—à–∏–±–∫–∞ –≤–æ—Å–ø—Ä–æ–∏–∑–≤–µ–¥–µ–Ω–∏—è:", playError);
            setVideoError(`–ù–µ —É–¥–∞–µ—Ç—Å—è –≤–æ—Å–ø—Ä–æ–∏–∑–≤–µ—Å—Ç–∏: ${playError.message}`);
            setVideoLoading(false);
          }
        };
        
        // –ù–æ–≤—ã–µ –æ–±—Ä–∞–±–æ—Ç—á–∏–∫–∏ —Å–æ–±—ã—Ç–∏–π
        videoElement.addEventListener('loadedmetadata', function onLoadedMetadata() {
          console.log("üìê –í–∏–¥–µ–æ –º–µ—Ç–∞–¥–∞–Ω–Ω—ã–µ –∑–∞–≥—Ä—É–∂–µ–Ω—ã:", {
            width: videoElement.videoWidth,
            height: videoElement.videoHeight,
            duration: videoElement.duration,
            readyState: videoElement.readyState,
            hasPlay: typeof videoElement.play === 'function'
          });
          
          forcePlay();
          
          // –£–¥–∞–ª—è–µ–º –æ–±—Ä–∞–±–æ—Ç—á–∏–∫ –ø–æ—Å–ª–µ –∏—Å–ø–æ–ª—å–∑–æ–≤–∞–Ω–∏—è
          videoElement.removeEventListener('loadedmetadata', onLoadedMetadata);
        });
        
        videoElement.addEventListener('canplay', function onCanPlay() {
          console.log("üéØ –í–∏–¥–µ–æ –≥–æ—Ç–æ–≤–æ –∫ –≤–æ—Å–ø—Ä–æ–∏–∑–≤–µ–¥–µ–Ω–∏—é");
          setVideoLoading(false);
          setVideoError(null);
          forcePlay();
        });
        
        videoElement.addEventListener('error', function onError(e) {
          console.error("üí• –û—à–∏–±–∫–∞ –≤–∏–¥–µ–æ —ç–ª–µ–º–µ–Ω—Ç–∞:", e);
          const errorMsg = e.target?.error?.message || e.message || '–ù–µ–∏–∑–≤–µ—Å—Ç–Ω–∞—è –æ—à–∏–±–∫–∞ –≤–∏–¥–µ–æ —ç–ª–µ–º–µ–Ω—Ç–∞';
          setVideoError(`–û—à–∏–±–∫–∞ –≤–∏–¥–µ–æ: ${errorMsg}`);
          setVideoLoading(false);
        });

        videoElement.addEventListener('playing', function onPlaying() {
          console.log("üé¨ –í–∏–¥–µ–æ –Ω–∞—á–∞–ª–æ –≤–æ—Å–ø—Ä–æ–∏–∑–≤–µ–¥–µ–Ω–∏–µ");
          setVideoLoading(false);
          setVideoError(null);
        });

        // –ü—Ä–∏–Ω—É–¥–∏—Ç–µ–ª—å–Ω–∞—è –ø–æ–ø—ã—Ç–∫–∞ –∑–∞–ø—É—Å–∫–∞ —á–µ—Ä–µ–∑ —Ä–∞–∑–Ω—ã–µ –∏–Ω—Ç–µ—Ä–≤–∞–ª—ã
        setTimeout(() => {
