// –í–∞—à –æ–±–Ω–æ–≤–ª–µ–Ω–Ω—ã–π ScriptEditor —Å –∫–Ω–æ–ø–∫–æ–π —Å—É—Ñ–ª—ë—Ä–∞
import React, { useState } from 'react';
import { Skeleton } from '@mui/material';

// –°–∫–µ–ª–µ—Ç–æ–Ω (–æ—Å—Ç–∞–≤–ª—è–µ–º –∫–∞–∫ –µ—Å—Ç—å)
const ScriptEditorSkeleton = () => {
  return (
    <div className="bg-white rounded-lg shadow-md p-4">
      <div className="flex items-center justify-between mb-4">
        <div className="flex items-center space-x-2">
          <Skeleton variant="text" width={200} height={20} />
          <span className="text-yellow-500">‚ú®</span>
        </div>
        <div className="bg-slate-100 rounded-full px-4 py-2 text-sm text-slate-600">
          <Skeleton variant="text" width={180} height={20} />
        </div>
      </div>

      <div className="border border-slate-200 rounded-lg overflow-hidden">
        <div className="p-4 bg-slate-50">
          <Skeleton variant="text" width="100%" height={20} />
          <Skeleton variant="text" width="80%" height={20} className="mt-2" />
          <Skeleton variant="text" width="70%" height={20} className="mt-2" />
          <Skeleton variant="text" width="90%" height={20} className="mt-2" />
        </div>

        <div className="absolute inset-y-0 right-0 w-1 bg-slate-200 rounded-r-lg">
          <div className="h-1/2 bg-slate-300 rounded-full animate-pulse"/>
        </div>
      </div>
    </div>
  );
};

// –û—Å–Ω–æ–≤–Ω–æ–π —Ä–µ–¥–∞–∫—Ç–æ—Ä —Å—Ü–µ–Ω–∞—Ä–∏—è —Å —Å—É—Ñ–ª—ë—Ä–æ–º
const ScriptEditor = ({ 
  script = '', 
  onSave, 
  title = '–†–µ–¥–∞–∫—Ç–æ—Ä —Å—Ü–µ–Ω–∞—Ä–∏—è',
  isLoading = false 
}) => {
  const [content, setContent] = useState(script);
  const [showTeleprompter, setShowTeleprompter] = useState(false);
  const [wordCount, setWordCount] = useState(0);
  const [charCount, setCharCount] = useState(0);

  // –ü–æ–¥—Å—á–µ—Ç —Å—Ç–∞—Ç–∏—Å—Ç–∏–∫–∏
  React.useEffect(() => {
    const words = content.trim().split(/\s+/).filter(word => word.length > 0);
    setWordCount(words.length);
    setCharCount(content.length);
  }, [content]);

  // –ï—Å–ª–∏ –∑–∞–≥—Ä—É–∂–∞–µ—Ç—Å—è - –ø–æ–∫–∞–∑—ã–≤–∞–µ–º —Å–∫–µ–ª–µ—Ç–æ–Ω
  if (isLoading) {
    return <ScriptEditorSkeleton />;
  }

  const handleSave = () => {
    if (onSave) {
      onSave(content);
    }
  };

  const handleOpenTeleprompter = () => {
    if (!content.trim()) {
      alert('–°–Ω–∞—á–∞–ª–∞ –Ω–∞–ø–∏—à–∏—Ç–µ —Ç–µ–∫—Å—Ç —Å—Ü–µ–Ω–∞—Ä–∏—è');
      return;
    }
    setShowTeleprompter(true);
  };

  const estimatedTime = Math.ceil(wordCount / 150); // 150 —Å–ª–æ–≤ –≤ –º–∏–Ω—É—Ç—É

  return (
    <>
      <div className="bg-white rounded-lg shadow-md p-4">
        {/* –ó–∞–≥–æ–ª–æ–≤–æ–∫ —Å –∫–Ω–æ–ø–∫–∞–º–∏ */}
        <div className="flex items-center justify-between mb-4">
          <div className="flex items-center space-x-2">
            <h2 className="text-lg font-semibold text-gray-800">{title}</h2>
            <span className="text-yellow-500">‚ú®</span>
          </div>
          
          {/* –°—Ç–∞—Ç–∏—Å—Ç–∏–∫–∞ –∏ –∫–Ω–æ–ø–∫–∏ */}
          <div className="flex items-center space-x-3">
            <div className="bg-slate-100 rounded-full px-4 py-2 text-sm text-slate-600">
              {wordCount} —Å–ª–æ–≤ ‚Ä¢ {charCount} —Å–∏–º–≤–æ–ª–æ–≤ ‚Ä¢ ~{estimatedTime} –º–∏–Ω
            </div>
            
            {/* –ö–Ω–æ–ø–∫–∞ —Å—É—Ñ–ª—ë—Ä–∞ */}
            {content.trim() && (
              <button
                onClick={handleOpenTeleprompter}
                className="bg-blue-500 hover:bg-blue-600 text-white px-4 py-2 rounded-lg text-sm font-medium flex items-center space-x-2 transition-colors"
                title="–ó–∞–ø—É—Å—Ç–∏—Ç—å —Å—É—Ñ–ª—ë—Ä –¥–ª—è —á—Ç–µ–Ω–∏—è —Å—Ü–µ–Ω–∞—Ä–∏—è"
              >
                <span>üé¨</span>
                <span>–°—É—Ñ–ª—ë—Ä</span>
              </button>
            )}
          </div>
        </div>

        {/* –†–µ–¥–∞–∫—Ç–æ—Ä —Ç–µ–∫—Å—Ç–∞ */}
        <div className="border border-slate-200 rounded-lg overflow-hidden">
          <textarea
            value={content}
            onChange={(e) => setContent(e.target.value)}
            className="w-full h-64 p-4 bg-slate-50 border-none outline-none resize-none text-gray-800 placeholder-gray-500"
            placeholder="–ù–∞—á–Ω–∏—Ç–µ –ø–∏—Å–∞—Ç—å –≤–∞—à —Å—Ü–µ–Ω–∞—Ä–∏–π –∑–¥–µ—Å—å...

–°–æ–≤–µ—Ç—ã:
‚Ä¢ –ü–∏—à–∏—Ç–µ –∫–æ—Ä–æ—Ç–∫–∏–º–∏ –ø—Ä–µ–¥–ª–æ–∂–µ–Ω–∏—è–º–∏ –¥–ª—è –ª—É—á—à–µ–≥–æ —á—Ç–µ–Ω–∏—è
‚Ä¢ –û—Ç–º–µ—á–∞–π—Ç–µ –º–µ—Å—Ç–∞ –¥–ª—è –ø–∞—É–∑ –∑–Ω–∞–∫–∞–º–∏ –ø—Ä–µ–ø–∏–Ω–∞–Ω–∏—è
‚Ä¢ –ò—Å–ø–æ–ª—å–∑—É–π—Ç–µ –ø—Ä–æ—Å—Ç—ã–µ —Å–ª–æ–≤–∞ –¥–ª—è –µ—Å—Ç–µ—Å—Ç–≤–µ–Ω–Ω–æ–π —Ä–µ—á–∏"
          />
          
          {/* –ü–æ–ª–æ—Å–∞ –ø—Ä–æ–∫—Ä—É—Ç–∫–∏ (–¥–µ–∫–æ—Ä–∞—Ç–∏–≤–Ω–∞—è) */}
          <div className="absolute inset-y-0 right-0 w-1 bg-slate-200 rounded-r-lg">
            <div 
              className="bg-blue-400 rounded-full transition-all duration-300"
              style={{ 
                height: content ? '30%' : '10%',
                marginTop: content ? '20%' : '0%'
              }}
            />
          </div>
        </div>

        {/* –ù–∏–∂–Ω—è—è –ø–∞–Ω–µ–ª—å */}
        <div className="flex items-center justify-between mt-4">
          <div className="text-sm text-gray-500">
            {content.trim() ? (
              <span className="text-green-600">‚úì –°—Ü–µ–Ω–∞—Ä–∏–π –≥–æ—Ç–æ–≤ –∫ –∏—Å–ø–æ–ª—å–∑–æ–≤–∞–Ω–∏—é</span>
            ) : (
              <span>–ù–∞—á–Ω–∏—Ç–µ –ø–∏—Å–∞—Ç—å —Å—Ü–µ–Ω–∞—Ä–∏–π</span>
            )}
          </div>
          
          <div className="flex space-x-2">
            {/* –ö–Ω–æ–ø–∫–∞ —Å–æ—Ö—Ä–∞–Ω–µ–Ω–∏—è */}
            <button
              onClick={handleSave}
              disabled={!content.trim()}
              className={`px-4 py-2 rounded-lg text-sm font-medium transition-colors ${
                content.trim()
                  ? 'bg-green-500 hover:bg-green-600 text-white'
                  : 'bg-gray-200 text-gray-400 cursor-not-allowed'
              }`}
            >
              üíæ –°–æ—Ö—Ä–∞–Ω–∏—Ç—å
            </button>
            
            {/* –î–æ–ø–æ–ª–Ω–∏—Ç–µ–ª—å–Ω–∞—è –∫–Ω–æ–ø–∫–∞ —Å—É—Ñ–ª—ë—Ä–∞ */}
            {content.trim() && (
              <button
                onClick={handleOpenTeleprompter}
                className="bg-blue-500 hover:bg-blue-600 text-white px-4 py-2 rounded-lg text-sm font-medium transition-colors"
              >
                üé¨ –û—Ç–∫—Ä—ã—Ç—å —Å—É—Ñ–ª—ë—Ä
              </button>
            )}
          </div>
        </div>

        {/* –ü–æ–¥—Å–∫–∞–∑–∫–∏ */}
        {!content.trim() && (
          <div className="mt-4 p-3 bg-blue-50 border border-blue-200 rounded-lg">
            <div className="text-sm text-blue-700">
              <strong>üí° –°–æ–≤–µ—Ç:</strong> –ü–æ—Å–ª–µ –Ω–∞–ø–∏—Å–∞–Ω–∏—è —Å—Ü–µ–Ω–∞—Ä–∏—è –≤—ã —Å–º–æ–∂–µ—Ç–µ –∏—Å–ø–æ–ª—å–∑–æ–≤–∞—Ç—å —Å—É—Ñ–ª—ë—Ä –¥–ª—è –∫–æ–º—Ñ–æ—Ä—Ç–Ω–æ–≥–æ —á—Ç–µ–Ω–∏—è –≤–æ –≤—Ä–µ–º—è –∑–∞–ø–∏—Å–∏ –≤–∏–¥–µ–æ
            </div>
          </div>
        )}
      </div>

      {/* –°—É—Ñ–ª—ë—Ä */}
      <SimpleTeleprompter
        isOpen={showTeleprompter}
        onClose={() => setShowTeleprompter(false)}
        scriptText={content}
        scriptTitle={title}
      />
    </>
  );
};

// –ö–æ–º–ø–æ–Ω–µ–Ω—Ç SimpleTeleprompter (—É–ø—Ä–æ—â–µ–Ω–Ω–∞—è –≤–µ—Ä—Å–∏—è)
const SimpleTeleprompter = ({ 
  isOpen, 
  onClose, 
  scriptText = "",
  scriptTitle = "–°—Ü–µ–Ω–∞—Ä–∏–π"
}) => {
  const [isPlaying, setIsPlaying] = useState(false);
  const [speed, setSpeed] = useState(2);
  const [fontSize, setFontSize] = useState(24);
  const [position, setPosition] = useState(0);
  
  const textRef = React.useRef(null);
  const animationRef = React.useRef(null);
  const lastTimeRef = React.useRef(Date.now());

  // –ê–≤—Ç–æ–ø—Ä–æ–∫—Ä—É—Ç–∫–∞
  React.useEffect(() => {
    if (isPlaying && textRef.current) {
      const animate = () => {
        const now = Date.now();
        const deltaTime = now - lastTimeRef.current;
        lastTimeRef.current = now;

        setPosition(prev => {
          const newPosition = prev + (speed * deltaTime / 1000);
          const maxScroll = textRef.current.scrollHeight - textRef.current.clientHeight;
          
          if (newPosition >= maxScroll) {
            setIsPlaying(false);
            return maxScroll;
          }
          
          return Math.max(0, newPosition);
        });

        animationRef.current = requestAnimationFrame(animate);
      };

      animationRef.current = requestAnimationFrame(animate);
    }

    return () => {
      if (animationRef.current) {
        cancelAnimationFrame(animationRef.current);
      }
    };
  }, [isPlaying, speed]);

  // –°–∏–Ω—Ö—Ä–æ–Ω–∏–∑–∞—Ü–∏—è –ø—Ä–æ–∫—Ä—É—Ç–∫–∏
  React.useEffect(() => {
    if (textRef.current) {
      textRef.current.scrollTop = position;
    }
  }, [position]);

  // –°–±—Ä–æ—Å –ø—Ä–∏ –æ—Ç–∫—Ä—ã—Ç–∏–∏
  React.useEffect(() => {
    if (isOpen) {
      setPosition(0);
      setIsPlaying(false);
      lastTimeRef.current = Date.now();
    }
  }, [isOpen]);

  if (!isOpen) return null;

  const handlePlayPause = () => {
    setIsPlaying(!isPlaying);
    lastTimeRef.current = Date.now();
  };

  const handleReset = () => {
    setIsPlaying(false);
    setPosition(0);
  };

  return (
    <div className="fixed inset-0 z-50 bg-black bg-opacity-50 flex items-center justify-center p-4">
      <div className="bg-white rounded-lg shadow-2xl max-w-2xl w-full max-h-[90vh] overflow-hidden">
        {/* –ó–∞–≥–æ–ª–æ–≤–æ–∫ */}
        <div className="flex items-center justify-between p-4 border-b border-gray-200">
          <h2 className="text-xl font-semibold text-gray-800">üé¨ –°—É—Ñ–ª—ë—Ä - {scriptTitle}</h2>
          <button
            onClick={onClose}
            className="text-gray-400 hover:text-gray-600 p-1"
          >
            <svg className="w-6 h-6" fill="none" stroke="currentColor" viewBox="0 0 24 24">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M6 18L18 6M6 6l12 12" />
            </svg>
          </button>
        </div>

        {/* –ü–∞–Ω–µ–ª—å —É–ø—Ä–∞–≤–ª–µ–Ω–∏—è */}
        <div className="p-4 bg-gray-50 border-b">
          <div className="flex flex-wrap items-center gap-4 justify-between">
            {/* –û—Å–Ω–æ–≤–Ω—ã–µ –∫–Ω–æ–ø–∫–∏ */}
            <div className="flex gap-2">
              <button
                onClick={handlePlayPause}
                className={`px-4 py-2 rounded text-sm font-medium ${
                  isPlaying 
                    ? 'bg-gray-500 hover:bg-gray-600 text-white' 
                    : 'bg-blue-500 hover:bg-blue-600 text-white'
                }`}
              >
                {isPlaying ? "‚è∏Ô∏è –ü–∞—É–∑–∞" : "‚ñ∂Ô∏è –°—Ç–∞—Ä—Ç"}
              </button>
              <button
                onClick={handleReset}
                className="px-4 py-2 bg-gray-300 hover:bg-gray-400 text-gray-700 rounded text-sm font-medium"
              >
                üîÑ –°–Ω–∞—á–∞–ª–∞
              </button>
            </div>

            {/* –ù–∞—Å—Ç—Ä–æ–π–∫–∏ —Å–∫–æ—Ä–æ—Å—Ç–∏ */}
            <div className="flex items-center gap-2">
              <span className="text-sm text-gray-600">–°–∫–æ—Ä–æ—Å—Ç—å:</span>
              {[0.5, 1, 2, 3, 4].map(speedValue => (
                <button
                  key={speedValue}
                  onClick={() => setSpeed(speedValue)}
                  className={`px-2 py-1 text-xs rounded ${
                    speed === speedValue 
                      ? 'bg-blue-500 text-white' 
                      : 'bg-gray-200 text-gray-700 hover:bg-gray-300'
                  }`}
                >
                  {speedValue}x
                </button>
              ))}
            </div>

