import { useState, useRef, useEffect } from "react";
import {
  Upload,
  Play,
  Pause,
  Scissors,
  Volume2,
  FileText,
  Wand2,
  Plus,
  X,
  Edit3,
  Sparkles,
  ChevronLeft,
  ChevronRight,
  Zap,
  Brain,
  Mic,
  Users,
  Eye,
  Heart,
  Share2,
  Clock,
  Star,
  Target,
  Video,
  Music,
  SkipBack,
  SkipForward,
  VolumeX,
  Download,
  Copy,
  Trash2,
  Split,
  RotateCw,
  Crop,
  Filter,
  Type,
  Image,
  Film
} from "lucide-react";

const VideoEditorApp = () => {
  // Существующие состояния
  const [videoFile, setVideoFile] = useState(null);
  const [videoUrl, setVideoUrl] = useState("");
  const [isPlaying, setIsPlaying] = useState(false);
  const [script, setScript] = useState("");
  const [topic, setTopic] = useState("");
  const [keyPoints, setKeyPoints] = useState([""]);
  const [duration, setDuration] = useState("~10 минут (1000 слов)");
  const [audioUrl, setAudioUrl] = useState("");
  const [currentStep, setCurrentStep] = useState(1);
  const [isLeftPanelCollapsed, setIsLeftPanelCollapsed] = useState(false);
  const [selectedText, setSelectedText] = useState("");
  const [aiPrompt, setAiPrompt] = useState("");
  const [showAiEditor, setShowAiEditor] = useState(false);
  const [isGeneratingText, setIsGeneratingText] = useState(false);
  const [isGeneratingScript, setIsGeneratingScript] = useState(false);
  const [isGeneratingKeyPoints, setIsGeneratingKeyPoints] = useState(false);
  const [audioMethod, setAudioMethod] = useState("ai");
  const [isRecording, setIsRecording] = useState(false);
  const [recordedAudio, setRecordedAudio] = useState(null);
  const [showAudioEditor, setShowAudioEditor] = useState(false);
  const [assessment, setAssessment] = useState({});
  const [contentType, setContentType] = useState("lifestyle");
  const [assessmentLoading, setAssessmentLoading] = useState(false);
  const [isExtendingScript, setIsExtendingScript] = useState(false);
  const [showTeleprompter, setShowTeleprompter] = useState(false);
  const [isAuthenticated, setIsAuthenticated] = useState(true);

  // Новые состояния для видеоредактора
  const [mediaLibrary, setMediaLibrary] = useState({
    videos: [],
    audios: [],
    images: []
  });
  const [timelineItems, setTimelineItems] = useState([]);
  const [currentTime, setCurrentTime] = useState(0);
  const [videoDuration, setVideoDuration] = useState(0);
  const [selectedTimelineItem, setSelectedTimelineItem] = useState(null);
  const [timelineZoom, setTimelineZoom] = useState(1);
  const [activeTab, setActiveTab] = useState('videos');
  const [playbackSpeed, setPlaybackSpeed] = useState(1);
  const [volume, setVolume] = useState(1);
  const [isMuted, setIsMuted] = useState(false);

  const videoRef = useRef(null);
  const fileInputRef = useRef(null);
  const audioInputRef = useRef(null);
  const imageInputRef = useRef(null);
  const timelineRef = useRef(null);

  // Обработчики файлов
  const handleVideoUpload = (event) => {
    const files = Array.from(event.target.files);
    files.forEach(file => {
      const url = URL.createObjectURL(file);
      const newVideo = {
        id: Date.now() + Math.random(),
        name: file.name,
        url: url,
        file: file,
        duration: 0, // Будет установлено при загрузке
        type: 'video'
      };
      
      setMediaLibrary(prev => ({
        ...prev,
        videos: [...prev.videos, newVideo]
      }));
      
      // Если это первое видео, установим его как основное
      if (!videoFile) {
        setVideoFile(file);
        setVideoUrl(url);
        setCurrentStep(Math.max(currentStep, 2));
      }
    });
  };

  const handleAudioUpload = (event) => {
    const files = Array.from(event.target.files);
    files.forEach(file => {
      const url = URL.createObjectURL(file);
      const newAudio = {
        id: Date.now() + Math.random(),
        name: file.name,
        url: url,
        file: file,
        duration: 0,
        type: 'audio'
      };
      
      setMediaLibrary(prev => ({
        ...prev,
        audios: [...prev.audios, newAudio]
      }));
    });
  };

  const handleImageUpload = (event) => {
    const files = Array.from(event.target.files);
    files.forEach(file => {
      const url = URL.createObjectURL(file);
      const newImage = {
        id: Date.now() + Math.random(),
        name: file.name,
        url: url,
        file: file,
        type: 'image'
      };
      
      setMediaLibrary(prev => ({
        ...prev,
        images: [...prev.images, newImage]
      }));
    });
  };

  // Добавление элемента на таймлайн
  const addToTimeline = (mediaItem) => {
    const newTimelineItem = {
      id: Date.now() + Math.random(),
      mediaId: mediaItem.id,
      type: mediaItem.type,
      name: mediaItem.name,
      url: mediaItem.url,
      startTime: timelineItems.length > 0 ? Math.max(...timelineItems.map(item => item.startTime + item.duration)) : 0,
      duration: mediaItem.duration || (mediaItem.type === 'image' ? 5 : 10), // 5 сек для изображений, 10 сек по умолчанию
      track: mediaItem.type === 'video' ? 0 : mediaItem.type === 'audio' ? 1 : 2,
      volume: 1,
      effects: []
    };
    
    setTimelineItems(prev => [...prev, newTimelineItem]);
  };

  // Управление воспроизведением
  const handlePlayPause = () => {
    if (videoRef.current) {
      if (isPlaying) {
        videoRef.current.pause();
      } else {
        videoRef.current.play();
      }
      setIsPlaying(!isPlaying);
    }
  };

  const handleTimeUpdate = () => {
    if (videoRef.current) {
      setCurrentTime(videoRef.current.currentTime);
    }
  };

  const handleLoadedMetadata = () => {
    if (videoRef.current) {
      setVideoDuration(videoRef.current.duration);
    }
  };

  const seekTo = (time) => {
    if (videoRef.current) {
      videoRef.current.currentTime = time;
      setCurrentTime(time);
    }
  };

  // Удаление элементов
  const removeFromLibrary = (itemId, type) => {
    setMediaLibrary(prev => ({
      ...prev,
      [type]: prev[type].filter(item => item.id !== itemId)
    }));
  };

  const removeFromTimeline = (itemId) => {
    setTimelineItems(prev => prev.filter(item => item.id !== itemId));
    setSelectedTimelineItem(null);
  };

  // Форматирование времени
  const formatTime = (seconds) => {
    const mins = Math.floor(seconds / 60);
    const secs = Math.floor(seconds % 60);
    return `${mins}:${secs.toString().padStart(2, '0')}`;
  };

  // Существующие функции (сократим для экономии места)
  const generateScript = () => {
    setScript("Пример сгенерированного скрипта для видео...");
    setCurrentStep(2);
  };

  const steps = [
    { num: 1, title: "Create Script", active: currentStep >= 1, completed: currentStep > 1, icon: FileText },
    { num: 2, title: "Edit Video", active: currentStep >= 2, completed: currentStep > 2, icon: Film },
    { num: 3, title: "AI Voiceover", active: currentStep >= 3, completed: currentStep > 3, icon: Mic },
    { num: 4, title: "Export", active: currentStep >= 4, completed: currentStep > 4, icon: Download },
  ];

  const contentTypes = [
    { id: "lifestyle", name: "Lifestyle", icon: Heart, color: "slate" },
    { id: "gaming", name: "Gaming", icon: Target, color: "blue" },
    { id: "tech", name: "Technology", icon: Zap, color: "indigo" },
    { id: "education", name: "Education", icon: Brain, color: "blue" },
    { id: "entertainment", name: "Entertainment", icon: Star, color: "slate" },
  ];

  return (
    <div className="min-h-screen bg-gradient-to-br from-slate-50 via-blue-50 to-indigo-50 text-gray-900 flex flex-col">
      {/* Progress Steps */}
      <div className="bg-gradient-to-r from-slate-100/80 to-blue-100/80 backdrop-blur-lg border-b border-slate-200/50 px-6 py-8">
        <div className="max-w-6xl mx-auto">
          <div className="text-center mb-8">
            <h2 className="text-3xl font-bold bg-gradient-to-r from-slate-700 to-blue-700 bg-clip-text text-transparent mb-2">
              Professional Video Editor
            </h2>
            <p className="text-gray-600 font-medium">
              Create amazing content with AI-powered tools
            </p>
          </div>
          <div className="flex items-center justify-between">
            {steps.map((step, index) => {
              const IconComponent = step.icon;
              return (
                <div key={step.num} className="flex items-center">
                  <div className="flex flex-col items-center text-center max-w-xs">
                    <div className={`w-10 h-10 rounded-2xl flex items-center justify-center text-sm font-semibold transition-all duration-500 shadow-lg ${
                      step.completed ? "bg-gradient-to-br from-green-500 to-emerald-600 text-white" :
                      step.active ? "bg-gradient-to-br from-slate-600 to-blue-600 text-white" :
                      "bg-white/80 text-gray-400"
                    }`}>
                      {step.completed ? <div className="text-xl">✓</div> : <IconComponent size={18} />}
                    </div>
                    <span className={`mt-4 text-lg font-semibold ${step.active ? "text-slate-700" : "text-gray-500"}`}>
                      {step.title}
                    </span>
                  </div>
                  {index < steps.length - 1 && (
                    <div className={`w-32 h-2 mx-8 rounded-full transition-all duration-500 ${
                      currentStep > step.num ? "bg-gradient-to-r from-green-500 to-emerald-600" : "bg-gray-200"
                    }`} />
                  )}
                </div>
              );
            })}
          </div>
        </div>
      </div>

      <div className="flex flex-1 relative">
        {/* Left Panel - Script Creation */}
        <div className={`w-[${isLeftPanelCollapsed ? "100px" : "900px"}] min-h-full bg-white/90 backdrop-blur-xl border-r border-slate-200/50 transition-all duration-300 ease-in-out flex flex-col shadow-xl`}>
          <div className="flex items-center justify-between p-6 border-b border-slate-200/50">
            {!isLeftPanelCollapsed && (
              <div>
                <h2 className="text-2xl font-bold bg-gradient-to-r from-slate-700 to-blue-700 bg-clip-text text-transparent">
                  Script Studio
                </h2>
                <p className="text-sm text-gray-600 font-medium">AI-powered content creation</p>
              </div>
            )}
            <button
              onClick={() => setIsLeftPanelCollapsed(!isLeftPanelCollapsed)}
              className="p-3 hover:bg-slate-100 rounded-xl transition-all duration-300"
            >
              {isLeftPanelCollapsed ? <ChevronRight size={20} /> : <ChevronLeft size={20} />}
            </button>
          </div>

          {!isLeftPanelCollapsed ? (
            <div className="p-6 overflow-y-auto flex-1 space-y-6">
              {/* Content Type Selection */}
              <div className="space-y-4">
                <label className="block text-gray-700 font-semibold text-lg">Content type</label>
                <div className="grid grid-cols-2 gap-3">
                  {contentTypes.map((type) => {
                    const IconComponent = type.icon;
                    return (
                      <button
                        key={type.id}
                        onClick={() => setContentType(type.id)}
                        className={`p-4 rounded-xl border-2 transition-all duration-300 ${
                          contentType === type.id
                            ? "border-blue-500 bg-blue-50 shadow-lg"
                            : "border-gray-200 bg-white hover:border-gray-300"
                        }`}
                      >
                        <IconComponent size={24} className={`mx-auto mb-2 ${contentType === type.id ? "text-blue-600" : "text-gray-400"}`} />
                        <span className={`text-sm font-bold ${contentType === type.id ? "text-blue-700" : "text-gray-600"}`}>
                          {type.name}
                        </span>
                      </button>
                    );
                  })}
                </div>
              </div>

              {/* Topic Input */}
              <div className="space-y-3">
                <label className="block text-gray-700 font-semibold">What is the video about?</label>
                <input
                  type="text"
                  value={topic}
                  onChange={(e) => setTopic(e.target.value)}
                  placeholder="Example: Review of S.T.A.L.K.E.R. 2 game"
                  className="w-full p-4 bg-white border-2 border-slate-200 rounded-xl focus:ring-2 focus:ring-blue-500 focus:border-blue-500"
                />
              </div>

              {/* Generate Script Button */}
              <button
                onClick={generateScript}
                className="w-full bg-gradient-to-r from-slate-600 to-blue-600 hover:from-slate-700 hover:to-blue-700 text-white font-semibold py-4 px-6 rounded-xl transition-all duration-300 flex items-center justify-center shadow-lg hover:shadow-xl"
              >
                <Wand2 className="mr-3" size={20} />
                Generate Script
              </button>

              {/* Script Display */}
              {script && (
                <div className="space-y-4">
                  <label className="block text-gray-700 font-semibold text-lg">Generated Script ✨</label>
                  <textarea
                    value={script}
                    onChange={(e) => setScript(e.target.value)}
                    rows={10}
                    className="w-full p-4 bg-white border-2 border-slate-200 rounded-xl focus:ring-2 focus:ring-blue-500 resize-none"
                  />
                </div>
              )}
            </div>
          ) : (
            <div className="p-4 space-y-4">
              <button className="p-4 bg-gradient-to-br from-slate-100 to-blue-100 rounded-xl hover:scale-110 transition-all duration-300">
                <FileText size={20} className="text-slate-600" />
              </button>
            </div>
          )}
        </div>

        {/* Right Panel - Video Editor */}
        <div className="flex-1 bg-gradient-to-br from-white/70 to-slate-50/70 backdrop-blur-xl flex flex-col">
          {/* Video Editor Header */}
          <div className="bg-white/80 border-b border-slate-200/50 p-4">
            <div className="flex items-center justify-between">
              <h3 className="text-xl font-bold text-gray-800">Video Editor</h3>
              <div className="flex items-center space-x-2">
                <button className="px-4 py-2 bg-blue-600 hover:bg-blue-700 text-white rounded-lg transition-colors">
                  <Download size={16} className="mr-2 inline" />
                  Export
                </button>
              </div>
            </div>
          </div>

          {/* Editor Layout */}
          <div className="flex flex-1">
            {/* Media Library Sidebar */}
            <div className="w-80 bg-white/90 border-r border-slate-200/50 flex flex-col">
              {/* Media Tabs */}
              <div className="border-b border-slate-200/50">
                <div className="flex">
                  {[
                    { id: 'videos', name: 'Videos', icon: Video },
                    { id: 'audios', name: 'Audio', icon: Music },
                    { id: 'images', name: 'Images', icon: Image }
                  ].map(tab => {
                    const IconComponent = tab.icon;
                    return (
                      <button
                        key={tab.id}
                        onClick={() => setActiveTab(tab.id)}
                        className={`flex-1 p-4 text-sm font-medium transition-colors ${
                          activeTab === tab.id
                            ? 'bg-blue-50 text-blue-600 border-b-2 border-blue-600'
                            : 'text-gray-600 hover:bg-gray-50'
                        }`}
                      >
                        <IconComponent size={16} className="mr-2 inline" />
                        {tab.name}
                      </button>
                    );
                  })}
                </div>
              </div>

              {/* Upload Area */}
              <div className="p-4 border-b border-slate-200/50">
                <div className="space-y-2">
                  <input
                    ref={fileInputRef}
                    type="file"
                    accept="video/*"
                    multiple
                    onChange={handleVideoUpload}
                    className="hidden"
                  />
                  <input
                    ref={audioInputRef}
                    type="file"
                    accept="audio/*"
                    multiple
                    onChange={handleAudioUpload}
                    className="hidden"
                  />
                  <input
                    ref={imageInputRef}
                    type="file"
                    accept="image/*"
                    multiple
                    onChange={handleImageUpload}
                    className="hidden"
                  />
                  
                  <button
                    onClick={() => {
                      if (activeTab === 'videos') fileInputRef.current?.click();
                      else if (activeTab === 'audios') audioInputRef.current?.click();
                      else imageInputRef.current?.click();
                    }}
                    className="w-full p-3 border-2 border-dashed border-gray-300 rounded-lg hover:border-blue-400 transition-colors text-center"
                  >
                    <Upload size={20} className="mx-auto mb-2 text-gray-400" />
                    <span className="text-sm text-gray-600">
                      Upload {activeTab === 'videos' ? 'Video' : activeTab === 'audios' ? 'Audio' : 'Images'}
                    </span>
                  </button>
                </div>
              </div>

              {/* Media List */}
              <div className="flex-1 overflow-y-auto p-4">
                <div className="space-y-2">
                  {mediaLibrary[activeTab].map(item => (
                    <div
                      key={item.id}
                      className="flex items-center p-3 bg-white rounded-lg border border-gray-200 hover:shadow-md transition-all cursor-pointer group"
                      onClick={() => addToTimeline(item)}
                    >
                      <div className="w-12 h-12 bg-gray-100 rounded-lg flex items-center justify-center mr-3">
                        {activeTab === 'videos' && <Video size={20} className="text-gray-600" />}
                        {activeTab === 'audios' && <Music size={20} className="text-gray-600" />}
                        {activeTab === 'images' && <Image size={20} className="text-gray-600" />}
                      </div>
                      <div className="flex-1 min-w-0">
                        <p className="text-sm font-medium text-gray-900 truncate">{item.name}</p>
                        <p className="text-xs text-gray-500">
                          {activeTab === 'images' ? 'Image' : formatTime(item.duration || 0)}
                        </p>
                      </div>
                      <button
                        onClick={(e) => {
                          e.stopPropagation();
                          removeFromLibrary(item.id, activeTab);
                        }}
                        className="opacity-0 group-hover:opacity-100 p-1 text-red-500 hover:bg-red-50 rounded transition-all"
                      >
                        <Trash2 size={16} />
                      </button>
                    </div>
                  ))}
                </div>
              </div>
            </div>

            {/* Main Editor Area */}
            <div className="flex-1 flex flex-col">
              {/* Video Preview */}
              <div className="flex-1 bg-black/90 relative">
                {videoUrl ? (
                  <video
                    ref={videoRef}
                    src={videoUrl}
                    className="w-full h-full object-contain"
                    onTimeUpdate={handleTimeUpdate}
                    onLoadedMetadata={handleLoadedMetadata}
                    onPlay={() => setIsPlaying(true)}
                    onPause={() => setIsPlaying(false)}
                  />
                ) : (
                  <div className="w-full h-full flex items-center justify-center text-white">
                    <div className="text-center">
                      <Film size={64} className="mx-auto mb-4 opacity-50" />
                      <p className="text-xl opacity-75">No video loaded</p>
                      <p className="text-sm opacity-50">Upload a video to start editing</p>
                    </div>
                  </div>
                )}

                {/* Video Controls Overlay */}
                <div className="absolute bottom-4 left-1/2 transform -translate-x-1/2 bg-black/50 backdrop-blur-sm rounded-lg p-3">
                  <div className="flex items-center space-x-4">
                    <button
                      onClick={() => seekTo(Math.max(0, currentTime - 10))}
                      className="text-white hover:text-blue-400 transition-colors"
                    >
                      <SkipBack size={20} />
                    </button>
                    <button
                      onClick={handlePlayPause}
                      className="w-12 h-12 bg-white/20 hover:bg-white/30 rounded-full flex items-center justify-center text-white transition-colors"
                    >
                      {isPlaying ? <Pause size={24} /> : <Play size={24} />}
                    </button>
                    <button
                      onClick={() => seekTo(Math.min(videoDuration, currentTime + 10))}
                      className="text-white hover:text-blue-400 transition-colors"
                    >
                      <SkipForward size={20} />
                    </button>
                    <div className="flex items-center space-x-2">
                      <button
                        onClick={() => setIsMuted(!isMuted)}
                        className="text-white hover:text-blue-400 transition-colors"
                      >
                        {isMuted ? <VolumeX size={20} /> : <Volume2 size={20} />}
                      </button>
                      <input
                        type="range"
                        min="0"
                        max="1"
                        step="0.1"
                        value={volume}
                        onChange={(e) => setVolume(parseFloat(e.target.value))}
                        className="w-20"
                      />
                    </div>
                    <span className="text-white text-sm">
                      {formatTime(currentTime)} / {formatTime(videoDuration)}
                    </span>
                  </div>
                </div>
              </div>

              {/* Timeline Area */}
              <div className="h-80 bg-gray-900 border-t border-gray-700">
                {/* Timeline Header */}
                <div className="bg-gray-800 p-3 border-b border-gray-700">
                  <div className="flex items-center justify-between">
                    <h4 className="text-white font-medium">Timeline</h4>
                    <div className="flex items-center space-x-4">
                      <div className="flex items-center space-x-2">
                        <span className="text-gray-300 text-sm">Zoom:</span>
                        <input
                          type="range"
                          min="0.5"
                          max="3"
                          step="0.1"
                          value={timelineZoom}
                          onChange={(e) => setTimelineZoom(parseFloat(e.target.value))}
                          className="w-20"
                        />
                      </div>
                      <div className="flex items-center space-x-2">
                        <button className="text-gray-300 hover:text-white p-1 rounded transition-colors">
                          <Split size={16} />
                        </button>
                        <button className="text-gray-300 hover:text-white p-1 rounded transition-colors">
                          <Copy size={16} />
                        </button>
                        <button className="text-gray-300 hover:text-white p-1 rounded transition-colors">
                          <Trash2 size={16} />
                        </button>
                      </div>
                    </div>
                  </div>
                </div>

                {/* Timeline Tracks */}
                <div className="flex-1 relative overflow-x-auto" ref={timelineRef}>
                  {/* Time Ruler */}
                  <div className="h-8 bg-gray-800 border-b border-gray-700 relative">
                    {Array.from({ length: Math.ceil(videoDuration / 5) + 1 }, (_, i) => (
                      <div
                        key={i}
                        className="absolute top-0 h-full border-l border-gray-600"
                        style={{ left: `${i * 100 * timelineZoom}px` }}
                      >
                        <span className="text-xs text-gray-300 ml-1 mt-1 inline-block">
                          {formatTime(i * 5)}
                        </span>
                      </div>
                    ))}
                    {/* Playhead */}
                    <div
                      className="absolute top-0 w-0.5 h-full bg-red-500 z-10"
                      style={{ left: `${(currentTime / videoDuration) * videoDuration * 20 * timelineZoom}px` }}
                    />
                  </div>

                  {/* Video Track */}
                  <div className="h-16 bg-gray-800 border-b border-gray-700 relative">
                    <div className="absolute left-2 top-2 text-gray-300 text-sm font-medium">Video</div>
                    {timelineItems.filter(item => item.track === 0).map(item => (
                      <div
                        key={item.id}
                        className={`absolute h-12 bg-blue-600 rounded border-2 cursor-pointer transition-all ${
                          selectedTimelineItem?.id === item.id ? 'border-white' : 'border-blue-500'
                        }`}
                        style={{
                          left: `${(item.startTime / videoDuration) * videoDuration * 20 * timelineZoom}px`,
                          width: `${(item.duration / videoDuration) * videoDuration * 20 * timelineZoom}px`,
                          top: '2px'
                        }}
                        onClick={() => setSelectedTimelineItem(item)}
                      >
                        <div className="p-1 text-white text-xs truncate">{item.name}</div>
                      </div>
                    ))}
                  </div>

                  {/* Audio Track */}
                  <div className="h-16 bg-gray-800 border-b border-gray-700 relative">
                    <div className="absolute left-2 top-2 text-gray-300 text-sm font-medium">Audio</div>
                    {timelineItems.filter(item => item.track === 1).map(item => (
                      <div
                        key={item.id}
                        className={`absolute h-12 bg-green-600 rounded border-2 cursor-pointer transition-all ${
                          selectedTimelineItem?.id === item.id ? 'border-white' : 'border-green-500'
                        }`}
                        style={{
                          left: `${(item.startTime / videoDuration) * videoDuration * 20 * timelineZoom}px`,
                          width: `${(item.duration / videoDuration) * videoDuration * 20 * timelineZoom}px`,
                          top: '2px'
                        }}
                        onClick={() => setSelectedTimelineItem(item)}
                      >
                        <div className="p-1 text-white text-xs truncate">{item.name}</div>
                      </div>
                    ))}
                  </div>

                  {/* Effects Track */}
                  <div className="h-16 bg-gray-800 border-b border-gray-700 relative">
                    <div className="absolute left-2 top-2 text-gray-300 text-sm font-medium">Effects</div>
                    {timelineItems.filter(item => item.track === 2).map(item => (
                      <div
                        key={item.id}
                        className={`absolute h-12 bg-purple-600 rounded border-2 cursor-pointer transition-all ${
                          selectedTimelineItem?.id === item.id ? 'border-white' : 'border-purple-500'
                        }`}
                        style={{
                          left: `${(item.startTime / videoDuration) * videoDuration * 20 * timelineZoom}px`,
                          width: `${(item.duration / videoDuration) * videoDuration * 20 * timelineZoom}px`,
                          top: '2px'
                        }}
                        onClick={() => setSelectedTimelineItem(item)}
                      >
                        <div className="p-1 text-white text-xs truncate">{item.name}</div>
                      </div>
                    ))}
                  </div>
                </div>
              </div>
            </div>

            {/* Properties Panel */}
            {selectedTimelineItem && (
              <div className="w-80 bg-white/90 border-l border-slate-200/50 p-4">
                <div className="space-y-4">
                  <div className="flex items-center justify-between">
                    <h4 className="font-semibold text-gray-800">Properties</h4>
                    <button
                      onClick={() => setSelectedTimelineItem(null)}
                      className="text-gray-500 hover:text-gray-700"
                    >
                      <X size={16} />
                    </button>
                  </div>
                  
                  <div className="space-y-3">
                    <div>
                      <label className="block text-sm font-medium text-gray-700 mb-1">Name</label>
                      <input
                        type="text"
                        value={selectedTimelineItem.name}
                        className="w-full p-2 border border-gray-300 rounded-lg text-sm"
                        readOnly
                      />
                    </div>
                    
                    <div>
                      <label className="block text-sm font-medium text-gray-700 mb-1">Duration</label>
                      <input
                        type="number"
                        value={selectedTimelineItem.duration}
                        onChange={(e) => {
                          const newDuration = parseFloat(e.target.value);
                          setTimelineItems(prev => prev.map(item => 
                            item.id === selectedTimelineItem.id 
                              ? { ...item, duration: newDuration }
                              : item
                          ));
                          setSelectedTimelineItem(prev => ({ ...prev, duration: newDuration }));
                        }}
                        className="w-full p-2 border border-gray-300 rounded-lg text-sm"
                        step="0.1"
                        min="0.1"
                      />
                    </div>
                    
                    {selectedTimelineItem.type === 'audio' && (
                      <div>
                        <label className="block text-sm font-medium text-gray-700 mb-1">Volume</label>
                        <input
                          type="range"
                          min="0"
                          max="2"
                          step="0.1"
                          value={selectedTimelineItem.volume}
                          onChange={(e) => {
                            const newVolume = parseFloat(e.target.value);
                            setTimelineItems(prev => prev.map(item => 
                              item.id === selectedTimelineItem.id 
                                ? { ...item, volume: newVolume }
                                : item
                            ));
                            setSelectedTimelineItem(prev => ({ ...prev, volume: newVolume }));
                          }}
                          className="w-full"
                        />
                        <div className="text-sm text-gray-500 mt-1">
                          {Math.round(selectedTimelineItem.volume * 100)}%
                        </div>
                      </div>
                    )}
                    
                    <div className="space-y-2">
                      <label className="block text-sm font-medium text-gray-700">Effects</label>
                      <div className="grid grid-cols-2 gap-2">
                        <button className="p-2 border border-gray-300 rounded-lg hover:bg-gray-50 text-sm">
                          <Filter size={16} className="mx-auto mb-1" />
                          Filter
                        </button>
                        <button className="p-2 border border-gray-300 rounded-lg hover:bg-gray-50 text-sm">
                          <RotateCw size={16} className="mx-auto mb-1" />
                          Rotate
                        </button>
                        <button className="p-2 border border-gray-300 rounded-lg hover:bg-gray-50 text-sm">
                          <Crop size={16} className="mx-auto mb-1" />
                          Crop
                        </button>
                        <button className="p-2 border border-gray-300 rounded-lg hover:bg-gray-50 text-sm">
                          <Type size={16} className="mx-auto mb-1" />
                          Text
                        </button>
                      </div>
                    </div>
                    
                    <button
                      onClick={() => removeFromTimeline(selectedTimelineItem.id)}
                      className="w-full p-2 bg-red-600 hover:bg-red-700 text-white rounded-lg text-sm transition-colors"
                    >
                      <Trash2 size={16} className="inline mr-2" />
                      Remove from Timeline
                    </button>
                  </div>
                </div>
              </div>
            )}
          </div>
        </div>
      </div>
    </div>
  );
};

export default VideoEditorApp;
