import { useRef, useState } from "react";
import {
  Upload,
  Scissors,
  Volume2,
  FileText,
  Wand2,
  Plus,
  X,
  Edit3,
  ChevronLeft,
  ChevronRight,
  Brain,
  Mic,
  Users,
  Eye,
  Clock,
  TrendingUp,
  BookOpen,
  Heart,
  Search,
  Lightbulb,
  Music,
  Target,
  AlertCircle,
  Award,
  Sparkles,
  Play,
  Settings,
  Zap,
} from "lucide-react";

const ContentStudio = () => {
  const [isLeftPanelCollapsed, setIsLeftPanelCollapsed] = useState(false);
  const [contentType, setContentType] = useState("tech");
  const [topic, setTopic] = useState("");
  const [keyPoints, setKeyPoints] = useState([""]);
  const [duration, setDuration] = useState("~5 minutes (500 words)");
  const [script, setScript] = useState("");
  const [isGeneratingKeyPoints, setIsGeneratingKeyPoints] = useState(false);
  const [isGeneratingScript, setIsGeneratingScript] = useState(false);
  const [isExtendingScript, setIsExtendingScript] = useState(false);
  const [isAuthenticated, setIsAuthenticated] = useState(true);
  const [audioMethod, setAudioMethod] = useState("ai");
  const [recordedAudio, setRecordedAudio] = useState(null);
  const [audioUrl, setAudioUrl] = useState("");
  const [assessment, setAssessment] = useState(null);
  const [assessmentLoading, setAssessmentLoading] = useState(false);
  const [selectedLanguage, setSelectedLanguage] = useState("english");
  const [activeTab, setActiveTab] = useState("create");
  
  const scriptRef = useRef(null);

  const contentTypes = [
    { id: "tech", name: "Technology", emoji: "ðŸ’»", color: "blue" },
    { id: "gaming", name: "Gaming", emoji: "ðŸŽ®", color: "purple" },
    { id: "education", name: "Education", emoji: "ðŸ“š", color: "green" },
    { id: "lifestyle", name: "Lifestyle", emoji: "âœ¨", color: "pink" },
    { id: "entertainment", name: "Entertainment", emoji: "ðŸŽ¬", color: "orange" },
  ];

  const languages = [
    { id: "english", name: "English", flag: "ðŸ‡ºðŸ‡¸" },
    { id: "russian", name: "Ð ÑƒÑÑÐºÐ¸Ð¹", flag: "ðŸ‡·ðŸ‡º" },
    { id: "spanish", name: "EspaÃ±ol", flag: "ðŸ‡ªðŸ‡¸" },
    { id: "french", name: "FranÃ§ais", flag: "ðŸ‡«ðŸ‡·" },
  ];

  const generateKeyPoints = () => {
    setIsGeneratingKeyPoints(true);
    setTimeout(() => {
      setKeyPoints([
        "Introduction to the topic",
        "Key features and benefits",
        "Practical examples",
        "Conclusion and recommendations"
      ]);
      setIsGeneratingKeyPoints(false);
    }, 2000);
  };

  const generateScript = () => {
    setIsGeneratingScript(true);
    setTimeout(() => {
      setScript(`Welcome to today's video! In this comprehensive review, we'll explore the latest innovations in ${topic || "technology"}. 

Let's start by examining the key features that make this topic particularly interesting. We'll cover the main aspects, provide practical insights, and share real-world examples.

Throughout this video, you'll discover actionable tips and strategies that you can implement immediately. Our analysis will help you understand the broader implications and potential benefits.

Finally, we'll wrap up with key takeaways and recommendations for your next steps. Don't forget to subscribe for more content like this!`);
      setIsGeneratingScript(false);
      setAssessment(JSON.stringify({
        total_score: 8.5,
        structure: 9,
        video_duration: 5,
        engagement: 8,
        readability: 9,
        emotional_tone: 7,
        clarity: 8,
        seo_optimization: 8,
        rhythm: 8,
        recommendations: [
          "Add more emotional hooks in the introduction",
          "Include specific examples for better engagement",
          "Consider adding a call-to-action in the middle"
        ]
      }));
    }, 3000);
  };

  const addKeyPoint = () => {
    if (keyPoints.length < 10) {
      setKeyPoints([...keyPoints, ""]);
    }
  };

  const updateKeyPoint = (index, value) => {
    const updated = [...keyPoints];
    updated[index] = value;
    setKeyPoints(updated);
  };

  const removeKeyPoint = (index) => {
    if (keyPoints.length > 1) {
      setKeyPoints(keyPoints.filter((_, i) => i !== index));
    }
  };

  const extendScript = () => {
    setIsExtendingScript(true);
    setTimeout(() => {
      setScript(prev => prev + "\n\nAdditional content section with more detailed explanations and examples...");
      setIsExtendingScript(false);
    }, 2000);
  };

  const CompactMetric = ({ icon: Icon, label, score, color = "blue" }) => {
    const colorClasses = {
      blue: "from-blue-500 to-blue-600",
      green: "from-green-500 to-green-600",
      purple: "from-purple-500 to-purple-600",
      orange: "from-orange-500 to-orange-600"
    };

    return (
      <div className="bg-white/80 backdrop-blur-sm rounded-lg p-3 border border-white/20 hover:border-white/40 transition-all group">
        <div className="flex items-center justify-between mb-2">
          <div className="flex items-center space-x-2">
            <div className={`p-1.5 rounded-lg bg-gradient-to-r ${colorClasses[color]} shadow-sm`}>
              <Icon size={14} className="text-white" />
            </div>
            <span className="text-xs font-semibold text-gray-800">{label}</span>
          </div>
          <div className={`px-2 py-1 rounded-full text-xs font-bold bg-gradient-to-r ${colorClasses[color]} text-white shadow-sm`}>
            {score}
          </div>
        </div>
        <div className="w-full bg-gray-200/60 rounded-full h-1.5">
          <div
            className={`h-1.5 rounded-full bg-gradient-to-r ${colorClasses[color]} transition-all duration-500`}
            style={{ width: `${Math.min(score * 10, 100)}%` }}
          />
        </div>
      </div>
    );
  };

  const ScoreCard = ({ assessment }) => {
    if (!assessment) return null;
    
    const data = JSON.parse(assessment);
    const score = data.total_score || 0;
    
    return (
      <div className="bg-gradient-to-br from-indigo-500 to-purple-600 rounded-xl p-4 text-white shadow-lg">
        <div className="flex items-center justify-between mb-3">
          <div className="flex items-center space-x-2">
            <Award size={18} className="text-white" />
            <span className="font-bold text-sm">Quality Score</span>
          </div>
          <div className="text-2xl font-bold">{score}</div>
        </div>
        
        <div className="grid grid-cols-3 gap-2 mb-3">
          <CompactMetric icon={Target} label="Structure" score={data.structure || 0} color="blue" />
          <CompactMetric icon={TrendingUp} label="Engage" score={data.engagement || 0} color="green" />
          <CompactMetric icon={Eye} label="Clarity" score={data.clarity || 0} color="purple" />
        </div>
        
        {data.recommendations && data.recommendations.length > 0 && (
          <div className="bg-white/10 rounded-lg p-3 backdrop-blur-sm">
            <div className="flex items-center space-x-2 mb-2">
              <Lightbulb size={14} className="text-yellow-300" />
              <span className="text-xs font-semibold">Top Tip</span>
            </div>
            <p className="text-xs text-white/90 leading-relaxed">
              {data.recommendations[0]}
            </p>
          </div>
        )}
      </div>
    );
  };

  const TabButton = ({ id, icon: Icon, label, active, onClick }) => (
    <button
      onClick={() => onClick(id)}
      className={`flex items-center space-x-2 px-4 py-2 rounded-lg transition-all font-medium text-sm ${
        active
          ? "bg-white text-indigo-600 shadow-md"
          : "text-white/80 hover:text-white hover:bg-white/10"
      }`}
    >
      <Icon size={16} />
      <span>{label}</span>
    </button>
  );

  return (
    <div className={`${isLeftPanelCollapsed ? "w-20" : "w-96"} h-screen bg-gradient-to-br from-slate-900 via-indigo-900 to-purple-900 transition-all duration-300 flex flex-col shadow-2xl`}>
      
      {/* Header */}
      <div className="p-4 border-b border-white/10">
        <div className="flex items-center justify-between">
          {!isLeftPanelCollapsed && (
            <div className="flex items-center space-x-3">
              <div className="w-10 h-10 bg-gradient-to-br from-yellow-400 to-orange-500 rounded-xl flex items-center justify-center shadow-lg">
                <Sparkles size={20} className="text-white" />
              </div>
              <div>
                <h1 className="text-xl font-bold text-white">ContentStudio</h1>
                <p className="text-xs text-white/60">AI-Powered Creation</p>
              </div>
            </div>
          )}
          <button
            onClick={() => setIsLeftPanelCollapsed(!isLeftPanelCollapsed)}
            className="p-2 hover:bg-white/10 rounded-lg transition-all"
          >
            {isLeftPanelCollapsed ? (
              <ChevronRight size={18} className="text-white/80" />
            ) : (
              <ChevronLeft size={18} className="text-white/80" />
            )}
          </button>
        </div>
      </div>

      {isLeftPanelCollapsed ? (
        /* Collapsed State */
        <div className="flex-1 p-3 space-y-3">
          {[
            { icon: FileText, color: "from-blue-500 to-blue-600" },
            { icon: Brain, color: "from-purple-500 to-purple-600" },
            { icon: Mic, color: "from-green-500 to-green-600" },
            { icon: Scissors, color: "from-orange-500 to-orange-600" },
          ].map((item, index) => (
            <button
              key={index}
              onClick={() => setIsLeftPanelCollapsed(false)}
              className={`w-full p-3 bg-gradient-to-r ${item.color} rounded-xl hover:scale-105 transition-all shadow-lg`}
            >
              <item.icon size={20} className="text-white mx-auto" />
            </button>
          ))}
        </div>
      ) : (
        /* Expanded State */
        <>
          {/* Navigation Tabs */}
          <div className="p-4 border-b border-white/10">
            <div className="flex space-x-2 bg-white/5 rounded-lg p-1">
              <TabButton
                id="create"
                icon={Wand2}
                label="Create"
                active={activeTab === "create"}
                onClick={setActiveTab}
              />
              <TabButton
                id="audio"
                icon={Volume2}
                label="Audio"
                active={activeTab === "audio"}
                onClick={setActiveTab}
              />
            </div>
          </div>

          {/* Content */}
          <div className="flex-1 overflow-y-auto p-4 space-y-4">
            
            {activeTab === "create" && (
              <>
                {/* Quick Setup */}
                <div className="bg-white/5 backdrop-blur-sm rounded-xl p-4 border border-white/10">
                  <h3 className="text-white font-semibold mb-3 flex items-center">
                    <Settings size={16} className="mr-2" />
                    Quick Setup
                  </h3>
                  
                  {/* Content Type Pills */}
                  <div className="grid grid-cols-2 gap-2 mb-4">
                    {contentTypes.slice(0, 4).map((type) => (
                      <button
                        key={type.id}
                        onClick={() => setContentType(type.id)}
                        className={`p-3 rounded-lg border transition-all text-left ${
                          contentType === type.id
                            ? "bg-white/20 border-white/30 text-white"
                            : "bg-white/5 border-white/10 text-white/70 hover:bg-white/10"
                        }`}
                      >
                        <div className="flex items-center space-x-2">
                          <span className="text-lg">{type.emoji}</span>
                          <span className="text-sm font-medium">{type.name}</span>
                        </div>
                      </button>
                    ))}
                  </div>

                  {/* Language */}
                  <div className="grid grid-cols-2 gap-2">
                    {languages.slice(0, 2).map((lang) => (
                      <button
                        key={lang.id}
                        onClick={() => setSelectedLanguage(lang.id)}
                        className={`p-2 rounded-lg border transition-all ${
                          selectedLanguage === lang.id
                            ? "bg-white/20 border-white/30 text-white"
                            : "bg-white/5 border-white/10 text-white/70 hover:bg-white/10"
                        }`}
                      >
                        <div className="flex items-center space-x-2 justify-center">
                          <span>{lang.flag}</span>
                          <span className="text-xs font-medium">{lang.name}</span>
                        </div>
                      </button>
                    ))}
                  </div>
                </div>

                {/* Topic Input */}
                <div className="bg-white/5 backdrop-blur-sm rounded-xl p-4 border border-white/10">
                  <label className="block text-white font-semibold mb-2 text-sm">
                    What's your video about?
                  </label>
                  <div className="relative">
                    <input
                      type="text"
                      value={topic}
                      onChange={(e) => setTopic(e.target.value)}
                      placeholder="S.T.A.L.K.E.R. 2 game review..."
                      className="w-full p-3 bg-white/10 border border-white/20 rounded-lg text-white placeholder-white/50 focus:outline-none focus:ring-2 focus:ring-indigo-400 focus:border-transparent transition-all"
                    />
                    <Brain size={16} className="absolute right-3 top-3 text-white/40" />
                  </div>
                </div>

                {/* Key Points */}
                <div className="bg-white/5 backdrop-blur-sm rounded-xl p-4 border border-white/10">
                  <div className="flex items-center justify-between mb-3">
                    <label className="text-white font-semibold text-sm">Key Points</label>
                    <div className="flex space-x-2">
                      <button
                        onClick={generateKeyPoints}
                        disabled={!topic.trim() || isGeneratingKeyPoints}
                        className="flex items-center px-3 py-1.5 text-xs bg-gradient-to-r from-indigo-500 to-purple-500 hover:from-indigo-600 hover:to-purple-600 text-white rounded-lg disabled:opacity-50 transition-all"
                      >
                        {isGeneratingKeyPoints ? (
                          <div className="animate-spin rounded-full h-3 w-3 border border-white border-t-transparent mr-1"></div>
                        ) : (
                          <Zap size={12} className="mr-1" />
                        )}
                        AI
                      </button>
                      <button
                        onClick={addKeyPoint}
                        disabled={keyPoints.length >= 6}
                        className="p-1.5 bg-white/10 hover:bg-white/20 text-white rounded-lg transition-all disabled:opacity-50"
                      >
                        <Plus size={12} />
                      </button>
                    </div>
                  </div>
                  
                  <div className="space-y-2 max-h-40 overflow-y-auto">
                    {keyPoints.map((point, index) => (
                      <div key={index} className="flex items-center space-x-2 group">
                        <div className="w-6 h-6 bg-gradient-to-r from-indigo-500 to-purple-500 rounded-full flex items-center justify-center text-white text-xs font-bold flex-shrink-0">
                          {index + 1}
                        </div>
                        <input
                          type="text"
                          value={point}
                          onChange={(e) => updateKeyPoint(index, e.target.value)}
                          placeholder="Enter point..."
                          className="flex-1 p-2 bg-white/10 border border-white/20 rounded-lg text-white placeholder-white/50 focus:outline-none focus:ring-1 focus:ring-indigo-400 text-sm transition-all"
                        />
                        {keyPoints.length > 1 && (
                          <button
                            onClick={() => removeKeyPoint(index)}
                            className="p-1 text-red-400 hover:text-red-300 opacity-0 group-hover:opacity-100 transition-all"
                          >
                            <X size={14} />
                          </button>
                        )}
                      </div>
                    ))}
                  </div>
                </div>

                {/* Duration */}
                <div className="bg-white/5 backdrop-blur-sm rounded-xl p-4 border border-white/10">
                  <label className="block text-white font-semibold mb-2 text-sm">Duration</label>
                  <select
                    value={duration}
                    onChange={(e) => setDuration(e.target.value)}
                    className="w-full p-3 bg-white/10 border border-white/20 rounded-lg text-white focus:outline-none focus:ring-2 focus:ring-indigo-400 transition-all"
                  >
                    <option>~5 minutes (500 words)</option>
                    <option>~10 minutes (1000 words)</option>
                    <option>~15 minutes (1500 words)</option>
                  </select>
                </div>

                {/* Generate Button */}
                <button
                  onClick={generateScript}
                  disabled={!topic || isGeneratingScript}
                  className="w-full bg-gradient-to-r from-indigo-500 to-purple-600 hover:from-indigo-600 hover:to-purple-700 disabled:opacity-50 text-white font-semibold py-4 rounded-xl transition-all shadow-lg hover:shadow-xl flex items-center justify-center"
                >
                  {isGeneratingScript ? (
                    <>
                      <div className="animate-spin rounded-full h-5 w-5 border border-white border-t-transparent mr-2"></div>
                      Generating...
                    </>
                  ) : (
                    <>
                      <Wand2 className="mr-2" size={18} />
                      Generate Script
                    </>
                  )}
                </button>

                {/* Script Display */}
                {script && (
                  <div className="bg-white/5 backdrop-blur-sm rounded-xl p-4 border border-white/10">
                    <div className="flex items-center justify-between mb-3">
                      <label className="text-white font-semibold text-sm flex items-center">
                        <FileText size={16} className="mr-2" />
                        Your Script
                      </label>
                      <button
                        onClick={extendScript}
                        disabled={isExtendingScript}
                        className="flex items-center px-3 py-1.5 text-xs bg-emerald-500 hover:bg-emerald-600 text-white rounded-lg transition-all"
                      >
                        {isExtendingScript ? (
                          <div className="animate-spin rounded-full h-3 w-3 border border-white border-t-transparent mr-1"></div>
                        ) : (
                          <Plus size={12} className="mr-1" />
                        )}
                        Extend
                      </button>
                    </div>
                    
                    <textarea
                      ref={scriptRef}
                      value={script}
                      onChange={(e) => setScript(e.target.value)}
                      rows={8}
                      className="w-full p-3 bg-white/10 border border-white/20 rounded-lg text-white placeholder-white/50 focus:outline-none focus:ring-2 focus:ring-indigo-400 resize-none text-sm leading-relaxed"
                    />
                    
                    <div className="flex items-center justify-between mt-3 text-xs text-white/60">
                      <div className="flex space-x-4">
                        <span className="flex items-center">
                          <Clock size={12} className="mr-1" />
                          ~{Math.ceil(script.length / 150)} min
                        </span>
                        <span className="flex items-center">
                          <FileText size={12} className="mr-1" />
                          {script.length} chars
                        </span>
                      </div>
                      <span className="text-emerald-400">âœ¨ Ready</span>
                    </div>
                  </div>
                )}

                {/* Score Card */}
                {assessment && (
                  <ScoreCard assessment={assessment} />
                )}
              </>
            )}

            {activeTab === "audio" && (
              <>
                {/* Audio Method Selection */}
                <div className="grid grid-cols-2 gap-3">
                  <button
                    onClick={() => setAudioMethod("ai")}
                    className={`p-4 rounded-xl border transition-all ${
                      audioMethod === "ai"
                        ? "bg-blue-500/20 border-blue-400 text-white shadow-lg"
                        : "bg-white/5 border-white/10 text-white/70 hover:bg-white/10"
                    }`}
                  >
                    <Brain size={24} className="mx-auto mb-2" />
                    <h4 className="font-semibold text-sm">AI Voice</h4>
                    <p className="text-xs opacity-80 mt-1">Instant generation</p>
                  </button>
                  
                  <button
                    onClick={() => setAudioMethod("record")}
                    className={`p-4 rounded-xl border transition-all ${
                      audioMethod === "record"
                        ? "bg-purple-500/20 border-purple-400 text-white shadow-lg"
                        : "bg-white/5 border-white/10 text-white/70 hover:bg-white/10"
                    }`}
                  >
                    <Mic size={24} className="mx-auto mb-2" />
                    <h4 className="font-semibold text-sm">Record</h4>
                    <p className="text-xs opacity-80 mt-1">Use teleprompter</p>
                  </button>
                </div>

                {audioMethod === "ai" && (
                  <div className="space-y-4">
                    <div className="bg-white/5 backdrop-blur-sm rounded-xl p-4 border border-white/10">
                      <label className="block text-white font-semibold mb-2 text-sm">Voice</label>
                      <select className="w-full p-3 bg-white/10 border border-white/20 rounded-lg text-white focus:outline-none focus:ring-2 focus:ring-blue-400">
                        <option>Alexei - Professional</option>
                        <option>Anya - Friendly</option>
                        <option>Dmitry - Energetic</option>
                      </select>
                    </div>

                    <div className="bg-white/5 backdrop-blur-sm rounded-xl p-4 border border-white/10">
                      <label className="block text-white font-semibold mb-3 text-sm">Speed</label>
                      <div className="flex items-center space-x-3">
                        <span className="text-xs text-white/60">Slow</span>
                        <input
                          type="range"
                          min="0.5"
                          max="2"
                          step="0.1"
                          defaultValue="1"
                          className="flex-1"
                        />
                        <span className="text-xs text-white/60">Fast</span>
                      </div>
                    </div>

                    <button
                      disabled={!script}
                      className="w-full bg-gradient-to-r from-blue-500 to-indigo-600 hover:from-blue-600 hover:to-indigo-700 disabled:opacity-50 text-white font-semibold py-4 rounded-xl transition-all shadow-lg flex items-center justify-center"
                    >
                      <Volume2 className="mr-2" size={18} />
                      Generate AI Voice
                    </button>
                  </div>
                )}

                {audioMethod === "record" && (
                  <div className="bg-white/5 backdrop-blur-sm rounded-xl p-6 border border-white/10 text-center">
                    <div className="w-16 h-16 bg-gradient-to-r from-purple-500 to-pink-500 rounded-2xl mx-auto mb-4 flex items-center justify-center">
                      <Eye size={28} className="text-white" />
                    </div>
                    <h4 className="font-bold text-white mb-2">Professional Recording</h4>
                    <p className="text-sm text-white/70 mb-4">Use teleprompter for best results</p>
                    <button className="bg-gradient-to-r from-purple-500 to-pink-600 hover:from-purple-600 hover:to-pink-700 text-white font-semibold py-3 px-6 rounded-xl transition-all shadow-lg flex items-center mx-auto">
                      <Play className="mr-2" size={16} />
                      Open Teleprompter
                    </button>
                  </div>
                )}

                {audioUrl && (
                  <div className="bg-gradient-to-r from-green-500/20 to-emerald-500/20 border border-green-400/30 rounded-xl p-4">
                    <div className="flex items-center space-x-3">
                      <div className="w-10 h-10 bg-gradient-to-r from-green-500 to-emerald-600 rounded-xl flex items-center justify-center">
                        <Volume2 size={18} className="text-white" />
                      </div>
                      <div className="flex-1">
                        <h4 className="font-bold text-white">Audio Ready!</h4>
                        <p className="text-sm text-green-300">Professional voiceover created</p>
                      </div>
                      <button className="bg-white/20 hover:bg-white/30 text-white px-3 py-2 rounded-lg transition-all">
                        <Edit3 size={14} />
                      </button>
                    </div>
                  </div>
                )}
              </>
            )}
          </div>
        </>
      )}
    </div>
  );
};

export default ContentStudio;
