import { useState, useEffect } from "react";
import { X, Download, Check, AlertCircle, FolderOpen, ChevronDown, ChevronUp } from "lucide-react";

export const VideoExportModal = ({
  isOpen,
  onClose,
  timelineItems = [],
  tracks,
  videoDuration,
}) => {
  const [exportSettings, setExportSettings] = useState({
    resolution: "1920x1080",
    fps: 30,
    bitrate: 5000,
    format: "mp4",
    quality: "high",
    saveDirectory: null,
    filename: `video_${Date.now()}`
  });

  const [isExporting, setIsExporting] = useState(false);
  const [exportProgress, setExportProgress] = useState(0);
  const [exportStatus, setExportStatus] = useState("idle");
  const [estimatedTime, setEstimatedTime] = useState(null);
  const [showAllElements, setShowAllElements] = useState(false);

  // Предустановки для быстрого выбора
  const presets = [
    { name: "YouTube 1080p", resolution: "1920x1080", fps: 30, quality: "high", format: "mp4" },
    { name: "Instagram", resolution: "1080x1080", fps: 30, quality: "medium", format: "mp4" },
    { name: "TikTok", resolution: "1080x1920", fps: 30, quality: "medium", format: "mp4" },
    { name: "Web", resolution: "1280x720", fps: 30, quality: "medium", format: "webm" },
  ];

  const qualityOptions = [
    { value: "low", label: "Низкое", bitrate: 1000 },
    { value: "medium", label: "Среднее", bitrate: 3000 },
    { value: "high", label: "Высокое", bitrate: 5000 },
    { value: "ultra", label: "Ультра", bitrate: 10000 },
  ];

  const formatOptions = [
    { value: "mp4", label: "MP4" },
    { value: "webm", label: "WebM" },
    { value: "mov", label: "MOV" },
  ];

  // Быстрый расчет времени экспорта
  useEffect(() => {
    if (videoDuration && timelineItems?.length) {
      const complexity = timelineItems.filter(item => item.trackType === "overlay").length;
      const baseTime = Math.max(2, videoDuration * 0.1);
      const complexityMultiplier = 1 + (complexity * 0.1);
      setEstimatedTime(Math.ceil(baseTime * complexityMultiplier));
    }
  }, [videoDuration, timelineItems]);

  // Синхронизация битрейта с качеством
  useEffect(() => {
    const quality = qualityOptions.find(q => q.value === exportSettings.quality);
    if (quality && quality.bitrate !== exportSettings.bitrate) {
      setExportSettings(prev => ({ ...prev, bitrate: quality.bitrate }));
    }
  }, [exportSettings.quality]);

  // Выбор папки для сохранения
  const handleSelectDirectory = async () => {
    try {
      if ('showDirectoryPicker' in window) {
        const directoryHandle = await window.showDirectoryPicker();
        setExportSettings(prev => ({ 
          ...prev, 
          saveDirectory: directoryHandle 
        }));
      } else {
        alert('Ваш браузер не поддерживает выбор папки. Файл будет сохранен в папку загрузок.');
      }
    } catch (error) {
      if (error.name !== 'AbortError') {
        console.error('Ошибка выбора папки:', error);
      }
    }
  };

  // Применение пресета
  const applyPreset = (preset) => {
    setExportSettings(prev => ({
      ...prev,
      ...preset
    }));
  };

  const getFileSize = () => {
    if (!videoDuration) return "~";
    const sizeInMB = (exportSettings.bitrate * videoDuration * 1000) / (8 * 1024 * 1024);
    return sizeInMB < 1024 ? `~${sizeInMB.toFixed(0)} МБ` : `~${(sizeInMB / 1024).toFixed(1)} ГБ`;
  };

  const handleExport = async () => {
    if (!timelineItems || timelineItems.length === 0) {
      alert('Нет элементов для экспорта!');
      return;
    }
    setIsExporting(true);
    setExportStatus("processing");
    setExportProgress(0);

    try {
      const videoExporter = new VideoExporter({
        timelineItems,
        tracks,
        videoDuration,
        settings: exportSettings,
        onProgress: setExportProgress
      });

      const exportedVideoBlob = await videoExporter.render();
      await downloadVideo(exportedVideoBlob);
      
      setExportStatus("success");
      setTimeout(() => {
        setExportStatus("idle");
        setExportProgress(0);
      }, 3000);
    } catch (error) {
      console.error("❌ ОШИБКА ЭКСПОРТА:", error);
      alert(`Ошибка экспорта: ${error.message}`);
      setExportStatus("error");
    } finally {
      setIsExporting(false);
    }
  };

  const downloadVideo = async (blob) => {
    const filename = `${exportSettings.filename}.${exportSettings.format}`;
    
    try {
      if (exportSettings.saveDirectory && 'showDirectoryPicker' in window) {
        const fileHandle = await exportSettings.saveDirectory.getFileHandle(filename, {
          create: true,
        });
        const writable = await fileHandle.createWritable();
        await writable.write(blob);
        await writable.close();
      } else {
        const url = URL.createObjectURL(blob);
        const a = document.createElement("a");
        a.href = url;
        a.download = filename;
        document.body.appendChild(a);
        a.click();
        document.body.removeChild(a);
        URL.revokeObjectURL(url);
      }
    } catch (error) {
      console.error('Ошибка сохранения:', error);
      throw error;
    }
  };

  const handleClose = () => {
    if (!isExporting) {
      setExportStatus("idle");
      setExportProgress(0);
      onClose();
    }
  };

  if (!isOpen) return null;

  // Логика отображения элементов
  const hasTimelineItems = timelineItems && timelineItems.length > 0;
  const elementsToShow = hasTimelineItems 
    ? (showAllElements ? timelineItems : timelineItems.slice(0, 3))
    : [];
  const hasMoreElements = hasTimelineItems && timelineItems.length > 3;

  return (
    <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50 p-4">
      <div className="bg-white rounded-2xl shadow-xl max-w-lg w-full max-h-[90vh] overflow-hidden">
        <div className="flex items-center justify-between p-4 border-b border-gray-200">
          <h2 className="text-lg font-semibold text-gray-800 flex items-center">
            <Download size={20} className="mr-2 text-blue-600" />
            Экспорт видео
          </h2>
          <button
            onClick={handleClose}
            className="p-1 hover:bg-gray-100 rounded-full transition-colors"
            disabled={isExporting}
          >
            <X size={18} />
          </button>
        </div>

        <div className="p-4 space-y-4 max-h-[calc(90vh-120px)] overflow-y-auto">
          {/* Статус экспорта */}
          {exportStatus !== "idle" && (
            <div className={`p-3 rounded-lg ${
              exportStatus === "processing" ? "bg-blue-50 border border-blue-200" :
              exportStatus === "success" ? "bg-green-50 border border-green-200" :
              "bg-red-50 border border-red-200"
            }`}>
              <div className="flex items-center">
                {exportStatus === "processing" && (
                  <>
                    <div className="animate-spin rounded-full h-4 w-4 border-b-2 border-blue-600 mr-2"></div>
                    <div className="flex-1">
                      <div className="flex justify-between text-sm text-blue-700 mb-1">
                        <span>Экспорт...</span>
                        <span>{exportProgress}%</span>
                      </div>
                      <div className="w-full bg-blue-200 rounded-full h-1.5">
                        <div
                          className="bg-blue-600 h-1.5 rounded-full transition-all duration-300"
                          style={{ width: `${exportProgress}%` }}
                        />
                      </div>
                    </div>
                  </>
                )}
                {exportStatus === "success" && (
                  <>
                    <Check size={16} className="text-green-600 mr-2" />
                    <span className="text-green-700 text-sm font-medium">Готово!</span>
                  </>
                )}
                {exportStatus === "error" && (
                  <>
                    <AlertCircle size={16} className="text-red-600 mr-2" />
                    <span className="text-red-700 text-sm font-medium">Ошибка экспорта</span>
                  </>
                )}
              </div>
            </div>
          )}

          {/* Быстрые пресеты */}
          <div>
            <label className="block text-sm font-medium text-gray-700 mb-2">
              Быстрые настройки
            </label>
            <div className="grid grid-cols-2 gap-2">
              {presets.map((preset) => (
                <button
                  key={preset.name}
                  onClick={() => applyPreset(preset)}
                  className="p-2 text-sm border border-gray-300 rounded-lg hover:bg-gray-50 transition-colors"
                  disabled={isExporting}
                >
                  {preset.name}
                </button>
              ))}
            </div>
          </div>

          {/* Настройки экспорта */}
          <div className="grid grid-cols-2 gap-3">
            <div>
              <label className="block text-xs font-medium text-gray-700 mb-1">
                Разрешение
              </label>
              <select
                value={exportSettings.resolution}
                onChange={(e) => setExportSettings(prev => ({ ...prev, resolution: e.target.value }))}
                className="w-full p-2 text-sm border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                disabled={isExporting}
              >
                <option value="3840x2160">4K</option>
                <option value="1920x1080">1080p</option>
                <option value="1280x720">720p</option>
                <option value="854x480">480p</option>
                <option value="1080x1920">9:16 (TikTok)</option>
                <option value="1080x1080">1:1 (Instagram)</option>
              </select>
            </div>

            <div>
              <label className="block text-xs font-medium text-gray-700 mb-1">
                FPS
              </label>
              <select
                value={exportSettings.fps}
                onChange={(e) => setExportSettings(prev => ({ ...prev, fps: parseInt(e.target.value) }))}
                className="w-full p-2 text-sm border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                disabled={isExporting}
              >
                <option value="24">24</option>
                <option value="30">30</option>
                <option value="60">60</option>
              </select>
            </div>

            <div>
              <label className="block text-xs font-medium text-gray-700 mb-1">
                Качество
              </label>
              <select
                value={exportSettings.quality}
                onChange={(e) => setExportSettings(prev => ({ ...prev, quality: e.target.value }))}
                className="w-full p-2 text-sm border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                disabled={isExporting}
              >
                {qualityOptions.map((quality) => (
                  <option key={quality.value} value={quality.value}>
                    {quality.label}
                  </option>
                ))}
              </select>
            </div>

            <div>
              <label className="block text-xs font-medium text-gray-700 mb-1">
                Формат
              </label>
              <select
                value={exportSettings.format}
                onChange={(e) => setExportSettings(prev => ({ ...prev, format: e.target.value }))}
                className="w-full p-2 text-sm border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                disabled={isExporting}
              >
                {formatOptions.map((format) => (
                  <option key={format.value} value={format.value}>
                    {format.label}
                  </option>
                ))}
              </select>
            </div>
          </div>

          {/* Имя файла и папка сохранения */}
          <div className="space-y-2">
            <div>
              <label className="block text-xs font-medium text-gray-700 mb-1">
                Имя файла
              </label>
              <input
                type="text"
                value={exportSettings.filename}
                onChange={(e) => setExportSettings(prev => ({ ...prev, filename: e.target.value }))}
                className="w-full p-2 text-sm border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                disabled={isExporting}
                placeholder="Имя файла"
              />
            </div>

            <div>
              <label className="block text-xs font-medium text-gray-700 mb-1">
                Папка для сохранения
              </label>
              <button
                onClick={handleSelectDirectory}
                className="w-full p-2 text-sm border border-gray-300 rounded-lg hover:bg-gray-50 transition-colors flex items-center justify-between"
                disabled={isExporting}
              >
                <span className="text-gray-600">
                  {exportSettings.saveDirectory ? exportSettings.saveDirectory.name : 'Выберите папку'}
                </span>
                <FolderOpen size={16} />
              </button>
            </div>
          </div>

          {/* Информация */}
          <div className="bg-gray-50 rounded-lg p-3">
            <div className="grid grid-cols-2 gap-2 text-xs">
              <div>
                <span className="text-gray-600">Длительность:</span>
                <span className="ml-1 font-medium">{Math.round(videoDuration)}с</span>
              </div>
              <div>
                <span className="text-gray-600">Размер:</span>
                <span className="ml-1 font-medium">{getFileSize()}</span>
              </div>
              <div>
                <span className="text-gray-600">Битрейт:</span>
                <span className="ml-1 font-medium">{exportSettings.bitrate} kbps</span>
              </div>
              <div>
                <span className="text-gray-600">Время:</span>
                <span className="ml-1 font-medium">~{estimatedTime}с</span>
              </div>
            </div>
          </div>

          {/* Элементы для экспорта */}
          <div className="bg-gray-50 rounded-lg p-3">
            <div className="flex justify-between items-center mb-2">
              <span className="text-xs font-medium text-gray-800">Элементы для экспорта</span>
              <span className="text-xs text-gray-600">{hasTimelineItems ? timelineItems.length : 0}</span>
            </div>
            {hasTimelineItems ? (
              <div className="space-y-1">
                {elementsToShow.map((item) => (
                  <div key={item.id} className="flex items-center justify-between text-xs">
                    <span className="text-gray-700 truncate">{item.name}</span>
                    <span className={`px-1 py-0.5 rounded text-xs ${
                      item.trackType === "main" ? "bg-blue-100 text-blue-700" :
                      item.trackType === "audio" ? "bg-green-100 text-green-700" :
                      "bg-purple-100 text-purple-700"
                    }`}>
                      {item.trackType}
                    </span>
                  </div>
                ))}
                {hasMoreElements && (
                  <button
                    onClick={() => setShowAllElements(!showAllElements)}
                    className="w-full text-xs text-blue-600 hover:text-blue-800 flex items-center justify-center py-1 transition-colors"
                  >
                    {showAllElements ? (
                      <>
                        Скрыть <ChevronUp size={12} className="ml-1" />
                      </>
                    ) : (
                      <>
                        +{timelineItems.length - 3} ещё <ChevronDown size={12} className="ml-1" />
                      </>
                    )}
                  </button>
                )}
              </div>
            ) : (
              <div className="text-xs text-gray-500 text-center py-2">
                Нет элементов для экспорта
              </div>
            )}
          </div>
        </div>

        {/* Кнопки */}
        <div className="flex justify-end space-x-3 p-4 border-t border-gray-200">
          <button
            onClick={handleClose}
            className="px-4 py-2 text-sm text-gray-600 hover:text-gray-800 transition-colors"
            disabled={isExporting}
          >
            Отмена
          </button>
          <button
            onClick={handleExport}
            disabled={isExporting || !hasTimelineItems}
            className={`px-4 py-2 text-sm rounded-lg font-medium transition-all flex items-center ${
              isExporting || !hasTimelineItems
                ? "bg-gray-300 text-gray-500 cursor-not-allowed"
                : "bg-blue-600 text-white hover:bg-blue-700"
            }`}
          >
            {isExporting ? (
              <>
                <div className="animate-spin rounded-full h-3 w-3 border-b-2 border-white mr-2"></div>
                Экспорт...
              </>
            ) : (
              <>
                <Download size={14} className="mr-2" />
                Экспортировать
              </>
            )}
          </button>
        </div>
      </div>
    </div>
  );
};

// Полностью переписанный VideoExporter класс
class VideoExporter {
  constructor({ timelineItems, tracks, videoDuration, settings, onProgress }) {
    this.timelineItems = timelineItems;
    this.tracks = tracks;
    this.videoDuration = videoDuration;
    this.settings = settings;
    this.onProgress = onProgress;

    // Создаем основной canvas для композиции
    this.canvas = document.createElement("canvas");
    this.ctx = this.canvas.getContext("2d", { alpha: false });

    const [width, height] = settings.resolution.split("x").map(Number);
    this.canvas.width = width;
    this.canvas.height = height;

    // Подготовленные элементы
    this.loadedMedia = new Map();
    this.mediaRecorder = null;
    this.chunks = [];
  }

  async render() {
    try {
      console.log('🎬 СТАРТ ЭКСПОРТА ВИДЕО');
      console.log('📊 Параметры:', {
        элементов: this.timelineItems.length,
        длительность: this.videoDuration,
        разрешение: this.settings.resolution,
        fps: this.settings.fps
      });

      // Подготавливаем все элементы
      await this.prepareAllElements();
      this.onProgress?.(30);

      // Настраиваем аудио ПЕРЕД созданием stream
      await this.setupAudio();
      this.onProgress?.(40);

      // Создаем видео поток с фиксированным FPS
      const videoStream = this.canvas.captureStream(this.settings.fps);
      console.log('🎥 Canvas stream создан с FPS:', this.settings.fps);

      // Комбинируем видео и аудио потоки
      const combinedStream = new MediaStream();
      
      // Добавляем видео трек
      videoStream.getVideoTracks().forEach(track => {
        combinedStream.addTrack(track);
      });

      // Добавляем аудио трек если есть
      if (this.audioDestination && this.audioDestination.stream) {
        this.audioDestination.stream.getAudioTracks().forEach(track => {
          combinedStream.addTrack(track);
          console.log('🔊 Добавлен аудио трек');
        });
      }

      // Настраиваем MediaRecorder
      const mimeType = this.getSupportedMimeType();
      const options = {
        mimeType: mimeType,
        videoBitsPerSecond: this.settings.bitrate * 1000,
      };

      // Добавляем аудио битрейт если есть аудио
      if (this.audioDestination) {
        options.audioBitsPerSecond = 128000;
      }

      console.log('⚙️ Опции MediaRecorder:', options);

      this.mediaRecorder = new MediaRecorder(combinedStream, options);
      this.chunks = [];

      this.mediaRecorder.ondataavailable = (event) => {
        if (event.data.size > 0) {
          this.chunks.push(event.data);
          console.log(`📦 Получен чанк: ${(event.data.size / 1024).toFixed(1)} KB`);
        }
      };

      return new Promise((resolve, reject) => {
        this.mediaRecorder.onstop = () => {
          console.log(`✅ Запись остановлена. Чанков: ${this.chunks.length}`);
          
          if (this.chunks.length === 0) {
            reject(new Error('Не удалось записать видео - нет данных'));
            return;
          }

          const blob = new Blob(this.chunks, {
            type: this.getOutputMimeType(),
          });
          
          console.log(`🎉 Экспорт завершен: ${(blob.size / 1024 / 1024).toFixed(2)} МБ`);
          this.cleanup();
          resolve(blob);
        };

        this.mediaRecorder.onerror = (error) => {
          console.error('❌ Ошибка MediaRecorder:', error);
          this.cleanup();
          reject(new Error(`Ошибка записи: ${error.message}`));
        };

        // Начинаем запись
        this.mediaRecorder.start(100);
        console.log('🔴 Запись начата');

        // Запускаем рендеринг кадров с правильным таймингом
        this.renderFramesWithBetterTiming()
          .then(() => {
            console.log('🎯 Рендеринг завершен');
            // Добавляем задержку перед остановкой
            setTimeout(() => {
              if (this.mediaRecorder?.state === 'recording') {
                this.mediaRecorder.stop();
              }
            }, 1000);
          })
          .catch(reject);
      });
    } catch (error) {
      console.error('💥 Ошибка экспорта:', error);
      this.cleanup();
      throw error;
    }
  }

  async renderFramesWithBetterTiming() {
    const frameCount = Math.ceil(this.videoDuration * this.settings.fps);
    const frameDuration = 1000 / this.settings.fps;
    
    console.log(`🎞️ Новый рендеринг ${frameCount} кадров с улучшенным таймингом`);

    // Запускаем аудио источники сразу
    this.startAudioSources();

    for (let frame = 0; frame < frameCount; frame++) {
      const currentTime = frame / this.settings.fps;
      
      try {
        await this.renderFrame(currentTime);
        
        // Обновляем прогресс
        const progress = 40 + Math.round((frame / frameCount) * 50);
        this.onProgress?.(progress);
        
        // Логируем прогресс
        if (frame % 100 === 0) {
          console.log(`📽️ Кадр ${frame}/${frameCount} (${currentTime.toFixed(1)}s)`);
        }
        
        // Ждем точно нужное время между кадрами
        await new Promise(resolve => setTimeout(resolve, frameDuration));
        
      } catch (error) {
        console.error(`❌ Ошибка рендеринга кадра ${frame}:`, error);
      }
    }

    this.onProgress?.(90);
    console.log('🎉 Все кадры отрендерены с новым алгоритмом');
  }

  async prepareAllElements() {
    console.log('📦 Подготовка элементов...');
    
    for (const item of this.timelineItems) {
      try {
        console.log(`📄 Подготавливаем: ${item.name}, тип: ${item.type}, trackType: ${item.trackType}`);
        
        const elementType = this.determineElementType(item);
        console.log(`🎯 Определенный тип: ${elementType}`);
        
        if (elementType === 'video') {
          await this.prepareVideoElement(item);
        } else if (elementType === 'image') {
          await this.prepareImageElement(item);
        }
        // Аудио элементы пропускаем для простоты
      } catch (error) {
        console.warn(`⚠️ Ошибка подготовки ${item.name}:`, error);
      }
    }
    
    console.log('✅ Все элементы подготовлены');
    console.log('📋 Загруженные медиа:', Array.from(this.loadedMedia.keys()));
  }

  // Удаляем дублирующий метод renderAllFrames - используем только renderFramesInRealTime

  determineElementType(item) {
    // Сначала проверяем trackType
    if (item.trackType === 'main') return 'video';
    if (item.trackType === 'overlay') return 'image';
    if (item.trackType === 'audio') return 'audio';
    
    // Потом проверяем type
    if (item.type === 'videos' || item.type === 'video') return 'video';
    if (item.type === 'images' || item.type === 'image') return 'image';
    if (item.type === 'audios' || item.type === 'audio') return 'audio';

    // Наконец проверяем по расширению URL
    const url = item.url || item.src || '';
    if (/\.(mp4|webm|mov|avi)$/i.test(url)) return 'video';
    if (/\.(jpg|jpeg|png|gif|webp)$/i.test(url)) return 'image';
    if (/\.(mp3|wav|ogg|m4a)$/i.test(url)) return 'audio';

    console.warn(`⚠️ Неизвестный тип элемента:`, item);
    return 'unknown';
  }

  async prepareVideoElement(item) {
    if (this.loadedMedia.has(item.id)) return;

    const video = document.createElement("video");
    video.crossOrigin = "anonymous";
    video.muted = true;
    video.preload = "metadata";
    
    const videoUrl = item.url || item.src;
    if (!videoUrl) return;

    video.src = videoUrl;
    
    return new Promise((resolve) => {
      const timeout = setTimeout(() => {
        console.warn(`⏰ Таймаут загрузки видео: ${item.name}`);
        resolve();
      }, 10000);

      video.onloadedmetadata = () => {
        clearTimeout(timeout);
        console.log(`🎥 Видео загружено: ${item.name}`);
        this.loadedMedia.set(item.id, video);
        resolve();
      };
      
      video.onerror = () => {
        clearTimeout(timeout);
        console.error(`❌ Ошибка загрузки видео: ${item.name}`);
        resolve();
      };
    });
  }

  async prepareImageElement(item) {
    if (this.loadedMedia.has(item.id)) return;

    const img = new Image();
    img.crossOrigin = "anonymous";
    
    const imageUrl = item.url || item.src;
    if (!imageUrl) return;

    img.src = imageUrl;
    
    return new Promise((resolve) => {
      const timeout = setTimeout(() => {
        console.warn(`⏰ Таймаут загрузки изображения: ${item.name}`);
        resolve();
      }, 5000);

      img.onload = () => {
        clearTimeout(timeout);
        console.log(`🖼️ Изображение загружено: ${item.name}`);
        this.loadedMedia.set(item.id, img);
        resolve();
      };
      
      img.onerror = () => {
        clearTimeout(timeout);
        console.error(`❌ Ошибка загрузки изображения: ${item.name}`);
        resolve();
      };
    });
  }

  async renderAllFrames() {
    const frameCount = Math.ceil(this.videoDuration * this.settings.fps);
    const frameDuration = 1 / this.settings.fps;
    
    console.log(`🎞️ Рендеринг ${frameCount} кадров (${this.settings.fps} FPS)`);

    // Получаем видео трек для ручного захвата кадров
    const videoTrack = this.canvas.captureStream(0).getVideoTracks()[0];

    for (let frame = 0; frame < frameCount; frame++) {
      const currentTime = frame * frameDuration;
      
      try {
        await this.renderFrame(currentTime);
        
        // Принудительно захватываем кадр
        if (videoTrack && videoTrack.requestFrame) {
          videoTrack.requestFrame();
        }
        
        // Даем время на обработку кадра
        await new Promise(resolve => setTimeout(resolve, 1000 / this.settings.fps));
        
        // Обновляем прогресс
        const progress = 30 + Math.round((frame / frameCount) * 60);
        this.onProgress?.(progress);
        
        // Логируем каждые 100 кадров для отладки
        if (frame % 100 === 0) {
          console.log(`📽️ Кадр ${frame}/${frameCount} (${currentTime.toFixed(1)}s)`);
        }
      } catch (error) {
        console.error(`❌ Ошибка рендеринга кадра ${frame}:`, error);
      }
    }

    this.onProgress?.(90);
    console.log('🎉 Все кадры отрендерены');
  }

  async renderFrame(currentTime) {
    // Очищаем canvas черным фоном
    this.ctx.fillStyle = "#000000";
    this.ctx.fillRect(0, 0, this.canvas.width, this.canvas.height);

    // Получаем активные элементы для текущего времени
    const activeItems = this.getActiveItems(currentTime);

    // Логируем информацию для отладки (только первые 5 секунд)
    if (currentTime < 5) {
      console.log(`🎬 Время ${currentTime.toFixed(2)}s: найдено ${activeItems.length} активных элементов`);
      activeItems.forEach(item => {
        console.log(`  - ${item.name} (${item.trackType}, ${this.determineElementType(item)})`);
      });
    }

    // Всегда рендерим индикатор времени для проверки
    this.ctx.fillStyle = "rgba(255, 255, 255, 0.9)";
    this.ctx.font = "bold 24px Arial";
    this.ctx.textAlign = "left";
    this.ctx.fillText(`${currentTime.toFixed(1)}s`, 20, 50);
    
    // Показываем информацию о элементах
    this.ctx.font = "16px Arial";
    this.ctx.fillText(`Элементов: ${activeItems.length}`, 20, 80);

    if (activeItems.length === 0) {
      // Рендерим сообщение об отсутствии контента
      this.ctx.fillStyle = "rgba(255, 255, 255, 0.7)";
      this.ctx.font = "20px Arial";
      this.ctx.textAlign = "center";
      this.ctx.fillText("Нет активного контента", this.canvas.width / 2, this.canvas.height / 2);
      return;
    }

    // Сортируем элементы по слоям (основное видео сначала, потом оверлеи)
    const sortedItems = this.sortItemsByLayer(activeItems);

    // Рендерим каждый активный элемент
    for (const item of sortedItems) {
      const relativeTime = currentTime - (item.startTime || 0);
      
      if (currentTime < 5) {
        console.log(`  🎯 Рендерим ${item.name} в относительном времени ${relativeTime.toFixed(2)}s`);
      }
      
      await this.renderItem(item, relativeTime);
    }
  }

  getActiveItems(currentTime) {
    const activeItems = this.timelineItems.filter(item => {
      const startTime = item.startTime || 0;
      const duration = item.duration || this.videoDuration;
      const endTime = startTime + duration;
      
      const isActive = currentTime >= startTime && currentTime < endTime;
      
      // Логируем первые несколько кадров для отладки
      if (currentTime < 1 && isActive) {
        console.log(`🎬 Активный элемент в ${currentTime.toFixed(1)}s: ${item.name} (${startTime}-${endTime})`);
      }
      
      return isActive;
    });

    return activeItems;
  }

  sortItemsByLayer(items) {
    const layerOrder = {
      main: 1,
      video: 1, 
      overlay: 2,
      image: 2,
      audio: 0 // Аудио не рендерится визуально
    };

    return [...items].sort((a, b) => {
      const orderA = layerOrder[a.trackType] || layerOrder[a.type] || 1;
      const orderB = layerOrder[b.trackType] || layerOrder[b.type] || 1;
      return orderA - orderB;
    });
  }

  async renderItem(item, relativeTime) {
    const elementType = this.determineElementType(item);
    
    if (elementType === 'audio') {
      return; // Аудио не рендерится визуально
    }

    const mediaElement = this.loadedMedia.get(item.id);
    if (!mediaElement) {
      console.warn(`⚠️ Медиа элемент не найден для ${item.name} (ID: ${item.id})`);
      this.renderPlaceholder(`Элемент не загружен: ${item.name}`);
      return;
    }

    console.log(`🎨 Рендерим ${item.name}, тип: ${elementType}, размеры canvas: ${this.canvas.width}x${this.canvas.height}`);

    this.ctx.save();
    
    try {
      if (elementType === 'video') {
        await this.renderVideoItem(mediaElement, relativeTime);
      } else if (elementType === 'image') {
        this.renderImageItem(mediaElement);
      }
      
      console.log(`✅ Успешно отрендерен ${item.name}`);
    } catch (error) {
      console.error(`❌ Ошибка рендеринга элемента ${item.name}:`, error);
      this.renderPlaceholder(`Ошибка: ${item.name}`);
    } finally {
      this.ctx.restore();
    }
  }

  async renderVideoItem(video, relativeTime) {
    if (!video || video.readyState < 2) {
      console.warn(`⚠️ Видео не готово: readyState = ${video?.readyState}`);
      this.renderPlaceholder('Загрузка видео...');
      return;
    }

    try {
      // Устанавливаем правильное время видео
      const targetTime = Math.max(0, Math.min(relativeTime, video.duration));
      
      console.log(`🎥 Устанавливаем время видео: ${targetTime.toFixed(2)}s (было: ${video.currentTime.toFixed(2)}s)`);
      
      // Если время отличается, ждем события seeked
      if (Math.abs(video.currentTime - targetTime) > 0.05) {
        await new Promise((resolve) => {
          const onSeeked = () => {
            video.removeEventListener('seeked', onSeeked);
            resolve();
          };
          
          const onError = () => {
            video.removeEventListener('error', onError);
            console.warn('⚠️ Ошибка поиска в видео');
            resolve(); // Продолжаем даже при ошибке
          };
          
          video.addEventListener('seeked', onSeeked);
          video.addEventListener('error', onError);
          
          video.currentTime = targetTime;
          
          // Таймаут на случай если событие не произойдет
          setTimeout(() => {
            video.removeEventListener('seeked', onSeeked);
            video.removeEventListener('error', onError);
            resolve();
          }, 100);
        });
      }
      
      // Дополнительная проверка готовности
      if (video.readyState < 2) {
        console.warn(`⚠️ Видео все еще не готово после поиска`);
        this.renderPlaceholder('Видео не готово');
        return;
      }

      console.log(`🎬 Рендерим видео: ${video.videoWidth}x${video.videoHeight} -> ${this.canvas.width}x${this.canvas.height}`);
      this.drawScaledElement(video);
      
    } catch (error) {
      console.error(`❌ Ошибка рендеринга видео:`, error);
      this.renderPlaceholder('Ошибка видео');
    }
  }

  renderImageItem(img) {
    if (!img || !img.complete || img.naturalWidth === 0) {
      console.warn(`⚠️ Изображение не готово: complete=${img?.complete}, size=${img?.naturalWidth}x${img?.naturalHeight}`);
      this.renderPlaceholder('Изображение не загружено');
      return;
    }

    try {
      console.log(`🖼️ Рендерим изображение: ${img.naturalWidth}x${img.naturalHeight} -> ${this.canvas.width}x${this.canvas.height}`);
      this.drawScaledElement(img);
    } catch (error) {
      console.error(`❌ Ошибка рендеринга изображения:`, error);
      this.renderPlaceholder('Ошибка изображения');
    }
  }

  drawScaledElement(element) {
    const elementWidth = element.videoWidth || element.naturalWidth || element.width;
    const elementHeight = element.videoHeight || element.naturalHeight || element.height;
    
    console.log(`📐 Размеры элемента: ${elementWidth}x${elementHeight}`);
    
    if (!elementWidth || !elementHeight) {
      console.warn('⚠️ Элемент не имеет размеров');
      this.renderPlaceholder('Нет размеров');
      return;
    }
    
    const canvasRatio = this.canvas.width / this.canvas.height;
    const elementRatio = elementWidth / elementHeight;
    
    let drawWidth, drawHeight, drawX, drawY;

    // Масштабируем с сохранением пропорций (contain)
    if (elementRatio > canvasRatio) {
      drawWidth = this.canvas.width;
      drawHeight = this.canvas.width / elementRatio;
      drawX = 0;
      drawY = (this.canvas.height - drawHeight) / 2;
    } else {
      drawHeight = this.canvas.height;
      drawWidth = this.canvas.height * elementRatio;
      drawX = (this.canvas.width - drawWidth) / 2;
      drawY = 0;
    }

    console.log(`🎨 Рисуем: x=${drawX}, y=${drawY}, w=${drawWidth}, h=${drawHeight}`);

    try {
      // Сначала рисуем тестовый прямоугольник для проверки
      this.ctx.fillStyle = "rgba(255, 0, 0, 0.3)";
      this.ctx.fillRect(drawX, drawY, drawWidth, drawHeight);
      
      // Затем рисуем сам элемент
      this.ctx.drawImage(element, drawX, drawY, drawWidth, drawHeight);
      
      // Проверяем, что что-то действительно нарисовано
      const imageData = this.ctx.getImageData(drawX + drawWidth/2, drawY + drawHeight/2, 1, 1);
      const pixel = imageData.data;
      console.log(`🎯 Пиксель в центре: R=${pixel[0]}, G=${pixel[1]}, B=${pixel[2]}, A=${pixel[3]}`);
      
      console.log(`✅ Элемент успешно нарисован`);
    } catch (error) {
      console.error('💥 Ошибка отрисовки:', error);
      this.renderPlaceholder('Ошибка отрисовки');
    }
  }

  renderPlaceholder(text) {
    // Рендерим яркий фон для лучшей видимости
    this.ctx.fillStyle = "rgba(255, 100, 100, 0.8)";
    this.ctx.fillRect(
      this.canvas.width * 0.1, 
      this.canvas.height * 0.3, 
      this.canvas.width * 0.8, 
      this.canvas.height * 0.4
    );
    
    // Белый текст на красном фоне
    this.ctx.fillStyle = "#ffffff";
    this.ctx.font = `bold ${Math.min(32, this.canvas.width / 20)}px Arial`;
    this.ctx.textAlign = "center";
    this.ctx.textBaseline = "middle";
    this.ctx.fillText(
      text, 
      this.canvas.width / 2, 
      this.canvas.height / 2
    );
    
    console.log(`🚨 Placeholder отрендерен: "${text}"`);
  }

  renderEmptyFrame(currentTime) {
    // Рендерим черный экран с индикатором времени
    this.ctx.fillStyle = "#000000";
    this.ctx.fillRect(0, 0, this.canvas.width, this.canvas.height);
    
    this.ctx.fillStyle = "#666666";
    this.ctx.font = `${Math.min(24, this.canvas.width / 50)}px Arial`;
    this.ctx.textAlign = "center";
    this.ctx.textBaseline = "middle";
    this.ctx.fillText(
      `${currentTime.toFixed(1)}s`, 
      this.canvas.width / 2, 
      this.canvas.height / 2
    );
  }

  getSupportedMimeType() {
    const formats = [
      'video/webm;codecs=vp9',
      'video/webm;codecs=vp8', 
      'video/webm'
    ];
    
    for (const format of formats) {
      if (MediaRecorder.isTypeSupported(format)) {
        console.log(`✅ Поддерживаемый формат: ${format}`);
        return format;
      }
    }
    
    console.log('⚠️ Используем базовый формат video/webm');
    return 'video/webm';
  }

  getOutputMimeType() {
    // Возвращаем тип для финального файла
    switch (this.settings.format) {
      case 'mp4':
        return 'video/mp4';
      case 'mov':
        return 'video/quicktime';
      case 'webm':
      default:
        return 'video/webm';
    }
  }

  cleanup() {
    console.log('🧹 Очистка ресурсов...');
    
    // Освобождаем URL объекты
    this.loadedMedia.forEach(element => {
      if (element.src && element.src.startsWith('blob:')) {
        URL.revokeObjectURL(element.src);
      }
    });

    // Очищаем массивы
    this.loadedMedia.clear();
    this.chunks = [];
    this.mediaRecorder = null;
  }
}

export const useVideoExporter = () => {
  const [isExportModalOpen, setIsExportModalOpen] = useState(false);

  const openExportModal = () => setIsExportModalOpen(true);
  const closeExportModal = () => setIsExportModalOpen(false);

  return {
    isExportModalOpen,
    openExportModal,
    closeExportModal,
  };
};
