import { Link, useLocation } from "react-router-dom";
import { ROUTES } from "../constants/routes";
import { Zap, Brain } from "lucide-react";

const Header = () => {
  const location = useLocation();

  const navigation = [
    { name: "Главная", path: ROUTES.HOME },
    { name: "Условия использования", path: ROUTES.TERMS_OF_USE },
    { name: "Конфиденциальность", path: ROUTES.PRIVACY_POLICY },
  ];

  return (
    <div className="bg-white/90 backdrop-blur-xl border-b border-slate-400/50 px-6 py-4 shadow-xl ">
      <div className="flex items-center justify-between">
        <div className="flex items-center space-x-4">
          <div className="flex items-center space-x-3">
            <div className="w-12 h-12 bg-gradient-to-br from-slate-600 to-blue-600 rounded-2xl flex items-center justify-center shadow-xl shadow-slate-500/30">
              <Link to="#" className="hover:text-slate-400 transition-colors">
                <Brain className="text-white" size={24} />
              </Link>
            </div>
            <div>
              <div className="text-2xl font-black bg-gradient-to-r from-slate-700 to-blue-700 bg-clip-text text-transparent">
                Scriptify
              </div>
              <div className="text-sm font-bold text-slate-600">
                CONTENT CREATION STUDIO
              </div>
            </div>
          </div>
        </div>
        <button className="group relative bg-gradient-to-r from-slate-600 to-blue-600 hover:from-slate-700 hover:to-blue-700 text-white px-5 py-2 rounded-xl font-semibold text-lg transition-all duration-300 shadow-lg hover:shadow-xl hover:scale-105 overflow-hidden">
          <div className="absolute inset-0 bg-gradient-to-r from-blue-600 to-slate-600 opacity-0 group-hover:opacity-100 transition-opacity duration-300"></div>
          <span className="relative flex items-center space-x-3">
            <Zap size={15} />
            <span>Upgrade Pro</span>
          </span>
        </button>
      </div>
    </div>
  );
};

export default Header;
