// src/components/layouts/MainLayout.js
import { useLocation } from "react-router-dom";
import { useAuth } from "../../contexts/AuthContext";
import Header from "../common/Header";
import Footer from "../common/Footer";

const MainLayout = ({ children }) => {
  const location = useLocation();
  const { isAuthenticated, loading } = useAuth();

  const isLoginPage = location.pathname === "/login";
  const isAuthPage = location.pathname === "/auth";

  const showHeaderLandingHeader = isLandingPage || !isAuthenticated;

  const showFooter = isAuthenticated && !isAuthPage;

  const getBackgroundClass = () => {
    if (isLandingPage && !isAuthenticated) {
      return "";
    }
    return "bg-gradient-to-br from-slate-50 via-blue-50 to-indigo-50";
  };

  return (
    <div className={`min-h-screen flex flex-col ${getBackgroundClass()}`}>
      {showHeaderLandingHeader && <Header />}

      <main className={`flex-1 ${showHeaderLandingHeader ? "" : ""}`}>{children}</main>

      {showHeaderLandingHeader && <Footer />}
    </div>
  );
};

export default MainLayout;
