import { useState, useRef, useEffect } from "react";
import { X, Download, Check, AlertCircle, Settings, Play, Film } from "lucide-react";

// ПОЛНЫЙ ИСПРАВЛЕННЫЙ FFmpeg ЭКСПОРТЕР
const FFmpegVideoExporter = ({
  isOpen,
  onClose,
  timelineItems = [],
  videoDuration = 60,
  tracks,
}) => {
  const [exportSettings, setExportSettings] = useState({
    resolution: "1920x1080",
    fps: 30,
    bitrate: 5000,
    format: "mp4",
    quality: "high",
    filename: `video_${Date.now()}`
  });

  const [ffmpeg, setFfmpeg] = useState(null);
  const [isLoading, setIsLoading] = useState(false);
  const [isExporting, setIsExporting] = useState(false);
  const [exportProgress, setExportProgress] = useState(0);
  const [exportStage, setExportStage] = useState("idle");
  const [previewFrame, setPreviewFrame] = useState(null);
  const [error, setError] = useState(null);
  
  const canvasRef = useRef(null);
  const previewCanvasRef = useRef(null);

  // ИСПРАВЛЕННАЯ инициализация FFmpeg
  useEffect(() => {
    const initFFmpeg = async () => {
      if (ffmpeg || !isOpen) return;

      setIsLoading(true);
      setExportStage("loading");
      setError(null);
      
      try {
        console.log('🔄 Начинаем инициализацию FFmpeg...');
        
        // Проверяем наличие FFmpeg
        let FFmpegClass, fetchFile;
        
        try {
          const ffmpegModule = await import('@ffmpeg/ffmpeg');
          const utilModule = await import('@ffmpeg/util');
          
          FFmpegClass = ffmpegModule.FFmpeg;
          fetchFile = utilModule.fetchFile;
          
          console.log('✅ FFmpeg модули загружены');
        } catch (importError) {
          throw new Error('FFmpeg модули не найдены. Выполните: npm install @ffmpeg/ffmpeg @ffmpeg/util');
        }

        const ffmpegInstance = new FFmpegClass();
        
        // Способ 1: Попробуем стандартную загрузку
        try {
          console.log('🔄 Метод 1: Стандартная загрузка...');
          await ffmpegInstance.load({
            coreURL: 'https://unpkg.com/@ffmpeg/core@0.12.6/dist/umd/ffmpeg-core.js',
            wasmURL: 'https://unpkg.com/@ffmpeg/core@0.12.6/dist/umd/ffmpeg-core.wasm',
          });
          console.log('✅ Метод 1 успешен');
        } catch (error1) {
          console.log('⚠️ Метод 1 не удался, пробуем метод 2...');
          
          // Способ 2: Альтернативный CDN
          try {
            console.log('🔄 Метод 2: Альтернативный CDN...');
            await ffmpegInstance.load({
              coreURL: 'https://cdn.jsdelivr.net/npm/@ffmpeg/core@0.12.6/dist/umd/ffmpeg-core.js',
              wasmURL: 'https://cdn.jsdelivr.net/npm/@ffmpeg/core@0.12.6/dist/umd/ffmpeg-core.wasm',
            });
            console.log('✅ Метод 2 успешен');
          } catch (error2) {
            console.log('⚠️ Метод 2 не удался, пробуем метод 3...');
            
            // Способ 3: Автозагрузка
            try {
              console.log('🔄 Метод 3: Автозагрузка...');
              await ffmpegInstance.load();
              console.log('✅ Метод 3 успешен (автозагрузка)');
            } catch (error3) {
              throw new Error(`Все методы загрузки не удались. Последняя ошибка: ${error3.message}`);
            }
          }
        }

        // Настраиваем события
        ffmpegInstance.on('progress', ({ progress }) => {
          if (isExporting) {
            const adjustedProgress = 70 + Math.round(progress * 25);
            setExportProgress(adjustedProgress);
          }
        });

        ffmpegInstance.on('log', ({ message }) => {
          console.log('FFmpeg:', message);
        });

        // Сохраняем fetchFile глобально
        window.fetchFile = fetchFile;
        
        setFfmpeg(ffmpegInstance);
        setExportStage("ready");
        console.log('🎉 FFmpeg полностью готов к использованию');
        
      } catch (error) {
        console.error('❌ Ошибка инициализации FFmpeg:', error);
        setError(error.message);
        setExportStage("error");
      } finally {
        setIsLoading(false);
      }
    };

    if (isOpen) {
      initFFmpeg();
    }
  }, [isOpen]);

  // Создание превью кадра
  useEffect(() => {
    if (isOpen && timelineItems.length > 0 && !isLoading && exportStage === "ready") {
      generatePreviewFrame();
    }
  }, [isOpen, timelineItems, exportSettings.resolution, isLoading, exportStage]);

  const generatePreviewFrame = async () => {
    try {
      const canvas = previewCanvasRef.current;
      if (!canvas) return;

      console.log('🖼️ Создаем превью кадр...');
      
      const ctx = canvas.getContext('2d');
      const [width, height] = exportSettings.resolution.split('x').map(Number);
      canvas.width = width;
      canvas.height = height;

      await renderFrameAtTime(ctx, 1, width, height);
      
      canvas.toBlob((blob) => {
        if (blob) {
          const url = URL.createObjectURL(blob);
          setPreviewFrame(url);
          console.log('✅ Превью создано');
        }
      });
    } catch (error) {
      console.warn('⚠️ Ошибка создания превью:', error);
    }
  };

  const renderFrameAtTime = async (ctx, currentTime, width, height) => {
    ctx.fillStyle = '#000000';
    ctx.fillRect(0, 0, width, height);

    const activeItems = timelineItems.filter(item => {
      const startTime = item.startTime || 0;
      const duration = item.duration || videoDuration;
      return currentTime >= startTime && currentTime < startTime + duration;
    });

    if (activeItems.length === 0) {
      ctx.fillStyle = '#333333';
      ctx.fillRect(0, 0, width, height);
      ctx.fillStyle = '#ffffff';
      ctx.font = `${Math.min(48, width / 20)}px Arial`;
      ctx.textAlign = 'center';
      ctx.textBaseline = 'middle';
      ctx.fillText('Нет контента', width / 2, height / 2);
      return;
    }

    const sortedItems = activeItems.sort((a, b) => {
      const layerOrder = { main: 1, overlay: 2, audio: 0 };
      return (layerOrder[a.trackType] || 1) - (layerOrder[b.trackType] || 1);
    });

    for (const item of sortedItems) {
      if (item.trackType === 'audio') continue;

      try {
        const element = await loadMediaElement(item);
        if (element) {
          const relativeTime = currentTime - (item.startTime || 0);
          await drawElementOnCanvas(ctx, element, item, relativeTime, width, height);
        }
      } catch (error) {
        console.warn(`⚠️ Ошибка загрузки ${item.name}:`, error);
        renderPlaceholder(ctx, `Ошибка: ${item.name}`, width, height);
      }
    }
  };

  const loadMediaElement = async (item) => {
    return new Promise((resolve, reject) => {
      const timeout = setTimeout(() => {
        reject(new Error('Timeout loading media'));
      }, 15000);

      if (item.type === 'videos' || item.trackType === 'main') {
        const video = document.createElement('video');
        video.crossOrigin = 'anonymous';
        video.muted = true;
        video.preload = 'metadata';
        
        video.onloadedmetadata = () => {
          clearTimeout(timeout);
          console.log(`✅ Видео загружено: ${item.name}`);
          resolve(video);
        };
        
        video.onerror = (e) => {
          clearTimeout(timeout);
          console.error(`❌ Ошибка загрузки видео ${item.name}:`, e);
          reject(new Error(`Ошибка загрузки видео: ${item.name}`));
        };
        
        video.src = item.url;
        
      } else if (item.type === 'images' || item.trackType === 'overlay') {
        const img = new Image();
        img.crossOrigin = 'anonymous';
        
        img.onload = () => {
          clearTimeout(timeout);
          console.log(`✅ Изображение загружено: ${item.name}`);
          resolve(img);
        };
        
        img.onerror = (e) => {
          clearTimeout(timeout);
          console.error(`❌ Ошибка загрузки изображения ${item.name}:`, e);
          reject(new Error(`Ошибка загрузки изображения: ${item.name}`));
        };
        
        img.src = item.url;
        
      } else {
        clearTimeout(timeout);
        resolve(null);
      }
    });
  };

  const renderPlaceholder = (ctx, text, width, height) => {
    ctx.fillStyle = 'rgba(255, 100, 100, 0.8)';
    ctx.fillRect(width * 0.1, height * 0.4, width * 0.8, height * 0.2);
    ctx.fillStyle = '#ffffff';
    ctx.font = `${Math.min(24, width / 30)}px Arial`;
    ctx.textAlign = 'center';
    ctx.textBaseline = 'middle';
    ctx.fillText(text, width / 2, height / 2);
  };

  const drawElementOnCanvas = async (ctx, element, item, relativeTime, canvasWidth, canvasHeight) => {
    try {
      if (element.tagName === 'VIDEO') {
        const targetTime = Math.max(0, Math.min(relativeTime, element.duration || 0));
        if (Math.abs(element.currentTime - targetTime) > 0.2) {
          element.currentTime = targetTime;
          await new Promise(resolve => setTimeout(resolve, 200));
        }
      }

      const elementWidth = element.videoWidth || element.naturalWidth || element.width;
      const elementHeight = element.videoHeight || element.naturalHeight || element.height;

      if (!elementWidth || !elementHeight) {
        console.warn(`⚠️ Элемент ${item.name} не имеет размеров`);
        return;
      }

      let drawWidth, drawHeight, drawX, drawY;

      if (item.trackType === 'main') {
        const canvasRatio = canvasWidth / canvasHeight;
        const elementRatio = elementWidth / elementHeight;

        if (elementRatio > canvasRatio) {
          drawWidth = canvasWidth;
          drawHeight = canvasWidth / elementRatio;
          drawX = 0;
          drawY = (canvasHeight - drawHeight) / 2;
        } else {
          drawHeight = canvasHeight;
          drawWidth = canvasHeight * elementRatio;
          drawX = (canvasWidth - drawWidth) / 2;
          drawY = 0;
        }
      } else {
        const overlayMaxSize = Math.min(canvasWidth, canvasHeight) * 0.25;
        const elementRatio = elementWidth / elementHeight;
        
        if (elementRatio > 1) {
          drawWidth = overlayMaxSize;
          drawHeight = overlayMaxSize / elementRatio;
        } else {
          drawHeight = overlayMaxSize;
          drawWidth = overlayMaxSize * elementRatio;
        }
        
        drawX = canvasWidth - drawWidth - 50;
        drawY = 50;
      }

      const opacity = item.opacity || 1;
      ctx.globalAlpha = opacity;
      ctx.drawImage(element, drawX, drawY, drawWidth, drawHeight);
      ctx.globalAlpha = 1;

      console.log(`✅ Отрисован ${item.name}: ${drawWidth}x${drawHeight} at (${drawX}, ${drawY})`);
      
    } catch (error) {
      console.error(`❌ Ошибка отрисовки ${item.name}:`, error);
      renderPlaceholder(ctx, `Ошибка: ${item.name}`, canvasWidth, canvasHeight);
    }
  };

  const startExport = async () => {
    if (!ffmpeg || isExporting || timelineItems.length === 0) return;

    setIsExporting(true);
    setExportProgress(0);
    setExportStage("preparing");
    setError(null);

    try {
      console.log('🎬 Начинаем экспорт видео');

      setExportStage("testing_ffmpeg");
      await testFFmpeg();
      setExportProgress(10);

      setExportStage("loading_media");
      const mediaElements = await prepareMediaElements();
      setExportProgress(20);

      setExportStage("rendering_frames");
      const frames = await renderAllFrames(mediaElements);
      setExportProgress(70);

      setExportStage("encoding");
      const videoBlob = await encodeWithFFmpeg(frames, []);
      setExportProgress(95);

      setExportStage("downloading");
      await downloadVideo(videoBlob);
      setExportProgress(100);

      setExportStage("completed");
      setTimeout(() => {
        setExportStage("ready");
        setExportProgress(0);
      }, 3000);

    } catch (error) {
      console.error('❌ Ошибка экспорта:', error);
      setError(error.message);
      setExportStage("error");
    } finally {
      setIsExporting(false);
    }
  };

  const testFFmpeg = async () => {
    try {
      await ffmpeg.exec(['-version']);
      console.log('✅ FFmpeg работает корректно');
    } catch (error) {
      throw new Error(`FFmpeg не отвечает: ${error.message}`);
    }
  };

  const prepareMediaElements = async () => {
    const elements = new Map();
    const videoElements = timelineItems.filter(item => item.trackType !== 'audio');
    
    console.log(`📦 Подготавливаем ${videoElements.length} медиа элементов...`);
    
    for (const item of videoElements) {
      try {
        console.log(`⏳ Загружаем: ${item.name}`);
        const element = await loadMediaElement(item);
        if (element) {
          elements.set(item.id, element);
          console.log(`✅ Загружен: ${item.name}`);
        }
      } catch (error) {
        console.warn(`⚠️ Не удалось загрузить ${item.name}:`, error);
      }
    }

    console.log(`📦 Подготовлено элементов: ${elements.size}/${videoElements.length}`);
    return elements;
  };

  const renderAllFrames = async (mediaElements) => {
    const canvas = canvasRef.current;
    if (!canvas) throw new Error('Canvas не найден');

    const ctx = canvas.getContext('2d');
    const [width, height] = exportSettings.resolution.split('x').map(Number);
    canvas.width = width;
    canvas.height = height;

    const frameCount = Math.ceil(videoDuration * exportSettings.fps);
    const frames = [];

    console.log(`🎞️ Рендерим ${frameCount} кадров (${exportSettings.fps} FPS)`);

    for (let frame = 0; frame < frameCount; frame++) {
      const currentTime = frame / exportSettings.fps;
      
      try {
        await renderFrameAtTime(ctx, currentTime, width, height);

        const blob = await new Promise(resolve => {
          canvas.toBlob(resolve, 'image/png', 1.0);
        });
        
        if (blob) {
          frames.push(blob);
        } else {
          console.warn(`⚠️ Не удалось создать blob для кадра ${frame}`);
        }

        const progress = 20 + Math.round((frame / frameCount) * 50);
        setExportProgress(progress);

        if (frame % 30 === 0) {
          await new Promise(resolve => setTimeout(resolve, 10));
        }
        
      } catch (error) {
        console.error(`❌ Ошибка рендеринга кадра ${frame}:`, error);
        const emptyBlob = await new Promise(resolve => {
          ctx.fillStyle = '#000000';
          ctx.fillRect(0, 0, width, height);
          canvas.toBlob(resolve, 'image/png', 1.0);
        });
        frames.push(emptyBlob);
      }
    }

    console.log(`✅ Отрендерено ${frames.length} кадров`);
    return frames;
  };

  const encodeWithFFmpeg = async (frames, audioFiles) => {
    if (!window.fetchFile) {
      throw new Error('fetchFile не найден. Проблема с загрузкой @ffmpeg/util');
    }

    console.log(`📦 Кодируем ${frames.length} кадров`);
    
    try {
      for (let i = 0; i < frames.length; i++) {
        const filename = `frame_${i.toString().padStart(6, '0')}.png`;
        await ffmpeg.writeFile(filename, await window.fetchFile(frames[i]));
        
        if (i % 50 === 0) {
          const progress = 70 + Math.round((i / frames.length) * 15);
          setExportProgress(progress);
        }
      }

      console.log('📁 Все кадры записаны в FFmpeg файловую систему');

      const args = [
        '-framerate', exportSettings.fps.toString(),
        '-i', 'frame_%06d.png',
        '-t', videoDuration.toString(),
        '-c:v', 'libx264',
        '-pix_fmt', 'yuv420p',
        '-b:v', `${exportSettings.bitrate}k`,
        '-r', exportSettings.fps.toString(),
        '-movflags', '+faststart',
        'output.mp4'
      ];

      console.log('🔧 FFmpeg команда:', args.join(' '));

      await ffmpeg.exec(args);
      setExportProgress(90);

      const data = await ffmpeg.readFile('output.mp4');
      const videoBlob = new Blob([data.buffer], { type: 'video/mp4' });

      console.log(`🎉 Видео создано: ${(videoBlob.size / 1024 / 1024).toFixed(2)} МБ`);

      await cleanupTempFiles(frames.length);
      return videoBlob;

    } catch (error) {
      console.error('❌ Ошибка кодирования FFmpeg:', error);
      throw new Error(`Ошибка кодирования: ${error.message}`);
    }
  };

  const cleanupTempFiles = async (frameCount) => {
    console.log('🧹 Очистка временных файлов...');
    
    for (let i = 0; i < frameCount; i++) {
      try {
        await ffmpeg.deleteFile(`frame_${i.toString().padStart(6, '0')}.png`);
      } catch (e) {}
    }

    try {
      await ffmpeg.deleteFile('output.mp4');
    } catch (e) {}
    
    console.log('✅ Временные файлы очищены');
  };

  const downloadVideo = async (blob) => {
    const filename = `${exportSettings.filename}.${exportSettings.format}`;
    const url = URL.createObjectURL(blob);
    const a = document.createElement('a');
    a.href = url;
    a.download = filename;
    document.body.appendChild(a);
    a.click();
    document.body.removeChild(a);
    URL.revokeObjectURL(url);
    
    console.log(`💾 Файл сохранен: ${filename}`);
  };

  const getStageText = () => {
    switch (exportStage) {
      case "loading": return "Загрузка FFmpeg... (может занять до 30 секунд)";
      case "testing_ffmpeg": return "Тестирование FFmpeg...";
      case "preparing": return "Подготовка к экспорту...";
      case "loading_media": return "Загрузка медиа файлов...";
      case "rendering_frames": return `Рендеринг кадров... (${Math.ceil(videoDuration * exportSettings.fps)} кадров)`;
      case "encoding": return "Кодирование видео с FFmpeg...";
      case "downloading": return "Сохранение файла...";
      case "completed": return "Экспорт завершен успешно!";
      case "error": return "Произошла ошибка";
      case "ready": return `Готов к экспорту (${timelineItems.length} элементов)`;
      default: return "Ожидание...";
    }
  };

  const getStageIcon = () => {
    switch (exportStage) {
      case "completed": return <Check size={20} className="text-green-600" />;
      case "error": return <AlertCircle size={20} className="text-red-600" />;
      case "ready": return <Play size={20} className="text-blue-600" />;
      case "loading": return <div className="animate-spin rounded-full h-5 w-5 border-b-2 border-orange-600"></div>;
      default: return <div className="animate-spin rounded-full h-5 w-5 border-b-2 border-blue-600"></div>;
    }
  };

  const canExport = ffmpeg && !isExporting && !isLoading && timelineItems.length > 0 && exportStage === "ready";

  if (!isOpen) return null;

  return (
    <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50 p-4">
      <div className="bg-white rounded-2xl shadow-xl max-w-3xl w-full max-h-[95vh] overflow-hidden">
        {/* Header */}
        <div className="flex items-center justify-between p-6 border-b border-gray-200">
          <h2 className="text-xl font-semibold text-gray-800 flex items-center">
            <Film size={24} className="mr-3 text-blue-600" />
            FFmpeg Video Export (Исправленная версия)
          </h2>
          <button
            onClick={onClose}
            className="p-2 hover:bg-gray-100 rounded-full transition-colors"
            disabled={isExporting}
          >
            <X size={20} />
          </button>
        </div>

        <div className="p-6 space-y-6 max-h-[calc(95vh-140px)] overflow-y-auto">
          
          {/* Error Display */}
          {error && (
            <div className="bg-red-50 border border-red-200 rounded-xl p-4">
              <div className="flex items-start">
                <AlertCircle size={20} className="text-red-600 mr-3 mt-0.5" />
                <div className="flex-1">
                  <h4 className="font-medium text-red-800">Ошибка инициализации</h4>
                  <p className="text-sm text-red-600 mt-1">{error}</p>
                  <div className="mt-3 space-y-2 text-xs text-red-500">
                    <p><strong>Решения:</strong></p>
                    <p>1. Убедитесь что установлены пакеты: <code className="bg-red-100 px-1 rounded">npm install @ffmpeg/ffmpeg @ffmpeg/util</code></p>
                    <p>2. Перезапустите сервер разработки</p>
                    <p>3. Очистите кэш браузера (Ctrl+Shift+R)</p>
                    <p>4. Проверьте подключение к интернету</p>
                  </div>
                </div>
              </div>
            </div>
          )}

          {/* Status */}
          <div className={`rounded-xl p-4 border ${
            exportStage === "error" ? "bg-red-50 border-red-200" :
            exportStage === "completed" ? "bg-green-50 border-green-200" :
            exportStage === "loading" ? "bg-orange-50 border-orange-200" :
            "bg-blue-50 border-blue-200"
          }`}>
            <div className="flex items-center justify-between mb-3">
              <div className="flex items-center">
                {getStageIcon()}
                <span className={`ml-3 font-medium ${
                  exportStage === "error" ? "text-red-900" :
                  exportStage === "completed" ? "text-green-900" :
                  exportStage === "loading" ? "text-orange-900" :
                  "text-blue-900"
                }`}>
                  {getStageText()}
                </span>
              </div>
              {(isExporting || isLoading) && (
                <span className={`text-sm font-medium ${
                  exportStage === "loading" ? "text-orange-700" : "text-blue-700"
                }`}>
                  {exportProgress}%
                </span>
              )}
            </div>
            {(isExporting || isLoading) && (
              <div className={`w-full rounded-full h-2 ${
                exportStage === "loading" ? "bg-orange-200" : "bg-blue-200"
              }`}>
                <div
                  className={`h-2 rounded-full transition-all duration-300 ${
                    exportStage === "loading" ? "bg-orange-600" : "bg-blue-600"
                  }`}
                  style={{ width: `${exportProgress}%` }}
                />
              </div>
            )}
          </div>

          {/* Preview */}
          {exportStage === "ready" && (
            <div className="bg-gray-50 rounded-xl p-4">
              <h3 className="text-sm font-medium text-gray-700 mb-3">Превью экспорта</h3>
              <div className="flex gap-4">
                <div className="flex-1">
                  <div className="bg-black rounded-lg aspect-video flex items-center justify-center overflow-hidden">
                    {previewFrame ? (
                      <img 
                        src={previewFrame} 
                        alt="Preview" 
                        className="max-w-full max-h-full object-contain"
                      />
                    ) : (
                      <div className="text-white text-sm flex items-center">
                        <div className="animate-spin rounded-full h-6 w-6 border-b-2 border-white mr-3"></div>
                        Создание превью...
                      </div>
                    )}
                  </div>
                </div>
                <div className="w-48 space-y-2 text-sm">
                  <div className="flex justify-between">
                    <span className="text-gray-600">Разрешение:</span>
                    <span className="font-medium">{exportSettings.resolution}</span>
                  </div>
                  <div className="flex justify-between">
                    <span className="text-gray-600">FPS:</span>
                    <span className="font-medium">{exportSettings.fps}</span>
                  </div>
                  <div className="flex justify-between">
                    <span className="text-gray-600">Длительность:</span>
                    <span className="font-medium">{Math.round(videoDuration)}s</span>
                  </div>
                  <div className="flex justify-between">
                    <span className="text-gray-600">Кадров:</span>
                    <span className="font-medium">{Math.ceil(videoDuration * exportSettings.fps)}</span>
                  </div>
                  <div className="flex justify-between">
                    <span className="text-gray-600">Элементов:</span>
                    <span className="font-medium">{timelineItems.length}</span>
                  </div>
                </div>
              </div>
            </div>
          )}

          {/* Settings */}
          <div className="bg-gray-50 rounded-xl p-4">
            <h3 className="text-sm font-medium text-gray-700 mb-3 flex items-center">
              <Settings size={16} className="mr-2" />
              Настройки экспорта
            </h3>
            <div className="grid grid-cols-2 gap-4">
              <div>
                <label className="block text-xs font-medium text-gray-700 mb-1">
                  Разрешение
                </label>
                <select
                  value={exportSettings.resolution}
                  onChange={(e) => setExportSettings(prev => ({ ...prev, resolution: e.target.value }))}
                  className="w-full p-2 text-sm border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500"
                  disabled={isExporting || isLoading}
                >
                  <option value="1280x720">720p (рекомендуется)</option>
                  <option value="1920x1080">1080p</option>
                  <option value="854x480">480p (быстро)</option>
                </select>
              </div>

              <div>
                <label className="block text-xs font-medium text-gray-700 mb-1">
                  FPS
                </label>
                <select
                  value={exportSettings.fps}
                  onChange={(e) => setExportSettings(prev => ({ ...prev, fps: parseInt(e.target.value) }))}
                  className="w-full p-2 text-sm border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500"
                  disabled={isExporting || isLoading}
                >
                  <option value="24">24 FPS (рекомендуется)</option>
                  <option value="30">30 FPS</option>
                </select>
              </div>

              <div>
                <label className="block text-xs font-medium text-gray-700 mb-1">
                  Битрейт
                </label>
                <select
                  value={exportSettings.bitrate}
                  onChange={(e) => setExportSettings(prev => ({ ...prev, bitrate: parseInt(e.target.value) }))}
                  className="w-full p-2 text-sm border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500"
                  disabled={isExporting || isLoading}
                >
                  <option value="2000">Низкое (2 Mbps)</option>
                  <option value="3000">Среднее (3 Mbps)</option>
                  <option value="5000">Высокое (5 Mbps)</option>
                </select>
              </div>

              <div>
                <label className="block text-xs font-medium text-gray-700 mb-1">
                  Имя файла
                </label>
                <input
                  type="text"
                  value={exportSettings.filename}
                  onChange={(e) => setExportSettings(prev => ({ ...prev, filename: e.target.value }))}
                  className="w-full p-2 text-sm border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500"
                  disabled={isExporting || isLoading}
                  placeholder="video_export"
                />
              </div>
            </div>
          </div>

          {/* Timeline Info */}
          <div className="bg-gray-50 rounded-xl p-4">
            <h3 className="text-sm font-medium text-gray-700 mb-3">Элементы таймлайна</h3>
            {timelineItems.length > 0 ? (
              <div className="space-y-2 max-h-40 overflow-y-auto">
                {timelineItems.map((item) => (
                  <div key={item.id} className="flex items-center justify-between text-xs bg-white rounded-lg p-3">
                    <div className="flex-1 min-w-0">
                      <div className="font-medium text-gray-900 truncate">{item.name}</div>
                      <div className="text-gray-500 text-xs mt-1">
                        {Math.round(item.startTime || 0)}s - {Math.round((item.startTime || 0) + (item.duration || 0))}s
                        {item.duration && ` (${Math.round(item.duration)}s)`}
                      </div>
                    </div>
                    <div className="flex items-center space-x-2 ml-4">
                      <span className={`px-2 py-1 rounded text-xs font-medium ${
                        item.trackType === "main" ? "bg-blue-100 text-blue-700" :
                        item.trackType === "audio" ? "bg-green-100 text-green-700" :
                        "bg-purple-100 text-purple-700"
                      }`}>
                        {item.trackType}
                      </span>
                      <div className="w-2 h-2 rounded-full bg-green-500" title="Готов к экспорту"></div>
                    </div>
                  </div>
                ))}
              </div>
            ) : (
              <div className="text-center py-8 text-gray-500">
                <Film size={48} className="mx-auto mb-3 opacity-50" />
                <p className="text-sm">Нет элементов в таймлайне</p>
                <p className="text-xs text-gray-400 mt-1">Добавьте видео, изображения или аудио для экспорта</p>
              </div>
            )}
          </div>

          {/* Debug Info */}
          {exportStage === "error" && (
            <div className="bg-yellow-50 border border-yellow-200 rounded-xl p-4">
              <h4 className="font-medium text-yellow-800 mb-2">Информация для отладки</h4>
              <div className="text-xs text-yellow-700 space-y-1">
                <p>• Браузер: {navigator.userAgent.includes('Chrome') ? 'Chrome' : navigator.userAgent.includes('Firefox') ? 'Firefox' : 'Другой'}</p>
                <p>• Элементов в timeline: {timelineItems.length}</p>
                <p>• Текущий этап: {exportStage}</p>
              </div>
            </div>
          )}
        </div>

        {/* Footer */}
        <div className="flex justify-between items-center p-6 border-t border-gray-200 bg-gray-50">
          <div className="text-xs text-gray-500">
            {exportStage === "ready" && timelineItems.length > 0 ? (
              `Готов к экспорту • Ожидаемое время: ~${Math.round(videoDuration * 1.5)}мин`
            ) : exportStage === "loading" ? (
              'Загрузка FFmpeg может занять до 30 секунд при первом запуске'
            ) : exportStage === "error" ? (
              'Проверьте консоль браузера для подробной информации'
            ) : (
              'Добавьте элементы в таймлайн для экспорта'
            )}
          </div>
          <div className="flex space-x-3">
            <button
              onClick={onClose}
              className="px-6 py-2 text-sm text-gray-600 hover:text-gray-800 transition-colors"
              disabled={isExporting}
            >
              Закрыть
            </button>
            <button
              onClick={startExport}
              disabled={!canExport}
              className={`px-6 py-2 text-sm rounded-lg font-medium transition-all flex items-center ${
                !canExport
                  ? "bg-gray-300 text-gray-500 cursor-not-allowed"
                  : "bg-blue-600 text-white hover:bg-blue-700 shadow-lg hover:shadow-xl"
              }`}
            >
              {isExporting ? (
                <>
                  <div className="animate-spin rounded-full h-4 w-4 border-b-2 border-white mr-2"></div>
                  Экспорт... {exportProgress}%
                </>
              ) : isLoading ? (
                <>
                  <div className="animate-spin rounded-full h-4 w-4 border-b-2 border-white mr-2"></div>
                  Загрузка FFmpeg...
                </>
              ) : exportStage === "error" ? (
                <>
                  <AlertCircle size={16} className="mr-2" />
                  Попробовать снова
                </>
              ) : (
                <>
                  <Download size={16} className="mr-2" />
                  Экспортировать видео
                </>
              )}
            </button>
          </div>
        </div>

        {/* Hidden canvases */}
        <canvas ref={canvasRef} style={{ display: 'none' }} />
        <canvas ref={previewCanvasRef} style={{ display: 'none' }} />
      </div>
    </div>
  );
};

// Hook для использования
export const useFFmpegExporter = () => {
  const [isExportModalOpen, setIsExportModalOpen] = useState(false);

  const openExportModal = () => setIsExportModalOpen(true);
  const closeExportModal = () => setIsExportModalOpen(false);

  return {
    isExportModalOpen,
    openExportModal,
    closeExportModal,
  };
};

export default FFmpegVideoExporter;
