import { useState, useRef, useEffect } from "react";
import axios from "axios";
import ScriptEditorSkeleton from "../../components/ScriptEditorSkeleton";
import VideoEditor from "../../components/VideoEditor/VideoEditor";
import DetailedAnalytics from "../../components/DetailedAnalytics";
import { cleanScript } from "../../components/helpers/cleanScript";
import { TeleprompterModal } from "../../components/ui/TeleprompterModal";
import {
  showNotification,
} from "../../components/NotificationSystem";
import MediaStorage from "../utils/MediaStorage";

import {
  Upload,
  Scissors,
  Volume2,
  FileText,
  Wand2,
  Plus,
  X,
  Edit3,
  Sparkles,
  ChevronLeft,
  ChevronRight,
  Zap,
  Brain,
  Mic,
  Users,
  Eye,
  Heart,
  Share2,
  Clock,
  Star,
  Target,
  HardDrive,
  AlertTriangle,
} from "lucide-react";

const apiClient = axios.create({
  baseURL: "http://localhost:5000",
  timeout: 30000,
  headers: {
    "Content-Type": "application/json",
  },
});

apiClient.interceptors.request.use(
  (config) => {
    const token = localStorage.getItem("authToken");
    if (token) {
      config.headers.Authorization = `Bearer ${token}`;
    }
    return config;
  },
  (error) => {
    return Promise.reject(error);
  }
);

apiClient.interceptors.response.use(
  (response) => response,
  (error) => {
    if (error.response?.status === 401) {
      localStorage.removeItem("authToken");
      localStorage.removeItem("userData");
    }
    return Promise.reject(error);
  }
);

const VideoEditorApp = () => {
  const [videoFile, setVideoFile] = useState(null);
  const [videoUrl, setVideoUrl] = useState("");
  const [isPlaying, setIsPlaying] = useState(false);
  const [script, setScript] = useState("");
  const [topic, setTopic] = useState("");
  const [keyPoints, setKeyPoints] = useState([""]);
  const [duration, setDuration] = useState("~10 минут (1000 слов)");
  const [audioUrl, setAudioUrl] = useState("");
  const [currentStep, setCurrentStep] = useState(1);
  const [isLeftPanelCollapsed, setIsLeftPanelCollapsed] = useState(false);
  const [selectedText, setSelectedText] = useState("");
  const [aiPrompt, setAiPrompt] = useState("");
  const [showAiEditor, setShowAiEditor] = useState(false);
  const [isGeneratingText, setIsGeneratingText] = useState(false);
  const [isGeneratingScript, setIsGeneratingScript] = useState(false);
  const [isGeneratingKeyPoints, setIsGeneratingKeyPoints] = useState(false);
  const [audioMethod, setAudioMethod] = useState("ai");
  const [isRecording, setIsRecording] = useState(false);
  const [recordedAudio, setRecordedAudio] = useState(null);
  const [showAudioEditor, setShowAudioEditor] = useState(false);
  const [assessment, setAssessment] = useState({});
  const [contentType, setContentType] = useState("lifestyle");
  const [assessmentLoading, setAssessmentLoading] = useState(false);
  const [isExtendingScript, setIsExtendingScript] = useState(false);
  const [showTeleprompter, setShowTeleprompter] = useState(false);

  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [authError, setAuthError] = useState("");
  const [showAuthModal, setShowAuthModal] = useState(false);

  const [storageInfo, setStorageInfo] = useState(null);
  const [isStorageReady, setIsStorageReady] = useState(false);
  const [isUploadingToStorage, setIsUploadingToStorage] = useState(false);
  const [uploadProgress, setUploadProgress] = useState(0);

  const videoRef = useRef(null);
  const fileInputRef = useRef(null);
  const scriptRef = useRef(null);

  const hasInitializedRef = useRef(false);

  useEffect(() => {
    if (hasInitializedRef.current) return;

    hasInitializedRef.current = true;

    const initStorage = async () => {
      try {
        await MediaStorage.init();
        setIsStorageReady(true);

        const cleanedCount = await MediaStorage.cleanupExpiredFiles();
        const info = await MediaStorage.getStorageInfo();
        setStorageInfo(info);

        const storageMode = info.mode === "fallback" ? "Memory" : "IndexedDB";

        let message = `Your media files are stored locally for 7 days using ${storageMode}.`;
        if (cleanedCount > 0) {
          message = `Removed ${cleanedCount} expired files.\n` + message;
        }

        showNotification({
          type: "info",
          title: `Local Storage Ready (${storageMode})`,
          message,
          duration: 5000,
        });
      } catch (error) {
        console.error("Storage initialization failed:", error);
        showNotification({
          type: "error",
          title: "Storage Error",
          message: "Failed to initialize local storage",
          duration: 5000,
        });
      }
    };

    initStorage();
  }, []);

  useEffect(() => {
    const handleStorageUpdate = (event) => {
      setStorageInfo(event.detail);
    };

    window.addEventListener("storageUpdated", handleStorageUpdate);
    return () =>
      window.removeEventListener("storageUpdated", handleStorageUpdate);
  }, []);

  // Проверяем аутентификацию при загрузке компонента
  useEffect(() => {
    const token = localStorage.getItem("authToken");
    if (token) {
      setIsAuthenticated(true);
    } else {
      setShowAuthModal(true);
    }
  }, []);

  useEffect(() => {
    (async () => {
      if (script.length && isAuthenticated) {
        setAssessmentLoading(true);
        try {
          const res = await apiClient.post("/api/script/quality", { script });
          setAssessment(res.data.data.quality);
        } catch (error) {
          console.error("Ошибка при оценке скрипта:", error);
          setAssessment({
            total_score: 8,
            video_duration: Math.ceil(script.length / 150),
          });
        } finally {
          setAssessmentLoading(false);
        }
      }
    })();
  }, [script, isAuthenticated]);

  // Сохранение файла в локальное хранилище
  const saveFileToStorage = async (file, type) => {
    try {
      // Проверяем лимиты перед загрузкой
      const canUpload = await MediaStorage.canUpload(type);
      if (!canUpload) {
        const remaining = await MediaStorage.getRemainingSpace(type);
        throw new Error(
          `Storage limit reached! You can upload ${remaining} more ${type}.`
        );
      }

      const savedFile = await MediaStorage.saveFile(file, type);

      // Показываем уведомление об успехе
      showNotification({
        type: "success",
        title: "File Uploaded",
        message: `${savedFile.name} saved successfully to local storage`,
        duration: 3000,
      });

      return savedFile;
    } catch (error) {
      console.error("Save file error:", error);

      // Показываем уведомление об ошибке
      showNotification({
        type: "error",
        title: "Upload Failed",
        message: error.message,
        duration: 5000,
      });

      throw error;
    }
  };

  // Функция для демо-аутентификации (в реальном приложении замените на настоящую)
  const handleDemoLogin = async () => {
    try {
      // Для демо создаем временный токен или используем тестовый аккаунт
      const response = await apiClient.post("/api/auth/login", {
        email: "demo@example.com",
        password: "demo123",
      });

      if (response.data.success) {
        localStorage.setItem("authToken", response.data.data.token);
        localStorage.setItem(
          "userData",
          JSON.stringify(response.data.data.user)
        );
        setIsAuthenticated(true);
        setShowAuthModal(false);
        setAuthError("");
      }
    } catch (error) {
      // Если демо аккаунт не существует, создаем его
      try {
        await apiClient.post("/api/auth/register", {
          name: "Demo User",
          email: "demo@example.com",
          password: "demo123",
        });

        // Затем логинимся
        const loginResponse = await apiClient.post("/api/auth/login", {
          email: "demo@example.com",
          password: "demo123",
        });

        if (loginResponse.data.success) {
          localStorage.setItem("authToken", loginResponse.data.data.token);
          localStorage.setItem(
            "userData",
            JSON.stringify(loginResponse.data.data.user)
          );
          setIsAuthenticated(true);
          setShowAuthModal(false);
          setAuthError("");
        }
      } catch (registerError) {
        console.error("Ошибка при регистрации/входе:", registerError);
        setAuthError(
          "Не удалось войти в систему. Проверьте подключение к серверу."
        );
      }
    }
  };

  const addKeyPoint = () => {
    if (keyPoints.length < 10) {
      setKeyPoints([...keyPoints, ""]);
    }
  };

  const updateKeyPoint = (index, value) => {
    const newKeyPoints = [...keyPoints];
    newKeyPoints[index] = value;
    setKeyPoints(newKeyPoints);
  };

  const removeKeyPoint = (index) => {
    const newKeyPoints = keyPoints.filter((_, i) => i !== index);
    setKeyPoints(newKeyPoints.length > 0 ? newKeyPoints : [""]);
  };

  const generateKeyPoints = async () => {
    if (!topic.trim() || !isAuthenticated) return;

    setIsGeneratingKeyPoints(true);

    try {
      const response = await apiClient.post("/api/script/key-points", {
        topic,
        contentType,
      });

      const { keyPoints } = response.data.data;
      const newKeyPoints = keyPoints.points || keyPoints;

      if (Array.isArray(newKeyPoints) && newKeyPoints.length > 0) {
        setKeyPoints(newKeyPoints);
      } else {
        throw new Error("Неверный формат ответа от сервера");
      }
    } catch (error) {
      console.error("Ошибка при генерации ключевых пунктов:", error);

      setKeyPoints([
        `Анализ "${topic}"`,
        "Основные моменты",
        "Перспективы развития",
        "Практическое применение",
      ]);
    } finally {
      setIsGeneratingKeyPoints(false);
    }
  };

  const generateScript = async () => {
    if (!topic.trim() || !isAuthenticated) return;

    setIsGeneratingScript(true);
    const validKeyPoints = keyPoints.filter((point) => point.trim() !== "");

    try {
      const res = await apiClient.post("/api/script/generate", {
        topic,
        duration,
        keyPoints: validKeyPoints,
        contentType,
      });
      setScript(cleanScript(res.data.data.script));
      setCurrentStep(2);
    } catch (error) {
      console.error("Ошибка при генерации скрипта:", error);
      setAuthError(
        "Ошибка при генерации скрипта. Проверьте подключение к серверу."
      );
    } finally {
      setIsGeneratingScript(false);
    }
  };

  const extendScript = async () => {
    if (!script.trim() || !topic.trim() || !isAuthenticated) return;

    setIsExtendingScript(true);
    const validKeyPoints = keyPoints.filter((point) => point.trim() !== "");

    try {
      const res = await apiClient.post("/api/script/extend", {
        script,
        topic,
        contentType,
      });

      setScript(script + "\n\n" + cleanScript(res.data.data.extension));
    } catch (error) {
      console.error("Ошибка при расширении скрипта:", error);
    } finally {
      setIsExtendingScript(false);
    }
  };

  const handleTextSelection = () => {
    const selection = window.getSelection();
    const selectedText = selection.toString().trim();

    if (selectedText && selectedText.length > 10) {
      setSelectedText(selectedText);
      setShowAiEditor(true);
      setAiPrompt("");
    }
  };

  const generateAiText = async () => {
    if (!aiPrompt.trim() || !isAuthenticated) return;

    setIsGeneratingText(true);

    try {
      const res = await apiClient.post("/api/script/improve", {
        selectedText,
        improvementCommand: aiPrompt,
        script,
      });

      const newScript = script.replace(
        selectedText,
        cleanScript(res.data.data.improvedText)
      );
      setScript(newScript);
    } catch (error) {
      console.error("Ошибка при улучшении текста:", error);
    } finally {
      setIsGeneratingText(false);
      setShowAiEditor(false);
      setSelectedText("");
    }
  };

  const handleVideoUpload = async (event) => {
    const file = event.target.files[0];
    if (!file) return;

    // Проверяем готовность хранилища
    if (!isStorageReady) {
      showNotification({
        type: "error",
        title: "Storage Not Ready",
        message: "Please wait for storage initialization",
        duration: 3000,
      });
      return;
    }

    setIsUploadingToStorage(true);
    setUploadProgress(0);

    try {
      // Симулируем прогресс загрузки
      const progressInterval = setInterval(() => {
        setUploadProgress((prev) => {
          if (prev >= 90) {
            clearInterval(progressInterval);
            return prev;
          }
          return prev + 10;
        });
      }, 100);

      // Сохраняем в локальное хранилище
      const savedVideo = await saveFileToStorage(file, "video");

      clearInterval(progressInterval);
      setUploadProgress(100);

      // Устанавливаем видео для воспроизведения
      setVideoFile(file);
      setVideoUrl(savedVideo.url); // Используем blob URL из хранилища

      if (script) {
        setCurrentStep(3);
      }

      showNotification({
        type: "success",
        title: "Video Saved",
        message: `Video "${file.name}" saved to local storage successfully`,
        duration: 4000,
      });
    } catch (error) {
      console.error("Video upload error:", error);

      showNotification({
        type: "error",
        title: "Upload Failed",
        message: error.message || "Failed to save video to local storage",
        duration: 5000,
      });
    } finally {
      setIsUploadingToStorage(false);
      setUploadProgress(0);
    }
  };

  const generateAudio = async () => {
    if (!isAuthenticated) return;

    try {
      // Здесь можно добавить реальную генерацию аудио
      setTimeout(() => {
        setAudioUrl("generated");
        setCurrentStep(4);
      }, 2000);
    } catch (error) {
      console.error("Ошибка при генерации аудио:", error);
    }
  };

  const startRecording = async () => {
    try {
      setIsRecording(true);
      setTimeout(() => {
        setIsRecording(false);
        setRecordedAudio("recorded_audio_blob");
        setShowAudioEditor(true);
      }, 5000);
    } catch (err) {
      console.error("Ошибка доступа к микрофону:", err);
      alert("Не удалось получить доступ к микрофону");
    }
  };

  const stopRecording = () => {
    setIsRecording(false);
    setRecordedAudio("recorded_audio_blob");
    setShowAudioEditor(true);
  };

  const handleTeleprompterRecording = async (blob, type) => {
    if (type === "video") {
      try {
        // Создаем File объект из blob
        const videoFile = new File(
          [blob],
          `teleprompter-video-${Date.now()}.webm`,
          {
            type: "video/webm",
          }
        );

        // Сохраняем в локальное хранилище
        if (isStorageReady) {
          const savedVideo = await saveFileToStorage(videoFile, "video");

          // Освобождаем старый URL
          if (videoUrl) {
            URL.revokeObjectURL(videoUrl);
          }

          setVideoFile(videoFile);
          setVideoUrl(savedVideo.url);

          showNotification({
            type: "success",
            title: "Teleprompter Video Saved",
            message: "Video recorded and saved to local storage",
            duration: 4000,
          });
        } else {
          // Fallback к обычному blob URL
          const newVideoUrl = URL.createObjectURL(blob);
          if (videoUrl) {
            URL.revokeObjectURL(videoUrl);
          }
          setVideoFile(videoFile);
          setVideoUrl(newVideoUrl);
        }

        if (script) {
          setCurrentStep(3);
        } else {
          setCurrentStep(2);
        }
      } catch (error) {
        console.error("Error saving teleprompter video:", error);

        // Fallback к обычному blob URL
        const newVideoUrl = URL.createObjectURL(blob);
        const videoFile = new File(
          [blob],
          `teleprompter-video-${Date.now()}.webm`,
          {
            type: "video/webm",
          }
        );

        if (videoUrl) {
          URL.revokeObjectURL(videoUrl);
        }

        setVideoFile(videoFile);
        setVideoUrl(newVideoUrl);

        if (script) {
          setCurrentStep(3);
        } else {
          setCurrentStep(2);
        }
      }
    } else {
      setRecordedAudio({
        blob: blob,
        type: type,
        url: URL.createObjectURL(blob),
      });
      setCurrentStep(4);
    }
  };

  const handlePlayPause = () => {
    if (videoRef.current) {
      if (isPlaying) {
        videoRef.current.pause();
      } else {
        videoRef.current.play();
      }
      setIsPlaying(!isPlaying);
    }
  };

  const steps = [
    {
      num: 1,
      title: "Create Script",
      active: currentStep >= 1,
      completed: currentStep > 1,
      icon: FileText,
      desc: "AI will create professional content",
    },
    {
      num: 2,
      title: "Upload Video",
      active: currentStep >= 2,
      completed: currentStep > 2,
      icon: Upload,
      desc: "Add your footage",
    },
    {
      num: 3,
      title: "AI Voiceover",
      active: currentStep >= 3,
      completed: currentStep > 3,
      icon: Mic,
      desc: "High-quality voiceover",
    },
    {
      num: 4,
      title: "Editing",
      active: currentStep >= 4,
      completed: currentStep > 4,
      icon: Scissors,
      desc: "Automatic processing",
    },
  ];

  const contentTypes = [
    { id: "lifestyle", name: "Lifestyle", icon: Heart, color: "slate" },
    { id: "gaming", name: "Gaming", icon: Target, color: "blue" },
    { id: "tech", name: "Technology", icon: Zap, color: "indigo" },
    { id: "education", name: "Education", icon: Brain, color: "blue" },
    { id: "entertainment", name: "Entertainment", icon: Star, color: "slate" },
  ];

  // Модальное окно авторизации
  if (showAuthModal) {
    return (
      <div className="min-h-screen bg-gradient-to-br from-slate-50 via-blue-50 to-indigo-50 flex items-center justify-center p-4">
        <div className="bg-white/95 backdrop-blur-xl rounded-2xl p-8 w-full max-w-md border border-slate-200/50 shadow-2xl">
          <div className="text-center mb-6">
            <div className="w-16 h-16 bg-gradient-to-r from-slate-600 to-blue-600 rounded-2xl mx-auto mb-4 flex items-center justify-center">
              <Brain size={32} className="text-white" />
            </div>
            <h2 className="text-2xl font-bold bg-gradient-to-r from-slate-700 to-blue-700 bg-clip-text text-transparent mb-2">
              Добро пожаловать в Scriptify
            </h2>
            <p className="text-gray-600 font-medium">
              Для работы требуется авторизация
            </p>
          </div>

          {authError && (
            <div className="bg-red-50 border border-red-200 text-red-700 px-4 py-3 rounded-xl mb-4 text-sm">
              {authError}
            </div>
          )}

          <button
            onClick={handleDemoLogin}
            className="w-full bg-gradient-to-r from-slate-600 to-blue-600 hover:from-slate-700 hover:to-blue-700 text-white font-semibold py-4 px-6 rounded-xl transition-all duration-300 shadow-lg hover:shadow-xl hover:scale-105"
          >
            Войти в демо режиме
          </button>

          <p className="text-xs text-gray-500 text-center mt-4">
            Демо режим создаст временный аккаунт для тестирования
          </p>
        </div>
      </div>
    );
  }

  if (!isAuthenticated) {
    return (
      <div className="min-h-screen bg-gradient-to-br from-slate-50 via-blue-50 to-indigo-50 flex items-center justify-center">
        <div className="text-center">
          <div className="animate-spin rounded-full h-16 w-16 border-b-2 border-blue-600 mx-auto mb-4"></div>
          <p className="text-gray-600 font-medium">Загрузка...</p>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gradient-to-br from-slate-50 via-blue-50 to-indigo-50 text-gray-900 flex flex-col">
      {/* Progress Steps */}
      <div className="bg-gradient-to-r from-slate-100/80 to-blue-100/80 backdrop-blur-lg border-b border-slate-200/50 px-6 py-8">
        <div className="max-w-6xl mx-auto">
          <div className="text-center mb-8">
            <h2 className="text-3xl font-bold bg-gradient-to-r from-slate-700 to-blue-700 bg-clip-text text-transparent mb-2">
              Create professional content
            </h2>
            <p className="text-gray-600 font-medium">
              From idea to finished video in 4 simple steps
            </p>

            {/* Storage Status Bar */}
            {storageInfo && (
              <div className="mt-4 text-sm text-gray-600 bg-white/80 backdrop-blur-sm px-4 py-2 rounded-xl inline-flex items-center space-x-3">
                <div className="flex items-center space-x-1">
                  <HardDrive size={14} className="text-blue-500" />
                  <span>Local Storage:</span>
                </div>
                <div className="text-blue-600 font-medium">
                  {storageInfo.usage.videos}/{storageInfo.limits.videos} videos
                </div>
                <div className="text-orange-600 font-medium">
                  {storageInfo.usage.totalSizeMB}MB used
                </div>
                <div className="text-gray-500">7 days retention</div>
              </div>
            )}
          </div>
          <div className="flex items-center justify-between">
            {steps.map((step, index) => {
              const IconComponent = step.icon;
              return (
                <div key={step.num} className="flex items-center">
                  <div className="flex flex-col items-center text-center max-w-xs">
                    <div
                      className={`w-10 h-10 rounded-2xl flex items-center justify-center text-sm font-semibold transition-all duration-500 shadow-lg relative overflow-hidden ${
                        step.completed
                          ? "bg-gradient-to-br from-green-500 to-emerald-600 text-white shadow-green-500/30"
                          : step.active
                          ? "bg-gradient-to-br from-slate-600 to-blue-600 text-white shadow-slate-500/30"
                          : "bg-white/80 text-gray-400 shadow-gray-200/50"
                      }`}
                    >
                      {step.completed ? (
                        <div className="text-xl">✓</div>
                      ) : (
                        <IconComponent size={18} />
                      )}
                    </div>
                    <span
                      className={`mt-4 text-lg font-semibold transition-colors duration-300 ${
                        step.active ? "text-slate-700" : "text-gray-500"
                      }`}
                    >
                      {step.title}
                    </span>
                    <span className="text-xs text-gray-500 mt-1 font-medium">
                      {step.desc}
                    </span>
                  </div>
                  {index < steps.length - 1 && (
                    <div
                      className={`w-32 h-2 mx-8 rounded-full transition-all duration-500 ${
                        currentStep > step.num
                          ? "bg-gradient-to-r from-green-500 to-emerald-600"
                          : "bg-gray-200"
                      }`}
                    />
                  )}
                </div>
              );
            })}
          </div>
        </div>
      </div>

      <div className="flex flex-1 relative">
        <div
          className={`w-[${
            isLeftPanelCollapsed ? "100px" : "600px"
          }] min-h-full bg-white/90 backdrop-blur-xl border-r border-slate-200/50 transition-all duration-300 ease-in-out flex flex-col shadow-xl shadow-slate-500/10`}
        >
          <div className="flex items-center justify-between p-6 border-b border-slate-200/50 from-slate-50 to-blue-50">
            {!isLeftPanelCollapsed && (
              <div>
                <h2 className="text-2xl font-bold bg-gradient-to-r from-slate-700 to-blue-700 bg-clip-text text-transparent">
                  Content Studio
                </h2>
                <p className="text-sm text-gray-600 font-medium">
                  Professional content creation tools
                </p>
                {storageInfo && (
                  <div className="text-xs text-gray-500 mt-1">
                    Local Storage: {storageInfo.usage.totalSizeMB}MB used •
                    {storageInfo.limits.videos - storageInfo.usage.videos} video
                    slots remaining
                  </div>
                )}
              </div>
            )}
            <button
              onClick={() => setIsLeftPanelCollapsed(!isLeftPanelCollapsed)}
              className="p-3 hover:bg-slate-100 rounded-xl transition-all duration-300 group"
            >
              {isLeftPanelCollapsed ? (
                <ChevronRight
                  size={20}
                  className="text-gray-600 group-hover:text-slate-700"
                />
              ) : (
                <ChevronLeft
                  size={20}
                  className="text-gray-600 group-hover:text-slate-700"
                />
              )}
            </button>
          </div>
          {isLeftPanelCollapsed ? (
            <div className="p-4 space-y-4">
              <div className="flex flex-col items-center space-y-4">
                <button
                  onClick={() => setIsLeftPanelCollapsed(false)}
                  className="p-4 bg-gradient-to-br from-slate-100 to-blue-100 hover:from-slate-200 hover:to-blue-200 rounded-xl transition-all duration-300 group hover:scale-110 shadow-lg"
                  title="Create script"
                >
                  <FileText
                    size={20}
                    className="text-slate-600 group-hover:text-blue-600"
                  />
                </button>
                <button
                  onClick={() => setIsLeftPanelCollapsed(false)}
                  className="p-4 bg-gradient-to-br from-blue-100 to-indigo-100 hover:from-blue-200 hover:to-indigo-200 rounded-xl transition-all duration-300 group hover:scale-110 shadow-lg"
                  title="Upload video"
                >
                  <Upload
                    size={20}
                    className="text-blue-600 group-hover:text-indigo-600"
                  />
                </button>
                <button
                  onClick={() => setIsLeftPanelCollapsed(false)}
                  className="p-4 bg-gradient-to-br from-indigo-100 to-purple-100 hover:from-indigo-200 hover:to-purple-200 rounded-xl transition-all duration-300 group hover:scale-110 shadow-lg"
                  title="AI voiceover"
                >
                  <Mic
                    size={20}
                    className="text-indigo-600 group-hover:text-purple-600"
                  />
                </button>
                <button
                  onClick={() => setIsLeftPanelCollapsed(false)}
                  className="p-4 bg-gradient-to-br from-purple-100 to-pink-100 hover:from-purple-200 hover:to-pink-200 rounded-xl transition-all duration-300 group hover:scale-110 shadow-lg"
                  title="Editing"
                >
                  <Scissors
                    size={20}
                    className="text-purple-600 group-hover:text-pink-600"
                  />
                </button>
              </div>
            </div>
          ) : (
            <div className="p-6 overflow-y-auto flex-1 space-y-6">
              <div className="bg-gradient-to-r from-slate-600 to-blue-600 text-white p-4 rounded-xl">
                <div className="flex items-center space-x-3 mb-2">
                  <Brain size={20} />
                  <span className="font-semibold text-lg">AI-Powered</span>
                </div>
                <p className="text-sm opacity-90">
                  Create professional content using artificial intelligence
                </p>
              </div>

              {/* Local Storage Info Panel */}
              {storageInfo && (
                <div className="bg-gradient-to-r from-blue-50 to-indigo-50 border border-blue-200 p-4 rounded-xl">
                  <div className="flex items-center space-x-2 mb-3">
                    <HardDrive size={16} className="text-blue-600" />
                    <span className="font-semibold text-blue-800">
                      Local Storage (MVP) -{" "}
                      {storageInfo.mode === "fallback"
                        ? "Memory Mode"
                        : "IndexedDB Mode"}
                    </span>
                    {!isStorageReady && (
                      <div className="animate-spin rounded-full h-4 w-4 border-b-2 border-blue-500"></div>
                    )}
                  </div>
                  <div className="grid grid-cols-3 gap-3 text-center text-xs">
                    <div>
                      <div
                        className={`font-bold ${
                          storageInfo.usage.videos >=
                          storageInfo.limits.videos * 0.8
                            ? "text-red-600"
                            : "text-green-600"
                        }`}
                      >
                        {storageInfo.usage.videos}/{storageInfo.limits.videos}
                      </div>
                      <div className="text-gray-600">Videos</div>
                    </div>
                    <div>
                      <div className="font-bold text-blue-600">
                        {storageInfo.usage.totalSizeMB}MB
                      </div>
                      <div className="text-gray-600">Used</div>
                    </div>
                    <div>
                      <div className="font-bold text-orange-600">7 days</div>
                      <div className="text-gray-600">Retention</div>
                    </div>
                  </div>

                  {storageInfo.mode === "fallback" && (
                    <div className="mt-3 p-2 bg-yellow-50 border border-yellow-200 rounded-lg">
                      <div className="flex items-center space-x-2">
                        <AlertTriangle size={12} className="text-yellow-500" />
                        <span className="text-xs text-yellow-700 font-medium">
                          Using fallback storage (files saved in memory only)
                        </span>
                      </div>
                    </div>
                  )}

                  {storageInfo.usage.videos >=
                    storageInfo.limits.videos * 0.8 && (
                    <div className="mt-3 p-2 bg-yellow-50 border border-yellow-200 rounded-lg">
                      <div className="flex items-center space-x-2">
                        <AlertTriangle size={12} className="text-yellow-500" />
                        <span className="text-xs text-yellow-700 font-medium">
                          Approaching storage limit
                        </span>
                      </div>
                    </div>
                  )}
                </div>
              )}

              {/* Content Type Selection */}
              <div className="space-y-4">
                <label className="block text-gray-700 font-semibold text-lg">
                  Content type
                </label>
                <div className="grid grid-cols-2 gap-3">
                  {contentTypes.map((type) => {
                    const IconComponent = type.icon;
                    return (
                      <button
                        key={type.id}
                        onClick={() => setContentType(type.id)}
                        className={`p-4 rounded-xl border-2 transition-all duration-300 ${
                          contentType === type.id
                            ? type.color === "slate"
                              ? "border-slate-500 bg-slate-50 shadow-lg shadow-slate-500/30"
                              : type.color === "blue"
                              ? "border-blue-500 bg-blue-50 shadow-lg shadow-blue-500/30"
                              : "border-indigo-500 bg-indigo-50 shadow-lg shadow-indigo-500/30"
                            : "border-gray-200 bg-white hover:border-gray-300"
                        }`}
                      >
                        <IconComponent
                          size={24}
                          className={`mx-auto mb-2 ${
                            contentType === type.id
                              ? type.color === "slate"
                                ? "text-slate-600"
                                : type.color === "blue"
                                ? "text-blue-600"
                                : "text-indigo-600"
                              : "text-gray-400"
                          }`}
                        />
                        <span
                          className={`text-sm font-bold ${
                            contentType === type.id
                              ? type.color === "slate"
                                ? "text-slate-700"
                                : type.color === "blue"
                                ? "text-blue-700"
                                : "text-indigo-700"
                              : "text-gray-600"
                          }`}
                        >
                          {type.name}
                        </span>
                      </button>
                    );
                  })}
                </div>
              </div>
              {/* Topic Input */}
              <div className="space-y-3">
                <label className="block text-gray-700 font-semibold">
                  What is the video about?
                </label>
                <div className="relative group">
                  <input
                    type="text"
                    value={topic}
                    onChange={(e) => setTopic(e.target.value)}
                    placeholder="Example: Review of S.T.A.L.K.E.R. 2 game"
                    className="w-full p-4 bg-white/90 backdrop-blur-sm border-2 border-slate-200 rounded-xl text-gray-900 placeholder-gray-400 focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-blue-500 transition-all duration-300 shadow-lg hover:shadow-xl font-medium"
                  />
                  <div className="absolute right-4 top-4">
                    <Brain size={20} className="text-slate-400" />
                  </div>
                </div>
              </div>
              {/* Key Points */}
              <div className="space-y-4">
                <div className="flex items-center justify-between">
                  <div>
                    <label className="block text-gray-700 font-semibold text-lg">
                      Key points
                    </label>
                    <p className="text-sm text-gray-500 font-medium">
                      What will engage viewers?
                    </p>
                  </div>
                  <div className="flex space-x-2">
                    <button
                      onClick={generateKeyPoints}
                      disabled={
                        !topic.trim() ||
                        isGeneratingKeyPoints ||
                        isGeneratingScript ||
                        isExtendingScript ||
                        !isAuthenticated
                      }
                      className="flex items-center px-4 py-2 text-sm bg-gradient-to-r from-indigo-500 to-purple-500 hover:from-indigo-600 hover:to-purple-600 text-white rounded-xl disabled:from-gray-300 disabled:to-gray-400 disabled:cursor-not-allowed transition-all duration-300 shadow-lg hover:shadow-xl hover:scale-105 font-semibold"
                    >
                      {isGeneratingKeyPoints ||
                      isGeneratingScript ||
                      isExtendingScript ? (
                        <>
                          <div className="animate-spin rounded-full h-4 w-4 border-b-2 border-white mr-2"></div>
                          AI thinking...
                        </>
                      ) : (
                        <>
                          <Brain size={16} className="mr-2" />
                          AI generation
                        </>
                      )}
                    </button>
                    <button
                      onClick={addKeyPoint}
                      disabled={
                        keyPoints.length >= 10 ||
                        isGeneratingKeyPoints ||
                        isGeneratingScript ||
                        isExtendingScript
                      }
                      className="flex items-center px-4 py-2 text-sm bg-gradient-to-r from-slate-500 to-blue-500 hover:from-slate-600 hover:to-blue-600 text-white rounded-xl disabled:from-gray-300 disabled:to-gray-400 disabled:cursor-not-allowed transition-all duration-300 shadow-lg hover:shadow-xl hover:scale-105 font-semibold"
                    >
                      <Plus size={16} className="mr-2" />
                      Add
                    </button>
                  </div>
                </div>
                <div className="space-y-3 max-h-80 overflow-y-auto">
                  {keyPoints.map((point, index) => (
                    <div
                      key={index}
                      className="flex items-center space-x-3 group"
                    >
                      <div className="w-8 h-8 bg-gradient-to-br from-slate-400 to-blue-400 rounded-lg flex items-center justify-center text-white font-semibold text-sm">
                        {index + 1}
                      </div>
                      <input
                        type="text"
                        value={point}
                        onChange={(e) => updateKeyPoint(index, e.target.value)}
                        placeholder="Enter key point..."
                        className="flex-1 p-3 bg-white/90 backdrop-blur-sm border border-slate-200 rounded-xl text-gray-900 placeholder-gray-400 focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-blue-500 transition-all duration-300 hover:shadow-md font-medium"
                      />
                      {keyPoints.length > 1 && (
                        <button
                          onClick={() => removeKeyPoint(index)}
                          className="p-2 text-red-400 hover:text-red-600 hover:bg-red-50 rounded-xl transition-all duration-300 opacity-0 group-hover:opacity-100"
                        >
                          <X size={16} />
                        </button>
                      )}
                    </div>
                  ))}
                </div>
              </div>
              {/* Duration */}
              <div className="space-y-3">
                <label className="block text-gray-700 font-semibold">
                  Video duration
                </label>
                <select
                  value={duration}
                  onChange={(e) => setDuration(e.target.value)}
                  className="w-full p-4 bg-white/90 backdrop-blur-sm border-2 border-slate-200 rounded-xl text-gray-900 focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-blue-500 transition-all duration-300 shadow-lg hover:shadow-xl font-medium"
                >
                  <option>~5 minutes (500 words)</option>
                  <option>~10 minutes (1000 words)</option>
                  <option>~15 minutes (1500 words)</option>
                  <option>~20 minutes (2000 words)</option>
                </select>
              </div>
              {/* Generate Button */}
              <button
                onClick={generateScript}
                disabled={
                  !topic ||
                  isGeneratingScript ||
                  isGeneratingKeyPoints ||
                  isExtendingScript ||
                  !isAuthenticated
                }
                className="w-full bg-gradient-to-r from-slate-600 to-blue-600 hover:from-slate-700 hover:to-blue-700 disabled:from-gray-400 disabled:to-gray-500 disabled:cursor-not-allowed text-white font-semibold py-4 px-6 rounded-xl transition-all duration-300 flex items-center justify-center shadow-lg hover:shadow-xl hover:scale-105 group overflow-hidden relative"
              >
                <div className="absolute inset-0 bg-gradient-to-r from-blue-600 to-slate-600 opacity-0 group-hover:opacity-100 transition-opacity duration-300"></div>
                <span className="relative flex items-center">
                  {isGeneratingScript ||
                  isGeneratingKeyPoints ||
                  isExtendingScript ? (
                    <>
                      <div className="animate-spin rounded-full h-5 w-5 border-b-2 border-white mr-3"></div>
                      {isExtendingScript
                        ? "Expanding script..."
                        : !isGeneratingKeyPoints
                        ? "Generating script..."
                        : "Waiting for key points"}
                    </>
                  ) : (
                    <>
                      <Wand2 className="mr-3" size={20} />
                      Generate Script
                    </>
                  )}
                </span>
              </button>
              {/* Tips */}
              {!script && (
                <div className="bg-gradient-to-r from-blue-100 to-indigo-100 border border-blue-300 p-4 rounded-xl">
                  <div className="flex items-start space-x-3">
                    <div className="text-xl">💡</div>
                    <div>
                      <h4 className="font-semibold text-gray-800 mb-2">
                        Tip from Scriptify:
                      </h4>
                      <p className="text-sm text-gray-700">
                        Structure content logically: introduction, main section
                        with key points, conclusion. This will help viewers
                        better absorb information.
                      </p>
                    </div>
                  </div>
                </div>
              )}

              {/* Script Editor */}
              {script && !isGeneratingScript ? (
                <div className="space-y-4">
                  <div className="flex items-center justify-between">
                    <div>
                      <label className="block text-gray-700 font-semibold text-lg">
                        Your script is ready ✨
                      </label>
                      <p className="text-sm text-gray-500">
                        Professional content for your video
                      </p>
                    </div>
                    <div className="flex items-center space-x-2">
                      <div className="flex items-center text-xs text-blue-600 bg-blue-50 px-3 py-2 rounded-xl border border-blue-200">
                        <Edit3 size={12} className="mr-2" />
                        Select text for AI improvement
                      </div>
                      <button
                        onClick={extendScript}
                        disabled={
                          isExtendingScript ||
                          isGeneratingKeyPoints ||
                          !isAuthenticated
                        }
                        className="flex items-center px-4 py-2 text-sm bg-gradient-to-r from-emerald-500 to-teal-500 hover:from-emerald-600 hover:to-teal-600 disabled:from-gray-300 disabled:to-gray-400 disabled:cursor-not-allowed text-white rounded-xl transition-all duration-300 shadow-lg hover:shadow-xl hover:scale-105 font-semibold"
                        title="Add more content to script"
                      >
                        {isExtendingScript ? (
                          <>
                            <div className="animate-spin rounded-full px-4 py-2 h-4 w-4 border-b-2 border-white mr-2"></div>
                            Expanding...
                          </>
                        ) : (
                          <>
                            <Plus size={16} className="mr-2" />
                            Write more
                          </>
                        )}
                      </button>
                    </div>
                  </div>
                  <textarea
                    ref={scriptRef}
                    value={script}
                    onChange={(e) => setScript(e.target.value)}
                    onMouseUp={handleTextSelection}
                    rows={12}
                    className="w-full p-4 bg-white/90 backdrop-blur-sm border-2 border-slate-200 rounded-xl text-gray-900 placeholder-gray-400 focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-blue-500 leading-relaxed transition-all duration-300 shadow-lg hover:shadow-xl resize-none font-medium"
                    style={{ userSelect: "text" }}
                  />
                  {/* Script Stats */}
                  <div className="flex items-center justify-between text-sm bg-gradient-to-r from-blue-50 to-indigo-50 p-3 rounded-xl border border-blue-200">
                    <div className="flex items-center space-x-4">
                      <div className="flex items-center space-x-1">
                        {assessmentLoading ? (
                          <div
                            className="animate-spin rounded-full h-5 w-5 border-b-2 mr-3"
                            style={{ borderColor: "rgba(59, 130, 246, 0.5)" }}
                          />
                        ) : (
                          <Eye size={20} className="text-blue-600" />
                        )}
                        <span className="font-medium text-blue-700">
                          {!assessmentLoading &&
                            `Quality: ${assessment.total_score || 8}/10`}
                        </span>
                      </div>
                      <div className="flex items-center space-x-1">
                        {assessmentLoading ? (
                          <div
                            className="animate-spin rounded-full h-5 w-5 border-b-2 mr-3"
                            style={{ borderColor: "rgba(59, 130, 246, 0.5)" }}
                          />
                        ) : (
                          <Clock size={20} className="text-slate-600" />
                        )}
                        <span className="font-medium text-blue-700">
                          {!assessmentLoading &&
                            `~${
                              assessment.video_duration ||
                              Math.ceil(script.length / 150)
                            } min`}
                        </span>
                      </div>
                      <div className="flex items-center space-x-1">
                        {assessmentLoading ? (
                          <div
                            className="animate-spin rounded-full h-5 w-5 border-b-2 mr-3"
                            style={{ borderColor: "rgba(59, 130, 246, 0.5)" }}
                          />
                        ) : (
                          <Users size={20} className="text-indigo-600" />
                        )}
                        <span className="font-medium text-blue-700">
                          {!assessmentLoading && ` ${script.length} characters`}
                        </span>
                      </div>
                    </div>
                    <div className="text-xs text-gray-500">
                      ✨ Ready for recording
                    </div>
                  </div>
                  <DetailedAnalytics
                    assessmentLoading={assessmentLoading}
                    assessment={assessment}
                  />
                </div>
              ) : script.length && isGeneratingScript ? (
                <ScriptEditorSkeleton />
              ) : null}

              {/* Video Upload Section */}
              {script && (
                <div className="space-y-6 border-t border-slate-200 pt-8">
                  <div>
                    <h3 className="text-xl font-bold bg-gradient-to-r from-slate-700 to-blue-700 bg-clip-text text-transparent mb-2">
                      Video Upload
                    </h3>
                    <p className="text-gray-600 font-medium">
                      Upload your video footage to continue
                    </p>
                  </div>

                  {/* Upload Area */}
                  <div className="space-y-4">
                    <input
                      type="file"
                      ref={fileInputRef}
                      onChange={handleVideoUpload}
                      accept="video/*"
                      className="hidden"
                    />

                    <button
                      onClick={() => {
                        if (isUploadingToStorage || !isStorageReady) return;
                        fileInputRef.current?.click();
                      }}
                      disabled={
                        isUploadingToStorage ||
                        !isStorageReady ||
                        (storageInfo &&
                          storageInfo.usage.videos >= storageInfo.limits.videos)
                      }
                      className={`w-full p-6 border-2 border-dashed rounded-2xl transition-all duration-200 text-center group ${
                        isUploadingToStorage || !isStorageReady
                          ? "border-gray-300 bg-gray-50 cursor-not-allowed"
                          : storageInfo &&
                            storageInfo.usage.videos >=
                              storageInfo.limits.videos
                          ? "border-red-300 bg-red-50"
                          : "border-gray-300 hover:border-blue-400 hover:bg-blue-50/50"
                      }`}
                    >
                      <div
                        className={`w-12 h-12 bg-gray-100 rounded-xl flex items-center justify-center mx-auto mb-3 transition-colors ${
                          isUploadingToStorage
                            ? "bg-blue-100"
                            : "group-hover:bg-blue-100"
                        }`}
                      >
                        {isUploadingToStorage ? (
                          <div className="animate-spin rounded-full h-5 w-5 border-b-2 border-blue-500"></div>
                        ) : !isStorageReady ? (
                          <HardDrive size={20} className="text-gray-400" />
                        ) : storageInfo &&
                          storageInfo.usage.videos >=
                            storageInfo.limits.videos ? (
                          <AlertTriangle size={20} className="text-red-500" />
                        ) : (
                          <Upload
                            size={20}
                            className={`text-gray-500 ${
                              !isUploadingToStorage && isStorageReady
                                ? "group-hover:text-blue-500"
                                : ""
                            }`}
                          />
                        )}
                      </div>
                      <span
                        className={`text-sm font-medium transition-colors ${
                          isUploadingToStorage
                            ? "text-blue-600"
                            : !isStorageReady
                            ? "text-gray-500"
                            : storageInfo &&
                              storageInfo.usage.videos >=
                                storageInfo.limits.videos
                            ? "text-red-600"
                            : "text-gray-700 group-hover:text-blue-600"
                        }`}
                      >
                        {isUploadingToStorage
                          ? `Saving to local storage... ${Math.round(
                              uploadProgress
                            )}%`
                          : !isStorageReady
                          ? "Initializing storage..."
                          : storageInfo &&
                            storageInfo.usage.videos >=
                              storageInfo.limits.videos
                          ? `Storage limit reached (${storageInfo.limits.videos} max)`
                          : "Upload Video File"}
                      </span>
                      <p className="text-xs text-gray-500 mt-1">
                        {isUploadingToStorage
                          ? "Please wait while video is being saved locally"
                          : !isStorageReady
                          ? "Setting up local storage..."
                          : storageInfo &&
                            storageInfo.usage.videos >=
                              storageInfo.limits.videos
                          ? "Delete some videos to upload more"
                          : "MP4, AVI, MOV up to 100MB • Stored locally for 7 days"}
                      </p>
                      {isUploadingToStorage && (
                        <div className="w-full bg-gray-200 rounded-full h-2 mt-3">
                          <div
                            className="bg-blue-500 h-2 rounded-full transition-all duration-300"
                            style={{ width: `${uploadProgress}%` }}
                          ></div>
                        </div>
                      )}

                      {/* Показываем оставшееся место */}
                      {isStorageReady && storageInfo && (
                        <div className="mt-2 text-xs">
                          <span
                            className={`font-medium ${
                              storageInfo.usage.videos >=
                              storageInfo.limits.videos * 0.8
                                ? "text-red-600"
                                : "text-green-600"
                            }`}
                          >
                            {storageInfo.limits.videos -
                              storageInfo.usage.videos}{" "}
                            video slots remaining
                          </span>
                        </div>
                      )}
                    </button>

                    {/* Video Preview */}
                    {videoFile && videoUrl && (
                      <div className="bg-gradient-to-r from-green-50 to-emerald-50 p-6 rounded-xl border-2 border-green-200 shadow-lg">
                        <div className="flex items-center space-x-4">
                          <div className="w-12 h-12 bg-gradient-to-r from-green-500 to-emerald-600 rounded-xl flex items-center justify-center shadow-lg">
                            <Upload size={20} className="text-white" />
                          </div>
                          <div className="flex-1">
                            <h4 className="font-bold text-green-800 text-lg">
                              Video uploaded successfully!
                            </h4>
                            <p className="text-sm text-green-600 font-medium">
                              {videoFile.name} • Saved to local storage
                            </p>
                          </div>
                        </div>
                        <video
                          src={videoUrl}
                          className="w-full mt-4 rounded-lg"
                          controls
                          style={{ maxHeight: "200px" }}
                        />
                      </div>
                    )}
                  </div>
                </div>
              )}

              {/* Audio Creation Section */}
              {script && videoFile && (
                <div className="space-y-6 border-t border-slate-200 pt-8">
                  <div>
                    <h3 className="text-xl font-bold bg-gradient-to-r from-slate-700 to-blue-700 bg-clip-text text-transparent mb-2">
                      Voiceover creation
                    </h3>
                    <p className="text-gray-600 font-medium">
                      Choose a method to create audio track
                    </p>
                  </div>
                  {/* Audio Method Selection */}
                  <div className="grid grid-cols-2 gap-4">
                    <button
                      onClick={() => setAudioMethod("ai")}
                      className={`p-6 rounded-xl border-2 transition-all duration-300 ${
                        audioMethod === "ai"
                          ? "border-blue-500 bg-blue-50 shadow-lg shadow-blue-500/30"
                          : "border-gray-200 bg-white hover:border-gray-300"
                      }`}
                    >
                      <Brain
                        size={32}
                        className={`mx-auto mb-3 ${
                          audioMethod === "ai"
                            ? "text-blue-600"
                            : "text-gray-400"
                        }`}
                      />
                      <h4
                        className={`font-semibold mb-2 ${
                          audioMethod === "ai"
                            ? "text-blue-700"
                            : "text-gray-600"
                        }`}
                      >
                        AI Voiceover
                      </h4>
                      <p className="text-sm text-gray-500">
                        Generate voice using artificial intelligence
                      </p>
                    </button>
                    <button
                      onClick={() => setAudioMethod("record")}
                      className={`p-6 rounded-xl border-2 transition-all duration-300 ${
                        audioMethod === "record"
                          ? "border-slate-500 bg-slate-50 shadow-lg shadow-slate-500/30"
                          : "border-gray-200 bg-white hover:border-gray-300"
                      }`}
                    >
                      <Mic
                        size={32}
                        className={`mx-auto mb-3 ${
                          audioMethod === "record"
                            ? "text-slate-600"
                            : "text-gray-400"
                        }`}
                      />
                      <h4
                        className={`font-semibold mb-2 ${
                          audioMethod === "record"
                            ? "text-slate-700"
                            : "text-gray-600"
                        }`}
                      >
                        Record yourself
                      </h4>
                      <p className="text-sm text-gray-500">
                        Use teleprompter for professional recording
                      </p>
                    </button>
                  </div>

                  {/* AI Voice Options */}
                  {audioMethod === "ai" && (
                    <div className="space-y-4">
                      <div>
                        <label className="block text-gray-700 font-semibold mb-3">
                          Select voice
                        </label>
                        <select className="w-full p-4 bg-white border-2 border-slate-200 rounded-xl text-gray-900 focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-blue-500 transition-all duration-300 shadow-lg hover:shadow-xl font-medium">
                          <option>Alexei - Male, professional</option>
                          <option>Anya - Female, friendly</option>
                          <option>Dmitry - Young male, energetic</option>
                          <option>Elena - Female, strict</option>
                          <option>Maxim - Male, charismatic</option>
                        </select>
                      </div>
                      <div>
                        <label className="block text-gray-700 font-semibold mb-3">
                          Speech speed
                        </label>
                        <div className="flex items-center space-x-4">
                          <span className="text-sm text-gray-500">Slow</span>
                          <input
                            type="range"
                            min="0.5"
                            max="2"
                            step="0.1"
                            defaultValue="1"
                            className="flex-1"
                          />
                          <span className="text-sm text-gray-500">Fast</span>
                        </div>
                      </div>
                      <button
                        onClick={generateAudio}
                        disabled={!isAuthenticated}
                        className="w-full bg-gradient-to-r from-blue-500 to-indigo-600 hover:from-blue-600 hover:to-indigo-700 text-white font-semibold py-4 px-6 rounded-xl transition-all duration-300 shadow-lg hover:shadow-xl hover:scale-105"
                      >
                        <Volume2 className="mr-3 inline" size={20} />
                        Generate AI voiceover
                      </button>
                    </div>
                  )}

                  {/* Recording Options */}
                  {audioMethod === "record" && (
                    <div className="space-y-4">
                      <div className="bg-gradient-to-r from-slate-50 to-blue-50 p-6 rounded-xl border-2 border-slate-200 shadow-lg">
                        <div className="text-center">
                          {!recordedAudio && (
                            <>
                              <div className="w-16 h-16 bg-gradient-to-r from-slate-600 to-blue-600 rounded-2xl mx-auto mb-4 flex items-center justify-center shadow-lg">
                                <Eye size={32} className="text-white" />
                              </div>
                              <h4 className="font-bold text-slate-800 mb-2 text-lg">
                                Professional recording
                              </h4>
                              <p className="text-sm text-slate-600 mb-6 font-medium">
                                Use a teleprompter for quality recording
                              </p>
                              <button
                                onClick={() => setShowTeleprompter(true)}
                                className="bg-gradient-to-r from-slate-600 to-blue-600 hover:from-slate-700 hover:to-blue-700 text-white font-semibold py-4 px-8 rounded-xl transition-all duration-300 shadow-lg hover:shadow-xl hover:scale-105 flex items-center mx-auto"
                              >
                                <Eye className="mr-3" size={20} />
                                Open teleprompter
                              </button>
                            </>
                          )}
                          {recordedAudio && (
                            <>
                              <div className="w-16 h-16 bg-gradient-to-r from-green-500 to-emerald-600 rounded-2xl mx-auto mb-4 flex items-center justify-center shadow-lg">
                                <Volume2 size={32} className="text-white" />
                              </div>
                              <h4 className="font-bold text-green-800 mb-2 text-lg">
                                Запись завершена!
                              </h4>
                              <p className="text-sm text-green-600 mb-6 font-medium">
                                {recordedAudio.type === "video"
                                  ? "Видео"
                                  : "Аудио"}{" "}
                                успешно записано
                              </p>
                              <div className="flex space-x-3 justify-center">
                                <button
                                  onClick={() => setShowAudioEditor(true)}
                                  className="bg-gradient-to-r from-blue-600 to-indigo-600 hover:from-blue-700 hover:to-indigo-700 text-white font-semibold py-3 px-6 rounded-xl transition-all duration-300 shadow-lg hover:shadow-xl"
                                >
                                  <Edit3 className="mr-2 inline" size={16} />
                                  Редактировать
                                </button>
                                <button
                                  onClick={() => setShowTeleprompter(true)}
                                  className="bg-gradient-to-r from-slate-600 to-slate-700 hover:from-slate-700 hover:to-slate-800 text-white font-semibold py-3 px-6 rounded-xl transition-all duration-300 shadow-lg hover:shadow-xl"
                                >
                                  Записать заново
                                </button>
                              </div>
                            </>
                          )}
                        </div>
                      </div>
                    </div>
                  )}
                  {/* Generated Audio Preview */}
                  {audioUrl && audioMethod === "ai" && (
                    <div className="bg-gradient-to-r from-blue-50 to-indigo-50 p-6 rounded-xl border-2 border-blue-200 shadow-lg">
                      <div className="flex items-center space-x-4">
                        <div className="w-12 h-12 bg-gradient-to-r from-blue-500 to-indigo-600 rounded-xl flex items-center justify-center shadow-lg">
                          <Volume2 size={20} className="text-white" />
                        </div>
                        <div className="flex-1">
                          <h4 className="font-bold text-blue-800 text-lg">
                            AI voiceover created!
                          </h4>
                          <p className="text-sm text-blue-600 font-medium">
                            Professional voiceover ready
                          </p>
                        </div>
                        <button
                          onClick={() => setShowAudioEditor(true)}
                          className="bg-gradient-to-r from-blue-600 to-indigo-600 hover:from-blue-700 hover:to-indigo-700 text-white font-semibold py-3 px-4 rounded-xl transition-all duration-300 shadow-lg hover:shadow-xl"
                        >
                          <Edit3 className="mr-2 inline" size={14} />
                          Edit
                        </button>
                      </div>
                    </div>
                  )}
                </div>
              )}
            </div>
          )}
        </div>

        <div className="w-2 bg-gradient-to-b from-transparent via-blue-400 to-transparent transition-all duration-300 relative group">
          <div className="absolute inset-y-0 -left-2 -right-2 bg-blue-400/10 transition-colors duration-300"></div>
        </div>

        {/* Right Panel */}
        <div
          className={`bg-gradient-to-br from-white/70 to-slate-50/70 backdrop-blur-xl p-6 flex flex-col transition-all duration-300 ease-in-out ${
            isLeftPanelCollapsed ? "w-[92%] min-w-[92%]" : "w-[90%] min-w-[69%]"
          }`}
        >
          <VideoEditor />
          <div className="bg-white/80 backdrop-blur-xl rounded-2xl mt-5 p-6 border border-slate-200/50 shadow-xl">
            <div className="flex items-center justify-between mb-4">
              <div className="flex items-center space-x-3">
                <div
                  className={`w-5 h-5 rounded-full shadow-lg ${
                    currentStep === 1
                      ? "bg-slate-500 shadow-slate-500/50"
                      : currentStep === 2
                      ? "bg-blue-500 shadow-blue-500/50"
                      : currentStep === 3
                      ? "bg-indigo-500 shadow-indigo-500/50"
                      : "bg-green-500 shadow-green-500/50"
                  } animate-pulse`}
                ></div>
                <span className="font-semibold text-gray-800 text-lg">
                  Control panel
                </span>
              </div>
              <div className="text-xs text-gray-500 bg-gray-100 px-3 py-1 rounded-full font-medium">
                Step {currentStep} of 4
              </div>
            </div>
            <div className="space-y-4">
              <p className="text-gray-600 leading-relaxed font-medium">
                {currentStep === 1
                  ? "📝 Ready to create a professional script. Enter a topic or use AI to generate key points"
                  : currentStep === 2
                  ? "📹 Script created! Now upload your video for further processing"
                  : currentStep === 3
                  ? "🎤 Create voiceover: choose AI generation or record your voice manually"
                  : "🎬 Project ready for final editing and export in high quality"}
              </p>
              {/* Action Buttons */}
              {currentStep === 3 &&
                script &&
                videoFile &&
                !audioUrl &&
                !recordedAudio && (
                  <div className="text-center">
                    <p className="text-gray-600 mb-4">
                      Go to the left panel to create voiceover
                    </p>
                    <div className="grid grid-cols-2 gap-3">
                      <div className="bg-blue-50 p-3 rounded-lg border border-blue-200">
                        <Brain
                          size={20}
                          className="mx-auto mb-2 text-blue-600"
                        />
                        <span className="text-xs font-medium text-blue-700">
                          AI voiceover
                        </span>
                      </div>
                      <div className="bg-slate-50 p-3 rounded-lg border border-slate-200">
                        <Mic
                          size={20}
                          className="mx-auto mb-2 text-slate-600"
                        />
                        <span className="text-xs font-medium text-slate-700">
                          Microphone recording
                        </span>
                      </div>
                    </div>
                  </div>
                )}
              {currentStep === 4 && (
                <div className="space-y-3">
                  <button className="w-full flex items-center justify-center px-6 py-4 bg-gradient-to-r from-slate-600 to-blue-600 hover:from-slate-700 hover:to-blue-700 text-white rounded-xl transition-all duration-300 shadow-lg hover:shadow-xl hover:scale-105 font-semibold">
                    <Scissors className="mr-3" size={18} />
                    Start editing
                  </button>
                  {/* Features */}
                  <div className="grid grid-cols-2 gap-2 mt-4">
                    <div className="bg-gradient-to-r from-blue-50 to-indigo-50 p-3 rounded-xl border border-blue-200">
                      <div className="flex items-center space-x-2">
                        <Eye size={14} className="text-blue-500" />
                        <span className="text-xs font-medium text-blue-700">
                          Smart transitions
                        </span>
                      </div>
                    </div>
                    <div className="bg-gradient-to-r from-slate-50 to-gray-50 p-3 rounded-xl border border-slate-200">
                      <div className="flex items-center space-x-2">
                        <Share2 size={14} className="text-slate-500" />
                        <span className="text-xs font-medium text-slate-700">
                          Auto trimming
                        </span>
                      </div>
                    </div>
                    <div className="bg-gradient-to-r from-indigo-50 to-purple-50 p-3 rounded-xl border border-indigo-200">
                      <div className="flex items-center space-x-2">
                        <Volume2 size={14} className="text-indigo-500" />
                        <span className="text-xs font-medium text-indigo-700">
                          Audio enhancement
                        </span>
                      </div>
                    </div>
                    <div className="bg-gradient-to-r from-purple-50 to-pink-50 p-3 rounded-xl border border-purple-200">
                      <div className="flex items-center space-x-2">
                        <Star size={14} className="text-purple-500" />
                        <span className="text-xs font-medium text-purple-700">
                          HD export
                        </span>
                      </div>
                    </div>
                  </div>
                </div>
              )}
              {/* Project Stats */}
              <div className="bg-gradient-to-r from-slate-50 to-blue-50 p-4 rounded-xl border border-slate-200">
                <h4 className="font-semibold text-gray-800 mb-3 flex items-center">
                  <Brain size={16} className="mr-2 text-slate-600" />
                  Project analysis
                </h4>
                <div className="grid grid-cols-3 gap-4 text-center">
                  <div>
                    <div className="text-xl font-bold text-slate-600">8.5</div>
                    <div className="text-xs text-gray-600 font-medium">
                      Quality
                    </div>
                  </div>
                  <div>
                    <div className="text-xl font-bold text-blue-600">95%</div>
                    <div className="text-xs text-gray-600 font-medium">
                      Readiness
                    </div>
                  </div>
                  <div>
                    <div className="text-xl font-bold text-indigo-600">HD</div>
                    <div className="text-xs text-gray-600 font-medium">
                      Export quality
                    </div>
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>

      {/* AI Editor Modal */}
      {showAiEditor && (
        <div className="fixed inset-0 bg-black/50 backdrop-blur-md flex items-center justify-center z-50 p-4">
          <div className="bg-white/95 backdrop-blur-xl rounded-2xl p-8 w-full max-w-3xl max-h-[85vh] overflow-y-auto border border-slate-200/50 shadow-2xl">
            <div className="flex items-center justify-between mb-8">
              <h3 className="text-2xl font-bold flex items-center bg-gradient-to-r from-slate-700 to-blue-700 bg-clip-text text-transparent">
                <Sparkles className="mr-3 text-slate-600" size={28} />
                AI Text Editor
              </h3>
              <button
                onClick={() => setShowAiEditor(false)}
                className="p-3 hover:bg-slate-100 rounded-xl transition-all duration-300"
              >
                <X size={24} className="text-gray-500" />
              </button>
            </div>
            <div className="space-y-6">
              <div>
                <label className="block text-gray-700 font-semibold mb-3">
                  Selected text:
                </label>
                <div className="p-6 bg-gradient-to-r from-slate-50 to-blue-50 border-2 border-slate-200 rounded-xl text-gray-800 leading-relaxed font-medium shadow-lg">
                  "{selectedText}"
                </div>
              </div>
              <div>
                <label className="block text-gray-700 font-semibold mb-3">
                  How can this segment be improved?
                </label>
                <textarea
                  value={aiPrompt}
                  onChange={(e) => setAiPrompt(e.target.value)}
                  placeholder="For example: Make this segment more interesting and detailed, add examples..."
                  rows={4}
                  className="w-full p-4 bg-white/90 backdrop-blur-sm border-2 border-slate-200 rounded-xl text-gray-900 placeholder-gray-400 focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-blue-500 transition-all duration-300 shadow-lg resize-none font-medium"
                />
              </div>
              <div className="flex space-x-4">
                <button
                  onClick={generateAiText}
                  disabled={
                    !aiPrompt.trim() || isGeneratingText || !isAuthenticated
                  }
                  className="flex items-center px-6 py-3 bg-gradient-to-r from-slate-600 to-blue-600 hover:from-slate-700 hover:to-blue-700 disabled:from-gray-400 disabled:to-gray-500 disabled:cursor-not-allowed text-white rounded-xl transition-all duration-300 shadow-lg hover:shadow-xl hover:scale-105 font-semibold"
                >
                  {isGeneratingText ? (
                    <>
                      <div className="animate-spin rounded-full h-4 w-4 border-b-2 border-white mr-2"></div>
                      Generating...
                    </>
                  ) : (
                    <>
                      <Sparkles className="mr-2" size={16} />
                      Improve with AI
                    </>
                  )}
                </button>
                <button
                  onClick={() => setShowAiEditor(false)}
                  className="px-6 py-3 border border-slate-300 hover:border-slate-400 text-slate-700 rounded-xl transition-all duration-300 bg-white hover:bg-slate-50 shadow-lg font-medium"
                >
                  Cancel
                </button>
              </div>
              {/* Quick Prompts */}
              <div>
                <p className="text-gray-700 font-semibold text-lg mb-4">
                  ⚡ Quick commands:
                </p>
                <div className="grid grid-cols-2 gap-3">
                  {[
                    "Make it more detailed",
                    "Simplify explanation",
                    "Add examples",
                    "Make it more emotional",
                    "Add statistics",
                    "Improve structure",
                  ].map((prompt) => (
                    <button
                      key={prompt}
                      onClick={() => setAiPrompt(prompt)}
                      className="px-4 py-3 text-sm bg-gradient-to-r from-slate-100 to-blue-100 hover:from-slate-200 hover:to-blue-200 text-slate-700 hover:text-blue-700 rounded-xl transition-all duration-300 border border-slate-200 hover:border-blue-300 font-medium shadow-lg hover:shadow-xl"
                    >
                      {prompt}
                    </button>
                  ))}
                </div>
              </div>
            </div>
          </div>
        </div>
      )}

      <TeleprompterModal
        isOpen={showTeleprompter}
        onClose={() => setShowTeleprompter(false)}
        script={script}
        onRecordingComplete={handleTeleprompterRecording}
      />
    </div>
  );
};

export default VideoEditorApp;
