import React, { useState, useRef, useEffect } from "react";
import {
  X,
  Download,
  Check,
  AlertCircle,
  Settings,
  Play,
  Film,
  Zap,
  Monitor,
  Clock
} from "lucide-react";

// –ò–º–ø–æ—Ä—Ç–∏—Ä—É–µ–º –º–µ–Ω–µ–¥–∂–µ—Ä –≤–æ—Ä–∫–µ—Ä–æ–≤
// import VideoExportWorkerManager from './WorkerManager.js';

const OptimizedFFmpegExporter = ({
  isOpen,
  onClose,
  timelineItems = [],
  videoDuration = 60,
}) => {
  const [exportSettings, setExportSettings] = useState({
    resolution: "1280x720",
    fps: 24,
    bitrate: 2000,
    format: "mp4",
    quality: "medium",
    filename: `video_${Date.now()}`,
    compatibilityMode: "standard",
    audioQuality: "standard",
    // –ù–æ–≤—ã–µ –Ω–∞—Å—Ç—Ä–æ–π–∫–∏ –æ–ø—Ç–∏–º–∏–∑–∞—Ü–∏–∏
    workerCount: Math.min(navigator.hardwareConcurrency || 2, 4),
    batchSize: 50, // –ö–∞–¥—Ä–æ–≤ –≤ –±–∞—Ç—á–µ
    parallelEncoding: true,
    useGPU: false, // –î–ª—è –±—É–¥—É—â–µ–≥–æ –∏—Å–ø–æ–ª—å–∑–æ–≤–∞–Ω–∏—è WebGPU
  });

  const [ffmpeg, setFfmpeg] = useState(null);
  const [workerManager, setWorkerManager] = useState(null);
  const [isLoading, setIsLoading] = useState(false);
  const [isExporting, setIsExporting] = useState(false);
  const [exportProgress, setExportProgress] = useState(0);
  const [exportStage, setExportStage] = useState("idle");
  const [previewFrame, setPreviewFrame] = useState(null);
  const [error, setError] = useState(null);
  
  // –°—Ç–∞—Ç–∏—Å—Ç–∏–∫–∞ –ø—Ä–æ–∏–∑–≤–æ–¥–∏—Ç–µ–ª—å–Ω–æ—Å—Ç–∏
  const [perfStats, setPerfStats] = useState({
    framesRendered: 0,
    renderingSpeed: 0,
    estimatedTimeRemaining: 0,
    memoryUsage: 0,
    workerStats: []
  });

  const canvasRef = useRef(null);
  const previewCanvasRef = useRef(null);
  const renderStartTime = useRef(null);

  // –°–∏–º—É–ª—è—Ü–∏—è WorkerManager –¥–ª—è –¥–µ–º–æ–Ω—Å—Ç—Ä–∞—Ü–∏–∏
  const createMockWorkerManager = () => ({
    initialize: async (config) => {
      console.log('üöÄ –ò–Ω–∏—Ü–∏–∞–ª–∏–∑–∞—Ü–∏—è –≤–æ—Ä–∫–µ—Ä–æ–≤ (–º–æ–∫):', config);
      await new Promise(resolve => setTimeout(resolve, 2000));
    },
    preloadMedia: async (mediaItems) => {
      console.log('üì¶ –ü—Ä–µ–¥–∑–∞–≥—Ä—É–∑–∫–∞ –º–µ–¥–∏–∞ (–º–æ–∫):', mediaItems.length);
      await new Promise(resolve => setTimeout(resolve, 1000));
    },
    renderVideo: async (timelineItems, totalFrames, fps) => {
      console.log('üé¨ –†–µ–Ω–¥–µ—Ä–∏–Ω–≥ –≤–∏–¥–µ–æ (–º–æ–∫):', totalFrames, '–∫–∞–¥—Ä–æ–≤');
      const frames = [];
      
      for (let i = 0; i < totalFrames; i++) {
        // –°–∏–º—É–ª—è—Ü–∏—è —Ä–µ–Ω–¥–µ—Ä–∏–Ω–≥–∞
        await new Promise(resolve => setTimeout(resolve, 10));
        
        frames.push({
          frameIndex: i,
          blob: new Blob(['mock frame data'], { type: 'image/jpeg' }),
          timestamp: i / fps
        });

        // –û–±–Ω–æ–≤–ª—è–µ–º –ø—Ä–æ–≥—Ä–µ—Å—Å
        if (i % 10 === 0) {
          const progress = (i / totalFrames) * 100;
          setExportProgress(30 + progress * 0.4); // 30-70% –¥–ª—è —Ä–µ–Ω–¥–µ—Ä–∏–Ω–≥–∞
          
          setPerfStats(prev => ({
            ...prev,
            framesRendered: i,
            renderingSpeed: i / ((Date.now() - renderStartTime.current) / 1000),
            estimatedTimeRemaining: ((totalFrames - i) / (i / ((Date.now() - renderStartTime.current) / 1000))) || 0
          }));
        }
      }
      
      return frames;
    },
    cleanup: async () => {
      console.log('üßπ –û—á–∏—Å—Ç–∫–∞ –≤–æ—Ä–∫–µ—Ä–æ–≤ (–º–æ–∫)');
    },
    getWorkerStats: () => ({
      workerCount: exportSettings.workerCount,
      isInitialized: true,
      framesCached: 0,
      workers: Array.from({ length: exportSettings.workerCount }, (_, i) => ({
        id: i,
        busy: false,
        lastActivity: 0
      }))
    })
  });

  // –ò–Ω–∏—Ü–∏–∞–ª–∏–∑–∞—Ü–∏—è FFmpeg –∏ –≤–æ—Ä–∫–µ—Ä–æ–≤
  useEffect(() => {
    const initializeExporter = async () => {
      if (!isOpen || workerManager) return;

      setIsLoading(true);
      setExportStage("loading");
      setError(null);

      try {
        console.log("üîÑ –ò–Ω–∏—Ü–∏–∞–ª–∏–∑–∞—Ü–∏—è –æ–ø—Ç–∏–º–∏–∑–∏—Ä–æ–≤–∞–Ω–Ω–æ–≥–æ —ç–∫—Å–ø–æ—Ä—Ç–µ—Ä–∞...");

        // –ò–Ω–∏—Ü–∏–∞–ª–∏–∑–∏—Ä—É–µ–º FFmpeg
        const ffmpegModule = await import("@ffmpeg/ffmpeg");
        const utilModule = await import("@ffmpeg/util");
        const FFmpegClass = ffmpegModule.FFmpeg;
        const fetchFile = utilModule.fetchFile;

        const ffmpegInstance = new FFmpegClass();
        await ffmpegInstance.load({
          coreURL: "https://unpkg.com/@ffmpeg/core@0.12.6/dist/umd/ffmpeg-core.js",
          wasmURL: "https://unpkg.com/@ffmpeg/core@0.12.6/dist/umd/ffmpeg-core.wasm",
        });

        window.fetchFile = fetchFile;
        setFfmpeg(ffmpegInstance);

        // –ò–Ω–∏—Ü–∏–∞–ª–∏–∑–∏—Ä—É–µ–º –º–µ–Ω–µ–¥–∂–µ—Ä –≤–æ—Ä–∫–µ—Ä–æ–≤
        const manager = createMockWorkerManager(); // –í —Ä–µ–∞–ª—å–Ω–æ–º –ø—Ä–æ–µ–∫—Ç–µ: new VideoExportWorkerManager();
        
        const [width, height] = exportSettings.resolution.split("x").map(Number);
        await manager.initialize({
          width,
          height,
          fps: exportSettings.fps,
          workerCount: exportSettings.workerCount
        });

        setWorkerManager(manager);
        setExportStage("ready");
        
        console.log("‚úÖ –û–ø—Ç–∏–º–∏–∑–∏—Ä–æ–≤–∞–Ω–Ω—ã–π —ç–∫—Å–ø–æ—Ä—Ç–µ—Ä –≥–æ—Ç–æ–≤");
        
      } catch (error) {
        console.error("‚ùå –û—à–∏–±–∫–∞ –∏–Ω–∏—Ü–∏–∞–ª–∏–∑–∞—Ü–∏–∏:", error);
        setError(error.message);
        setExportStage("error");
      } finally {
        setIsLoading(false);
      }
    };

    if (isOpen) {
      initializeExporter();
    }

    return () => {
      if (workerManager) {
        workerManager.cleanup();
      }
    };
  }, [isOpen, exportSettings.workerCount]);

  // –û—Å–Ω–æ–≤–Ω–∞—è —Ñ—É–Ω–∫—Ü–∏—è —ç–∫—Å–ø–æ—Ä—Ç–∞
  const startOptimizedExport = async () => {
    if (!ffmpeg || !workerManager || isExporting || timelineItems.length === 0) return;

    setIsExporting(true);
    setExportProgress(0);
    setExportStage("preparing");
    setError(null);
    renderStartTime.current = Date.now();

    try {
      console.log("üöÄ –ù–∞—á–∏–Ω–∞–µ–º –æ–ø—Ç–∏–º–∏–∑–∏—Ä–æ–≤–∞–Ω–Ω—ã–π —ç–∫—Å–ø–æ—Ä—Ç...");

      const totalDuration = calculateTotalDuration();
      const totalFrames = Math.ceil(totalDuration * exportSettings.fps);
      
      console.log(`üìä –ü–∞—Ä–∞–º–µ—Ç—Ä—ã —ç–∫—Å–ø–æ—Ä—Ç–∞:
        ‚Ä¢ –ö–∞–¥—Ä–æ–≤: ${totalFrames}
        ‚Ä¢ FPS: ${exportSettings.fps}
        ‚Ä¢ –í–æ—Ä–∫–µ—Ä–æ–≤: ${exportSettings.workerCount}
        ‚Ä¢ –ë–∞—Ç—á —Ä–∞–∑–º–µ—Ä: ${exportSettings.batchSize}`);

      // –≠—Ç–∞–ø 1: –ü—Ä–µ–¥–∑–∞–≥—Ä—É–∑–∫–∞ –º–µ–¥–∏–∞
      setExportStage("preloading_media");
      setExportProgress(5);
      
      const videoItems = timelineItems.filter(item => item.trackType !== "audio");
      await workerManager.preloadMedia(videoItems);
      setExportProgress(15);

      // –≠—Ç–∞–ø 2: –ü–∞—Ä–∞–ª–ª–µ–ª—å–Ω—ã–π —Ä–µ–Ω–¥–µ—Ä–∏–Ω–≥ –∫–∞–¥—Ä–æ–≤
      setExportStage("parallel_rendering");
      setExportProgress(20);

      const renderedFrames = await workerManager.renderVideo(
        timelineItems,
        totalFrames,
        exportSettings.fps
      );
      setExportProgress(70);

      // –≠—Ç–∞–ø 3: –ü–æ–¥–≥–æ—Ç–æ–≤–∫–∞ –∞—É–¥–∏–æ
      setExportStage("processing_audio");
      const audioFiles = await prepareAudioFiles();
      setExportProgress(75);

      // –≠—Ç–∞–ø 4: –ö–æ–¥–∏—Ä–æ–≤–∞–Ω–∏–µ —Å FFmpeg
      setExportStage("encoding");
      const videoBlob = await encodeWithOptimizedFFmpeg(
        renderedFrames,
        audioFiles,
        totalDuration
      );
      setExportProgress(95);

      // –≠—Ç–∞–ø 5: –ó–∞–≥—Ä—É–∑–∫–∞
      setExportStage("downloading");
      await downloadVideo(videoBlob);
      setExportProgress(100);

      setExportStage("completed");
      
      const totalTime = (Date.now() - renderStartTime.current) / 1000;
      console.log(`‚úÖ –≠–∫—Å–ø–æ—Ä—Ç –∑–∞–≤–µ—Ä—à–µ–Ω –∑–∞ ${totalTime.toFixed(1)}—Å (${(totalFrames / totalTime).toFixed(1)} FPS)`);

      setTimeout(() => {
        setExportStage("ready");
        setExportProgress(0);
      }, 3000);

    } catch (error) {
      console.error("‚ùå –û—à–∏–±–∫–∞ –æ–ø—Ç–∏–º–∏–∑–∏—Ä–æ–≤–∞–Ω–Ω–æ–≥–æ —ç–∫—Å–ø–æ—Ä—Ç–∞:", error);
      setError(error.message);
      setExportStage("error");
    } finally {
      setIsExporting(false);
    }
  };

  const calculateTotalDuration = () => {
    if (timelineItems.length === 0) return videoDuration;
    
    const maxEndTime = Math.max(
      ...timelineItems.map(item => (item.startTime || 0) + (item.duration || 0))
    );
    
    return Math.max(maxEndTime, videoDuration);
  };

  const prepareAudioFiles = async () => {
    const audioItems = timelineItems.filter(item => item.trackType === "audio");
    const audioFiles = [];

    for (let i = 0; i < Math.min(audioItems.length, 3); i++) {
      const item = audioItems[i];
      try {
        const response = await fetch(item.url);
        if (!response.ok) throw new Error(`HTTP ${response.status}`);

        const blob = await response.blob();
        audioFiles.push({
          blob,
          startTime: item.startTime || 0,
          duration: item.duration || videoDuration,
          name: item.name,
          index: i,
        });
      } catch (error) {
        console.warn(`‚ö†Ô∏è –û—à–∏–±–∫–∞ –∑–∞–≥—Ä—É–∑–∫–∏ –∞—É–¥–∏–æ ${item.name}:`, error);
      }
    }

    return audioFiles;
  };

  const encodeWithOptimizedFFmpeg = async (frames, audioFiles, duration) => {
    if (!window.fetchFile || frames.length === 0) {
      throw new Error("–ù–µ—Ç –∫–∞–¥—Ä–æ–≤ –¥–ª—è –∫–æ–¥–∏—Ä–æ–≤–∞–Ω–∏—è");
    }

    try {
      console.log(`üéûÔ∏è –û–ø—Ç–∏–º–∏–∑–∏—Ä–æ–≤–∞–Ω–Ω–æ–µ –∫–æ–¥–∏—Ä–æ–≤–∞–Ω–∏–µ ${frames.length} –∫–∞–¥—Ä–æ–≤...`);

      // –ü–∞—Ä–∞–ª–ª–µ–ª—å–Ω–∞—è –∑–∞–ø–∏—Å—å –∫–∞–¥—Ä–æ–≤
      const writePromises = frames.map(async (frame, index) => {
        const frameData = await window.fetchFile(frame.blob);
        const filename = `frame_${index.toString().padStart(6, "0")}.jpg`;
        await ffmpeg.writeFile(filename, frameData);
        
        if (index % 100 === 0) {
          const progress = 75 + Math.round((index / frames.length) * 15);
          setExportProgress(progress);
        }
      });

      await Promise.all(writePromises);

      // –ó–∞–ø–∏—Å—å –∞—É–¥–∏–æ
      for (let i = 0; i < audioFiles.length; i++) {
        const audioFile = audioFiles[i];
        const audioFileName = `audio_${i}.mp3`;
        await ffmpeg.writeFile(
          audioFileName,
          await window.fetchFile(audioFile.blob)
        );
      }

      setExportProgress(90);

      // –û–ø—Ç–∏–º–∏–∑–∏—Ä–æ–≤–∞–Ω–Ω—ã–µ –∞—Ä–≥—É–º–µ–Ω—Ç—ã FFmpeg
      const args = [
        "-framerate", exportSettings.fps.toString(),
        "-i", "frame_%06d.jpg",
        "-t", Math.min(duration, frames.length / exportSettings.fps).toString()
      ];

      if (audioFiles.length > 0) {
        args.push("-i", "audio_0.mp3");
        args.push("-c:a", "aac");
        args.push("-b:a", "128k");
        args.push("-ar", "44100");
        args.push("-shortest");
      }

      // –û–ø—Ç–∏–º–∏–∑–∏—Ä–æ–≤–∞–Ω–Ω–æ–µ –∫–æ–¥–∏—Ä–æ–≤–∞–Ω–∏–µ
      args.push(
        "-c:v", "libx264",
        "-preset", exportSettings.parallelEncoding ? "ultrafast" : "medium",
        "-crf", "23",
        "-pix_fmt", "yuv420p",
        "-threads", exportSettings.workerCount.toString(),
        "-movflags", "+faststart",
        "output.mp4"
      );

      console.log("üîß –û–ø—Ç–∏–º–∏–∑–∏—Ä–æ–≤–∞–Ω–Ω–∞—è –∫–æ–º–∞–Ω–¥–∞ FFmpeg:", args.join(" "));

      await ffmpeg.exec(args);

      const data = await ffmpeg.readFile("output.mp4");
      const videoBlob = new Blob([data.buffer], { type: "video/mp4" });

      // –û—á–∏—Å—Ç–∫–∞
      await cleanupTempFiles(frames.length, audioFiles.length);
      
      return videoBlob;

    } catch (error) {
      throw new Error(`–û—à–∏–±–∫–∞ –æ–ø—Ç–∏–º–∏–∑–∏—Ä–æ–≤–∞–Ω–Ω–æ–≥–æ –∫–æ–¥–∏—Ä–æ–≤–∞–Ω–∏—è: ${error.message}`);
    }
  };

  const cleanupTempFiles = async (frameCount, audioCount) => {
    console.log("üßπ –û—á–∏—Å—Ç–∫–∞ –≤—Ä–µ–º–µ–Ω–Ω—ã—Ö —Ñ–∞–π–ª–æ–≤...");

    for (let i = 0; i < frameCount; i++) {
      try {
        await ffmpeg.deleteFile(`frame_${i.toString().padStart(6, "0")}.jpg`);
      } catch (e) {}
    }

    for (let i = 0; i < audioCount; i++) {
      try {
        await ffmpeg.deleteFile(`audio_${i}.mp3`);
      } catch (e) {}
    }

    try {
      await ffmpeg.deleteFile("output.mp4");
    } catch (e) {}
  };

  const downloadVideo = async (blob) => {
    const filename = `${exportSettings.filename}.${exportSettings.format}`;
    const url = URL.createObjectURL(blob);
    const a = document.createElement("a");
    a.href = url;
    a.download = filename;
    document.body.appendChild(a);
    a.click();
    document.body.removeChild(a);
    URL.revokeObjectURL(url);
  };

  const getStageText = () => {
    switch (exportStage) {
      case "loading":
        return "üîÑ –ò–Ω–∏—Ü–∏–∞–ª–∏–∑–∞—Ü–∏—è –≤–æ—Ä–∫–µ—Ä–æ–≤ –∏ FFmpeg...";
      case "preparing":
        return "üìã –ê–Ω–∞–ª–∏–∑ timeline –∏ –ø–æ–¥–≥–æ—Ç–æ–≤–∫–∞...";
      case "preloading_media":
        return "üì¶ –ü—Ä–µ–¥–∑–∞–≥—Ä—É–∑–∫–∞ –º–µ–¥–∏–∞ –≤ –≤–æ—Ä–∫–µ—Ä—ã...";
      case "parallel_rendering":
        return `üé≠ –ü–∞—Ä–∞–ª–ª–µ–ª—å–Ω—ã–π —Ä–µ–Ω–¥–µ—Ä–∏–Ω–≥ (${exportSettings.workerCount} –≤–æ—Ä–∫–µ—Ä–æ–≤)...`;
      case "processing_audio":
        return "üéµ –û–±—Ä–∞–±–æ—Ç–∫–∞ –∞—É–¥–∏–æ —Ç—Ä–µ–∫–æ–≤...";
      case "encoding":
        return "‚ö° –ë—ã—Å—Ç—Ä–æ–µ –∫–æ–¥–∏—Ä–æ–≤–∞–Ω–∏–µ...";
      case "downloading":
        return "üíæ –°–æ—Ö—Ä–∞–Ω–µ–Ω–∏–µ —Ñ–∞–π–ª–∞...";
      case "completed":
        return "‚úÖ –û–ø—Ç–∏–º–∏–∑–∏—Ä–æ–≤–∞–Ω–Ω—ã–π —ç–∫—Å–ø–æ—Ä—Ç –∑–∞–≤–µ—Ä—à–µ–Ω!";
      case "error":
        return "‚ùå –ü—Ä–æ–∏–∑–æ—à–ª–∞ –æ—à–∏–±–∫–∞";
      case "ready":
        return `üöÄ –ì–æ—Ç–æ–≤ –∫ –±—ã—Å—Ç—Ä–æ–º—É —ç–∫—Å–ø–æ—Ä—Ç—É (${timelineItems.length} —ç–ª–µ–º–µ–Ω—Ç–æ–≤)`;
      default:
        return "‚è≥ –û–∂–∏–¥–∞–Ω–∏–µ...";
    }
  };

  const formatTime = (seconds) => {
    const minutes = Math.floor(seconds / 60);
    const secs = Math.floor(seconds % 60);
    return `${minutes}:${secs.toString().padStart(2, '0')}`;
  };

  const canExport = ffmpeg && workerManager && !isExporting && !isLoading && 
                   timelineItems.length > 0 && exportStage === "ready";

  if (!isOpen) return null;

  return (
    <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-[9999] p-4">
      <div className="bg-white rounded-2xl shadow-xl max-w-5xl w-full max-h-[95vh] overflow-hidden">
        <div className="flex items-center justify-between p-6 border-b border-gray-200">
          <h2 className="text-xl font-semibold text-gray-800 flex items-center">
            <Zap size={24} className="mr-3 text-yellow-500" />
            ‚ö° –ë–´–°–¢–†–´–ô –≠–ö–°–ü–û–†–¢ –° –í–û–†–ö–ï–†–ê–ú–ò
            <span className="ml-3 text-sm bg-green-100 text-green-800 px-2 py-1 rounded-full">
              {exportSettings.workerCount}x –±—ã—Å—Ç—Ä–µ–µ
            </span>
          </h2>
          <button
            onClick={onClose}
            className="p-2 hover:bg-gray-100 rounded-full transition-colors"
            disabled={isExporting}
          >
            <X size={20} />
          </button>
        </div>

        <div className="p-6 space-y-6 max-h-[calc(95vh-140px)] overflow-y-auto">
          {/* –°—Ç–∞—Ç—É—Å –∏ –ø—Ä–æ–≥—Ä–µ—Å—Å */}
          <div className={`rounded-xl p-4 border ${
            exportStage === "error" ? "bg-red-50 border-red-200" :
            exportStage === "completed" ? "bg-green-50 border-green-200" :
            exportStage === "loading" ? "bg-yellow-50 border-yellow-200" :
            "bg-blue-50 border-blue-200"
          }`}>
            <div className="flex items-center justify-between mb-3">
              <div className="flex items-center">
                {exportStage === "completed" ? 
                  <Check size={20} className="text-green-600" /> :
                  exportStage === "error" ? 
                  <AlertCircle size={20} className="text-red-600" /> :
                  <div className="animate-spin rounded-full h-5 w-5 border-b-2 border-blue-600"></div>
                }
                <span className="ml-3 font-medium text-gray-900">
                  {getStageText()}
                </span>
              </div>
              {(isExporting || isLoading) && (
                <span className="text-sm font-medium text-blue-700">
                  {exportProgress}%
                </span>
              )}
            </div>

            {(isExporting || isLoading) && (
              <div className="w-full rounded-full h-2 bg-blue-200">
                <div
                  className="h-2 rounded-full transition-all duration-300 bg-blue-600"
                  style={{ width: `${exportProgress}%` }}
                />
              </div>
            )}

            {/* –°—Ç–∞—Ç–∏—Å—Ç–∏–∫–∞ –ø—Ä–æ–∏–∑–≤–æ–¥–∏—Ç–µ–ª—å–Ω–æ—Å—Ç–∏ */}
            {isExporting && perfStats.framesRendered > 0 && (
              <div className="mt-3 grid grid-cols-3 gap-4 text-sm">
                <div>
                  <span className="text-gray-600">–°–∫–æ—Ä–æ—Å—Ç—å:</span>
                  <span className="ml-1 font-medium">
                    {perfStats.renderingSpeed.toFixed(1)} FPS
                  </span>
                </div>
                <div>
                  <span className="text-gray-600">–û—Å—Ç–∞–ª–æ—Å—å:</span>
                  <span className="ml-1 font-medium">
                    {formatTime(perfStats.estimatedTimeRemaining)}
                  </span>
                </div>
                <div>
                  <span className="text-gray-600">–ö–∞–¥—Ä–æ–≤:</span>
                  <span className="ml-1 font-medium">
                    {perfStats.framesRendered}
                  </span>
                </div>
              </div>
            )}
          </div>

          {/* –ù–∞—Å—Ç—Ä–æ–π–∫–∏ –æ–ø—Ç–∏–º–∏–∑–∞—Ü–∏–∏ */}
          <div className="bg-gray-50 rounded-xl p-4">
            <h3 className="text-sm font-medium text-gray-700 mb-3 flex items-center">
              <Monitor size={16} className="mr-2" />
              ‚ö° –ù–∞—Å—Ç—Ä–æ–π–∫–∏ –æ–ø—Ç–∏–º–∏–∑–∞—Ü–∏–∏
            </h3>
            
            <div className="grid grid-cols-3 gap-4 mb-4">
              <div>
                <label className="block text-xs font-medium text-gray-700 mb-1">
                  –ö–æ–ª–∏—á–µ—Å—Ç–≤–æ –≤–æ—Ä–∫–µ—Ä–æ–≤
                </label>
                <select
                  value={exportSettings.workerCount}
                  onChange={(e) => setExportSettings(prev => ({
                    ...prev,
                    workerCount: parseInt(e.target.value)
                  }))}
                  className="w-full p-2 text-sm border border-gray-300 rounded-lg"
                  disabled={isExporting || isLoading}
                >
                  <option value="1">1 –≤–æ—Ä–∫–µ—Ä (–º–µ–¥–ª–µ–Ω–Ω–æ)</option>
                  <option value="2">2 –≤–æ—Ä–∫–µ—Ä–∞ (–±—ã—Å—Ç—Ä–æ)</option>
                  <option value="4">4 –≤–æ—Ä–∫–µ—Ä–∞ (–æ—á–µ–Ω—å –±—ã—Å—Ç—Ä–æ)</option>
                  <option value="6">6 –≤–æ—Ä–∫–µ—Ä–æ–≤ (–º–∞–∫—Å–∏–º—É–º)</option>
                </select>
              </div>

              <div>
                <label className="block text-xs font-medium text-gray-700 mb-1">
                  –†–∞–∑–º–µ—Ä –±–∞—Ç—á–∞
                </label>
                <select
                  value={exportSettings.batchSize}
                  onChange={(e) => setExportSettings(prev => ({
                    ...prev,
                    batchSize: parseInt(e.target.value)
                  }))}
                  className="w-full p-2 text-sm border border-gray-300 rounded-lg"
                  disabled={isExporting || isLoading}
                >
                  <option value="25">25 –∫–∞–¥—Ä–æ–≤ (–º–µ–Ω—å—à–µ –ø–∞–º—è—Ç–∏)</option>
                  <option value="50">50 –∫–∞–¥—Ä–æ–≤ (–æ–ø—Ç–∏–º–∞–ª—å–Ω–æ)</option>
                  <option value="100">100 –∫–∞–¥—Ä–æ–≤ (–±—ã—Å—Ç—Ä–µ–µ)</option>
                </select>
              </div>

              <div>
                <label className="block text-xs font-medium text-gray-700 mb-1">
                  –†–µ–∂–∏–º –∫–æ–¥–∏—Ä–æ–≤–∞–Ω–∏—è
                </label>
                <select
                  value={exportSettings.parallelEncoding}
                  onChange={(e) => setExportSettings(prev => ({
                    ...prev,
                    parallelEncoding: e.target.value === 'true'
                  }))}
                  className="w-full p-2 text-sm border border-gray-300 rounded-lg"
                  disabled={isExporting || isLoading}
                >
                  <option value="true">–ë—ã—Å—Ç—Ä–æ–µ (ultrafast)</option>
                  <option value="false">–ö–∞—á–µ—Å—Ç–≤–µ–Ω–Ω–æ–µ (medium)</option>
                </select>
              </div>
            </div>

            {/* –û—Å–Ω–æ–≤–Ω—ã–µ –Ω–∞—Å—Ç—Ä–æ–π–∫–∏ */}
            <div className="grid grid-cols-4 gap-4">
              <div>
                <label className="block text-xs font-medium text-gray-700 mb-1">
                  –†–∞–∑—Ä–µ—à–µ–Ω–∏–µ
                </label>
                <select
                  value={exportSettings.resolution}
                  onChange={(e) => setExportSettings(prev => ({
                    ...prev,
                    resolution: e.target.value
                  }))}
                  className="w-full p-2 text-sm border border-gray-300 rounded-lg"
                  disabled={isExporting || isLoading}
                >
                  <option value="640x480">480p (—Å–∞–º—ã–π –±—ã—Å—Ç—Ä—ã–π)</option>
                  <option value="1280x720">720p (–±—ã—Å—Ç—Ä–æ)</option>
                  <option value="1920x1080">1080p (–º–µ–¥–ª–µ–Ω–Ω–µ–µ)</option>
                </select>
              </div>

              <div>
                <label className="block text-xs font-medium text-gray-700 mb-1">
                  FPS
                </label>
                <select
                  value={exportSettings.fps}
                  onChange={(e) => setExportSettings(prev => ({
                    ...prev,
                    fps: parseInt(e.target.value)
                  }))}
                  className="w-full p-2 text-sm border border-gray-300 rounded-lg"
                  disabled={isExporting || isLoading}
                >
                  <option value="24">24 FPS</option>
                  <option value="30">30 FPS</option>
                  <option value="60">60 FPS</option>
                </select>
              </div>

              <div>
                <label className="block text-xs font-medium text-gray-700 mb-1">
                  –°–æ–≤–º–µ—Å—Ç–∏–º–æ—Å—Ç—å
                </label>
                <select
                  value={exportSettings.compatibilityMode}
                  onChange={(e) => setExportSettings(prev => ({
                    ...prev,
                    compatibilityMode: e.target.value
                  }))}
                  className="w-full p-2 text-sm border border-gray-300 rounded-lg"
                  disabled={isExporting || isLoading}
                >
                  <option value="standard">–°—Ç–∞–Ω–¥–∞—Ä—Ç–Ω–∞—è</option>
                  <option value="baseline">–ú–∞–∫—Å–∏–º–∞–ª—å–Ω–∞—è</option>
                </select>
              </div>

              <div>
                <label className="block text-xs font-medium text-gray-700 mb-1">
                  –ò–º—è —Ñ–∞–π–ª–∞
                </label>
                <input
                  type="text"
                  value={exportSettings.filename}
                  onChange={(e) => setExportSettings(prev => ({
                    ...prev,
                    filename: e.target.value
                  }))}
                  className="w-full p-2 text-sm border border-gray-300 rounded-lg"
                  disabled={isExporting || isLoading}
                />
              </div>
            </div>
          </div>

          {/* –î–∏–∞–≥–Ω–æ—Å—Ç–∏–∫–∞ */}
          {timelineItems.length > 0 && (
            <div className="bg-blue-50 border border-blue-200 rounded-xl p-4">
              <h4 className="font-medium text-blue-800 mb-2 flex items-center">
                <Clock size={16} className="mr-2" />
                üìä –û—Ü–µ–Ω–∫–∞ –ø—Ä–æ–∏–∑–≤–æ–¥–∏—Ç–µ–ª—å–Ω–æ—Å—Ç–∏
              </h4>
              
              <div className="grid grid-cols-2 gap-4 text-sm">
                <div className="space-y-1">
                  <p><strong>–≠–ª–µ–º–µ–Ω—Ç–æ–≤:</strong> {timelineItems.length}</p>
                  <p><strong>–î–ª–∏—Ç–µ–ª—å–Ω–æ—Å—Ç—å:</strong> {calculateTotalDuration().toFixed(1)}—Å</p>
                  <p><strong>–ö–∞–¥—Ä–æ–≤ –≤—Å–µ–≥–æ:</strong> {Math.ceil(calculateTotalDuration() * exportSettings.fps)}</p>
                  <p><strong>–í–æ—Ä–∫–µ—Ä–æ–≤:</strong> {exportSettings.workerCount}</p>
                </div>
                
                <div className="space-y-1">
                  <p><strong>–û—Ü–µ–Ω–∫–∞ –≤—Ä–µ–º–µ–Ω–∏:</strong> ~{Math.ceil(calculateTotalDuration() * exportSettings.fps / (exportSettings.workerCount * 30))}—Å</p>
                  <p><strong>–£—Å–∫–æ—Ä–µ–Ω–∏–µ:</strong> {exportSettings.workerCount}x</p>
                  <p><strong>–ü–∞–º—è—Ç—å:</strong> ~{Math.ceil(calculateTotalDuration() * exportSettings.fps * 0.1)}MB</p>
                  <p><strong>–†–µ–∂–∏–º:</strong> {exportSettings.parallelEncoding ? '–ë—ã—Å—Ç—Ä—ã–π' : '–ö–∞—á–µ—Å—Ç–≤–µ–Ω–Ω—ã–π'}</p>
                </div>
              </div>
            </div>
          )}

          {error && (
            <div className="bg-red-50 border border-red-200 rounded-xl p-4">
              <div className="flex items-start">
                <AlertCircle size={20} className="text-red-600 mr-3 mt-0.5" />
                <div>
                  <h4 className="font-medium text-red-800">–û—à–∏–±–∫–∞ —ç–∫—Å–ø–æ—Ä—Ç–∞</h4>
                  <p className="text-sm text-red-600 mt-1">{error}</p>
                </div>
              </div>
            </div>
          )}
        </div>

        <div className="flex justify-between items-center p-6 border-t border-gray-200 bg-gray-50">
          <div className="text-xs text-gray-500">
            {exportStage === "ready" && timelineItems.length > 0
              ? `‚ö° –ì–æ—Ç–æ–≤ –∫ –±—ã—Å—Ç—Ä–æ–º—É —ç–∫—Å–ø–æ—Ä—Ç—É ‚Ä¢ ${exportSettings.workerCount} –≤–æ—Ä–∫–µ—Ä–æ–≤ ‚Ä¢ ${exportSettings.parallelEncoding ? '–ë—ã—Å—Ç—Ä—ã–π' : '–ö–∞—á–µ—Å—Ç–≤–µ–Ω–Ω—ã–π'} —Ä–µ–∂–∏–º`
              : exportStage === "loading"
              ? "üîÑ –ò–Ω–∏—Ü–∏–∞–ª–∏–∑–∞—Ü–∏—è –≤–æ—Ä–∫–µ—Ä–æ–≤..."
              : "‚è≥ –ü–æ–¥–≥–æ—Ç–æ–≤–∫–∞ —Å–∏—Å—Ç–µ–º—ã"}
          </div>
          
          <div className="flex space-x-3">
            <button
              onClick={onClose}
              className="px-6 py-2 text-sm text-gray-600 hover:text-gray-800 transition-colors"
              disabled={isExporting}
            >
              –ó–∞–∫—Ä—ã—Ç—å
            </button>
            
            <button
              onClick={startOptimizedExport}
              disabled={!canExport}
              className={`px-6 py-2 text-sm rounded-lg font-medium transition-all flex items-center ${
                !canExport
                  ? "bg-gray-300 text-gray-500 cursor-not-allowed"
                  : "bg-gradient-to-r from-yellow-500 to-orange-500 text-white hover:from-yellow-600 hover:to-orange-600 shadow-lg"
              }`}
            >
              {isExporting ? (
                <>
                  <div className="animate-spin rounded-full h-4 w-4 border-b-2 border-white mr-2"></div>
                  –≠–∫—Å–ø–æ—Ä—Ç... {exportProgress}%
                </>
              ) : isLoading ? (
                <>
                  <div className="animate-spin rounded-full h-4 w-4 border-b-2 border-white mr-2"></div>
                  –ó–∞–≥—Ä—É–∑–∫–∞...
                </>
              ) : (
                <>
                  <Zap size={16} className="mr-2" />
                  ‚ö° –ë—ã—Å—Ç—Ä—ã–π —ç–∫—Å–ø–æ—Ä—Ç
                </>
              )}
            </button>
          </div>
        </div>

        <canvas ref={canvasRef} style={{ display: "none" }} />
        <canvas ref={previewCanvasRef} style={{ display: "none" }} />
      </div>
    </div>
  );
};

export default OptimizedFFmpegExporter;
