import React, { useState } from "react";
import { X, Sparkles, Image, ArrowRight, Settings, Download } from "lucide-react";

const ThumbnailCreator = ({ isOpen, onClose, exportSettings }) => {
  const [thumbnailSettings, setThumbnailSettings] = useState({
    style: "youtube",
    text: "",
    theme: "bright",
    size: "1280x720",
    aiPrompt: "",
    includeTitle: true,
    includeEmoji: true
  });

  const [isGeneratingThumbnail, setIsGeneratingThumbnail] = useState(false);
  const [generatedThumbnail, setGeneratedThumbnail] = useState(null);

  // –§—É–Ω–∫—Ü–∏—è —Å–æ–∑–¥–∞–Ω–∏—è thumbnail —Å AI
  const generateThumbnailWithAI = async () => {
    setIsGeneratingThumbnail(true);
    
    try {
      const canvas = document.createElement('canvas');
      const ctx = canvas.getContext('2d');
      
      const [width, height] = thumbnailSettings.size.split('x').map(Number);
      canvas.width = width;
      canvas.height = height;
      
      // –°–æ–∑–¥–∞–µ–º –∫—Ä–∞—Å–∏–≤—ã–π –≥—Ä–∞–¥–∏–µ–Ω—Ç–Ω—ã–π —Ñ–æ–Ω
      const gradient = ctx.createLinearGradient(0, 0, width, height);
      if (thumbnailSettings.theme === 'bright') {
        gradient.addColorStop(0, '#FF6B6B');
        gradient.addColorStop(0.5, '#4ECDC4');
        gradient.addColorStop(1, '#45B7D1');
      } else if (thumbnailSettings.theme === 'dark') {
        gradient.addColorStop(0, '#2C3E50');
        gradient.addColorStop(0.5, '#3498DB');
        gradient.addColorStop(1, '#8E44AD');
      } else if (thumbnailSettings.theme === 'warm') {
        gradient.addColorStop(0, '#FF9500');
        gradient.addColorStop(0.5, '#FF6B35');
        gradient.addColorStop(1, '#F7931E');
      } else {
        gradient.addColorStop(0, '#1E3A8A');
        gradient.addColorStop(0.5, '#3B82F6');
        gradient.addColorStop(1, '#60A5FA');
      }
      
      ctx.fillStyle = gradient;
      ctx.fillRect(0, 0, width, height);
      
      // –î–æ–±–∞–≤–ª—è–µ–º —Ç–µ–∫—Å—Ç –µ—Å–ª–∏ —É–∫–∞–∑–∞–Ω
      if (thumbnailSettings.text || thumbnailSettings.aiPrompt) {
        const text = thumbnailSettings.text || thumbnailSettings.aiPrompt;
        ctx.fillStyle = '#FFFFFF';
        ctx.font = `bold ${Math.min(width / 15, 80)}px Arial`;
        ctx.textAlign = 'center';
        ctx.textBaseline = 'middle';
        
        // –î–æ–±–∞–≤–ª—è–µ–º —Ç–µ–Ω—å –¥–ª—è —Ç–µ–∫—Å—Ç–∞
        ctx.shadowColor = 'rgba(0, 0, 0, 0.7)';
        ctx.shadowBlur = 10;
        ctx.shadowOffsetX = 3;
        ctx.shadowOffsetY = 3;
        
        ctx.fillText(text, width / 2, height / 2);
        
        // –î–æ–±–∞–≤–ª—è–µ–º —ç–º–æ–¥–∑–∏ –µ—Å–ª–∏ –Ω—É–∂–Ω–æ
        if (thumbnailSettings.includeEmoji) {
          ctx.font = `${Math.min(width / 10, 120)}px Arial`;
          ctx.fillText('üé¨', width / 2, height / 2 - height / 4);
        }
      }
      
      // –î–æ–±–∞–≤–ª—è–µ–º –¥–µ–∫–æ—Ä–∞—Ç–∏–≤–Ω—ã–µ —ç–ª–µ–º–µ–Ω—Ç—ã
      ctx.shadowColor = 'transparent';
      ctx.fillStyle = 'rgba(255, 255, 255, 0.2)';
      for (let i = 0; i < 10; i++) {
        const x = Math.random() * width;
        const y = Math.random() * height;
        const radius = Math.random() * 30 + 10;
        ctx.beginPath();
        ctx.arc(x, y, radius, 0, Math.PI * 2);
        ctx.fill();
      }
      
      // –ö–æ–Ω–≤–µ—Ä—Ç–∏—Ä—É–µ–º –≤ blob
      canvas.toBlob((blob) => {
        if (blob) {
          const url = URL.createObjectURL(blob);
          setGeneratedThumbnail(url);
        }
        setIsGeneratingThumbnail(false);
      }, 'image/png');
      
    } catch (error) {
      console.error('–û—à–∏–±–∫–∞ —Å–æ–∑–¥–∞–Ω–∏—è thumbnail:', error);
      setIsGeneratingThumbnail(false);
    }
  };

  // –§—É–Ω–∫—Ü–∏—è —Å–∫–∞—á–∏–≤–∞–Ω–∏—è thumbnail
  const downloadThumbnail = () => {
    if (generatedThumbnail) {
      const a = document.createElement('a');
      a.href = generatedThumbnail;
      a.download = `${exportSettings?.filename || 'video'}_thumbnail.png`;
      document.body.appendChild(a);
      a.click();
      document.body.removeChild(a);
    }
  };

  // –§—É–Ω–∫—Ü–∏—è –ø–µ—Ä–µ—Ö–æ–¥–∞ –Ω–∞ —Å—Ç—Ä–∞–Ω–∏—Ü—É –ø—Ä–∏–ª–æ–∂–µ–Ω–∏—è
  const navigateToApp = () => {
    window.location.href = 'http://localhost:3000/app';
  };

  // –®–∞–±–ª–æ–Ω—ã –¥–ª—è –±—ã—Å—Ç—Ä–æ–≥–æ —Å–æ–∑–¥–∞–Ω–∏—è
  const quickTemplates = [
    { name: "Gaming Highlight", prompt: "Epic gaming moment with glowing effects" },
    { name: "Tutorial", prompt: "Educational, clean, professional style" },
    { name: "Vlog", prompt: "Personal, warm, friendly atmosphere" },
    { name: "Tech Review", prompt: "Modern, sleek, technology focused" },
    { name: "Music Video", prompt: "Vibrant, artistic, music-themed" },
    { name: "Comedy", prompt: "Fun, bright, humorous style" }
  ];

  if (!isOpen) return null;

  return (
    <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-[9999] p-4">
      <div className="bg-white rounded-2xl shadow-xl max-w-5xl w-full max-h-[95vh] overflow-hidden">
        <div className="flex items-center justify-between p-6 border-b border-gray-200">
          <h2 className="text-xl font-semibold text-gray-800 flex items-center">
            <Sparkles size={24} className="mr-3 text-purple-600" />
            AI Thumbnail Creator
          </h2>
          <button
            onClick={() => {
              onClose();
              navigateToApp();
            }}
            className="p-2 hover:bg-gray-100 rounded-full transition-colors"
          >
            <X size={20} />
          </button>
        </div>

        <div className="p-6 space-y-6 max-h-[calc(95vh-140px)] overflow-y-auto">
          <div className="grid grid-cols-1 lg:grid-cols-2 gap-8">
            {/* –ù–∞—Å—Ç—Ä–æ–π–∫–∏ */}
            <div className="space-y-6">
              <div className="bg-gradient-to-r from-purple-50 to-blue-50 rounded-xl p-6">
                <h3 className="text-lg font-semibold text-gray-800 mb-4 flex items-center">
                  <Settings size={20} className="mr-2 text-purple-600" />
                  Thumbnail Settings
                </h3>
                
                <div className="space-y-4">
                  <div>
                    <label className="block text-sm font-medium text-gray-700 mb-2">
                      Style Template
                    </label>
                    <select
                      value={thumbnailSettings.style}
                      onChange={(e) => setThumbnailSettings(prev => ({ ...prev, style: e.target.value }))}
                      className="w-full p-3 border border-gray-300 rounded-lg"
                    >
                      <option value="youtube">YouTube Style</option>
                      <option value="instagram">Instagram Style</option>
                      <option value="tiktok">TikTok Style</option>
                      <option value="minimal">Minimal</option>
                      <option value="cinematic">Cinematic</option>
                    </select>
                  </div>

                  <div>
                    <label className="block text-sm font-medium text-gray-700 mb-2">
                      Theme
                    </label>
                    <select
                      value={thumbnailSettings.theme}
                      onChange={(e) => setThumbnailSettings(prev => ({ ...prev, theme: e.target.value }))}
                      className="w-full p-3 border border-gray-300 rounded-lg"
                    >
                      <option value="bright">Bright & Colorful</option>
                      <option value="dark">Dark & Dramatic</option>
                      <option value="warm">Warm & Orange</option>
                      <option value="cool">Cool & Blue</option>
                    </select>
                  </div>

                  <div>
                    <label className="block text-sm font-medium text-gray-700 mb-2">
                      Resolution
                    </label>
                    <select
                      value={thumbnailSettings.size}
                      onChange={(e) => setThumbnailSettings(prev => ({ ...prev, size: e.target.value }))}
                      className="w-full p-3 border border-gray-300 rounded-lg"
                    >
                      <option value="1280x720">YouTube (1280x720)</option>
                      <option value="1080x1080">Instagram (1080x1080)</option>
                      <option value="1080x1920">TikTok (1080x1920)</option>
                      <option value="1920x1080">Full HD (1920x1080)</option>
                    </select>
                  </div>

                  <div>
                    <label className="block text-sm font-medium text-gray-700 mb-2">
                      Thumbnail Text
                    </label>
                    <input
                      type="text"
                      value={thumbnailSettings.text}
                      onChange={(e) => setThumbnailSettings(prev => ({ ...prev, text: e.target.value }))}
                      placeholder="Enter your video title..."
                      className="w-full p-3 border border-gray-300 rounded-lg"
                    />
                  </div>

                  <div>
                    <label className="block text-sm font-medium text-gray-700 mb-2">
                      AI Enhancement Prompt
                    </label>
                    <textarea
                      value={thumbnailSettings.aiPrompt}
                      onChange={(e) => setThumbnailSettings(prev => ({ ...prev, aiPrompt: e.target.value }))}
                      placeholder="Describe how you want your thumbnail to look..."
                      rows={3}
                      className="w-full p-3 border border-gray-300 rounded-lg resize-none"
                    />
                  </div>

                  <div className="flex items-center space-x-6">
                    <label className="flex items-center">
                      <input
                        type="checkbox"
                        checked={thumbnailSettings.includeEmoji}
                        onChange={(e) => setThumbnailSettings(prev => ({ ...prev, includeEmoji: e.target.checked }))}
                        className="rounded border-gray-300 text-purple-600 focus:ring-purple-500"
                      />
                      <span className="ml-2 text-sm text-gray-700">Include Emoji</span>
                    </label>
                  </div>

                  <button
                    onClick={generateThumbnailWithAI}
                    disabled={isGeneratingThumbnail}
                    className="w-full bg-gradient-to-r from-purple-600 to-blue-600 hover:from-purple-700 hover:to-blue-700 disabled:from-gray-400 disabled:to-gray-500 text-white font-semibold py-4 px-6 rounded-xl transition-all duration-300 shadow-lg hover:shadow-xl disabled:cursor-not-allowed flex items-center justify-center"
                  >
                    {isGeneratingThumbnail ? (
                      <>
                        <div className="animate-spin rounded-full h-5 w-5 border-b-2 border-white mr-3"></div>
                        Generating AI Thumbnail...
                      </>
                    ) : (
                      <>
                        <Sparkles size={20} className="mr-3" />
                        Generate AI Thumbnail
                      </>
                    )}
                  </button>
                </div>
              </div>

              {/* Quick Templates */}
              <div className="bg-gray-50 rounded-xl p-6">
                <h4 className="text-lg font-semibold text-gray-800 mb-4">Quick Templates</h4>
                <div className="grid grid-cols-2 gap-3">
                  {quickTemplates.map((template) => (
                    <button
                      key={template.name}
                      onClick={() => setThumbnailSettings(prev => ({ 
                        ...prev, 
                        aiPrompt: template.prompt,
                        text: template.name 
                      }))}
                      className="p-3 text-sm bg-white hover:bg-blue-50 border border-gray-200 hover:border-blue-300 rounded-lg transition-all text-left"
                    >
                      <div className="font-medium text-gray-800">{template.name}</div>
                      <div className="text-xs text-gray-500 mt-1">{template.prompt}</div>
                    </button>
                  ))}
                </div>
              </div>
            </div>

            {/* Preview */}
            <div className="space-y-6">
              <div className="bg-gray-50 rounded-xl p-6">
                <h3 className="text-lg font-semibold text-gray-800 mb-4 flex items-center">
                  <Image size={20} className="mr-2 text-blue-600" />
                  Thumbnail Preview
                </h3>
                
                <div className="bg-black rounded-lg aspect-video flex items-center justify-center overflow-hidden">
                  {generatedThumbnail ? (
                    <img 
                      src={generatedThumbnail} 
                      alt="Generated Thumbnail" 
                      className="max-w-full max-h-full object-contain"
                    />
                  ) : (
                    <div className="text-white text-center">
                      <div className="text-4xl mb-4">üé¨</div>
                      <div className="text-lg">Generate your thumbnail</div>
                      <div className="text-sm text-gray-400 mt-2">Click "Generate AI Thumbnail" to create</div>
                    </div>
                  )}
                </div>

                {generatedThumbnail && (
                  <div className="flex space-x-3 mt-4">
                    <button
                      onClick={downloadThumbnail}
                      className="flex-1 bg-green-600 hover:bg-green-700 text-white font-medium py-3 px-4 rounded-lg transition-colors flex items-center justify-center"
                    >
                      <Download size={18} className="mr-2" />
                      Download Thumbnail
                    </button>
                    <button
                      onClick={generateThumbnailWithAI}
                      className="px-4 py-3 border border-gray-300 hover:border-gray-400 text-gray-700 rounded-lg transition-colors"
                    >
                      Regenerate
                    </button>
                  </div>
                )}
              </div>

              {/* AI Tips */}
              <div className="bg-blue-50 border border-blue-200 rounded-xl p-6">
                <h4 className="font-semibold text-blue-800 mb-3 flex items-center">
                  <Sparkles size={18} className="mr-2" />
                  AI Tips for Better Thumbnails
                </h4>
                <ul className="text-sm text-blue-700 space-y-2">
                  <li>‚Ä¢ Use descriptive words like "epic", "amazing", "shocking"</li>
                  <li>‚Ä¢ Mention colors you want: "bright red", "neon blue"</li>
                  <li>‚Ä¢ Include style references: "YouTube style", "cinematic"</li>
                  <li>‚Ä¢ Describe emotions: "excited", "mysterious", "fun"</li>
                  <li>‚Ä¢ Add specific elements: "glowing text", "particles", "effects"</li>
                </ul>
              </div>
            </div>
          </div>
        </div>

        <div className="flex justify-between items-center p-6 border-t border-gray-200 bg-gray-50">
          <div className="text-sm text-gray-500">
            {generatedThumbnail ? 'Thumbnail ready for download' : 'Customize your thumbnail settings and generate'}
          </div>
          <div className="flex space-x-3">
            <button
              onClick={() => {
                onClose();
                navigateToApp();
              }}
              className="px-6 py-2 text-gray-600 hover:text-gray-800 transition-colors"
            >
              Skip & Continue
            </button>
            {generatedThumbnail && (
              <button
                onClick={() => {
                  downloadThumbnail();
                  onClose();
                  navigateToApp();
                }}
                className="px-6 py-2 bg-blue-600 text-white rounded-lg hover:bg-blue-700 font-medium transition-colors flex items-center"
              >
                Download & Continue
                <ArrowRight size={16} className="ml-2" />
              </button>
            )}
          </div>
        </div>
      </div>
    </div>
  );
};

export default ThumbnailCreator;
