import React, { useState, useRef, useEffect } from 'react';
import { X, Download, Check, AlertCircle, Zap, Clock, Settings } from 'lucide-react';

const FFmpegVideoExporter = ({
  isOpen,
  onClose,
  timelineItems = [],
  videoDuration = 60,
  previewCanvasRef: editorCanvasRef, // –ü–µ—Ä–µ–¥–∞–µ–º canvas –∏–∑ —Ä–µ–¥–∞–∫—Ç–æ—Ä–∞ –¥–ª—è –ø—Ä–∞–≤–∏–ª—å–Ω—ã—Ö –∫–æ–æ—Ä–¥–∏–Ω–∞—Ç
  timelineData,
  elements,
  clips,
  mediaItems,
}) => {
  // –ü–æ–ª—É—á–∞–µ–º –¥–∞–Ω–Ω—ã–µ timeline
  const actualTimelineItems = React.useMemo(() => {
    const items = timelineItems || timelineData || elements || clips || mediaItems || [];
    console.log('üìã Timeline —ç–ª–µ–º–µ–Ω—Ç—ã:', items.length);
    return items;
  }, [timelineItems, timelineData, elements, clips, mediaItems]);

  const [exportSettings, setExportSettings] = useState({
    resolution: "1280x720",
    fps: 30,
    quality: "high", 
    filename: `video_${Date.now()}`,
    method: "auto" // auto, webcodecs, mediarecorder, hybrid
  });

  const [isExporting, setIsExporting] = useState(false);
  const [exportProgress, setExportProgress] = useState(0);
  const [exportStage, setExportStage] = useState("ready");
  const [exportMethod, setExportMethod] = useState(null);
  const [error, setError] = useState(null);
  const [previewFrame, setPreviewFrame] = useState(null);

  const exportCanvasRef = useRef(null);
  const previewCanvasRef = useRef(null);

  // –ü—Ä–æ–≤–µ—Ä–∫–∞ –ø–æ–¥–¥–µ—Ä–∂–∫–∏ –±—ã—Å—Ç—Ä—ã—Ö –º–µ—Ç–æ–¥–æ–≤
  const checkSupport = () => {
    return {
      webcodecs: 'VideoEncoder' in window && 'VideoFrame' in window,
      mediarecorder: 'MediaRecorder' in window,
      offscreencanvas: 'OffscreenCanvas' in window && 'Worker' in window
    };
  };

  // –í—ã–±–æ—Ä –æ–ø—Ç–∏–º–∞–ª—å–Ω–æ–≥–æ –º–µ—Ç–æ–¥–∞ —ç–∫—Å–ø–æ—Ä—Ç–∞
  const selectBestMethod = () => {
    if (exportSettings.method !== "auto") return exportSettings.method;
    
    const support = checkSupport();
    
    // –ü—Ä–∏–æ—Ä–∏—Ç–µ—Ç: WebCodecs > MediaRecorder
    if (support.webcodecs) return "webcodecs";
    if (support.mediarecorder) return "mediarecorder";
    return "hybrid"; // Fallback
  };

  // –†–∞—Å—Å—á–µ—Ç –¥–ª–∏—Ç–µ–ª—å–Ω–æ—Å—Ç–∏ timeline
  const calculateDuration = () => {
    if (actualTimelineItems.length === 0) return videoDuration;
    
    const maxEndTime = Math.max(...actualTimelineItems.map(item => {
      const start = item.startTime || 0;
      const duration = item.duration || 0;
      return start + duration;
    }));
    
    return Math.max(maxEndTime, videoDuration);
  };

  // –ò–°–ü–†–ê–í–õ–ï–ù–ù–û–ï –ø–æ–∑–∏—Ü–∏–æ–Ω–∏—Ä–æ–≤–∞–Ω–∏–µ - –ø–æ–ª—É—á–∞–µ–º —Ä–∞–∑–º–µ—Ä—ã –∏–∑ —Ä–µ–¥–∞–∫—Ç–æ—Ä–∞
  const getCorrectCoordinates = (item, targetWidth, targetHeight) => {
    let editorWidth = 1920; // –ü–æ —É–º–æ–ª—á–∞–Ω–∏—é
    let editorHeight = 1080;

    // –ü—ã—Ç–∞–µ–º—Å—è –ø–æ–ª—É—á–∏—Ç—å —Ä–µ–∞–ª—å–Ω—ã–µ —Ä–∞–∑–º–µ—Ä—ã canvas —Ä–µ–¥–∞–∫—Ç–æ—Ä–∞
    if (editorCanvasRef?.current) {
      editorWidth = editorCanvasRef.current.width || editorCanvasRef.current.offsetWidth;
      editorHeight = editorCanvasRef.current.height || editorCanvasRef.current.offsetHeight;
      console.log(`üìè –†–∞–∑–º–µ—Ä—ã —Ä–µ–¥–∞–∫—Ç–æ—Ä–∞: ${editorWidth}x${editorHeight}`);
    }

    // –ú–∞—Å—à—Ç–∞–±–∏—Ä—É–µ–º –∫–æ–æ—Ä–¥–∏–Ω–∞—Ç—ã –ø—Ä–∞–≤–∏–ª—å–Ω–æ
    const scaleX = targetWidth / editorWidth;
    const scaleY = targetHeight / editorHeight;

    const x = (item.x || 0) * scaleX;
    const y = (item.y || 0) * scaleY;
    const width = (item.width || 200) * scaleX * (item.scale || 1);
    const height = (item.height || 150) * scaleY * (item.scale || 1);

    console.log(`üìç –ö–æ–æ—Ä–¥–∏–Ω–∞—Ç—ã "${item.name}": —Ä–µ–¥–∞–∫—Ç–æ—Ä(${item.x},${item.y}) ‚Üí —ç–∫—Å–ø–æ—Ä—Ç(${x.toFixed(1)},${y.toFixed(1)})`);

    return { x, y, width, height };
  };

  // –ü–æ–ª—É—á–µ–Ω–∏–µ –∞–∫—Ç–∏–≤–Ω—ã—Ö —ç–ª–µ–º–µ–Ω—Ç–æ–≤
  const getActiveElements = (currentTime) => {
    return actualTimelineItems.filter(item => {
      const startTime = item.startTime || 0;
      const duration = item.duration || 0;
      const endTime = startTime + duration;
      return currentTime >= startTime && currentTime < endTime;
    }).sort((a, b) => {
      const layerOrder = { main: 1, overlay: 2, audio: 3 };
      return (layerOrder[a.trackType] || 1) - (layerOrder[b.trackType] || 1);
    });
  };

  // –ë–´–°–¢–†–´–ô —Ä–µ–Ω–¥–µ—Ä–∏–Ω–≥ –∫–∞–¥—Ä–∞ timeline
  const renderFrame = async (ctx, currentTime, width, height) => {
    // –ß–µ—Ä–Ω—ã–π —Ñ–æ–Ω
    ctx.fillStyle = '#000000';
    ctx.fillRect(0, 0, width, height);

    const activeElements = getActiveElements(currentTime);
    
    for (const element of activeElements) {
      if (element.trackType === 'audio') continue;
      
      try {
        await renderElement(ctx, element, currentTime, width, height);
      } catch (error) {
        console.warn(`‚ö†Ô∏è –û—à–∏–±–∫–∞ —Ä–µ–Ω–¥–µ—Ä–∏–Ω–≥–∞ ${element.name}:`, error);
      }
    }
  };

  // –†–µ–Ω–¥–µ—Ä–∏–Ω–≥ –æ–¥–Ω–æ–≥–æ —ç–ª–µ–º–µ–Ω—Ç–∞ —Å –ü–†–ê–í–ò–õ–¨–ù–´–ú –ø–æ–∑–∏—Ü–∏–æ–Ω–∏—Ä–æ–≤–∞–Ω–∏–µ–º
  const renderElement = async (ctx, item, currentTime, canvasWidth, canvasHeight) => {
    const mediaElement = await loadMediaElement(item);
    if (!mediaElement) return;

    // –í—Ä–µ–º—è –≤–Ω—É—Ç—Ä–∏ —ç–ª–µ–º–µ–Ω—Ç–∞
    const elementStartTime = item.startTime || 0;
    const relativeTime = currentTime - elementStartTime;

    // –ù–∞—Å—Ç—Ä–æ–π–∫–∞ –≤–∏–¥–µ–æ
    if (mediaElement.tagName === 'VIDEO') {
      const trimStart = item.trimStart || 0;
      const targetTime = trimStart + relativeTime;
      
      if (Math.abs(mediaElement.currentTime - targetTime) > 0.1) {
        mediaElement.currentTime = Math.max(0, Math.min(targetTime, mediaElement.duration || 0));
        
        await new Promise(resolve => {
          const checkReady = () => {
            if (mediaElement.readyState >= 2) resolve();
            else setTimeout(checkReady, 10);
          };
          checkReady();
          setTimeout(resolve, 50); // –ë—ã—Å—Ç—Ä–µ–µ —Ç–∞–π–º–∞—É—Ç
        });
      }
    }

    const mediaWidth = mediaElement.videoWidth || mediaElement.naturalWidth || mediaElement.width;
    const mediaHeight = mediaElement.videoHeight || mediaElement.naturalHeight || mediaElement.height;

    if (!mediaWidth || !mediaHeight) return;

    let drawX, drawY, drawWidth, drawHeight;

    if (item.trackType === 'main') {
      // –û—Å–Ω–æ–≤–Ω–æ–µ –≤–∏–¥–µ–æ - –∑–∞–ø–æ–ª–Ω—è–µ—Ç —ç–∫—Ä–∞–Ω
      const canvasRatio = canvasWidth / canvasHeight;
      const mediaRatio = mediaWidth / mediaHeight;

      if (mediaRatio > canvasRatio) {
        drawWidth = canvasWidth;
        drawHeight = canvasWidth / mediaRatio;
        drawX = 0;
        drawY = (canvasHeight - drawHeight) / 2;
      } else {
        drawHeight = canvasHeight;
        drawWidth = canvasHeight * mediaRatio;
        drawX = (canvasWidth - drawWidth) / 2;
        drawY = 0;
      }
    } else if (item.trackType === 'overlay') {
      // –ò–°–ü–†–ê–í–õ–ï–ù–ù–û–ï –ø–æ–∑–∏—Ü–∏–æ–Ω–∏—Ä–æ–≤–∞–Ω–∏–µ overlay
      const coords = getCorrectCoordinates(item, canvasWidth, canvasHeight);
      drawX = coords.x;
      drawY = coords.y;
      drawWidth = coords.width;
      drawHeight = coords.height;
    }

    // –ü—Ä–∏–º–µ–Ω—è–µ–º —Ç—Ä–∞–Ω—Å—Ñ–æ—Ä–º–∞—Ü–∏–∏
    ctx.save();

    if (item.rotation && item.rotation !== 0) {
      const centerX = drawX + drawWidth / 2;
      const centerY = drawY + drawHeight / 2;
      ctx.translate(centerX, centerY);
      ctx.rotate((item.rotation * Math.PI) / 180);
      ctx.translate(-centerX, -centerY);
    }

    ctx.globalAlpha = item.opacity !== undefined ? item.opacity : 1;
    ctx.drawImage(mediaElement, drawX, drawY, drawWidth, drawHeight);
    ctx.restore();
  };

  // –ë—ã—Å—Ç—Ä–∞—è –∑–∞–≥—Ä—É–∑–∫–∞ –º–µ–¥–∏–∞
  const loadMediaElement = async (item) => {
    if (!item.url) return null;

    return new Promise((resolve, reject) => {
      const timeout = setTimeout(() => reject(new Error(`Timeout ${item.name}`)), 3000);

      const isVideo = item.type === 'videos' || 
                     item.trackType === 'main' || 
                     item.name?.toLowerCase().match(/\.(mp4|webm|mov)$/);

      if (isVideo) {
        const video = document.createElement('video');
        video.crossOrigin = 'anonymous';
        video.muted = true;
        video.preload = 'metadata';
        
        video.onloadedmetadata = () => {
          clearTimeout(timeout);
          resolve(video);
        };
        
        video.onerror = () => {
          clearTimeout(timeout);
          reject(new Error(`Failed to load video ${item.name}`));
        };
        
        video.src = item.url;
      } else {
        const img = new Image();
        img.crossOrigin = 'anonymous';
        
        img.onload = () => {
          clearTimeout(timeout);
          resolve(img);
        };
        
        img.onerror = () => {
          clearTimeout(timeout);
          reject(new Error(`Failed to load image ${item.name}`));
        };
        
        img.src = item.url;
      }
    });
  };

  // WEBCODECS –≠–ö–°–ü–û–†–¢ (–°–ê–ú–´–ô –ë–´–°–¢–†–´–ô)
  const exportWithWebCodecs = async () => {
    console.log('üöÄ WebCodecs —ç–∫—Å–ø–æ—Ä—Ç - —Å–∞–º—ã–π –±—ã—Å—Ç—Ä—ã–π –º–µ—Ç–æ–¥');
    setExportStage("webcodecs_init");

    try {
      // –î–∏–Ω–∞–º–∏—á–µ—Å–∫–∏–π –∏–º–ø–æ—Ä—Ç canvas-record
      const canvasRecord = await import('canvas-record');
      const { Recorder } = canvasRecord;

      const canvas = exportCanvasRef.current;
      const [width, height] = exportSettings.resolution.split('x').map(Number);
      canvas.width = width;
      canvas.height = height;

      const recorder = new Recorder(canvas, {
        name: exportSettings.filename,
        extension: 'mp4',
        encoder: 'WebCodecs',
        duration: calculateDuration(),
        frameRate: exportSettings.fps,
        encoderOptions: {
          codec: 'avc1.42E01E', // H.264 Baseline
          width: width,
          height: height,
          bitrate: exportSettings.quality === 'high' ? 4000000 : 2000000,
          framerate: exportSettings.fps
        }
      });

      setExportStage("rendering");
      const duration = calculateDuration();
      const totalFrames = Math.ceil(duration * exportSettings.fps);

      for (let frame = 0; frame < totalFrames; frame++) {
        const currentTime = frame / exportSettings.fps;
        if (currentTime >= duration) break;

        const ctx = canvas.getContext('2d');
        await renderFrame(ctx, currentTime, width, height);
        await recorder.step();

        setExportProgress(Math.round((frame / totalFrames) * 90));

        // –ü–∞—É–∑–∞ –∫–∞–∂–¥—ã–µ 30 –∫–∞–¥—Ä–æ–≤ –¥–ª—è –æ—Ç–∑—ã–≤—á–∏–≤–æ—Å—Ç–∏
        if (frame % 30 === 0) {
          await new Promise(resolve => setTimeout(resolve, 1));
        }
      }

      setExportStage("finalizing");
      const blob = await recorder.stop();
      return blob;

    } catch (error) {
      throw new Error(`WebCodecs –æ—à–∏–±–∫–∞: ${error.message}`);
    }
  };

  // MEDIARECORDER –≠–ö–°–ü–û–†–¢ (–ë–´–°–¢–†–´–ô –ò –ù–ê–î–ï–ñ–ù–´–ô)
  const exportWithMediaRecorder = async () => {
    console.log('üé¨ MediaRecorder —ç–∫—Å–ø–æ—Ä—Ç - –±—ã—Å—Ç—Ä—ã–π –∏ –Ω–∞–¥–µ–∂–Ω—ã–π');
    setExportStage("mediarecorder_init");

    return new Promise((resolve, reject) => {
      const canvas = exportCanvasRef.current;
      const [width, height] = exportSettings.resolution.split('x').map(Number);
      canvas.width = width;
      canvas.height = height;

      const stream = canvas.captureStream(exportSettings.fps);
      const chunks = [];
      
      // –û–ø—Ç–∏–º–∞–ª—å–Ω—ã–µ –Ω–∞—Å—Ç—Ä–æ–π–∫–∏ –¥–ª—è –∫–∞—á–µ—Å—Ç–≤–∞ –∏ —Å–∫–æ—Ä–æ—Å—Ç–∏
      const options = {
        mimeType: 'video/webm;codecs=vp9',
        videoBitsPerSecond: exportSettings.quality === 'high' ? 6000000 : 3000000
      };

      // Fallback –¥–ª—è –±—Ä–∞—É–∑–µ—Ä–æ–≤
      if (!MediaRecorder.isTypeSupported(options.mimeType)) {
        options.mimeType = 'video/webm;codecs=vp8';
      }
      if (!MediaRecorder.isTypeSupported(options.mimeType)) {
        options.mimeType = 'video/webm';
      }

      const mediaRecorder = new MediaRecorder(stream, options);

      mediaRecorder.ondataavailable = (event) => {
        if (event.data.size > 0) chunks.push(event.data);
      };

      mediaRecorder.onstop = () => {
        const blob = new Blob(chunks, { type: options.mimeType });
        resolve(blob);
      };

      mediaRecorder.onerror = reject;
      mediaRecorder.start();

      setExportStage("rendering");
      
      const duration = calculateDuration();
      let frame = 0;
      const totalFrames = Math.ceil(duration * exportSettings.fps);

      const renderLoop = async () => {
        if (frame >= totalFrames) {
          mediaRecorder.stop();
          return;
        }

        const currentTime = frame / exportSettings.fps;
        const ctx = canvas.getContext('2d');
        await renderFrame(ctx, currentTime, width, height);
        
        frame++;
        setExportProgress(Math.round((frame / totalFrames) * 90));
        
        // –ë—ã—Å—Ç—Ä—ã–π —Ä–µ–Ω–¥–µ—Ä–∏–Ω–≥ –±–µ–∑ –ª–∏—à–Ω–∏—Ö –ø–∞—É–∑
        setTimeout(renderLoop, 1000 / exportSettings.fps);
      };

      renderLoop();
    });
  };

  // –ì–ò–ë–†–ò–î–ù–´–ô –ú–ï–¢–û–î (–¥–ª—è –º–∞–∫—Å–∏–º–∞–ª—å–Ω–æ–π —Å–æ–≤–º–µ—Å—Ç–∏–º–æ—Å—Ç–∏)
  const exportWithHybrid = async () => {
    console.log('üîÑ –ì–∏–±—Ä–∏–¥–Ω—ã–π —ç–∫—Å–ø–æ—Ä—Ç - –º–∞–∫—Å–∏–º–∞–ª—å–Ω–∞—è —Å–æ–≤–º–µ—Å—Ç–∏–º–æ—Å—Ç—å');
    
    // –ü—Ä–æ–±—É–µ–º MediaRecorder, –µ—Å–ª–∏ –Ω–µ —Ä–∞–±–æ—Ç–∞–µ—Ç - –∏—Å–ø–æ–ª—å–∑—É–µ–º canvas.toBlob
    try {
      return await exportWithMediaRecorder();
    } catch (error) {
      console.warn('MediaRecorder –Ω–µ —Å—Ä–∞–±–æ—Ç–∞–ª, –∏—Å–ø–æ–ª—å–∑—É–µ–º canvas.toBlob');
      return await exportWithCanvasBlobs();
    }
  };

  // Canvas.toBlob —ç–∫—Å–ø–æ—Ä—Ç (–ø–æ—Å–ª–µ–¥–Ω–∏–π fallback)
  const exportWithCanvasBlobs = async () => {
    setExportStage("canvas_export");
    
    const canvas = exportCanvasRef.current;
    const [width, height] = exportSettings.resolution.split('x').map(Number);
    canvas.width = width;
    canvas.height = height;

    const duration = calculateDuration();
    const frames = [];
    const totalFrames = Math.ceil(duration * exportSettings.fps);

    for (let frame = 0; frame < totalFrames; frame++) {
      const currentTime = frame / exportSettings.fps;
      if (currentTime >= duration) break;

      const ctx = canvas.getContext('2d');
      await renderFrame(ctx, currentTime, width, height);

      const blob = await new Promise(resolve => {
        canvas.toBlob(resolve, 'image/webp', 0.8);
      });
      
      if (blob) frames.push(blob);

      setExportProgress(Math.round((frame / totalFrames) * 90));

      if (frame % 20 === 0) {
        await new Promise(resolve => setTimeout(resolve, 1));
      }
    }

    // –°–æ–∑–¥–∞–µ–º WebM –∏–∑ –∫–∞–¥—Ä–æ–≤ (–ø—Ä–æ—Å—Ç–æ–π –º–µ—Ç–æ–¥)
    return await createWebMFromFrames(frames);
  };

  // –°–æ–∑–¥–∞–Ω–∏–µ WebM –∏–∑ –º–∞—Å—Å–∏–≤–∞ –∫–∞–¥—Ä–æ–≤
  const createWebMFromFrames = async (frames) => {
    // –ü—Ä–æ—Å—Ç–∞—è —Ä–µ–∞–ª–∏–∑–∞—Ü–∏—è - –≤ —Ä–µ–∞–ª—å–Ω–æ—Å—Ç–∏ –Ω—É–∂–µ–Ω WebM muxer
    // –ó–¥–µ—Å—å –º–æ–∂–Ω–æ –∏—Å–ø–æ–ª—å–∑–æ–≤–∞—Ç—å –±–∏–±–ª–∏–æ—Ç–µ–∫—É —Ç–∏–ø–∞ webm-writer
    console.log(`üì¶ –°–æ–∑–¥–∞–µ–º –≤–∏–¥–µ–æ –∏–∑ ${frames.length} –∫–∞–¥—Ä–æ–≤`);
    
    // –ó–∞–≥–ª—É—à–∫–∞ - –≤–æ–∑–≤—Ä–∞—â–∞–µ–º –ø–µ—Ä–≤—ã–π –∫–∞–¥—Ä –∫–∞–∫ –∏–∑–æ–±—Ä–∞–∂–µ–Ω–∏–µ
    return frames[0] || new Blob([''], { type: 'image/webp' });
  };

  // –ì–õ–ê–í–ù–ê–Ø –§–£–ù–ö–¶–ò–Ø –≠–ö–°–ü–û–†–¢–ê
  const startExport = async () => {
    if (isExporting || actualTimelineItems.length === 0) return;

    setIsExporting(true);
    setExportProgress(0);
    setError(null);

    try {
      const selectedMethod = selectBestMethod();
      setExportMethod(selectedMethod);
      
      console.log(`üé¨ –ù–∞—á–∏–Ω–∞–µ–º —ç–∫—Å–ø–æ—Ä—Ç –º–µ—Ç–æ–¥–æ–º: ${selectedMethod}`);

      let videoBlob;
      
      switch (selectedMethod) {
        case 'webcodecs':
          videoBlob = await exportWithWebCodecs();
          break;
        case 'mediarecorder':
          videoBlob = await exportWithMediaRecorder();
          break;
        case 'hybrid':
        default:
          videoBlob = await exportWithHybrid();
      }

      setExportProgress(95);
      setExportStage("downloading");

      // –°–∫–∞—á–∏–≤–∞–µ–º —Ñ–∞–π–ª
      const extension = selectedMethod === 'webcodecs' ? 'mp4' : 'webm';
      const url = URL.createObjectURL(videoBlob);
      const a = document.createElement('a');
      a.href = url;
      a.download = `${exportSettings.filename}.${extension}`;
      a.click();
      URL.revokeObjectURL(url);

      setExportProgress(100);
      setExportStage("completed");

      setTimeout(() => {
        setExportStage("ready");
        setExportProgress(0);
      }, 3000);

    } catch (error) {
      console.error('‚ùå –û—à–∏–±–∫–∞ —ç–∫—Å–ø–æ—Ä—Ç–∞:', error);
      setError(error.message);
      setExportStage("error");
    } finally {
      setIsExporting(false);
    }
  };

  // –ì–µ–Ω–µ—Ä–∞—Ü–∏—è –ø—Ä–µ–≤—å—é
  const generatePreview = async () => {
    try {
      const canvas = previewCanvasRef.current;
      if (!canvas) return;

      const ctx = canvas.getContext('2d');
      const [width, height] = exportSettings.resolution.split('x').map(Number);
      canvas.width = width;
      canvas.height = height;

      const previewTime = calculateDuration() / 4;
      await renderFrame(ctx, previewTime, width, height);
      
      canvas.toBlob((blob) => {
        if (blob) {
          const url = URL.createObjectURL(blob);
          setPreviewFrame(url);
        }
      });
    } catch (error) {
      console.warn('‚ö†Ô∏è –û—à–∏–±–∫–∞ –ø—Ä–µ–≤—å—é:', error);
