import React, { useState, useRef, useEffect } from "react";
import {
  X,
  Download,
  Check,
  AlertCircle,
  Play,
  Film,
  Zap,
  Settings,
  Volume2,
} from "lucide-react";

const FFmpegVideoExporter = ({
  isOpen,
  onClose,
  timelineItems = [],
  videoDuration = 60,
}) => {
  const [ffmpeg, setFfmpeg] = useState(null);
  const [isExporting, setIsExporting] = useState(false);
  const [exportProgress, setExportProgress] = useState(0);
  const [exportStage, setExportStage] = useState("idle");
  const [error, setError] = useState(null);
  const [exportedBlob, setExportedBlob] = useState(null);
  const [debugLog, setDebugLog] = useState([]); // –î–ª—è –¥–∏–∞–≥–Ω–æ—Å—Ç–∏–∫–∏

  const [exportSettings, setExportSettings] = useState({
    resolution: "1280x720",
    fps: 24,
    quality: "fast",
    duration: Math.min(videoDuration, 30),
    includeAudio: true, // –ü–µ—Ä–µ–∫–ª—é—á–∞—Ç–µ–ª—å –∞—É–¥–∏–æ
  });

  const canvasRef = useRef(null);
  const mediaCache = useRef(new Map());

  // –§—É–Ω–∫—Ü–∏—è –¥–ª—è –ª–æ–≥–∏—Ä–æ–≤–∞–Ω–∏—è
  const addLog = (message, type = "info") => {
    console.log(`[${type.toUpperCase()}] ${message}`);
    setDebugLog(prev => [...prev.slice(-4), { message, type, time: new Date().toLocaleTimeString() }]);
  };

  // –û–ø—Ç–∏–º–∏–∑–∏—Ä–æ–≤–∞–Ω–Ω–∞—è –∏–Ω–∏—Ü–∏–∞–ª–∏–∑–∞—Ü–∏—è FFmpeg
  useEffect(() => {
    const initFFmpeg = async () => {
      if (!isOpen || ffmpeg) return;

      try {
        setExportStage("loading");
        addLog("üöÄ –ò–Ω–∏—Ü–∏–∞–ª–∏–∑–∞—Ü–∏—è FFmpeg...");

        const { FFmpeg } = await import("@ffmpeg/ffmpeg");
        const { fetchFile } = await import("@ffmpeg/util");

        const ffmpegInstance = new FFmpeg();

        await ffmpegInstance.load({
          coreURL: "https://unpkg.com/@ffmpeg/core@0.12.6/dist/umd/ffmpeg-core.js",
          wasmURL: "https://unpkg.com/@ffmpeg/core@0.12.6/dist/umd/ffmpeg-core.wasm",
        });

        ffmpegInstance.on("progress", ({ progress }) => {
          if (isExporting) {
            setExportProgress(Math.min(90, Math.round(progress * 85) + 70));
          }
        });

        window.fetchFile = fetchFile;
        setFfmpeg(ffmpegInstance);
        setExportStage("ready");
        addLog("‚úÖ FFmpeg –≥–æ—Ç–æ–≤", "success");
      } catch (error) {
        addLog(`‚ùå –û—à–∏–±–∫–∞ FFmpeg: ${error.message}`, "error");
        setError(error.message);
        setExportStage("error");
      }
    };

    initFFmpeg();
  }, [isOpen]);

  // –ë–µ–∑–æ–ø–∞—Å–Ω–∞—è –∑–∞–≥—Ä—É–∑–∫–∞ –º–µ–¥–∏–∞ —Å —É–ª—É—á—à–µ–Ω–Ω–æ–π –æ–±—Ä–∞–±–æ—Ç–∫–æ–π –æ—à–∏–±–æ–∫
  const loadMediaWithCache = async (item) => {
    const cacheKey = `${item.id}_${item.url}`;

    if (mediaCache.current.has(cacheKey)) {
      return mediaCache.current.get(cacheKey);
    }

    return new Promise((resolve, reject) => {
      const timeout = setTimeout(() => {
        reject(new Error(`Timeout loading ${item.name}`));
      }, 5000);

      const isVideo = item.type === "videos" || item.trackType === "main";

      if (isVideo) {
        const video = document.createElement("video");
        video.crossOrigin = "anonymous";
        video.muted = true;
        video.preload = "metadata";

        video.onloadedmetadata = () => {
          clearTimeout(timeout);
          mediaCache.current.set(cacheKey, video);
          addLog(`‚úÖ –í–∏–¥–µ–æ –∑–∞–≥—Ä—É–∂–µ–Ω–æ: ${item.name}`);
          resolve(video);
        };

        video.onerror = (e) => {
          clearTimeout(timeout);
          addLog(`‚ùå –û—à–∏–±–∫–∞ –∑–∞–≥—Ä—É–∑–∫–∏ –≤–∏–¥–µ–æ: ${item.name}`, "error");
          reject(new Error(`Video load failed: ${item.name}`));
        };

        video.src = item.url;
      } else {
        const img = new Image();
        img.crossOrigin = "anonymous";

        img.onload = () => {
          clearTimeout(timeout);
          mediaCache.current.set(cacheKey, img);
          addLog(`‚úÖ –ò–∑–æ–±—Ä–∞–∂–µ–Ω–∏–µ –∑–∞–≥—Ä—É–∂–µ–Ω–æ: ${item.name}`);
          resolve(img);
        };

        img.onerror = (e) => {
          clearTimeout(timeout);
          addLog(`‚ùå –û—à–∏–±–∫–∞ –∑–∞–≥—Ä—É–∑–∫–∏ –∏–∑–æ–±—Ä–∞–∂–µ–Ω–∏—è: ${item.name}`, "error");
          reject(new Error(`Image load failed: ${item.name}`));
        };

        img.src = item.url;
      }
    });
  };

  // –ë—ã—Å—Ç—Ä—ã–π —Ä–µ–Ω–¥–µ—Ä–∏–Ω–≥
  const renderFrameFast = async (ctx, currentTime, width, height) => {
    try {
      ctx.fillStyle = "#000000";
      ctx.fillRect(0, 0, width, height);

      const activeItems = timelineItems.filter((item) => {
        const start = item.startTime || 0;
        const end = start + (item.duration || 0);
        return currentTime >= start && currentTime <= end && item.trackType !== "audio";
      });

      const sortedItems = activeItems.sort((a, b) => {
        const order = { main: 1, overlay: 2 };
        return (order[a.trackType] || 1) - (order[b.trackType] || 1);
      });

      for (const item of sortedItems) {
        try {
          const element = await loadMediaWithCache(item);
          if (!element) continue;

          ctx.save();

          if (element.tagName === "VIDEO") {
            const relativeTime = currentTime - (item.startTime || 0);
            const targetTime = Math.max(0, relativeTime);

            if (Math.abs(element.currentTime - targetTime) > 0.3) {
              element.currentTime = targetTime;
              // –ñ–¥–µ–º –≥–æ—Ç–æ–≤–Ω–æ—Å—Ç–∏ –≤–∏–¥–µ–æ
              await new Promise(resolve => {
                const checkReady = () => {
                  if (element.readyState >= 2) resolve();
                  else setTimeout(checkReady, 10);
                };
                checkReady();
                setTimeout(resolve, 100); // –ú–∞–∫—Å–∏–º—É–º 100–º—Å
              });
            }
          }

          let x = 0, y = 0, w = width, h = height;

          if (item.trackType === "overlay") {
            x = item.x || 0;
            y = item.y || 0;
            w = item.width || 200;
            h = item.height || 150;
          }

          ctx.globalAlpha = item.opacity || 1;
          ctx.drawImage(element, x, y, w, h);
          ctx.restore();
        } catch (error) {
          addLog(`‚ö†Ô∏è –ü—Ä–æ–ø—É—Å–∫–∞–µ–º ${item.name}: ${error.message}`, "warn");
        }
      }
    } catch (error) {
      addLog(`‚ùå –û—à–∏–±–∫–∞ —Ä–µ–Ω–¥–µ—Ä–∏–Ω–≥–∞ –∫–∞–¥—Ä–∞: ${error.message}`, "error");
      throw error;
    }
  };

  // –£–ª—É—á—à–µ–Ω–Ω–∞—è –ø–æ–¥–≥–æ—Ç–æ–≤–∫–∞ –∞—É–¥–∏–æ —Å –±–µ–∑–æ–ø–∞—Å–Ω–æ–π –∑–∞–≥—Ä—É–∑–∫–æ–π
  const prepareAudioFiles = async (duration) => {
    if (!exportSettings.includeAudio) {
      addLog("üîá –ê—É–¥–∏–æ –æ—Ç–∫–ª—é—á–µ–Ω–æ –≤ –Ω–∞—Å—Ç—Ä–æ–π–∫–∞—Ö");
      return [];
    }

    try {
      addLog("üéµ –ü–æ–¥–≥–æ—Ç–∞–≤–ª–∏–≤–∞–µ–º –∞—É–¥–∏–æ —Ç—Ä–µ–∫–∏...");
      
      const audioItems = timelineItems.filter(
        (item) => item.trackType === "audio" || (item.type === "videos" && item.trackType === "main")
      );

      if (audioItems.length === 0) {
        addLog("üîá –ê—É–¥–∏–æ —Ç—Ä–µ–∫–æ–≤ –Ω–µ –Ω–∞–π–¥–µ–Ω–æ");
        return [];
      }

      const audioFiles = [];

      for (let i = 0; i < Math.min(audioItems.length, 2); i++) { // –û–≥—Ä–∞–Ω–∏—á–∏–≤–∞–µ–º 2 —Ç—Ä–µ–∫–∞–º–∏
        const item = audioItems[i];
        try {
          addLog(`üì• –ó–∞–≥—Ä—É–∂–∞–µ–º –∞—É–¥–∏–æ: ${item.name}`);
          
          // –î–æ–±–∞–≤–ª—è–µ–º timeout –¥–ª—è fetch
          const controller = new AbortController();
          const timeoutId = setTimeout(() => controller.abort(), 10000);

          const response = await fetch(item.url, { 
            signal: controller.signal 
          });
          clearTimeout(timeoutId);

          if (!response.ok) {
            throw new Error(`HTTP ${response.status}`);
          }

          const blob = await response.blob();
          
          // –ü—Ä–æ–≤–µ—Ä—è–µ–º —Ä–∞–∑–º–µ—Ä —Ñ–∞–π–ª–∞
          if (blob.size === 0) {
            throw new Error("–ü—É—Å—Ç–æ–π —Ñ–∞–π–ª");
          }

          audioFiles.push({
            blob,
            startTime: item.startTime || 0,
            duration: Math.min(item.duration || duration, duration),
            name: item.name,
            index: i,
            type: item.type,
            trackType: item.trackType
          });

          addLog(`‚úÖ –ê—É–¥–∏–æ –∑–∞–≥—Ä—É–∂–µ–Ω–æ: ${item.name} (${(blob.size / 1024).toFixed(1)}KB)`);
        } catch (error) {
          addLog(`‚ö†Ô∏è –ü—Ä–æ–ø—É—Å–∫–∞–µ–º –∞—É–¥–∏–æ ${item.name}: ${error.message}`, "warn");
        }
      }

      addLog(`üéµ –ü–æ–¥–≥–æ—Ç–æ–≤–ª–µ–Ω–æ ${audioFiles.length} –∞—É–¥–∏–æ —Ç—Ä–µ–∫–æ–≤`);
      return audioFiles;
    } catch (error) {
      addLog(`‚ùå –û—à–∏–±–∫–∞ –ø–æ–¥–≥–æ—Ç–æ–≤–∫–∏ –∞—É–¥–∏–æ: ${error.message}`, "error");
      return []; // –ü—Ä–æ–¥–æ–ª–∂–∞–µ–º –±–µ–∑ –∞—É–¥–∏–æ
    }
  };

  // –°–æ–∑–¥–∞–Ω–∏–µ –∫–∞–¥—Ä–æ–≤
  const createFramesBatch = async (duration) => {
    try {
      const canvas = canvasRef.current;
      if (!canvas) {
        throw new Error("Canvas –Ω–µ –Ω–∞–π–¥–µ–Ω");
      }

      const ctx = canvas.getContext("2d", { alpha: false });
      const [width, height] = exportSettings.resolution.split("x").map(Number);
      canvas.width = width;
      canvas.height = height;

      const maxFrames = Math.min(
        Math.ceil(duration * exportSettings.fps),
        300
      );

      addLog(`üéûÔ∏è –°–æ–∑–¥–∞–µ–º ${maxFrames} –∫–∞–¥—Ä–æ–≤`);

      const frames = [];
      const batchSize = 5; // –£–º–µ–Ω—å—à–∞–µ–º —Ä–∞–∑–º–µ—Ä –±–∞—Ç—á–∞ –¥–ª—è —Å—Ç–∞–±–∏–ª—å–Ω–æ—Å—Ç–∏

      for (let i = 0; i < maxFrames; i += batchSize) {
        const batch = [];

        for (let j = 0; j < batchSize && i + j < maxFrames; j++) {
          const frameIndex = i + j;
          const currentTime = frameIndex / exportSettings.fps;

          await renderFrameFast(ctx, currentTime, width, height);

          const blob = await new Promise((resolve) => {
            canvas.toBlob(resolve, "image/jpeg", 0.8);
          });

          if (blob) batch.push(blob);
        }

        frames.push(...batch);

        const progress = Math.round((i / maxFrames) * 40);
        setExportProgress(progress);

        // –î–∞–µ–º –±—Ä–∞—É–∑–µ—Ä—É –±–æ–ª—å—à–µ –≤—Ä–µ–º–µ–Ω–∏ –Ω–∞ –æ—Ç–¥—ã—Ö
        await new Promise((resolve) => setTimeout(resolve, 20));
      }

      addLog(`‚úÖ –°–æ–∑–¥–∞–Ω–æ ${frames.length} –∫–∞–¥—Ä–æ–≤`);
      return frames;
    } catch (error) {
      addLog(`‚ùå –û—à–∏–±–∫–∞ —Å–æ–∑–¥–∞–Ω–∏—è –∫–∞–¥—Ä–æ–≤: ${error.message}`, "error");
      throw error;
    }
  };

  // –ë–µ–∑–æ–ø–∞—Å–Ω–æ–µ –∫–æ–¥–∏—Ä–æ–≤–∞–Ω–∏–µ
  const encodeVideoSafe = async (frames, audioFiles, duration) => {
    if (!window.fetchFile || frames.length === 0) {
      throw new Error("–ù–µ—Ç –∫–∞–¥—Ä–æ–≤ –¥–ª—è –∫–æ–¥–∏—Ä–æ–≤–∞–Ω–∏—è");
    }

    try {
      setExportProgress(40);
      addLog(`üé¨ –ö–æ–¥–∏—Ä–æ–≤–∞–Ω–∏–µ ${frames.length} –∫–∞–¥—Ä–æ–≤ + ${audioFiles.length} –∞—É–¥–∏–æ`);

      // –ó–∞–ø–∏—Å—ã–≤–∞–µ–º –≤–∏–¥–µ–æ –∫–∞–¥—Ä—ã
      for (let i = 0; i < frames.length; i++) {
        const frameData = await window.fetchFile(frames[i]);
        const filename = `f${i.toString().padStart(4, "0")}.jpg`;
        await ffmpeg.writeFile(filename, frameData);

        if (i % 25 === 0) {
          setExportProgress(40 + Math.round((i / frames.length) * 25));
        }
      }

      setExportProgress(65);

      // –ó–∞–ø–∏—Å—ã–≤–∞–µ–º –∞—É–¥–∏–æ —Ñ–∞–π–ª—ã (–µ—Å–ª–∏ –µ—Å—Ç—å)
      for (let i = 0; i < audioFiles.length; i++) {
        const audioFile = audioFiles[i];
        const audioFileName = `audio_${i}.${audioFile.type === "videos" ? "mp4" : "mp3"}`;
        
        const audioData = await window.fetchFile(audioFile.blob);
        await ffmpeg.writeFile(audioFileName, audioData);
        
        addLog(`üìù –ó–∞–ø–∏—Å–∞–Ω: ${audioFileName}`);
      }

      setExportProgress(70);

      // –°—Ç—Ä–æ–∏–º –±–µ–∑–æ–ø–∞—Å–Ω—É—é –∫–æ–º–∞–Ω–¥—É FFmpeg
      const args = [
        "-framerate", exportSettings.fps.toString(),
        "-i", "f%04d.jpg",
        "-t", Math.min(duration, frames.length / exportSettings.fps).toString(),
      ];

      // –î–æ–±–∞–≤–ª—è–µ–º –∞—É–¥–∏–æ —Ç–æ–ª—å–∫–æ –µ—Å–ª–∏ –µ—Å—Ç—å —Ñ–∞–π–ª—ã
      if (audioFiles.length > 0) {
        // –î–æ–±–∞–≤–ª—è–µ–º —Ç–æ–ª—å–∫–æ –ø–µ—Ä–≤—ã–π –∞—É–¥–∏–æ —Ñ–∞–π–ª –¥–ª—è –ø—Ä–æ—Å—Ç–æ—Ç—ã
        const audioFile = audioFiles[0];
        const audioFileName = `audio_0.${audioFile.type === "videos" ? "mp4" : "mp3"}`;
        
        args.push("-i", audioFileName);
        args.push("-c:a", "aac");
        args.push("-b:a", "128k");
        args.push("-ar", "44100");
        args.push("-ac", "2");
        args.push("-shortest");
      }

      // –ü—Ä–æ—Å—Ç—ã–µ –≤–∏–¥–µ–æ –Ω–∞—Å—Ç—Ä–æ–π–∫–∏
      args.push(
        "-c:v", "libx264",
        "-preset", "fast", // –ò–∑–º–µ–Ω–∏–ª–∏ –Ω–∞ "fast" –≤–º–µ—Å—Ç–æ "ultrafast"
        "-crf", "23", // –õ—É—á—à–µ –∫–∞—á–µ—Å—Ç–≤–æ
        "-pix_fmt", "yuv420p",
        "-movflags", "+faststart",
        "output.mp4"
      );

      addLog(`üîß FFmpeg: ${args.join(" ")}`);

      await ffmpeg.exec(args);

      setExportProgress(90);

      const data = await ffmpeg.readFile("output.mp4");
      const videoBlob = new Blob([data.buffer], { type: "video/mp4" });

      // –û—á–∏—â–∞–µ–º —Ñ–∞–π–ª—ã
      try {
        for (let i = 0; i < frames.length; i++) {
          await ffmpeg.deleteFile(`f${i.toString().padStart(4, "0")}.jpg`);
        }
        for (let i = 0; i < audioFiles.length; i++) {
          const ext = audioFiles[i].type === "videos" ? "mp4" : "mp3";
          await ffmpeg.deleteFile(`audio_${i}.${ext}`);
        }
        await ffmpeg.deleteFile("output.mp4");
      } catch (e) {
        addLog("‚ö†Ô∏è –ù–µ —É–¥–∞–ª–æ—Å—å –æ—á–∏—Å—Ç–∏—Ç—å –≤—Ä–µ–º–µ–Ω–Ω—ã–µ —Ñ–∞–π–ª—ã", "warn");
      }

      return videoBlob;
    } catch (error) {
      addLog(`‚ùå –û—à–∏–±–∫–∞ –∫–æ–¥–∏—Ä–æ–≤–∞–Ω–∏—è: ${error.message}`, "error");
      throw error;
    }
  };

  // –û—Å–Ω–æ–≤–Ω–∞—è —Ñ—É–Ω–∫—Ü–∏—è —ç–∫—Å–ø–æ—Ä—Ç–∞ —Å —É–ª—É—á—à–µ–Ω–Ω–æ–π –æ–±—Ä–∞–±–æ—Ç–∫–æ–π –æ—à–∏–±–æ–∫
  const startSafeExport = async () => {
    if (!ffmpeg || isExporting || timelineItems.length === 0) return;

    setIsExporting(true);
    setExportProgress(0);
    setExportStage("processing");
    setError(null);
    setExportedBlob(null);
    setDebugLog([]); // –û—á–∏—â–∞–µ–º –ª–æ–≥

    try {
      addLog("üöÄ –ù–∞—á–∏–Ω–∞–µ–º –±–µ–∑–æ–ø–∞—Å–Ω—ã–π —ç–∫—Å–ø–æ—Ä—Ç...");

      const actualDuration = Math.min(exportSettings.duration, 30);

      setExportStage("rendering");
      const frames = await createFramesBatch(actualDuration);

      if (frames.length === 0) {
        throw new Error("–ù–µ —É–¥–∞–ª–æ—Å—å —Å–æ–∑–¥–∞—Ç—å –∫–∞–¥—Ä—ã");
      }

      setExportStage("processing_audio");
      const audioFiles = await prepareAudioFiles(actualDuration);

      setExportStage("encoding");
      const videoBlob = await encodeVideoSafe(frames, audioFiles, actualDuration);

      setExportProgress(100);
      setExportStage("completed");
      setExportedBlob(videoBlob);

      addLog(`‚úÖ –≠–∫—Å–ø–æ—Ä—Ç –∑–∞–≤–µ—Ä—à–µ–Ω! –†–∞–∑–º–µ—Ä: ${(videoBlob.size / 1024 / 1024).toFixed(2)}MB`, "success");
    } catch (error) {
      addLog(`‚ùå –û—à–∏–±–∫–∞ —ç–∫—Å–ø–æ—Ä—Ç–∞: ${error.message}`, "error");
      setError(error.message);
      setExportStage("error");
    } finally {
      setIsExporting(false);
    }
  };

  const downloadVideo = () => {
    if (!exportedBlob) return;

    const url = URL.createObjectURL(exportedBlob);
    const a = document.createElement("a");
    a.href = url;
    a.download = `safe_export_${Date.now()}.mp4`;
    document.body.appendChild(a);
    a.click();
    document.body.removeChild(a);
    URL.revokeObjectURL(url);
  };

  const getStageText = () => {
    switch (exportStage) {
      case "loading": return "–ó–∞–≥—Ä—É–∑–∫–∞ FFmpeg...";
      case "processing": return "–ü–æ–¥–≥–æ—Ç–æ–≤–∫–∞...";
      case "rendering": return "–†–µ–Ω–¥–µ—Ä–∏–Ω–≥ –∫–∞–¥—Ä–æ–≤...";
      case "processing_audio": return "–û–±—Ä–∞–±–æ—Ç–∫–∞ –∞—É–¥–∏–æ...";
      case "encoding": return "–ö–æ–¥–∏—Ä–æ–≤–∞–Ω–∏–µ...";
      case "completed": return "‚úÖ –ì–æ—Ç–æ–≤–æ!";
      case "error": return "‚ùå –û—à–∏–±–∫–∞";
      default: return "–ì–æ—Ç–æ–≤ –∫ —ç–∫—Å–ø–æ—Ä—Ç—É";
    }
  };

  const audioTracksCount = timelineItems.filter(
    (item) => item.trackType === "audio" || (item.type === "videos" && item.trackType === "main")
  ).length;

  if (!isOpen) return null;

  return (
    <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
      <div className="bg-white rounded-2xl shadow-xl max-w-2xl w-full p-6 max-h-[90vh] overflow-y-auto">
        <h2 className="text-xl font-bold mb-4 flex items-center">
          <Zap size={24} className="mr-3 text-yellow-600" />
          üõ†Ô∏è –ë–µ–∑–æ–ø–∞—Å–Ω—ã–π —ç–∫—Å–ø–æ—Ä—Ç —Å –¥–∏–∞–≥–Ω–æ—Å—Ç–∏–∫–æ–π
        </h2>

        <div className="space-y-4">
          {/* –ù–∞—Å—Ç—Ä–æ–π–∫–∏ */}
          <div className="bg-yellow-50 p-4 rounded-lg">
            <h3 className="font-medium text-yellow-800 mb-3 flex items-center">
              <Settings size={16} className="mr-2" />
              –ù–∞—Å—Ç—Ä–æ–π–∫–∏ —ç–∫—Å–ø–æ—Ä—Ç–∞
            </h3>
            <div className="grid grid-cols-2 gap-3">
              <div>
                <label className="block text-xs font-medium mb-1">–†–∞–∑—Ä–µ—à–µ–Ω–∏–µ</label>
                <select
                  value={exportSettings.resolution}
                  onChange={(e) => setExportSettings(prev => ({ ...prev, resolution: e.target.value }))}
                  className="w-full p-2 text-sm border rounded"
                  disabled={isExporting}
                >
                  <option value="640x480">480p (–±—ã—Å—Ç—Ä–æ)</option>
                  <option value="1280x720">720p (—Å—Ä–µ–¥–Ω–µ)</option>
                </select>
              </div>

              <div>
                <label className="block text-xs font-medium mb-1">–î–ª–∏—Ç–µ–ª—å–Ω–æ—Å—Ç—å</label>
                <select
                  value={exportSettings.duration}
                  onChange={(e) => setExportSettings(prev => ({ ...prev, duration: parseInt(e.target.value) }))}
                  className="w-full p-2 text-sm border rounded"
                  disabled={isExporting}
                >
                  <option value="10">10 —Å–µ–∫</option>
                  <option value="20">20 —Å–µ–∫</option>
                  <option value="30">30 —Å–µ–∫</option>
                </select>
              </div>

              <div className="col-span-2">
                <label className="flex items-center space-x-2">
                  <input
                    type="checkbox"
                    checked={exportSettings.includeAudio}
                    onChange={(e) => setExportSettings(prev => ({ ...prev, includeAudio: e.target.checked }))}
                    disabled={isExporting}
                    className="rounded"
                  />
                  <span className="text-sm font-medium">–í–∫–ª—é—á–∏—Ç—å –∞—É–¥–∏–æ ({audioTracksCount} —Ç—Ä–µ–∫–æ–≤)</span>
                </label>
              </div>
            </div>
          </div>

          {/* –°—Ç–∞—Ç—É—Å */}
          <div className={`p-4 rounded-lg ${
            exportStage === "error" ? "bg-red-50" :
            exportStage === "completed" ? "bg-green-50" : "bg-blue-50"
          }`}>
            <div className="flex items-center justify-between mb-2">
              <span className="font-medium">{getStageText()}</span>
              {isExporting && <span className="text-sm">{exportProgress}%</span>}
            </div>
            {isExporting && (
              <div className="w-full bg-gray-200 rounded-full h-2">
                <div
                  className="bg-blue-600 h-2 rounded-full transition-all"
                  style={{ width: `${exportProgress}%` }}
                />
              </div>
            )}
          </div>

          {/* –õ–æ–≥ –¥–∏–∞–≥–Ω–æ—Å—Ç–∏–∫–∏ */}
          {debugLog.length > 0 && (
            <div className="bg-gray-50 p-3 rounded-lg">
              <h4 className="text-sm font-medium mb-2">üìã –õ–æ–≥ –¥–∏–∞–≥–Ω–æ—Å—Ç–∏–∫–∏:</h4>
              <div className="space-y-1 max-h-32 overflow-y-auto">
                {debugLog.map((log, idx) => (
                  <div key={idx} className={`text-xs ${
                    log.type === "error" ? "text-red-600" :
                    log.type === "warn" ? "text-orange-600" :
                    log.type === "success" ? "text-green-600" : "text-gray-600"
                  }`}>
                    [{log.time}] {log.message}
                  </div>
                ))}
              </div>
            </div>
          )}

          {/* –û—à–∏–±–∫–∞ */}
          {error && (
            <div className="bg-red-50 p-4 rounded-lg">
              <div className="text-red-800 font-medium">–û—à–∏–±–∫–∞ —ç–∫—Å–ø–æ—Ä—Ç–∞</div>
              <div className="text-red-600 text-sm">{error}</div>
            </div>
          )}

          {/* –†–µ–∑—É–ª—å—Ç–∞—Ç */}
          {exportedBlob && (
            <div className="bg-green-50 p-4 rounded-lg">
              <div className="text-green-800 font-medium mb-2">
                ‚úÖ –í–∏–¥–µ–æ –≥–æ—Ç–æ–≤–æ! –†–∞–∑–º–µ—Ä: {(exportedBlob.size / 1024 / 1024).toFixed(2)}MB
              </div>
              <button
                onClick={downloadVideo}
                className="bg-green-600 text-white px-4 py-2 rounded hover:bg-green-700 flex items-center"
              >
                <Download size={16} className="mr-2" />
                –°–∫–∞—á–∞—Ç—å
              </button>
            </div>
          )}

          {/* –°—Ç–∞—Ç–∏—Å—Ç–∏–∫–∞ */}
          <div className="text-xs text-gray-600 bg-gray-50 p-3 rounded">
            <div>–≠–ª–µ–º–µ–Ω—Ç–æ–≤ timeline: {timelineItems.length}</div>
            <div>–ê—É–¥–∏–æ —Ç—Ä–µ–∫–æ–≤: {audioTracksCount}</div>
            <div>–í–∫–ª—é—á–∏—Ç—å –∞—É–¥–∏–æ: {exportSettings.includeAudio ? "–î–∞" : "–ù–µ—Ç"}</div>
            <div>–ú–∞–∫—Å. –∫–∞–¥—Ä–æ–≤: {Math.min(Math.ceil(exportSettings.duration * exportSettings.fps), 300)}</div>
          </div>
        </div>

        <div className="flex justify-between mt-6">
          <button
            onClick={onClose}
            className="px-4 py-2 text-gray-600 hover:text-gray-800"
            disabled={isExporting}
          >
            –ó–∞–∫—Ä—ã—Ç—å
          </button>

          <button
            onClick={startSafeExport}
            disabled={!ffmpeg || isExporting || timelineItems.length === 0}
            className="bg-yellow-600 text-white px-6 py-2 rounded hover:bg-yellow-700 flex items-center disabled:bg-gray-400"
          >
            <Zap size={16} className="mr-2" />
            {isExporting ? `${exportProgress}%` : "–ë–µ–∑–æ–ø–∞—Å–Ω—ã–π —ç–∫—Å–ø–æ—Ä—Ç"}
          </button>
        </div>

        <canvas ref={canvasRef} style={{ display: "none" }} />
      </div>
    </div>
  );
};

export const useFFmpegExporter = () => {
  const [isExportModalOpen, setIsExportModalOpen] = useState(false);

  const openExportModal = () => setIsExportModalOpen(true);
  const closeExportModal = () => setIsExportModalOpen(false);

  return {
    isExportModalOpen,
    openExportModal,
    closeExportModal,
  };
};

export default FFmpegVideoExporter;
