import React, { useState, useRef, useEffect } from "react";
import { X, Download, Check, AlertCircle, Settings, Play, Film } from "lucide-react";

const FFmpegVideoExporter = ({
  isOpen,
  onClose,
  timelineItems = [],
  videoDuration = 60,
  tracks,
  timelineData,
  elements,
  clips,
  mediaItems,
}) => {
  // –ü–æ–ª—É—á–∞–µ–º –¥–∞–Ω–Ω—ã–µ timeline –∏–∑ —Ä–∞–∑–Ω—ã—Ö –∏—Å—Ç–æ—á–Ω–∏–∫–æ–≤
  const actualTimelineItems = React.useMemo(() => {
    const items = timelineItems || timelineData || elements || clips || mediaItems || [];
    
    console.log('üìã Timeline –¥–∞–Ω–Ω—ã–µ:', {
      –≤—Å–µ–≥–æ_—ç–ª–µ–º–µ–Ω—Ç–æ–≤: items.length,
      –≤–∏–¥–µ–æ: items.filter(i => i.trackType === 'main' || i.type === 'videos').length,
      overlay: items.filter(i => i.trackType === 'overlay').length,
      –∞—É–¥–∏–æ: items.filter(i => i.trackType === 'audio').length,
      –∏–∑–æ–±—Ä–∞–∂–µ–Ω–∏—è: items.filter(i => i.type === 'images').length
    });
    
    return items;
  }, [timelineItems, timelineData, elements, clips, mediaItems]);

  const [exportSettings, setExportSettings] = useState({
    resolution: "1280x720",
    fps: 30,
    quality: "high",
    filename: `capcut_export_${Date.now()}`
  });

  const [ffmpeg, setFfmpeg] = useState(null);
  const [isLoading, setIsLoading] = useState(false);
  const [isExporting, setIsExporting] = useState(false);
  const [exportProgress, setExportProgress] = useState(0);
  const [exportStage, setExportStage] = useState("idle");
  const [previewFrame, setPreviewFrame] = useState(null);
  const [error, setError] = useState(null);
  
  const canvasRef = useRef(null);
  const previewCanvasRef = useRef(null);

  // –†–∞—Å—Å—á–∏—Ç—ã–≤–∞–µ–º –æ–±—â—É—é –¥–ª–∏—Ç–µ–ª—å–Ω–æ—Å—Ç—å timeline
  const calculateTimelineDuration = () => {
    if (actualTimelineItems.length === 0) return videoDuration;
    
    const maxEndTime = Math.max(...actualTimelineItems.map(item => {
      const start = item.startTime || 0;
      const duration = item.duration || 0;
      return start + duration;
    }));
    
    const finalDuration = Math.max(maxEndTime, videoDuration);
    console.log(`‚è±Ô∏è –î–ª–∏—Ç–µ–ª—å–Ω–æ—Å—Ç—å timeline: ${finalDuration}s`);
    return finalDuration;
  };

  // –ò–Ω–∏—Ü–∏–∞–ª–∏–∑–∞—Ü–∏—è FFmpeg
  useEffect(() => {
    const initFFmpeg = async () => {
      if (ffmpeg || !isOpen) return;

      setIsLoading(true);
      setExportStage("loading");
      setError(null);
      
      try {
        const ffmpegModule = await import('@ffmpeg/ffmpeg');
        const utilModule = await import('@ffmpeg/util');
        
        const FFmpegClass = ffmpegModule.FFmpeg;
        const fetchFile = utilModule.fetchFile;
        
        const ffmpegInstance = new FFmpegClass();
        
        await ffmpegInstance.load({
          coreURL: 'https://unpkg.com/@ffmpeg/core@0.12.6/dist/umd/ffmpeg-core.js',
          wasmURL: 'https://unpkg.com/@ffmpeg/core@0.12.6/dist/umd/ffmpeg-core.wasm',
        });

        ffmpegInstance.on('progress', ({ progress }) => {
          if (isExporting) {
            const adjustedProgress = 70 + Math.round(progress * 25);
            setExportProgress(Math.min(adjustedProgress, 95));
          }
        });

        window.fetchFile = fetchFile;
        setFfmpeg(ffmpegInstance);
        setExportStage("ready");
        console.log('‚úÖ FFmpeg –≥–æ—Ç–æ–≤ –∫ —Ä–∞–±–æ—Ç–µ');
        
      } catch (error) {
        console.error('‚ùå –û—à–∏–±–∫–∞ –∏–Ω–∏—Ü–∏–∞–ª–∏–∑–∞—Ü–∏–∏ FFmpeg:', error);
        setError(`–û—à–∏–±–∫–∞ –∑–∞–≥—Ä—É–∑–∫–∏ FFmpeg: ${error.message}`);
        setExportStage("error");
      } finally {
        setIsLoading(false);
      }
    };

    if (isOpen) {
      initFFmpeg();
    }
  }, [isOpen]);

  // –ü–†–ê–í–ò–õ–¨–ù–û–ï –ø–æ–ª—É—á–µ–Ω–∏–µ –∞–∫—Ç–∏–≤–Ω—ã—Ö —ç–ª–µ–º–µ–Ω—Ç–æ–≤ –Ω–∞ –æ–ø—Ä–µ–¥–µ–ª–µ–Ω–Ω–æ–º –≤—Ä–µ–º–µ–Ω–∏
  const getActiveElementsAtTime = (currentTime) => {
    const activeElements = actualTimelineItems.filter(item => {
      const startTime = item.startTime || 0;
      const duration = item.duration || 0;
      const endTime = startTime + duration;
      
      // –≠–ª–µ–º–µ–Ω—Ç –∞–∫—Ç–∏–≤–µ–Ω –µ—Å–ª–∏ —Ç–µ–∫—É—â–µ–µ –≤—Ä–µ–º—è –ø–æ–ø–∞–¥–∞–µ—Ç –≤ –µ–≥–æ –¥–∏–∞–ø–∞–∑–æ–Ω
      const isActive = currentTime >= startTime && currentTime < endTime;
      
      if (isActive) {
        console.log(`‚úÖ –ê–∫—Ç–∏–≤–µ–Ω "${item.name}": ${startTime}s-${endTime}s`);
      }
      
      return isActive;
    });

    // –°–æ—Ä—Ç–∏—Ä—É–µ–º –ø–æ —Å–ª–æ—è–º: main (—Ñ–æ–Ω) ‚Üí overlay (–ø–æ–≤–µ—Ä—Ö) ‚Üí audio (–∏–≥–Ω–æ—Ä–∏—Ä—É–µ–º –ø—Ä–∏ —Ä–µ–Ω–¥–µ—Ä–µ)
    return activeElements.sort((a, b) => {
      const layerOrder = { main: 1, overlay: 2, audio: 3 };
      return (layerOrder[a.trackType] || 1) - (layerOrder[b.trackType] || 1);
    });
  };

  // –ö–õ–Æ–ß–ï–í–ê–Ø —Ñ—É–Ω–∫—Ü–∏—è —Ä–µ–Ω–¥–µ—Ä–∏–Ω–≥–∞ –∫–∞–¥—Ä–∞
  const renderTimelineFrame = async (ctx, currentTime, canvasWidth, canvasHeight) => {
    // –û—á–∏—â–∞–µ–º canvas —á–µ—Ä–Ω—ã–º —Ñ–æ–Ω–æ–º
    ctx.fillStyle = '#000000';
    ctx.fillRect(0, 0, canvasWidth, canvasHeight);

    const activeElements = getActiveElementsAtTime(currentTime);
    
    if (activeElements.length === 0) {
      // –ï—Å–ª–∏ –Ω–µ—Ç –∞–∫—Ç–∏–≤–Ω—ã—Ö —ç–ª–µ–º–µ–Ω—Ç–æ–≤, –ø–æ–∫–∞–∑—ã–≤–∞–µ–º —á–µ—Ä–Ω—ã–π —ç–∫—Ä–∞–Ω
      return;
    }

    // –†–µ–Ω–¥–µ—Ä–∏–º –∫–∞–∂–¥—ã–π –∞–∫—Ç–∏–≤–Ω—ã–π —ç–ª–µ–º–µ–Ω—Ç
    for (const element of activeElements) {
      if (element.trackType === 'audio') continue; // –ê—É–¥–∏–æ –Ω–µ —Ä–µ–Ω–¥–µ—Ä–∏–º
      
      try {
        await renderSingleElement(ctx, element, currentTime, canvasWidth, canvasHeight);
      } catch (error) {
        console.warn(`‚ö†Ô∏è –û—à–∏–±–∫–∞ —Ä–µ–Ω–¥–µ—Ä–∏–Ω–≥–∞ "${element.name}":`, error);
      }
    }
  };

  // –†–µ–Ω–¥–µ—Ä–∏–Ω–≥ –æ–¥–Ω–æ–≥–æ —ç–ª–µ–º–µ–Ω—Ç–∞
  const renderSingleElement = async (ctx, timelineElement, currentTime, canvasWidth, canvasHeight) => {
    const mediaElement = await loadMediaElement(timelineElement);
    if (!mediaElement) return;

    // –í—ã—á–∏—Å–ª—è–µ–º –æ—Ç–Ω–æ—Å–∏—Ç–µ–ª—å–Ω–æ–µ –≤—Ä–µ–º—è –≤–Ω—É—Ç—Ä–∏ —ç–ª–µ–º–µ–Ω—Ç–∞
    const elementStartTime = timelineElement.startTime || 0;
    const relativeTime = currentTime - elementStartTime;

    // –ù–∞—Å—Ç—Ä–∞–∏–≤–∞–µ–º –≤–∏–¥–µ–æ –Ω–∞ –Ω—É–∂–Ω–æ–µ –≤—Ä–µ–º—è
    if (mediaElement.tagName === 'VIDEO') {
      const trimStart = timelineElement.trimStart || 0;
      const targetTime = trimStart + relativeTime;
      
      if (Math.abs(mediaElement.currentTime - targetTime) > 0.1) {
        mediaElement.currentTime = Math.max(0, Math.min(targetTime, mediaElement.duration || 0));
        
        // –ñ–¥–µ–º –∑–∞–≥—Ä—É–∑–∫–∏ –∫–∞–¥—Ä–∞
        await new Promise(resolve => {
          const checkReady = () => {
            if (mediaElement.readyState >= 2) {
              resolve();
            } else {
              setTimeout(checkReady, 50);
            }
          };
          checkReady();
          setTimeout(resolve, 100); // –¢–∞–π–º–∞—É—Ç
        });
      }
    }

    // –ü–æ–ª—É—á–∞–µ–º —Ä–∞–∑–º–µ—Ä—ã –º–µ–¥–∏–∞ —ç–ª–µ–º–µ–Ω—Ç–∞
    const mediaWidth = mediaElement.videoWidth || mediaElement.naturalWidth || mediaElement.width;
    const mediaHeight = mediaElement.videoHeight || mediaElement.naturalHeight || mediaElement.height;

    if (!mediaWidth || !mediaHeight) {
      console.warn(`‚ö†Ô∏è –ù–µ—Ç —Ä–∞–∑–º–µ—Ä–æ–≤ –¥–ª—è "${timelineElement.name}"`);
      return;
    }

    // –ü–†–ê–í–ò–õ–¨–ù–û–ï –ø–æ–∑–∏—Ü–∏–æ–Ω–∏—Ä–æ–≤–∞–Ω–∏–µ –∫–∞–∫ –≤ CapCut
    let drawX, drawY, drawWidth, drawHeight;

    if (timelineElement.trackType === 'main') {
      // –û—Å–Ω–æ–≤–Ω–æ–µ –≤–∏–¥–µ–æ –∑–∞–ø–æ–ª–Ω—è–µ—Ç –≤–µ—Å—å —ç–∫—Ä–∞–Ω —Å —Å–æ—Ö—Ä–∞–Ω–µ–Ω–∏–µ–º –ø—Ä–æ–ø–æ—Ä—Ü–∏–π
      const canvasRatio = canvasWidth / canvasHeight;
      const mediaRatio = mediaWidth / mediaHeight;

      if (mediaRatio > canvasRatio) {
        // –ú–µ–¥–∏–∞ —à–∏—Ä–µ - –º–∞—Å—à—Ç–∞–±–∏—Ä—É–µ–º –ø–æ —à–∏—Ä–∏–Ω–µ
        drawWidth = canvasWidth;
        drawHeight = canvasWidth / mediaRatio;
        drawX = 0;
        drawY = (canvasHeight - drawHeight) / 2;
      } else {
        // –ú–µ–¥–∏–∞ –≤—ã—à–µ - –º–∞—Å—à—Ç–∞–±–∏—Ä—É–µ–º –ø–æ –≤—ã—Å–æ—Ç–µ
        drawHeight = canvasHeight;
        drawWidth = canvasHeight * mediaRatio;
        drawX = (canvasWidth - drawWidth) / 2;
        drawY = 0;
      }

    } else if (timelineElement.trackType === 'overlay') {
      // Overlay —ç–ª–µ–º–µ–Ω—Ç—ã –∏—Å–ø–æ–ª—å–∑—É—é—Ç —Ç–æ—á–Ω—ã–µ –∫–æ–æ—Ä–¥–∏–Ω–∞—Ç—ã –∏–∑ UI
      const uiX = timelineElement.x || 0;
      const uiY = timelineElement.y || 0;
      const uiWidth = timelineElement.width || 200;
      const uiHeight = timelineElement.height || 150;
      const uiScale = timelineElement.scale || 1;

      // –ú–∞—Å—à—Ç–∞–±–∏—Ä—É–µ–º –∫–æ–æ—Ä–¥–∏–Ω–∞—Ç—ã –æ—Ç UI –∫ —Ñ–∏–Ω–∞–ª—å–Ω–æ–º—É –≤–∏–¥–µ–æ
      // –ü—Ä–µ–¥–ø–æ–ª–∞–≥–∞–µ–º, —á—Ç–æ UI –∏–º–µ–µ—Ç —Ä–∞–∑–º–µ—Ä 1920x1080 (–º–æ–∂–Ω–æ –Ω–∞—Å—Ç—Ä–æ–∏—Ç—å)
      const UI_WIDTH = 1920;
      const UI_HEIGHT = 1080;
      
      const scaleX = canvasWidth / UI_WIDTH;
      const scaleY = canvasHeight / UI_HEIGHT;

      drawX = uiX * scaleX;
      drawY = uiY * scaleY;
      drawWidth = uiWidth * scaleX * uiScale;
      drawHeight = uiHeight * scaleY * uiScale;

      console.log(`üìç Overlay "${timelineElement.name}": UI(${uiX},${uiY},${uiWidth}x${uiHeight}) ‚Üí Canvas(${drawX.toFixed(1)},${drawY.toFixed(1)},${drawWidth.toFixed(1)}x${drawHeight.toFixed(1)})`);
    }

    // –ü—Ä–∏–º–µ–Ω—è–µ–º —Ç—Ä–∞–Ω—Å—Ñ–æ—Ä–º–∞—Ü–∏–∏
    ctx.save();

    // –ü–æ–≤–æ—Ä–æ—Ç
    if (timelineElement.rotation && timelineElement.rotation !== 0) {
      const centerX = drawX + drawWidth / 2;
      const centerY = drawY + drawHeight / 2;
      ctx.translate(centerX, centerY);
      ctx.rotate((timelineElement.rotation * Math.PI) / 180);
      ctx.translate(-centerX, -centerY);
    }

    // –ü—Ä–æ–∑—Ä–∞—á–Ω–æ—Å—Ç—å
    const opacity = timelineElement.opacity !== undefined ? timelineElement.opacity : 1;
    ctx.globalAlpha = Math.max(0, Math.min(1, opacity));

    // –†–ï–ù–î–ï–†–ò–ù–ì
    ctx.drawImage(mediaElement, drawX, drawY, drawWidth, drawHeight);

    ctx.restore();
  };

  // –ó–∞–≥—Ä—É–∑–∫–∞ –º–µ–¥–∏–∞ —ç–ª–µ–º–µ–Ω—Ç–∞
  const loadMediaElement = async (item) => {
    if (!item.url) {
      console.error(`‚ùå –ù–µ—Ç URL –¥–ª—è "${item.name}"`);
      return null;
    }

    return new Promise((resolve, reject) => {
      const timeout = setTimeout(() => {
        reject(new Error(`–¢–∞–π–º–∞—É—Ç –∑–∞–≥—Ä—É–∑–∫–∏ ${item.name}`));
      }, 5000);

      const isVideo = item.type === 'videos' || 
                     item.trackType === 'main' || 
                     item.name?.toLowerCase().match(/\.(mp4|webm|mov|avi)$/);

      if (isVideo) {
        const video = document.createElement('video');
        video.crossOrigin = 'anonymous';
        video.muted = true;
        video.preload = 'metadata';
        
        video.onloadedmetadata = () => {
          clearTimeout(timeout);
          resolve(video);
        };
        
        video.onerror = () => {
          clearTimeout(timeout);
          reject(new Error(`–û—à–∏–±–∫–∞ –∑–∞–≥—Ä—É–∑–∫–∏ –≤–∏–¥–µ–æ ${item.name}`));
        };
        
        video.src = item.url;
        
      } else {
        const img = new Image();
        img.crossOrigin = 'anonymous';
        
        img.onload = () => {
          clearTimeout(timeout);
          resolve(img);
        };
        
        img.onerror = () => {
          clearTimeout(timeout);
          reject(new Error(`–û—à–∏–±–∫–∞ –∑–∞–≥—Ä—É–∑–∫–∏ –∏–∑–æ–±—Ä–∞–∂–µ–Ω–∏—è ${item.name}`));
        };
        
        img.src = item.url;
      }
    });
  };

  // –ì–µ–Ω–µ—Ä–∞—Ü–∏—è –ø—Ä–µ–≤—å—é
  const generatePreview = async () => {
    try {
      const canvas = previewCanvasRef.current;
      if (!canvas) return;

      const ctx = canvas.getContext('2d');
      const [width, height] = exportSettings.resolution.split('x').map(Number);
      canvas.width = width;
      canvas.height = height;

      // –ü—Ä–µ–≤—å—é –Ω–∞ –≤—Ä–µ–º–µ–Ω–∏ 1 —Å–µ–∫—É–Ω–¥–∞ –∏–ª–∏ —Å–µ—Ä–µ–¥–∏–Ω–µ –ø–µ—Ä–≤–æ–≥–æ —ç–ª–µ–º–µ–Ω—Ç–∞
      const firstElement = actualTimelineItems.find(item => item.trackType === 'main');
      const previewTime = firstElement ? 
        (firstElement.startTime || 0) + ((firstElement.duration || 0) / 4) : 
        1;

      console.log(`üñºÔ∏è –ì–µ–Ω–µ—Ä–∏—Ä—É–µ–º –ø—Ä–µ–≤—å—é –Ω–∞ –≤—Ä–µ–º–µ–Ω–∏ ${previewTime}s`);

      await renderTimelineFrame(ctx, previewTime, width, height);
      
      canvas.toBlob((blob) => {
        if (blob) {
          const url = URL.createObjectURL(blob);
          setPreviewFrame(url);
        }
      });
    } catch (error) {
      console.warn('‚ö†Ô∏è –û—à–∏–±–∫–∞ —Å–æ–∑–¥–∞–Ω–∏—è –ø—Ä–µ–≤—å—é:', error);
    }
  };

  // –ì–µ–Ω–µ—Ä–∏—Ä—É–µ–º –ø—Ä–µ–≤—å—é –ø—Ä–∏ –∑–∞–≥—Ä—É–∑–∫–µ
  useEffect(() => {
    if (isOpen && actualTimelineItems.length > 0 && exportStage === "ready") {
      generatePreview();
    }
  }, [isOpen, actualTimelineItems, exportSettings.resolution, exportStage]);

  // –ë–´–°–¢–†–´–ô —ç–∫—Å–ø–æ—Ä—Ç timeline
  const startExport = async () => {
    if (!ffmpeg || isExporting || actualTimelineItems.length === 0) return;

    setIsExporting(true);
    setExportProgress(0);
    setExportStage("preparing");
    setError(null);

    try {
      const timelineDuration = calculateTimelineDuration();
      console.log(`üé¨ –ù–∞—á–∏–Ω–∞–µ–º —ç–∫—Å–ø–æ—Ä—Ç timeline: ${timelineDuration}s, ${actualTimelineItems.length} —ç–ª–µ–º–µ–Ω—Ç–æ–≤`);

      setExportStage("rendering_frames");
      const frames = await renderAllFrames(timelineDuration);
      setExportProgress(50);

      setExportStage("processing_audio");
      const audioFiles = await prepareAudioTracks();
      setExportProgress(60);

      setExportStage("encoding");
      const videoBlob = await encodeToMP4(frames, audioFiles, timelineDuration);
      setExportProgress(95);

      setExportStage("downloading");
      await downloadVideo(videoBlob);
      setExportProgress(100);

      setExportStage("completed");
      setTimeout(() => {
        setExportStage("ready");
        setExportProgress(0);
      }, 2000);

    } catch (error) {
      console.error('‚ùå –û—à–∏–±–∫–∞ —ç–∫—Å–ø–æ—Ä—Ç–∞:', error);
      setError(error.message);
      setExportStage("error");
    } finally {
      setIsExporting(false);
    }
  };

  // –û–ü–¢–ò–ú–ò–ó–ò–†–û–í–ê–ù–ù–´–ô —Ä–µ–Ω–¥–µ—Ä–∏–Ω–≥ –≤—Å–µ—Ö –∫–∞–¥—Ä–æ–≤
  const renderAllFrames = async (duration) => {
    const canvas = canvasRef.current;
    if (!canvas) throw new Error('Canvas –Ω–µ –Ω–∞–π–¥–µ–Ω');

    const ctx = canvas.getContext('2d');
    const [width, height] = exportSettings.resolution.split('x').map(Number);
    canvas.width = width;
    canvas.height = height;

    const fps = exportSettings.fps;
    const totalFrames = Math.ceil(duration * fps);
    const frames = [];

    console.log(`üéûÔ∏è –†–µ–Ω–¥–µ—Ä–∏–º ${totalFrames} –∫–∞–¥—Ä–æ–≤ (${fps} FPS)`);

    for (let frame = 0; frame < totalFrames; frame++) {
      const currentTime = frame / fps;
      
      if (currentTime >= duration) break;

      try {
        await renderTimelineFrame(ctx, currentTime, width, height);

        // –°–æ—Ö—Ä–∞–Ω—è–µ–º –∫–∞–¥—Ä –∫–∞–∫ JPEG –¥–ª—è —Å–∫–æ—Ä–æ—Å—Ç–∏
        const blob = await new Promise(resolve => {
          canvas.toBlob(resolve, 'image/jpeg', 0.8);
        });
        
        if (blob) {
          frames.push(blob);
        }

        // –û–±–Ω–æ–≤–ª—è–µ–º –ø—Ä–æ–≥—Ä–µ—Å—Å
        const progress = Math.round((frame / totalFrames) * 40);
        setExportProgress(10 + progress);

        // –ù–µ–±–æ–ª—å—à–∞—è –ø–∞—É–∑–∞ –∫–∞–∂–¥—ã–µ 60 –∫–∞–¥—Ä–æ–≤ –¥–ª—è –æ—Ç–∑—ã–≤—á–∏–≤–æ—Å—Ç–∏ UI
        if (frame % 60 === 0) {
          await new Promise(resolve => setTimeout(resolve, 1));
        }
        
      } catch (error) {
        console.error(`‚ùå –û—à–∏–±–∫–∞ —Ä–µ–Ω–¥–µ—Ä–∏–Ω–≥–∞ –∫–∞–¥—Ä–∞ ${frame}:`, error);
        // –ü—Ä–æ–¥–æ–ª–∂–∞–µ–º —Å —Å–ª–µ–¥—É—é—â–∏–º –∫–∞–¥—Ä–æ–º
      }
    }

    console.log(`‚úÖ –û—Ç—Ä–µ–Ω–¥–µ—Ä–µ–Ω–æ ${frames.length} –∫–∞–¥—Ä–æ–≤`);
    return frames;
  };

  // –ü–æ–¥–≥–æ—Ç–æ–≤–∫–∞ –∞—É–¥–∏–æ —Ç—Ä–µ–∫–æ–≤
  const prepareAudioTracks = async () => {
    const audioItems = actualTimelineItems.filter(item => item.trackType === 'audio');
    const audioFiles = [];

    for (const item of audioItems.slice(0, 3)) { // –ú–∞–∫—Å–∏–º—É–º 3 –∞—É–¥–∏–æ —Ç—Ä–µ–∫–∞
      try {
        const response = await fetch(item.url);
        const blob = await response.blob();
        
        audioFiles.push({
          blob,
          startTime: item.startTime || 0,
          duration: item.duration || 0,
          name: item.name
        });
        
        console.log(`üéµ –ê—É–¥–∏–æ –≥–æ—Ç–æ–≤–æ: ${item.name}`);
      } catch (error) {
        console.warn(`‚ö†Ô∏è –û—à–∏–±–∫–∞ –∑–∞–≥—Ä—É–∑–∫–∏ –∞—É–¥–∏–æ ${item.name}:`, error);
      }
    }

    return audioFiles;
  };

  // –ü–†–û–°–¢–û–ï –∏ –ë–´–°–¢–†–û–ï –∫–æ–¥–∏—Ä–æ–≤–∞–Ω–∏–µ –≤ MP4
  const encodeToMP4 = async (frames, audioFiles, duration) => {
    if (!window.fetchFile || frames.length === 0) {
      throw new Error('–ù–µ—Ç –∫–∞–¥—Ä–æ–≤ –¥–ª—è –∫–æ–¥–∏—Ä–æ–≤–∞–Ω–∏—è');
    }

    console.log(`üì¶ –ö–æ–¥–∏—Ä—É–µ–º ${frames.length} –∫–∞–¥—Ä–æ–≤ + ${audioFiles.length} –∞—É–¥–∏–æ`);

    // –û—á–∏—â–∞–µ–º —Å—Ç–∞—Ä—ã–µ —Ñ–∞–π–ª—ã
    await cleanupFiles();

    // –ó–∞–ø–∏—Å—ã–≤–∞–µ–º –∫–∞–¥—Ä—ã
    for (let i = 0; i < frames.length; i++) {
      const filename = `frame_${i.toString().padStart(6, '0')}.jpg`;
      await ffmpeg.writeFile(filename, await window.fetchFile(frames[i]));
    }

    // –ó–∞–ø–∏—Å—ã–≤–∞–µ–º –∞—É–¥–∏–æ
    for (let i = 0; i < audioFiles.length; i++) {
      const filename = `audio_${i}.mp3`;
      await ffmpeg.writeFile(filename, await window.fetchFile(audioFiles[i].blob));
    }

    // –ü–†–û–°–¢–ê–Ø –∫–æ–º–∞–Ω–¥–∞ FFmpeg –¥–ª—è –±—ã—Å—Ç—Ä–æ–≥–æ –∫–æ–¥–∏—Ä–æ–≤–∞–Ω–∏—è
    const args = [
      '-framerate', exportSettings.fps.toString(),
      '-i', 'frame_%06d.jpg',
      '-t', duration.toString()
    ];

    // –î–æ–±–∞–≤–ª—è–µ–º –ø–µ—Ä–≤—ã–π –∞—É–¥–∏–æ —Ç—Ä–µ–∫ (–ø—Ä–æ—Å—Ç–æ–µ —Ä–µ—à–µ–Ω–∏–µ)
    if (audioFiles.length > 0) {
      args.push('-i', 'audio_0.mp3');
      args.push('-c:a', 'aac');
      args.push('-b:a', '128k');
      args.push('-shortest');
    }

    // –û–ü–¢–ò–ú–ò–ó–ò–†–û–í–ê–ù–ù–´–ï –Ω–∞—Å—Ç—Ä–æ–π–∫–∏ H.264
    args.push(
      '-c:v', 'libx264',
      '-preset', 'fast',              // –ë—ã—Å—Ç—Ä–æ–µ –∫–æ–¥–∏—Ä–æ–≤–∞–Ω–∏–µ
      '-crf', exportSettings.quality === 'high' ? '20' : '25',
      '-pix_fmt', 'yuv420p',
      '-movflags', '+faststart',
      'output.mp4'
    );

    console.log('üîß FFmpeg –∫–æ–º–∞–Ω–¥–∞:', args.join(' '));

    await ffmpeg.exec(args);

    const data = await ffmpeg.readFile('output.mp4');
    const videoBlob = new Blob([data.buffer], { type: 'video/mp4' });

    console.log(`üéâ –í–∏–¥–µ–æ –≥–æ—Ç–æ–≤–æ: ${(videoBlob.size / 1024 / 1024).toFixed(2)} –ú–ë`);

    // –û—á–∏—â–∞–µ–º —Ñ–∞–π–ª—ã
    await cleanupFiles();
    
    return videoBlob;
  };

  // –û—á–∏—Å—Ç–∫–∞ –≤—Ä–µ–º–µ–Ω–Ω—ã—Ö —Ñ–∞–π–ª–æ–≤
  const cleanupFiles = async () => {
    const filesToClean = [
      'output.mp4',
      ...Array.from({length: 1000}, (_, i) => `frame_${i.toString().padStart(6, '0')}.jpg`),
      ...Array.from({length: 10}, (_, i) => `audio_${i}.mp3`)
    ];

    for (const file of filesToClean) {
      try {
        await ffmpeg.deleteFile(file);
      } catch (e) {
        // –ò–≥–Ω–æ—Ä–∏—Ä—É–µ–º –æ—à–∏–±–∫–∏ —É–¥–∞–ª–µ–Ω–∏—è
      }
    }
  };

  // –°–∫–∞—á–∏–≤–∞–Ω–∏–µ –≤–∏–¥–µ–æ
  const downloadVideo = async (blob) => {
    const filename = `${exportSettings.filename}.mp4`;
    const url = URL.createObjectURL(blob);
    const a = document.createElement('a');
    a.href = url;
    a.download = filename;
    document.body.appendChild(a);
    a.click();
    document.body.removeChild(a);
    URL.revokeObjectURL(url);
    
    console.log(`üíæ –í–∏–¥–µ–æ —Å–æ—Ö—Ä–∞–Ω–µ–Ω–æ: ${filename}`);
  };

  // –°—Ç–∞—Ç—É—Å —Ç–µ–∫—Å—Ç
  const getStatusText = () => {
    switch (exportStage) {
      case "loading": return "–ó–∞–≥—Ä—É–∑–∫–∞ FFmpeg...";
      case "preparing": return "–ü–æ–¥–≥–æ—Ç–æ–≤–∫–∞ timeline...";
      case "rendering_frames": return "–†–µ–Ω–¥–µ—Ä–∏–Ω–≥ –∫–∞–¥—Ä–æ–≤ timeline...";
      case "processing_audio": return "–û–±—Ä–∞–±–æ—Ç–∫–∞ –∞—É–¥–∏–æ —Ç—Ä–µ–∫–æ–≤...";
      case "encoding": return "–ë—ã—Å—Ç—Ä–æ–µ –∫–æ–¥–∏—Ä–æ–≤–∞–Ω–∏–µ MP4...";
      case "downloading": return "–°–æ—Ö—Ä–∞–Ω–µ–Ω–∏–µ –≤–∏–¥–µ–æ...";
      case "completed": return "‚úÖ –≠–∫—Å–ø–æ—Ä—Ç –∑–∞–≤–µ—Ä—à–µ–Ω —É—Å–ø–µ—à–Ω–æ!";
      case "error": return "‚ùå –ü—Ä–æ–∏–∑–æ—à–ª–∞ –æ—à–∏–±–∫–∞";
      case "ready": return `–ì–æ—Ç–æ–≤ –∫ —ç–∫—Å–ø–æ—Ä—Ç—É (${actualTimelineItems.length} —ç–ª–µ–º–µ–Ω—Ç–æ–≤ timeline)`;
      default: return "–û–∂–∏–¥–∞–Ω–∏–µ...";
    }
  };

  const canExport = ffmpeg && !isExporting && !isLoading && actualTimelineItems.length > 0 && exportStage === "ready";

  if (!isOpen) return null;

  return (
    <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50 p-4">
      <div className="bg-white rounded-2xl shadow-xl max-w-4xl w-full max-h-[95vh] overflow-hidden">
        {/* Header */}
        <div className="flex items-center justify-between p-6 border-b border-gray-200">
          <h2 className="text-xl font-semibold text-gray-800 flex items-center">
            <Film size={24} className="mr-3 text-blue-600" />
            üé¨ CapCut-Style Timeline Exporter
          </h2>
          <button
            onClick={onClose}
            className="p-2 hover:bg-gray-100 rounded-full transition-colors"
            disabled={isExporting}
          >
            <X size={20} />
          </button>
        </div>

        <div className="p-6 space-y-6 max-h-[calc(95vh-140px)] overflow-y-auto">
          
          {/* Timeline Status */}
          {actualTimelineItems.length === 0 ? (
            <div className="bg-red-50 border-2 border-red-300 rounded-xl p-6">
              <div className="flex items-start">
                <AlertCircle size={24} className="text-red-600 mr-3 mt-0.5" />
                <div>
                  <h4 className="font-bold text-red-800 text-lg mb-2">–ù–µ—Ç —ç–ª–µ–º–µ–Ω—Ç–æ–≤ timeline</h4>
                  <p className="text-red-700">
                    –î–æ–±–∞–≤—å—Ç–µ –≤–∏–¥–µ–æ, –∏–∑–æ–±—Ä–∞–∂–µ–Ω–∏—è –∏–ª–∏ –∞—É–¥–∏–æ –≤ timeline –¥–ª—è —ç–∫—Å–ø–æ—Ä—Ç–∞.
                  </p>
                </div>
              </div>
            </div>
          ) : (
            <div className="bg-green-50 border border-green-200 rounded-xl p-4">
              <h4 className="font-medium text-green-800 mb-2">üìã Timeline –≥–æ—Ç–æ–≤ –∫ —ç–∫—Å–ø–æ—Ä—Ç—É</h4>
              <div className="text-sm text-green-700 space-y-1">
                <p><strong>–í—Å–µ–≥–æ —ç–ª–µ–º–µ–Ω—Ç–æ–≤:</strong> {actualTimelineItems.length}</p>
                <p><strong>–û—Å–Ω–æ–≤–Ω—ã—Ö –≤–∏–¥–µ–æ:</strong> {actualTimelineItems.filter(i => i.trackType === 'main').length}</p>
                <p><strong>Overlay —ç–ª–µ–º–µ–Ω—Ç–æ–≤:</strong> {actualTimelineItems.filter(i => i.trackType === 'overlay').length}</p>
                <p><strong>–ê—É–¥–∏–æ —Ç—Ä–µ–∫–æ–≤:</strong> {actualTimelineItems.filter(i => i.trackType === 'audio').length}</p>
                <p><strong>–î–ª–∏—Ç–µ–ª—å–Ω–æ—Å—Ç—å:</strong> {calculateTimelineDuration().toFixed(1)} —Å–µ–∫—É–Ω–¥</p>
              </div>
            </div>
          )}

          {/* Error Display */}
          {error && (
            <div className="bg-red-50 border border-red-200 rounded-xl p-4">
              <div className="flex items-start">
                <AlertCircle size={20} className="text-red-600 mr-3 mt-0.5" />
                <div>
                  <h4 className="font-medium text-red-800">–û—à–∏–±–∫–∞ —ç–∫—Å–ø–æ—Ä—Ç–∞</h4>
                  <p className="text-sm text-red-600 mt-1">{error}</p>
                </div>
              </div>
            </div>
          )}

          {/* Export Status */}
          <div className={`rounded-xl p-4 border ${
            exportStage === "error" ? "bg-red-50 border-red-200" :
            exportStage === "completed" ? "bg-green-50 border-green-200" :
            exportStage === "loading" ? "bg-orange-50 border-orange-200" :
            "bg-blue-50 border-blue-200"
          }`}>
            <div className="flex items-center justify-between mb-3">
              <div className="flex items-center">
                {exportStage === "completed" ? (
                  <Check size={20} className="text-green-600" />
                ) : exportStage === "error" ? (
                  <AlertCircle size={20} className="text-red-600" />
                ) : exportStage === "ready" ? (
                  <Play size={20} className="text-blue-600" />
                ) : (
                  <div className="animate-spin rounded-full h-5 w-5 border-b-2 border-blue-600"></div>
                )}
                <span className="ml-3 font-medium">{getStatusText()}</span>
              </div>
              {(isExporting || isLoading) && (
                <span className="text-sm font-medium">{exportProgress}%</span>
              )}
            </div>
            {(isExporting || isLoading) && (
              <div className="w-full bg-gray-200 rounded-full h-2">
                <div
                  className="bg-blue-600 h-2 rounded-full transition-all duration-300"
                  style={{ width: `${exportProgress}%` }}
                />
              </div>
            )}
          </div>

          {/* Export Settings */}
          <div className="bg-gray-50 rounded-xl p-4">
            <h3 className="text-sm font-medium text-gray-700 mb-3 flex items-center">
              <Settings size={16} className="mr-2" />
              –ù–∞—Å—Ç—Ä–æ–π–∫–∏ —ç–∫—Å–ø–æ—Ä—Ç–∞
            </h3>
            <div className="grid grid-cols-2 gap-4">
              <div>
                <label className="block text-xs font-medium text-gray-700 mb-1">
                  –†–∞–∑—Ä–µ—à–µ–Ω–∏–µ
                </label>
                <select
                  value={exportSettings.resolution}
                  onChange={(e) => setExportSettings(prev => ({ ...prev, resolution: e.target.value }))}
                  className="w-full p-2 text-sm border border-gray-300 rounded-lg"
                  disabled={isExporting || isLoading}
                >
                  <option value="1920x1080">1080p (Full HD)</option>
                  <option value="1280x720">720p (HD)</option>
                  <option value="854x480">480p (SD)</option>
                </select>
              </div>

              <div>
                <label className="block text-xs font-medium text-gray-700 mb-1">
                  –ö–∞–¥—Ä/—Å–µ–∫
                </label>
                <select
                  value={exportSettings.fps}
                  onChange={(e) => setExportSettings(prev => ({ ...prev, fps: parseInt(e.target.value) }))}
                  className="w-full p-2 text-sm border border-gray-300 rounded-lg"
                  disabled={isExporting || isLoading}
                >
                  <option value="24">24 FPS</option>
                  <option value="30">30 FPS</option>
                  <option value="60">60 FPS</option>
                </select>
              </div>

              <div>
                <label className="block text-xs font-medium text-gray-700 mb-1">
                  –ö–∞—á–µ—Å—Ç–≤–æ
                </label>
                <select
                  value={exportSettings.quality}
                  onChange={(e) => setExportSettings(prev => ({ ...prev, quality: e.target.value }))}
                  className="w-full p-2 text-sm border border-gray-300 rounded-lg"
                  disabled={isExporting || isLoading}
                >
                  <option value="high">–í—ã—Å–æ–∫–æ–µ (CRF 20)</option>
                  <option value="medium">–°—Ä–µ–¥–Ω–µ–µ (CRF 25)</option>
                </select>
              </div>

              <div>
                <label className="block text-xs font-medium text-gray-700 mb-1">
                  –ò–º—è —Ñ–∞–π–ª–∞
                </label>
                <input
                  type="text"
                  value={exportSettings.filename}
                  onChange={(e) => setExportSettings(prev => ({ ...prev, filename: e.target.value }))}
                  className="w-full p-2 text-sm border border-gray-300 rounded-lg"
                  disabled={isExporting || isLoading}
                />
              </div>
            </div>
          </div>

          {/* Preview */}
          {exportStage === "ready" && actualTimelineItems.length > 0 && (
            <div className="bg-gray-50 rounded-xl p-4">
              <h3 className="text-sm font-medium text-gray-700 mb-3">–ü—Ä–µ–≤—å—é timeline</h3>
              <div className="flex gap-4">
                <div className="flex-1">
                  <div className="bg-black rounded-lg aspect-video flex items-center justify-center overflow-hidden relative">
                    {previewFrame ? (
                      <img 
                        src={previewFrame} 
                        alt="Preview" 
                        className="max-w-full max-h-full object-contain"
                      />
                    ) : (
                      <div className="text-white text-sm">–°–æ–∑–¥–∞–Ω–∏–µ –ø—Ä–µ–≤—å—é...</div>
                    )}
                    
                    <button
                      onClick={generatePreview}
                      className="absolute top-2 right-2 px-2 py-1 bg-blue-600 text-white text-xs rounded hover:bg-blue-700"
                    >
                      –û–±–Ω–æ–≤–∏—Ç—å
                    </button>
                  </div>
                </div>
                <div className="w-48 space-y-2 text-sm">
                  <div className="flex justify-between">
                    <span className="text-gray-600">–†–∞–∑—Ä–µ—à–µ–Ω–∏–µ:</span>
                    <span className="font-medium">{exportSettings.resolution}</span>
                  </div>
                  <div className="flex justify-between">
                    <span className="text-gray-600">FPS:</span>
                    <span className="font-medium">{exportSettings.fps}</span>
                  </div>
                  <div className="flex justify-between">
                    <span className="text-gray-600">–ö–∞—á–µ—Å—Ç–≤–æ:</span>
                    <span className="font-medium">{exportSettings.quality}</span>
                  </div>
                  <div className="flex justify-between">
                    <span className="text-gray-600">–î–ª–∏—Ç–µ–ª—å–Ω–æ—Å—Ç—å:</span>
                    <span className="font-medium">{calculateTimelineDuration().toFixed(1)}s</span>
                  </div>
                </div>
              </div>
            </div>
          )}
        </div>

        {/* Footer */}
        <div className="flex justify-between items-center p-6 border-t border-gray-200 bg-gray-50">
          <div className="text-xs text-gray-500">
            {exportStage === "ready" && actualTimelineItems.length > 0 ? (
              `Timeline –≥–æ—Ç–æ–≤ ‚Ä¢ ${exportSettings.resolution} ‚Ä¢ ${exportSettings.fps} FPS ‚Ä¢ ${exportSettings.quality} –∫–∞—á–µ—Å—Ç–≤–æ`
            ) : exportStage === "loading" ? (
              '–ò–Ω–∏—Ü–∏–∞–ª–∏–∑–∞—Ü–∏—è...'
            ) : (
              '–î–æ–±–∞–≤—å—Ç–µ —ç–ª–µ–º–µ–Ω—Ç—ã –≤ timeline'
            )}
          </div>
          <div className="flex space-x-3">
            <button
              onClick={onClose}
              className="px-6 py-2 text-sm text-gray-600 hover:text-gray-800 transition-colors"
              disabled={isExporting}
            >
              –ó–∞–∫—Ä—ã—Ç—å
            </button>
            <button
              onClick={startExport}
              disabled={!canExport}
              className={`px-6 py-2 text-sm rounded-lg font-medium transition-all flex items-center ${
                !canExport
                  ? "bg-gray-300 text-gray-500 cursor-not-allowed"
                  : "bg-gradient-to-r from-blue-600 to-purple-600 text-white hover:from-blue-700 hover:to-purple-700 shadow-lg"
              }`}
            >
              {isExporting ? (
                <>
                  <div className="animate-spin rounded-full h-4 w-4 border-b-2 border-white mr-2"></div>
                  –≠–∫—Å–ø–æ—Ä—Ç... {exportProgress}%
                </>
              ) : isLoading ? (
                <>
                  <div className="animate-spin rounded-full h-4 w-4 border-b-2 border-white mr-2"></div>
                  –ó–∞–≥—Ä—É–∑–∫–∞...
                </>
              ) : (
                <>
                  <Download size={16} className="mr-2" />
                  –≠–∫—Å–ø–æ—Ä—Ç Timeline
                </>
              )}
            </button>
          </div>
        </div>

        {/* Hidden canvases */}
        <canvas ref={canvasRef} style={{ display: 'none' }} />
        <canvas ref={previewCanvasRef} style={{ display: 'none' }} />
      </div>
    </div>
  );
};

export const useFFmpegExporter = () => {
  const [isExportModalOpen, setIsExportModalOpen] = useState(false);

  const openExportModal = () => setIsExportModalOpen(true);
  const closeExportModal = () => setIsExportModalOpen(false);

  return {
    isExportModalOpen,
    openExportModal,
    closeExportModal,
  };
};

export default FFmpegVideoExporter;
